INSERT INTO birthday_login_bonus_drama_script VALUES(1110501046,1110501,11,'111011','虽说蜡烛很好，但这底座的部分——\n不，是名为生日蛋糕的祝福\n文化吗。嗯，谢谢你的好意，\n但是对于食量小的人来说，这个尺寸有点近似于拷问了……','4','1','1','0','0','');
