INSERT INTO unit_comments VALUES(1001001,100101,1,1,1,4.0,2,'只要大家开心，我就开心！\n所以，来，笑一笑！');
INSERT INTO unit_comments VALUES(1001002,100101,1,2,1,3.0,2,'有困难时要互相帮助！\n有什么事就找我商量吧！\n我应该能帮得上忙的！');
INSERT INTO unit_comments VALUES(1001003,100101,1,3,1,7.5,2,'无论什么时候都要活力十足！\n累了的时候要好好休息哦？\n我……还很精神呢。');
INSERT INTO unit_comments VALUES(1001004,100101,1,4,5,6.0,2,'其实啊……\n和你在一起……很开心！\n诶嘿嘿……谢谢你♪');
INSERT INTO unit_comments VALUES(1001005,100101,1,5,4,6.0,6,'怎么回事呢……\n时不时会觉得胸口热乎乎的，\n难道是感冒了！？');
INSERT INTO unit_comments VALUES(1001006,100131,1,1,1,0.0,1,'我要变强！\n我要和大家一起……\n一直开心下去！');
INSERT INTO unit_comments VALUES(1001007,100131,1,2,1,0.0,1,'即使伤痕累累也没关系！\n因为有人在背后支撑着我……\n无论多少次，我都会站起来的！');
INSERT INTO unit_comments VALUES(1001008,100131,1,3,1,0.0,1,'有骑士先生待在身边，\n我就觉得自己精神百倍！\n……是不是有点夸张了？');
INSERT INTO unit_comments VALUES(1001009,100131,1,4,1,0.0,1,'我能听到……骑士先生的声音哦。\n无论相隔多远，\n我都能听出来！');
INSERT INTO unit_comments VALUES(1001010,100131,1,5,1,0.0,1,'无论是什么样的对手，\n现在的我都不会输！\n因为那个人在支持着我！');
INSERT INTO unit_comments VALUES(1001011,100101,2,6,1,11.3,2,'生日快乐，骑士先生！\n你想要什么礼物？\n料理？装备？还是……\n对了！我来陪你修行吧！');
INSERT INTO unit_comments VALUES(1001012,100131,2,6,1,0.0,1,'生日快乐！\n这个给你！猫猫护身符！\n当我不在你身边的时候，\n这个一定会代替我保护你！');
INSERT INTO unit_comments VALUES(1002001,100201,1,1,4,6.0,5,'那、那个……\n和我聊天，真的没关系吗？\n是吗，谢谢你！');
INSERT INTO unit_comments VALUES(1002002,100201,1,2,2,4.0,5,'如果要战斗，\n治疗和支援就交给我吧！\n至于攻击，就一起加油……吧');
INSERT INTO unit_comments VALUES(1002003,100201,1,3,4,4.0,8,'拜托了……\n别那样一直盯着我看……\n好、好害羞啊……啊呜……');
INSERT INTO unit_comments VALUES(1002004,100201,1,4,1,4.0,2,'肚子饿不饿？\n要是想吃饭就告诉我哦\n简单的食物的话我是能马上做出来的');
INSERT INTO unit_comments VALUES(1002005,100201,1,5,1,3.0,5,'需要我帮忙吗？\n要去哪里的话\n就把我也带上吧……');
INSERT INTO unit_comments VALUES(1002006,100231,1,1,1,0.0,1,'要是有什么我能帮忙的，\n尽管说哦\n如果是小伤\n我马上就能治好');
INSERT INTO unit_comments VALUES(1002007,100231,1,2,1,0.0,1,'只要是为了骑士君\n我……愿意\n更加、更加努力！');
INSERT INTO unit_comments VALUES(1002008,100231,1,3,1,0.0,1,'给予你治愈的光辉！\n只是看着就觉得被治愈了？\n才没有……总觉得\n好害羞啊……');
INSERT INTO unit_comments VALUES(1002009,100231,1,4,1,0.0,1,'要学什么样的魔法呢……\n两情相悦的魔法……\n那、那种魔法才不可能有对吧！');
INSERT INTO unit_comments VALUES(1002010,100231,1,5,1,0.0,1,'新学会的魔法呀，\n有防火、防寒、耐热……还有\n防止鱼骨刺到喉咙的\n魔法……要保密哦？');
INSERT INTO unit_comments VALUES(1002011,100201,2,6,1,7.9,5,'骑士君。生日快乐\n呃……这是生日的蛋糕！\n那个……不介意的话，请尝尝吧！\n嗯……今后也请多指教！');
INSERT INTO unit_comments VALUES(1002012,100231,2,6,1,0.0,1,'生日快乐，骑士君\n今天除了蛋糕\n我还准备了各种食物\n大家一起庆祝吧！');
INSERT INTO unit_comments VALUES(1003001,100301,1,1,1,4.0,2,'若是为了达到目的，\n我不惜付出任何努力，\n希望你也有同样的决心');
INSERT INTO unit_comments VALUES(1003002,100301,1,2,2,4.5,6,'关于下次的修行\n我想和你商量一下，可以吗？\n地点要选在哪里呢……');
INSERT INTO unit_comments VALUES(1003003,100301,1,3,1,4.5,2,'想要变强……\n只要坚守这个信念\n你一定也能变强');
INSERT INTO unit_comments VALUES(1003004,100301,1,4,6,2.0,2,'为什么呢……\n和你比剑\n就像是在跳舞一样');
INSERT INTO unit_comments VALUES(1003005,100301,1,5,1,4.5,2,'下次要不要一起去钓鱼？\n虽然一个人也不会无聊\n但我想和你一起去');
INSERT INTO unit_comments VALUES(1003006,100331,1,1,1,0.0,1,'来，准备好！\n你不是要一招取胜吗？\n没时间悠哉游哉了！');
INSERT INTO unit_comments VALUES(1003007,100331,1,2,1,0.0,1,'那决不放弃的眼神……\n你果然很厉害\n我的眼光没错');
INSERT INTO unit_comments VALUES(1003008,100331,1,3,1,0.0,1,'你会越来越强\n这应该不是你的极限\n证明给我看吧');
INSERT INTO unit_comments VALUES(1003009,100331,1,4,1,0.0,1,'（好奇妙的感觉……\n虽然希望能一直持续下去，\n却不能那么做）');
INSERT INTO unit_comments VALUES(1003010,100331,1,5,1,0.0,1,'我虽然说过\n什么都会听你的……可是不能\n让我做羞耻的事哦？');
INSERT INTO unit_comments VALUES(1003011,100301,2,6,2,2.6,5,'生日快乐\n其实我不太清楚\n送什么礼物你会高兴……不介意的话，\n我们一起去选吧？');
INSERT INTO unit_comments VALUES(1003012,100331,2,6,1,0.0,1,'今天是你的生日啊。生日快乐\n难得你过生日\n今天就暂停修行一天吧……咦？\n为什么你看起来不太高兴？');
INSERT INTO unit_comments VALUES(1004001,100401,1,1,1,2.5,2,'啊，是哥哥！\n喂喂，你要和未奏希玩什么游戏？');
INSERT INTO unit_comments VALUES(1004002,100401,1,2,4,4.0,4,'呜~用来恶作剧的超辣糖果，\n不小心自己吃掉了……');
INSERT INTO unit_comments VALUES(1004003,100401,1,3,1,6.0,6,'好~恶作剧设置完毕！\n接下来就是引哥哥上钩……\n啊……难道你看到了？');
INSERT INTO unit_comments VALUES(1004004,100401,1,4,1,4.5,2,'未奏希负责准备恶作剧……\n哥哥负责上钩……\n嗯，真是最棒的组合！');
INSERT INTO unit_comments VALUES(1004005,100401,1,5,5,4.0,2,'哥哥，以后还要一起玩哦。\n约好了！\n来，拉钩♪');
INSERT INTO unit_comments VALUES(1004006,100431,1,1,1,0.0,1,'未奏希探险队，全体集合！\n其实只有未奏希和哥哥\n两人而已啦');
INSERT INTO unit_comments VALUES(1004007,100431,1,2,1,0.0,1,'今后也能尽情\n捉弄哥哥呢！\n啊，未奏希是想说\n和哥哥一起玩哦？');
INSERT INTO unit_comments VALUES(1004008,100431,1,3,1,0.0,1,'哥哥！\n快点来探险吧！\n未奏希一直在等\n哥哥哦！');
INSERT INTO unit_comments VALUES(1004009,100431,1,4,1,0.0,1,'为了最喜欢的哥哥，\n要准备很多\n恶作剧才行！');
INSERT INTO unit_comments VALUES(1004010,100431,1,5,1,0.0,1,'未奏希呀，好像真的\n有了哥哥一样，\n很开心哦♪');
INSERT INTO unit_comments VALUES(1004011,100401,2,6,1,7.6,2,'生日快乐，哥哥！\n给你，这是生日礼物！\n还有，生日蛋糕！\n快点打开！快尝尝！好激动啊');
INSERT INTO unit_comments VALUES(1004012,100431,2,6,1,0.0,1,'生日快乐！未奏希\n最喜欢哥哥了！真的哦！\n唔，因为总是捉弄你，\n所以你都不相信未奏希了呀……');
INSERT INTO unit_comments VALUES(1005001,100501,1,1,1,4.6,2,'如果遇到了困难，一定\n要呼唤我！\n虽然我只是实习骑士，\n但我有一颗当英雄的心！');
INSERT INTO unit_comments VALUES(1005002,100501,1,2,1,3.2,2,'我们一起去打败邪恶组织吧！\n即使是半吊子，\n只要两人一起，就能顶一个人用！');
INSERT INTO unit_comments VALUES(1005003,100501,1,3,1,8.8,4,'爸爸和妈妈总是\n把我打扮得过于可爱\n又是蝴蝶结又是蕾丝的……但是\n我比较喜欢简约的服装');
INSERT INTO unit_comments VALUES(1005004,100501,1,4,1,4.6,5,'偶尔想和你\n单独去安静的地方\n即便是英雄，也是\n需要休息的！');
INSERT INTO unit_comments VALUES(1005005,100501,1,5,4,9.8,5,'虽然很想一直和你\n一起玩……但是\n变得更要好了后，万一秘密……\n没、没有，没什么！');
INSERT INTO unit_comments VALUES(1005006,100531,1,1,1,0.0,1,'怎么样！\n只要拥有直面恶势力的勇气\n正义必将取得胜利！');
INSERT INTO unit_comments VALUES(1005007,100531,1,2,1,0.0,1,'唔噢噢噢！兴奋起来了！\n誓要成为真正的超级英雄\n突击！');
INSERT INTO unit_comments VALUES(1005008,100531,1,3,1,0.0,1,'若是为了世界和平\n无论面对任何敌人，我都不会输的！\n英雄决不食言！');
INSERT INTO unit_comments VALUES(1005009,100531,1,4,1,0.0,1,'有你给予的勇气\n我一定能成功地变身为\n最强的英雄！');
INSERT INTO unit_comments VALUES(1005010,100531,1,5,1,0.0,1,'和你一起的话就会涌出百倍的勇气，\n不对，是百万倍！\n以后也要一起加油哦！');
INSERT INTO unit_comments VALUES(1005011,100501,2,6,1,5.3,2,'诶、今天是你生日！？\n那，我把自己珍藏的猛虎周边\n送给你当生日礼物！\n这是我们关系要好的见证！');
INSERT INTO unit_comments VALUES(1005012,100531,2,6,1,0.0,1,'生日快乐♪\n送给一直照顾我的你\n猛虎纸杯蛋糕！\n我第一次亲手做了蛋糕哦');
INSERT INTO unit_comments VALUES(1006001,100601,1,1,5,3.5,2,'哥哥，\n待会儿你还能陪我\n练习萨克斯吗？');
INSERT INTO unit_comments VALUES(1006002,100601,1,2,4,5.0,5,'哈啊，哈啊……\n已经练习不下去了……\n哥哥的训练强度太高了……');
INSERT INTO unit_comments VALUES(1006003,100601,1,3,6,3.0,4,'诶？还要继续练习？\n今天我已经好累啦……\n我已经不行了，哥哥……');
INSERT INTO unit_comments VALUES(1006004,100601,1,4,5,5.5,2,'哥哥真是的\n总是这么高强度，我……\n心跳得好厉害呢……');
INSERT INTO unit_comments VALUES(1006005,100601,1,5,3,4.5,4,'那个啊，我和姐姐之中\n哥哥觉得哪一个……\n不，没什么……');
INSERT INTO unit_comments VALUES(1006006,100631,1,1,1,0.0,1,'呵呵，哥哥快看！\n这是伴随着爵士乐旋律的\n茜里式钢管舞！');
INSERT INTO unit_comments VALUES(1006007,100631,1,2,1,0.0,1,'哥哥，你就那么\n想看吗……\n那、那……虽然很害羞\n我准备好了！茜里大回环！');
INSERT INTO unit_comments VALUES(1006008,100631,1,3,1,0.0,1,'哈啊，哈啊……已经不行了……\n哥哥一夸我，\n我就不由自主\n得意忘形起来，转得过头了♪');
INSERT INTO unit_comments VALUES(1006009,100631,1,4,1,0.0,1,'头、头好晕呀~\n哥哥抱♪\n是你把茜里弄成这样的……\n要负起责任哦！');
INSERT INTO unit_comments VALUES(1006010,100631,1,5,1,0.0,1,'茜里的这副模样，可不是\n谁都能看到的哦？\n哥哥是特例，\n所以想让你看到茜里的一切♪');
INSERT INTO unit_comments VALUES(1006011,100601,2,6,1,5.9,5,'啊！哥哥你真是的，\n又长大了呀\n茜里\n好像紧张起来了♪');
INSERT INTO unit_comments VALUES(1006012,100631,2,6,1,0.0,1,'哥哥，你今天过生日对吧\n生日快乐！\n那就和茜里一起，\n走进大人的世界吧♪');
INSERT INTO unit_comments VALUES(1007001,100701,1,1,1,4.5,2,'1是布丁，2是布丁，\n3、4也是布丁，\n5当然也是布丁！');
INSERT INTO unit_comments VALUES(1007002,100701,1,2,1,6.0,2,'宫子喜欢在天上飞~\n也喜欢恶作剧~\n可是呀，宫子更喜欢布丁~');
INSERT INTO unit_comments VALUES(1007003,100701,1,3,7,5.0,6,'被魔物追赶，\n还以为自己要死了~\n……嗯，仔细想想，\n宫子早就死啦！');
INSERT INTO unit_comments VALUES(1007004,100701,1,4,5,7.0,6,'宫子是幽灵哦~\n所以，即使你死了\n也能一直在一起♪\n嗯？你不高兴吗？');
INSERT INTO unit_comments VALUES(1007005,100701,1,5,2,3.0,5,'今天要一起出门~\n所以宫子今天会一直\n附在你身上~');
INSERT INTO unit_comments VALUES(1007006,100731,1,1,1,0.0,1,'要买来更多更多更多更多\n布丁才行！\n不然的话，\n宫子可能就要成佛了');
INSERT INTO unit_comments VALUES(1007007,100731,1,2,1,0.0,1,'宫子的布丁，\n绝对不给你~！\n要是你敢偷吃一口，\n连做梦宫子都要缠着你！');
INSERT INTO unit_comments VALUES(1007008,100731,1,3,1,0.0,1,'幽灵是吓人的存在~\n你也给我再害怕一点啊！');
INSERT INTO unit_comments VALUES(1007009,100731,1,4,1,0.0,1,'唔！宫子和布丁，\n哪一个更重要！？\n两个都重要？\n那就好~');
INSERT INTO unit_comments VALUES(1007010,100731,1,5,1,0.0,1,'只有宫子是幽灵，太不公平了！\n你也死掉当幽灵吧！\n那样一来，就能\n永远在一起了~');
INSERT INTO unit_comments VALUES(1007011,100701,2,6,1,5.8,2,'宫子虽然死了，\n但很重视生日庆祝哦！\n所以快点开始\n生日布丁派对吧♪');
INSERT INTO unit_comments VALUES(1007012,100731,2,6,1,0.0,1,'生日快乐！生日这天啊，\n就是要一起开心地吃好吃的\n所以今天和宫子一起\n来吃布丁蛋糕吧♪');
INSERT INTO unit_comments VALUES(1008001,100801,1,1,1,7.0,2,'啊，来得正好！\n我刚才找到了\n看起来会更可爱的角度！\n你也来看看！');
INSERT INTO unit_comments VALUES(1008002,100801,1,2,5,6.5,2,'我的这份美丽……\n有朝一日会被称为\n“行走的观光名胜”吗？\n要做本观光手册才行！');
INSERT INTO unit_comments VALUES(1008003,100801,1,3,6,7.0,1,'啊，你来得正好！\n我不知道要如何停止\n发散我的魅力！\n快阻止我！不，别阻止我！');
INSERT INTO unit_comments VALUES(1008004,100801,1,4,5,4.5,2,'能和如此可爱的我在一起，\n你要感到开心哦？\n你可是世界上最幸福的人啊♪');
INSERT INTO unit_comments VALUES(1008005,100801,1,5,5,4.0,2,'我是想和你在一起\n才会在这里\n你并不是什么\n陪衬角色，放心吧！');
INSERT INTO unit_comments VALUES(1008006,100831,1,1,1,0.0,1,'无论怎样的宝石和宝物……\n都无法胜过\n我的美丽与光芒……\n我可真是罪孽深重啊♪');
INSERT INTO unit_comments VALUES(1008007,100831,1,2,1,0.0,1,'啊啊，我可真美♪\n要描述我的美……\n不，用语言是无法描述的！');
INSERT INTO unit_comments VALUES(1008008,100831,1,3,1,0.0,1,'不及我美丽，\n并不是值得羞愧的事\n因为这种事情是理所当然的嘛，\n根本用不着说出来♪');
INSERT INTO unit_comments VALUES(1008009,100831,1,4,1,0.0,1,'如果说，将比任何宝石\n都更有价值的我……\n作为礼物\n送给你，你会高兴吗？');
INSERT INTO unit_comments VALUES(1008010,100831,1,5,1,0.0,1,'有我这样的宝物\n存在于世……有与你一起\n创造的心跳回忆……\n世界充满了希望呢');
INSERT INTO unit_comments VALUES(1008011,100801,2,6,1,9.9,2,'生日快乐♪\n今天的你看起来光芒四射\n既俊丽又美丽，既华丽又壮丽\n你一定是世界上第二美的人！');
INSERT INTO unit_comments VALUES(1008012,100831,2,6,1,0.0,1,'Happy birthday♪\n今天你可以独占\n我的美哦♪\n你能承受得住吗？');
INSERT INTO unit_comments VALUES(1009001,100901,1,1,1,3.5,2,'你也要快点找回\n前世的记忆！我的宿敌兼\n盟友，闪光的吉格鲁特！');
INSERT INTO unit_comments VALUES(1009002,100901,1,2,1,2.5,2,'记住我的真名了吗？\n我名为安妮罗洁·冯·\n修特帕鲁！');
INSERT INTO unit_comments VALUES(1009003,100901,1,3,4,5.0,3,'咕！封印着魔力的\n“眼瞳”好痛！\n眼药水？不需要！');
INSERT INTO unit_comments VALUES(1009004,100901,1,4,1,5.5,2,'吉格鲁特哟，即使在现世\n我们的盟友之情也不会消失！\n哈~哈哈哈！');
INSERT INTO unit_comments VALUES(1009005,100901,1,5,1,5.0,5,'我们二人齐心协力，\n粉碎“机关”的野心吧！\n而在功成之际，我们就像前世一样\n缔结夫妻的誓约……');
INSERT INTO unit_comments VALUES(1009006,100931,1,1,1,0.0,1,'将其刻入眼中，印入灵魂吧！\n这就是我禁忌的\n究极毁灭魔法！');
INSERT INTO unit_comments VALUES(1009007,100931,1,2,1,0.0,1,'如果你也有招式\n就应该帅气地使出来！\n比方说……');
INSERT INTO unit_comments VALUES(1009008,100931,1,3,1,0.0,1,'当我那流转着不竭魔力的\n眼瞳散发光芒之时\n敌人便会命绝于此！');
INSERT INTO unit_comments VALUES(1009009,100931,1,4,1,0.0,1,'给我仔细看看这舍弃防御、强化攻击的\n“歼灭形态”吧！\n你、你在看哪里！？');
INSERT INTO unit_comments VALUES(1009010,100931,1,5,1,0.0,1,'吉格鲁特……多亏了你\n我才能一直\n贯彻自己的信念……谢谢');
INSERT INTO unit_comments VALUES(1009011,100901,2,6,1,6.8,2,'今天是你转生之日啊\n作为纪念，我就授予你此物吧！\n看！暗黑骑士的徽章！\n如何，很帅气吧？');
INSERT INTO unit_comments VALUES(1009012,100931,2,6,1,0.0,1,'吉格鲁特，生日快乐！\n我送你一册日记本作为礼物！\n好好记录我们的活动吧！\n你……会写字吧？');
INSERT INTO unit_comments VALUES(1010001,101001,1,1,1,5.5,2,'王子先生、王子先生\n要不要喝点茶汤？\n茶汤就是热茶哦！');
INSERT INTO unit_comments VALUES(1010002,101001,1,2,1,6.0,2,'女孩子都有一个梦想\n那就是和命中注定的王子先生\n一起住在梦之国里♪');
INSERT INTO unit_comments VALUES(1010003,101001,1,3,5,5.5,2,'在古今中外的绘本里，\n保护公主，向来都是\n王子的使命哦？');
INSERT INTO unit_comments VALUES(1010004,101001,1,4,1,4.5,2,'如果你受伤了，\n就来找我吧\n我有特别的咒语哦\n痛痛飞走吧~');
INSERT INTO unit_comments VALUES(1010005,101001,1,5,1,5.5,2,'早上醒来之后，\n我会先和小鸟们\n道早安~');
INSERT INTO unit_comments VALUES(1010006,101031,1,1,1,0.0,1,'“咕噜灵波☆”啊呀呀，\n这里有洞窟呢\n不过我已经用魔法将它点亮了，\n而且很漂亮吧？');
INSERT INTO unit_comments VALUES(1010007,101031,1,2,1,0.0,1,'诶、要牵手吗？\n谢谢，我很开心……\n那我就照亮王子\n脚下的道路，我们并肩前行吧♪');
INSERT INTO unit_comments VALUES(1010008,101031,1,3,1,0.0,1,'王子先生的道路\n由我来照亮~\n所以，王子先生就放心地\n走在我身边吧~');
INSERT INTO unit_comments VALUES(1010009,101031,1,4,1,0.0,1,'和王子先生一起的话，不管在哪\n都是童话世界呀~\n来吧，王子先生，带着真步公主\n去探险吧♪');
INSERT INTO unit_comments VALUES(1010010,101031,1,5,1,0.0,1,'要一直一起走哦\n哪怕偶尔会迷路\n只要我们两个在一起，就不用担心\n对吧，真步公主的王子先生？♪');
INSERT INTO unit_comments VALUES(1010011,101001,2,6,2,4.0,5,'祝你生日快乐♪\n能和王子先生一起\n庆祝生日，真是太幸福了♪\n今后也和和美美地共度吧~');
INSERT INTO unit_comments VALUES(1010012,101031,2,6,1,0.0,1,'今天是你的生日吗？\n那可真是值得庆贺啊~\n我都想把今天定为\n真步真步王国的纪念日了♪');
INSERT INTO unit_comments VALUES(1010013,101061,1,1,1,0.0,1,'さ～て、みんなを\nマホマホ王国にご招待や～\nみらくるまほりんくるりんぱ♪\nも一つおまけにくるりんぱ～♪');
INSERT INTO unit_comments VALUES(1010014,101061,1,2,1,0.0,1,'マホマホ王国のパレード開始や～\n王子はんも、\nマホマホ王国の王子として、\nみんなと一緒に楽しんでな♪');
INSERT INTO unit_comments VALUES(1010015,101061,1,3,1,0.0,1,'これがうちと王子はんの力…\nこないなことができるなんて、\nやっぱり王子はんとうちは\n運命の相手なんやね～♪');
INSERT INTO unit_comments VALUES(1010016,101061,1,4,1,0.0,1,'王子はんがそばにおってくれたら\nうち、なんでもできる気がする…\n王子はん、\nこれからもずっとそばにいてな♪');
INSERT INTO unit_comments VALUES(1010017,101061,1,5,1,0.0,1,'王子と姫がそろうとき、\nマホマホ王国への扉が開かれる…\nさあ、王子はん\n二人でマホマホ王国に凱旋や～♪');
INSERT INTO unit_comments VALUES(1011001,101101,1,1,5,4.0,6,'哥哥和我\n虽然没有血缘关系\n但也是兄妹哦！\n诶？你不明白？');
INSERT INTO unit_comments VALUES(1011002,101101,1,2,1,3.0,6,'我的箭术百发八中！\n嗯？这样一来等于\n基本没射中？');
INSERT INTO unit_comments VALUES(1011003,101101,1,3,2,4.0,1,'下次想和哥哥一起\n来一场甜点之旅呢\n你愿意陪我吗？');
INSERT INTO unit_comments VALUES(1011004,101101,1,4,1,4.0,2,'只要和哥哥在一起，\n我就觉得自己无所不能！\n好，继续前进吧！');
INSERT INTO unit_comments VALUES(1011005,101101,1,5,5,6.0,2,'我们的关系越来越好了\n要是哥哥……\n也能这么想就好了');
INSERT INTO unit_comments VALUES(1011006,101131,1,1,1,0.0,1,'听说最近\n“流行吃蛋糕”呢！\n我们赶快去吃吧！');
INSERT INTO unit_comments VALUES(1011007,101131,1,2,1,0.0,1,'箭术也要提高才行……\n在哥哥面前\n我可不想失手！');
INSERT INTO unit_comments VALUES(1011008,101131,1,3,1,0.0,1,'“熟能生气”？\n意思是说\n哥哥不愿意教我吗？');
INSERT INTO unit_comments VALUES(1011009,101131,1,4,1,0.0,1,'诶嘿嘿，按这个势头……\n直中哥哥的红心的\n日子也快要来临了吧？');
INSERT INTO unit_comments VALUES(1011010,101131,1,5,1,0.0,1,'伤害哥哥的人，\n我会用这把弓将他们\n全部干掉！');
INSERT INTO unit_comments VALUES(1011011,101101,2,6,1,7.2,2,'生日快乐！\n哥哥，生日果然还是\n应该和家人一起过\n和我办派对吧！');
INSERT INTO unit_comments VALUES(1011012,101131,2,6,1,0.0,1,'Happy birthday！\n即使哥哥长大了，\n我也长大了\n我们俩依旧还是兄妹哦♪');
INSERT INTO unit_comments VALUES(1011013,101161,1,1,1,0.0,1,'妹さえいれば兄に金棒ですよ！\n私の活躍しっかり見てて下さいね\nお兄ちゃん♪');
INSERT INTO unit_comments VALUES(1011014,101161,1,2,1,0.0,1,'お兄ちゃんといっしょですから\n今の私は無敵ですよ！\nイヤでも鉄砲でもどんとこいです！');
INSERT INTO unit_comments VALUES(1011015,101161,1,3,1,0.0,1,'例えお兄ちゃんになにがあっても\n私はお兄ちゃんの妹ですからね\n絶対に見つけて、再会して…\nそして、ずっと一緒にいるんです！');
INSERT INTO unit_comments VALUES(1011016,101161,1,4,1,0.0,1,'任せてください、お兄ちゃん\n私がいる限り、お兄ちゃんには\n指一本触れさせませんからね！');
INSERT INTO unit_comments VALUES(1011017,101161,1,5,1,0.0,1,'お兄ちゃんのおかげで、\nとってもいい調子なんです\nこのまま恋も受験も\nバッチリいただいちゃいますよ！');
INSERT INTO unit_comments VALUES(1012001,101201,1,1,2,4.0,1,'要是有什么事，\n就尽管来拜托我吧！\n初音我会全力相助的！');
INSERT INTO unit_comments VALUES(1012002,101201,1,2,2,3.0,1,'睡了个好觉，活力满满！\n喂喂，再发呆\n就要丢下你咯！');
INSERT INTO unit_comments VALUES(1012003,101201,1,3,4,5.0,5,'嗯~虽然有点困……\n不过没关系！\n耶☆');
INSERT INTO unit_comments VALUES(1012004,101201,1,4,2,9.0,6,'呼呼……\n栞栞……好柔软……\n嗯？咦？是你啊？');
INSERT INTO unit_comments VALUES(1012005,101201,1,5,5,6.0,2,'耶~Peace Peace！\n魔物什么的，\n我用超能力就能秒杀！\n我很强喔！');
INSERT INTO unit_comments VALUES(1012006,101231,1,1,1,0.0,1,'我说，你要不要也\n一起睡睡看？充分沐浴了\n阳光的草原当被褥！\n很舒服哦☆');
INSERT INTO unit_comments VALUES(1012007,101231,1,2,1,0.0,1,'我告诉你哦，就算使用了\n大量超能力，只要这样就能用\n大地之力充满电！\n完美☆');
INSERT INTO unit_comments VALUES(1012008,101231,1,3,1,0.0,1,'诶嘿嘿\n待在你身边暖呼呼的，\n好像……有点困了\n呼啊……');
INSERT INTO unit_comments VALUES(1012009,101231,1,4,1,0.0,1,'呼呼……\n有你在，真舒心……\n应该能做个好梦……');
INSERT INTO unit_comments VALUES(1012010,101231,1,5,1,0.0,1,'我好像做了一个\n很美好的梦\n梦见和你一起……\n诶嘿嘿，希望这个梦能成真');
INSERT INTO unit_comments VALUES(1012011,101201,2,6,1,7.2,2,'作为庆祝你的生日的礼物，\n就让你枕在初音的膝上吧！\n枕着很舒服哦☆\n嗯~连我也觉得困了……呼~');
INSERT INTO unit_comments VALUES(1012012,101231,2,6,1,0.0,1,'今天是你的生日呀，祝你生日快乐！\n让我用超能力庆祝……\n哎呀，可是会犯困呢~\n那就……我咬♪');
INSERT INTO unit_comments VALUES(1013001,101301,1,1,1,7.3,2,'每日例行的定期巡查！\n找到稀有的魔导书的时候，\n我会激动得浑身颤抖哦！\n来吧，你也来这边的世界吧！');
INSERT INTO unit_comments VALUES(1013002,101301,1,2,2,1.5,5,'嘿哈！\n我得把脸埋进钟爱角色的抱枕里，\n补充一下魔力才行！');
INSERT INTO unit_comments VALUES(1013003,101301,1,3,1,7.4,2,'我是守护众人的魔法少女\n……不管男女老少，所有生命都平等对待\n当然了，童心未泯的大朋友们也是！');
INSERT INTO unit_comments VALUES(1013004,101301,1,4,1,5.2,2,'总是受你照顾，\n偶尔让七七香\n请你吃点什么吧！\n好咧，钱就由我来付吧！');
INSERT INTO unit_comments VALUES(1013005,101301,1,5,1,4.5,2,'作为魔法少女，\n我想让人人都笑逐颜开！\n当然了，也包括你！');
INSERT INTO unit_comments VALUES(1013006,101331,1,1,1,0.0,1,'面对强大的力量，七七香陷入绝望\n就在此时，新魔导书的力量\n为七七香带来了奇迹！\n下回，七七香之死！敬请期待');
INSERT INTO unit_comments VALUES(1013007,101331,1,2,1,0.0,1,'每次使用新魔法的时候\n我总会心跳加速！\n一旦能够如愿地成功，\n我就会兴奋到不行！');
INSERT INTO unit_comments VALUES(1013008,101331,1,3,1,0.0,1,'这个魔法，是我以自己的生命为代价\n释放出来的最终必杀技……诶嘿嘿\n和你在一起的每一天，都非常快乐\n……像这样的剧情不错吧！？');
INSERT INTO unit_comments VALUES(1013009,101331,1,4,1,0.0,1,'你和我组队挑战的话，\n不管是怎样的敌人都不可怕！\n我们既是同志也是搭档！\n我们两人在一起，才是一名完整的魔法少女！');
INSERT INTO unit_comments VALUES(1013010,101331,1,5,1,0.0,1,'在我的魔法少女物语里，\n你这个搭档是必不可少的！\n我们一起成为\n能够守护大家的魔法少女吧！');
INSERT INTO unit_comments VALUES(1013011,101301,2,6,2,2.8,1,'生日快乐！！\n这是给你的礼物！\n我推荐的作品整套185本！\n……你拿得回去吗？');
INSERT INTO unit_comments VALUES(1013012,101331,2,6,1,0.0,1,'生日快乐！\n这副眼镜送你！\n戴上看看！嗯！\n完美！很适合你哦！');
INSERT INTO unit_comments VALUES(1014001,101401,1,1,1,0.0,1,'我喜欢的推理小说要出新作了。\n你要不要也读读？\n我可以借你全套哦！');
INSERT INTO unit_comments VALUES(1014002,101401,1,2,1,5.4,2,'偶尔也放下侦探和助手的工作，\n出去走走，休息一下怎么样？\n大脑也是需要歇一歇的嘛♪');
INSERT INTO unit_comments VALUES(1014003,101401,1,3,4,6.2,1,'这份工作时常昼夜颠倒，\n而且总要东奔西跑，还需要毅力，\n虽然辛苦，你却做得很不错啊。\n帮大忙了。');
INSERT INTO unit_comments VALUES(1014004,101401,1,4,5,4.8,2,'只要和助手你在一起，\n不管是多么困难的案件\n都准能一下子解决。');
INSERT INTO unit_comments VALUES(1014005,101401,1,5,1,6.5,5,'虽然在此之前我都是\n一个人侦查案件，\n但是我不想回到从前了……\n因为我遇见了你嘛。');
INSERT INTO unit_comments VALUES(1014006,101431,1,1,1,0.0,1,'看招！\n呵呵，不要小看【自卫团】\n引以为傲的\n名侦探之力哦♪');
INSERT INTO unit_comments VALUES(1014007,101431,1,2,1,0.0,1,'以风卷残云之速找出真相，\n以迅雷不及掩耳之势抓住犯人！\n一切都交给\n名侦探·香澄吧！');
INSERT INTO unit_comments VALUES(1014008,101431,1,3,1,0.0,1,'虽然我并不喜欢粗鲁行事……\n但如果是为了解决事件，\n我也不打算\n老老实实待着哦！');
INSERT INTO unit_comments VALUES(1014009,101431,1,4,1,0.0,1,'虽然我们是侦探和助手的关系，\n但是现在就算撇开这一层关系，\n我也认为你是我重要的搭档哦♪');
INSERT INTO unit_comments VALUES(1014010,101431,1,5,1,0.0,1,'正因为有你在身边\n为我提供确切的支援，\n我才能充分地发挥力量。\n我们俩就是名侦探和名助手，对吧！');
INSERT INTO unit_comments VALUES(1014011,101401,2,6,1,6.7,5,'你说今天是你的生日？\n生日快乐！\n那我们赶快完成搜查，\n来庆祝一番吧！');
INSERT INTO unit_comments VALUES(1014012,101431,2,6,1,0.0,1,'作为生日的余兴节目，\n今天就让助手扮演一下侦探吧。\n老师，今天我们要挑战什么样的谜题呢？\n呵呵♪');
INSERT INTO unit_comments VALUES(1015001,101501,1,1,1,3.0,2,'哎呀呀，你是来见老师的吗？\n呵呵呵，好高兴啊');
INSERT INTO unit_comments VALUES(1015002,101501,1,2,1,3.0,2,'今天要读什么书呢……\n你喜欢哪本？');
INSERT INTO unit_comments VALUES(1015003,101501,1,3,2,1.0,5,'唔呵呵♪\n你可以把老师当成\n自己的妈妈哦');
INSERT INTO unit_comments VALUES(1015004,101501,1,4,2,0.0,2,'好孩子~好孩子♪\n老师会一直\n陪在你身边的哦');
INSERT INTO unit_comments VALUES(1015005,101501,1,5,1,4.0,5,'老师永远都是\n站在你这边的\n尽管对我撒娇吧');
INSERT INTO unit_comments VALUES(1015006,101531,1,1,1,0.0,1,'如果有什么难过的事\n不要客气，要和老师说哦？\n无论发生了什么，我都会\n成为你的助力的');
INSERT INTO unit_comments VALUES(1015007,101531,1,2,1,0.0,1,'一直以来辛苦你啦♪\n你的努力\n老师都有好好看在眼里哦\n好孩子~好孩子');
INSERT INTO unit_comments VALUES(1015008,101531,1,3,1,0.0,1,'哎呀呀，受伤了吗？\n来这边给我看看？\n老师帮你治疗哦~\n好了，痛痛飞走~');
INSERT INTO unit_comments VALUES(1015009,101531,1,4,1,0.0,1,'今天，我是只属于你的\n美里老师哦~\n要对其他孩子保密哦？\n不然他们会吃醋的呢♪');
INSERT INTO unit_comments VALUES(1015010,101531,1,5,1,0.0,1,'要是觉得和我在一起\n会感到安心，那我就很开心啦\n和你在一起，\n我也会觉得心里十分温暖');
INSERT INTO unit_comments VALUES(1015011,101501,2,6,5,10.4,2,'今天是一年一度的特别日子\n能够为你庆祝生日\n老师真的好高兴啊\n生日快乐♪');
INSERT INTO unit_comments VALUES(1015012,101531,2,6,1,0.0,1,'生日快乐！\n今天你是主角，\n无论你说什么，我都会听的哦~\n尽情撒娇也没问题哦');
INSERT INTO unit_comments VALUES(1016001,101601,1,1,2,2.0,1,'哟，秀才！\n我想拜托你再帮我计算\n可以吗？');
INSERT INTO unit_comments VALUES(1016002,101601,1,2,1,5.0,6,'我说啊，秀才你\n九九乘法表记到哪一行？\n不是吧？真的假的？真是天才！');
INSERT INTO unit_comments VALUES(1016003,101601,1,3,2,4.5,1,'我今天要在\n待会儿的活动上担任模特~\n有兴趣的话就来看吧！');
INSERT INTO unit_comments VALUES(1016004,101601,1,4,5,7.0,2,'和秀才说话，就会觉得\n心里暖呼呼的~\n诶？我没说谎，真的真的！\n是真话！');
INSERT INTO unit_comments VALUES(1016005,101601,1,5,2,4.0,1,'哟，秀才！\n一起吃饭吧！\n我来告诉你\n最近发现的好吃的餐馆吧~');
INSERT INTO unit_comments VALUES(1016006,101631,1,1,1,0.0,1,'我要用爱之箭\n把各位粉丝们\n迷得神魂颠倒~\n当然，秀才也是♪');
INSERT INTO unit_comments VALUES(1016007,101631,1,2,1,0.0,1,'我想了下次拍摄用的新姿势~\n这样如何？\n又强又可爱，不错吧？');
INSERT INTO unit_comments VALUES(1016008,101631,1,3,1,0.0,1,'哇啊，大海好美啊！\n我看过很多次海了\n不过这应该是最美的一次！\n是因为一起看海的人吗？');
INSERT INTO unit_comments VALUES(1016009,101631,1,4,1,0.0,1,'模特同行们以为秀才是我的男朋友\n超有趣！不过，我也有点……\n不……没什么！');
INSERT INTO unit_comments VALUES(1016010,101631,1,5,1,0.0,1,'不用因为我是模特\n就对我特殊对待！\n在我看来，秀才你才更厉害呢！');
INSERT INTO unit_comments VALUES(1016011,101601,2,6,2,4.0,1,'Happy birthday！\n我来给你庆祝☆\n嗯~一起庆祝生日\n有点像情侣呢？');
INSERT INTO unit_comments VALUES(1016012,101631,2,6,1,0.0,1,'今天是秀才的生日对吧！\n和学院里的所有人一起开派对吧？\n啊，不过，只有两个人的生日派对\n说不定也挺开心的♪');
INSERT INTO unit_comments VALUES(1017001,101701,1,1,1,1.5,2,'你好吗！\n活力十足地打招呼\n心情就会很舒畅！');
INSERT INTO unit_comments VALUES(1017002,101701,1,2,1,3.0,2,'不要着急，慢慢来~\n心急吃不了热豆腐嘛~');
INSERT INTO unit_comments VALUES(1017003,101701,1,3,5,4.0,2,'苦瓜，猪排骨肉……\n还有沙翁！\n真怀念故乡的食物啊~');
INSERT INTO unit_comments VALUES(1017004,101701,1,4,1,0.0,2,'和你在一起，\n什么样的冒险都不在话下！');
INSERT INTO unit_comments VALUES(1017005,101701,1,5,5,4.0,2,'希望有一天能让你看看我的故乡~\n蔚蓝的大海和白色的沙滩！\n你看了一定会感动的！');
INSERT INTO unit_comments VALUES(1017006,101731,1,1,1,0.0,1,'你好吗！\n今天好像也会很开心~\n好兴奋，\n忍不住要跳起舞来了♪');
INSERT INTO unit_comments VALUES(1017007,101731,1,2,1,0.0,1,'有好多朋友，\n每天都很开心~\n开心过头了，\n不由得跳起舞来了~');
INSERT INTO unit_comments VALUES(1017008,101731,1,3,1,0.0,1,'一起去海边吧！\n在水里\n很舒服喔~');
INSERT INTO unit_comments VALUES(1017009,101731,1,4,1,0.0,1,'游泳很舒服的！\n你也一起来吧！\n我会拉着你的手\n不会让你摔倒的');
INSERT INTO unit_comments VALUES(1017010,101731,1,5,1,0.0,1,'大家一起跳舞真开心！\n来，\n你也一起跳吧！');
INSERT INTO unit_comments VALUES(1017011,101701,2,6,1,4.2,2,'噢噢！\n今天是你的生日吗？\n既然是这样\n我就要尽全力给你庆祝了！');
INSERT INTO unit_comments VALUES(1017012,101731,2,6,1,0.0,1,'生日快乐！\n作为祝福，我来为你\n献上24小时不间断的\n祝福之舞吧！');
INSERT INTO unit_comments VALUES(1018001,101801,1,1,1,9.0,2,'虽然我看着这样，但好歹也是新任教师哦\n要是语文有什么不懂的\n地方就来问我哦\n老师我会教·你·的♪');
INSERT INTO unit_comments VALUES(1018002,101801,1,2,1,7.5,5,'虽然我负责教语文\n不过少女漫画和恋爱小说\n我懂得更多\n要是有这些科目就好了~');
INSERT INTO unit_comments VALUES(1018003,101801,1,3,1,5.5,2,'魔族的女子学校时代……\n有很多开心的回忆\n你也在这个学院里\n度过美好的时光吧！');
INSERT INTO unit_comments VALUES(1018004,101801,1,4,1,10.0,6,'作业很棘手吗？\n老师会仔细教导你的♪\n首先……这篇文章的意思是……\n诶？贴得太紧了？');
INSERT INTO unit_comments VALUES(1018005,101801,1,5,1,8.0,5,'我们走在一起，经常会被\n误认为是情侣呢\n我们看起来像恋人吗？\n我倒是不反感……');
INSERT INTO unit_comments VALUES(1018006,101831,1,1,1,0.0,1,'摔、摔倒了~\n身为老师，我应该更加稳重才行\n我还差得远呢……');
INSERT INTO unit_comments VALUES(1018007,101831,1,2,1,0.0,1,'我可不是天然呆喔？\n只是偶尔~会大意而已……\n真的哦？');
INSERT INTO unit_comments VALUES(1018008,101831,1,3,1,0.0,1,'我被魔物袭击了，\n可它一看到我的脸，\n就满脸通红地倒下了……\n为什么会这样呢？');
INSERT INTO unit_comments VALUES(1018009,101831,1,4,1,0.0,1,'我说，要是我又摔倒了\n你会帮我吗？\n诶嘿嘿，我现在很习惯依赖你了。\n明明我才是老师');
INSERT INTO unit_comments VALUES(1018010,101831,1,5,1,0.0,1,'第一个男朋友会是什么样的人呢？\n希望能像你一样温柔，\n即使我摔倒也不会嘲笑我，\n还会扶我起来……');
INSERT INTO unit_comments VALUES(1018011,101801,2,6,1,6.2,2,'生日快乐♪\n比昨天长大一岁了呢\n作为庆祝，让老师\n好好摸摸你的头♪');
INSERT INTO unit_comments VALUES(1018012,101831,2,6,1,0.0,1,'今天是你的生日对吧！\n比自己小的男孩，在刚到达\n适婚年龄之后，就立刻对自己求婚……这种事\n就像小说一样，让人十分憧憬呢♪');
INSERT INTO unit_comments VALUES(1018013,101861,1,1,1,0.0,1,'君にだけ\n特別な授業をしてあげる\nじっくり教える予定だから\nサボっちゃダメよ？');
INSERT INTO unit_comments VALUES(1018014,101861,1,2,1,0.0,1,'いつも優しくて頼れる君の姿から\n色んなことを学んでるのよ\n例えばどんなことかって？\nふふ、いいわよ。教えてあげる');
INSERT INTO unit_comments VALUES(1018015,101861,1,3,1,0.0,1,'たまには愛の鞭も必要かしら！\nあれ？　どうしてうれしそうな\n顔をするの？\nしっかり反省して欲しいのに～！');
INSERT INTO unit_comments VALUES(1018016,101861,1,4,1,0.0,1,'君のおかげで私も前に進めたわ\n特大の花丸よりもすごいご褒美を\nあげなきゃ！\nどんなことかは、まだ内緒よ');
INSERT INTO unit_comments VALUES(1018017,101861,1,5,1,0.0,1,'どうしていつも彼氏じゃないって\n否定しないの？　先生のこと\nあんまりからかっちゃだめよ\n勘違いしちゃうじゃない');
INSERT INTO unit_comments VALUES(1020001,102001,1,1,5,8.0,2,'红、白、黄~\n花儿开得好漂亮♪\n虫儿在鸣叫\n蝴蝶在飞舞♪');
INSERT INTO unit_comments VALUES(1020002,102001,1,2,5,3.5,2,'兔子♪\n猫咪♪\n狗狗♪\n大家都是美美的朋友~♪');
INSERT INTO unit_comments VALUES(1020003,102001,1,3,1,4.0,2,'哥哥，\n你愿意和美美玩吗？\n太好了！好开心♪');
INSERT INTO unit_comments VALUES(1020004,102001,1,4,5,4.0,2,'美美啊，\n最喜欢哥哥了！\n也喜欢兔子和鲜花！');
INSERT INTO unit_comments VALUES(1020005,102001,1,5,2,2.5,5,'美美喜欢哥哥！\n抱抱！\n哥哥也抱抱美美~');
INSERT INTO unit_comments VALUES(1020006,102031,1,1,1,0.0,1,'哇啊，小格里芬\n跑得好快呀！\n哥哥，快点快点♪');
INSERT INTO unit_comments VALUES(1020007,102031,1,2,1,0.0,1,'美美想做\n花环♪做好了\n就给哥哥戴哦♪');
INSERT INTO unit_comments VALUES(1020008,102031,1,3,1,0.0,1,'爸爸妈妈和朋友、\n动物和鲜花\n还有哥哥！\n美美全都很珍惜哦♪');
INSERT INTO unit_comments VALUES(1020009,102031,1,4,1,0.0,1,'这里是秘密花田哦！\n不过可以破例\n告诉哥哥\n因为美美最喜欢你了！');
INSERT INTO unit_comments VALUES(1020010,102031,1,5,1,0.0,1,'美美和小小格里芬\n都很喜欢哥哥♪\n和你在一起真开心♪');
INSERT INTO unit_comments VALUES(1020011,102001,2,6,2,2.7,5,'生日快乐♪\n美美也想快点长大，\n成为哥哥的\n姐姐♪');
INSERT INTO unit_comments VALUES(1020012,102031,2,6,1,0.0,1,'美美，要唱祝福的歌了哦\n噜嗒嗒噜嗒嗒♪\n生日快乐♪\n最喜欢、最喜欢♪哥哥啦♪');
INSERT INTO unit_comments VALUES(1021001,102101,1,1,1,0.0,2,'呆在哥哥身边\n就会觉得心情非常平静\n诶嘿，不由得就对你撒娇了……');
INSERT INTO unit_comments VALUES(1021002,102101,1,2,2,7.0,1,'那、那个……\n哥哥对我这样的人也很温柔呢\n为什么？');
INSERT INTO unit_comments VALUES(1021003,102101,1,3,1,7.0,5,'啊呃噫呜呃哦啊哦……\n为、为了能和大家多说话，\n我在做练习');
INSERT INTO unit_comments VALUES(1021004,102101,1,4,1,4.5,2,'那、那个，\n要不要玩过家家？\n我当妈妈，\n哥哥当爸爸♪');
INSERT INTO unit_comments VALUES(1021005,102101,1,5,2,0.0,2,'诶嘿，你今天也来找我玩了！\n好开心♪');
INSERT INTO unit_comments VALUES(1021006,102131,1,1,1,0.0,1,'那、那个……\n对、对不起！\n还是算了……');
INSERT INTO unit_comments VALUES(1021007,102131,1,2,1,0.0,1,'啊呜！？\n我脸上有什么东西吗？\n别、别老是盯着我看呀');
INSERT INTO unit_comments VALUES(1021008,102131,1,3,1,0.0,1,'我还想在森林里\n多练习一下……\n要是还没练好\n就让人看到了，那多羞人啊……');
INSERT INTO unit_comments VALUES(1021009,102131,1,4,1,0.0,1,'呜呜……别老盯着我看\n啊、呃……不过，\n并不是讨厌被你看……');
INSERT INTO unit_comments VALUES(1021010,102131,1,5,1,0.0,1,'呃……那个……\n我喜欢演戏……\n想让哥哥帮忙看一下……呢');
INSERT INTO unit_comments VALUES(1021011,102101,2,6,5,5.6,2,'生生、生日……\n快乐！\n能能、能收下……\n我的……礼物吗？');
INSERT INTO unit_comments VALUES(1021012,102131,2,6,1,0.0,1,'哥哥，生日……\n快、快乐！\n我、我今天……\n想、想和你一起庆祝……');
INSERT INTO unit_comments VALUES(1022001,102201,1,1,2,5.5,1,'那、那个。\n我有些话想跟你说……\n还、还是算了！');
INSERT INTO unit_comments VALUES(1022002,102201,1,2,5,9.0,1,'今天是新游戏的发售日……\n要是你有兴趣，\n一起去……还、还是算了！');
INSERT INTO unit_comments VALUES(1022003,102201,1,3,2,4.5,1,'面对你的时候，\n我能稍微正常地说话，\n真希望我能说得更加流畅啊');
INSERT INTO unit_comments VALUES(1022004,102201,1,4,2,4.5,5,'和你呆在一起\n好像变得稀松平常了\n稀松平常这件事，\n或许还挺特别的……');
INSERT INTO unit_comments VALUES(1022005,102201,1,5,5,5.5,6,'下次要一起去买东西吗？\n嗯，练习得很完美\n诶、莫非\n你听到刚才的话了！？');
INSERT INTO unit_comments VALUES(1022006,102231,1,1,1,0.0,1,'这、这花并、并不是\n想给你看\n才摘的！');
INSERT INTO unit_comments VALUES(1022007,102231,1,2,1,0.0,1,'好美的花田……\n不、不过或许\n和我这样的人不相衬\n……没、没什么');
INSERT INTO unit_comments VALUES(1022008,102231,1,3,1,0.0,1,'我其实也不讨厌\n这种地方\n好……好歹我也是女孩子嘛');
INSERT INTO unit_comments VALUES(1022009,102231,1,4,1,0.0,1,'难得来到了花田……\n喜欢，讨厌，喜欢，讨厌……喜\n哇啊啊！没、没什么！');
INSERT INTO unit_comments VALUES(1022010,102231,1,5,1,0.0,1,'我说，你带我来\n这种地方，也就是说多少\n有在把我当成女孩子\n看待……吗');
INSERT INTO unit_comments VALUES(1022011,102201,2,6,5,5.5,4,'生、生日、快、快……\n果、果然还是不行！\n祝福说不出口！\n礼物送不出手！');
INSERT INTO unit_comments VALUES(1022012,102231,2,6,1,0.0,1,'生、生日，快乐！\n要是你在游戏里赢过我，\n就给你礼物\n你很擅长玩这个游戏，对吧？');
INSERT INTO unit_comments VALUES(1023001,102301,1,1,1,4.2,5,'噗吉是我重要的朋友哦，\n哥哥也和噗吉和睦相处吧！');
INSERT INTO unit_comments VALUES(1023002,102301,1,2,2,4.1,1,'我比哥哥先来救济院，\n所以我是姐姐！\n哥哥是弟弟哦♪');
INSERT INTO unit_comments VALUES(1023003,102301,1,3,5,3.7,2,'我、噗吉和哥哥！\n只要我们三个在一起，\n就什么都不用怕！');
INSERT INTO unit_comments VALUES(1023004,102301,1,4,3,2.6,1,'噗吉生气了，它在说：\n“多和我们玩玩啊！”');
INSERT INTO unit_comments VALUES(1023005,102301,1,5,6,2.9,2,'哥哥喜欢噗吉吗？\n我很喜欢！\n就像喜欢哥哥一样！');
INSERT INTO unit_comments VALUES(1023006,102331,1,1,1,0.0,1,'哥、哥哥！\n你这样跑来跑去\n会被噗吉\n打到哦！');
INSERT INTO unit_comments VALUES(1023007,102331,1,2,1,0.0,1,'嘿！呀啊！坏魔物！\n别、别过来！\n啊、那边也不行！\n不许去哥哥那边！');
INSERT INTO unit_comments VALUES(1023008,102331,1,3,1,0.0,1,'哥哥快看，\n这就是我的噗吉全力挥动的\n练习成果！');
INSERT INTO unit_comments VALUES(1023009,102331,1,4,1,0.0,1,'哥哥！\n你还好吗？有没有受伤吧？\n我和噗吉来了，所以没事啦，\n放心吧！');
INSERT INTO unit_comments VALUES(1023010,102331,1,5,1,0.0,1,'伤害哥哥的坏魔物！\n我绝对不会放过你！\n上吧，噗吉！\n“好的，绫音！”');
INSERT INTO unit_comments VALUES(1023011,102301,2,6,2,1.9,1,'Happy birthday♪\n噗吉说也\n想给哥哥庆祝！\n我们三个一起开派对吧！');
INSERT INTO unit_comments VALUES(1023012,102331,2,6,1,0.0,1,'哥哥，生日快乐♪\n噗吉好像也有\n礼物要送给你哦！\n能收到这么多礼物，真好啊！');
INSERT INTO unit_comments VALUES(1025001,102501,1,1,4,6.0,6,'对不起，我总是把事情搞砸……\n我、我要切腹赔罪！\n诶、那才是添乱？');
INSERT INTO unit_comments VALUES(1025002,102501,1,2,1,2.5,6,'日常起居就由我来照顾——\n哇！\n撞、撞到脑袋了……');
INSERT INTO unit_comments VALUES(1025003,102501,1,3,1,4.0,4,'做饭，洗衣，还有打扫房间……\n要、要做的事太多\n晕头转向了~');
INSERT INTO unit_comments VALUES(1025004,102501,1,4,5,2.5,2,'我会努力服侍你的！\n有事请尽管吩咐喔？');
INSERT INTO unit_comments VALUES(1025005,102501,1,5,4,7.0,5,'那个……我会努力打扫的，\n所以结束之后，那个……\n你能不能表扬我？');
INSERT INTO unit_comments VALUES(1025006,102531,1,1,1,0.0,1,'这位魔像先生\n被扔在路边了\n好可怜……');
INSERT INTO unit_comments VALUES(1025007,102531,1,2,1,0.0,1,'我要把魔像先生弄干净\n你能帮我吗？\n诶嘿嘿，谢谢');
INSERT INTO unit_comments VALUES(1025008,102531,1,3,1,0.0,1,'有没有受伤？\n回复魔法我可是很擅长哦♪\n除此之外的魔法……只是勉勉强强');
INSERT INTO unit_comments VALUES(1025009,102531,1,4,1,0.0,1,'啊！又搞砸了~\n为了不在你面前失败\n我明明很小心了的~');
INSERT INTO unit_comments VALUES(1025010,102531,1,5,1,0.0,1,'欢迎回来\n主人\n我、我说笑的啦，诶嘿嘿♪');
INSERT INTO unit_comments VALUES(1025011,102501,2,6,2,5.7,1,'祝你生日快乐\n蛋糕也准备好了！\n我会避免摔倒，\n轻轻~地拿过去哦');
INSERT INTO unit_comments VALUES(1025012,102531,2,6,1,0.0,1,'生日快乐♪\n今天我想作为一个普通的女孩子\n为你庆祝生日，\n可以……吗？');
INSERT INTO unit_comments VALUES(1026001,102601,1,1,5,5.4,1,'可以的话，\n真希望能一直无所事事……\n我说，你不觉得吗？');
INSERT INTO unit_comments VALUES(1026002,102601,1,2,2,6.8,5,'哎，和我一起\n悠闲地玩游戏吧~\n偶尔休息一下也很重要哦？');
INSERT INTO unit_comments VALUES(1026003,102601,1,3,4,6.5,5,'我饿了，你有没有什么吃的？\n要是有好吃的豆沙面包\n就太棒了~');
INSERT INTO unit_comments VALUES(1026004,102601,1,4,1,3.8,2,'平时我总是一个人闲逛，\n所以能有你当玩伴\n我还挺开心的~');
INSERT INTO unit_comments VALUES(1026005,102601,1,5,4,9.5,2,'……像我这样的性格，\n其实不太习惯别人\n进入自己的世界，\n但是我却意外地不讨厌你');
INSERT INTO unit_comments VALUES(1026006,102631,1,1,1,0.0,1,'竟然能在最喜欢的橡子里\n打滚……\n怎么回事？\n这里是天堂吗？');
INSERT INTO unit_comments VALUES(1026007,102631,1,2,1,0.0,1,'啊啊，这个空间简直是\n为了我而\n存在的……\n我都不想回去了');
INSERT INTO unit_comments VALUES(1026008,102631,1,3,1,0.0,1,'虽然柔软的床也很棒\n不过在自然的包围之中悠闲度过\n也别有一番乐趣……');
INSERT INTO unit_comments VALUES(1026009,102631,1,4,1,0.0,1,'快点快点，你也过来这边\n一起享受\n这个乐园吧……\n我的身边还有位置哦~');
INSERT INTO unit_comments VALUES(1026010,102631,1,5,1,0.0,1,'谢谢你带我\n到这么棒的地方来\n如果是来这里，\n我一百个愿意出门，下次再带我来吧');
INSERT INTO unit_comments VALUES(1026011,102601,2,6,1,6.2,2,'生日快乐~\n我没有忘记买礼物哦~\n我指定了送货的时间，\n所以应该很快就会送到啦~');
INSERT INTO unit_comments VALUES(1026012,102631,2,6,1,0.0,1,'生日快乐~\n正因为是特别的日子，\n才要悠闲度过呀\n我也会陪你的哦~');
INSERT INTO unit_comments VALUES(1027001,102701,1,1,1,5.0,2,'我在一边想着您\n一边诚心诚意地制作药物哦');
INSERT INTO unit_comments VALUES(1027002,102701,1,2,1,6.0,2,'我的本职是开花店的\n也会制作花束\n最近毒草的搭配很受欢迎');
INSERT INTO unit_comments VALUES(1027003,102701,1,3,1,9.0,6,'我不讨厌花……\n花既是生活来源，又可以用作药材。\n美观？这我倒是没想过');
INSERT INTO unit_comments VALUES(1027004,102701,1,4,2,5.5,5,'像这样进行研究\n真让人兴奋……\n一想到只要药做好了\n就能让您任我摆布，我就……');
INSERT INTO unit_comments VALUES(1027005,102701,1,5,2,3.5,5,'来做花朵占卜吧\n您喜欢我……很喜欢我……\n喜欢我……很喜欢我……');
INSERT INTO unit_comments VALUES(1027006,102731,1,1,1,0.0,1,'您喜欢什么样的女性？\n开朗的我？冷酷的我？\n安静的我？当然\n不可能有除我以外的人对吧？');
INSERT INTO unit_comments VALUES(1027007,102731,1,2,1,0.0,1,'听说……森林的魔物会读心\n让人看到所渴望的幻境\n如果您遇到那样的魔物\n会看到怎样的幻境呢');
INSERT INTO unit_comments VALUES(1027008,102731,1,3,1,0.0,1,'请只看着我一个人\n只看着如今在你眼前的我\n就算是另一个我，\n也不许将目光转向她');
INSERT INTO unit_comments VALUES(1027009,102731,1,4,1,0.0,1,'如果您的冒牌货出现了\n我就把他吊起来观赏……\n不，冒牌货终究是冒牌货\n我想要的只有您……');
INSERT INTO unit_comments VALUES(1027010,102731,1,5,1,0.0,1,'您想要什么样的我？\n只要是您的愿望，\n我就会变成您想要的样子\n因为我爱您……');
INSERT INTO unit_comments VALUES(1027011,102701,2,6,1,7.2,5,'为了给您庆祝生日，\n我做了生日蛋糕哦\n佐料是安眠药、麻药、\n一见钟情药、媚药以及……我的爱');
INSERT INTO unit_comments VALUES(1027012,102731,2,6,1,0.0,1,'生日快乐\n我准备了礼物哦\n是婚姻登记表、结婚戒指\n和我们的新房');
INSERT INTO unit_comments VALUES(1028001,102801,1,1,1,5.0,2,'我发现了可以一次性\n买齐蜡笔、绘本和积木\n而且很廉价的店！\n你能来帮我挑选吗？');
INSERT INTO unit_comments VALUES(1028002,102801,1,2,1,4.5,2,'做料理时，\n要把食材通通用光才行\n不然就太浪费了！');
INSERT INTO unit_comments VALUES(1028003,102801,1,3,1,7.0,2,'虽然钱也很重要，可我只是\n想要看到大家的笑容\n当然，也包括你的哦');
INSERT INTO unit_comments VALUES(1028004,102801,1,4,2,3.5,5,'下次，我想和你\n找个地方一起玩玩呢\n啊，当然，\n是不用花钱的地方哦？');
INSERT INTO unit_comments VALUES(1028005,102801,1,5,1,2.0,2,'我打算举办一个茶话会\n当然了，\n也会邀请你的\n敬请期待！');
INSERT INTO unit_comments VALUES(1028006,102831,1,1,1,0.0,1,'限定道具的队尾在这里……\n哎呀，真巧呀你也来看看吧？\n这是我们主办的义卖会！');
INSERT INTO unit_comments VALUES(1028007,102831,1,2,1,0.0,1,'我去吆喝招揽客人，\n你能举着这块指示牌引导他们吗？');
INSERT INTO unit_comments VALUES(1028008,102831,1,3,1,0.0,1,'来的客人比想象的还要多，\n吓了我一跳！机会难得\n你也来看看吧！');
INSERT INTO unit_comments VALUES(1028009,102831,1,4,1,0.0,1,'谢谢你帮我整队♪\n和你在一起工作\n就会比平时开心好多倍呀♪\n下次也拜托你了！');
INSERT INTO unit_comments VALUES(1028010,102831,1,5,1,0.0,1,'义卖会盛况非凡呢♪\n待会儿赶快去吃饭吧！\n有你在，疲劳都烟消云散了♪');
INSERT INTO unit_comments VALUES(1028011,102801,2,6,1,5.1,2,'Happy birthday♪\n礼物是救济院的大家\n挑的哦。当然我也有参与\n心怀感激地收下吧！');
INSERT INTO unit_comments VALUES(1028012,102831,2,6,1,0.0,1,'今天是你的生日对吧？\n我们出去庆祝吧\n这种日子就该好好休息\n偶尔也要享受一下♪');
INSERT INTO unit_comments VALUES(1029001,102901,1,1,1,3.6,2,'我对目前的地位\n并不满足！\n我要以顶级偶像为目标\n继续磨练唱功和舞技！');
INSERT INTO unit_comments VALUES(1029002,102901,1,2,6,6.0,5,'你在看我的写真集？这件\n夸张的泳装是用来拍摄的啦！\n私下穿的要\n更可爱一些……你想看吗？');
INSERT INTO unit_comments VALUES(1029003,102901,1,3,1,3.6,2,'通往顶级偶像的路\n还很长啊……\n再加把劲\n练习歌舞吧！');
INSERT INTO unit_comments VALUES(1029004,102901,1,4,1,3.4,2,'我希望唱功和舞技\n都能更加出色！\n因为我想让你\n看到最闪耀的我♪');
INSERT INTO unit_comments VALUES(1029005,102901,1,5,1,2.3,5,'你的偶像学习有进展吗？\n可是……除我以外的偶像\n可以不用了解太多！');
INSERT INTO unit_comments VALUES(1029006,102931,1,1,1,0.0,1,'要是成为了顶级偶像\n世界看起来会是什么样呢？\n到了那时\n你会在我身边吗？');
INSERT INTO unit_comments VALUES(1029007,102931,1,2,1,0.0,1,'要想站上舞台的话\n可不能忘记挑战精神！\n今后也要积极向前哦！');
INSERT INTO unit_comments VALUES(1029008,102931,1,3,1,0.0,1,'我会努力成为\n顶尖偶像的！\n应援就拜托你啦！');
INSERT INTO unit_comments VALUES(1029009,102931,1,4,1,0.0,1,'如果你希望的话\n放弃做偶像也可以\n我曾经这样想过哦？\n只是想过一下下而已啦！');
INSERT INTO unit_comments VALUES(1029010,102931,1,5,1,0.0,1,'你说这首歌充分地表现了\n单相思女孩的心情？\n呼呼呼……你真迟钝呀♪');
INSERT INTO unit_comments VALUES(1029011,102901,2,6,2,1.2,1,'生日快乐♪为了\n给你庆祝生日，\n我从很早之前就\n调整了我的日程！开心吗？');
INSERT INTO unit_comments VALUES(1029012,102931,2,6,1,0.0,1,'Happy birthday to you♪\nHappy birthday to you♪\n怎么样？这是专门为你开的\n个人演唱会哦！');
INSERT INTO unit_comments VALUES(1030001,103001,1,1,1,5.5,2,'锵锵锵！\n妮侬会为将军\n尽忠职守！\n请拭目以待吧！');
INSERT INTO unit_comments VALUES(1030002,103001,1,2,3,4.0,2,'大事不好！\n恶霸地方官出现了！\n一起击败他吧！');
INSERT INTO unit_comments VALUES(1030003,103001,1,3,6,4.0,1,'啊、将军！\n您在那里做什么……哈！？\n莫非是忍法修行！？\n妮侬也要修行！');
INSERT INTO unit_comments VALUES(1030004,103001,1,4,2,4.5,1,'为了将军，\n上刀山下火海我都在所不辞！\n因为……我的命，\n是属于将军的！');
INSERT INTO unit_comments VALUES(1030005,103001,1,5,1,5.5,5,'大和抚子……\n这个词在妮侬的国家里也为人所熟知\n如果我变成那样，\n将军也会被迷得神魂颠倒哦？');
INSERT INTO unit_comments VALUES(1030006,103031,1,1,1,0.0,1,'终有一天……\n妮侬要和将军一同\n统一天下！\n这是妮侬的梦想！');
INSERT INTO unit_comments VALUES(1030007,103031,1,2,1,0.0,1,'没有什么东西是妮侬斩不断的！\n将军就怀着\n乘船出游的心情\n放心交给我吧！');
INSERT INTO unit_comments VALUES(1030008,103031,1,3,1,0.0,1,'妮侬把将军的草鞋\n放在怀里\n捂暖了！\n一起外出吧！');
INSERT INTO unit_comments VALUES(1030009,103031,1,4,1,0.0,1,'将军，我们牵手吧！\n诶嘿嘿……\n这样一来，妮侬与将军就\n结下了深厚的感情！');
INSERT INTO unit_comments VALUES(1030010,103031,1,5,1,0.0,1,'将军！您是要吃饭？\n还是要泡澡？\n还是……要·我·呢？\n好、好害羞……');
INSERT INTO unit_comments VALUES(1030011,103001,2,6,1,6.2,2,'生日快乐！\n我准备了庆祝用的\n红白馒头！您我演双簧\n我来喂您吃！');
INSERT INTO unit_comments VALUES(1030012,103031,2,6,1,0.0,1,'为将军庆祝生日！\n生日就是要开宴会\n饮酒作乐！\n热热闹闹的！');
INSERT INTO unit_comments VALUES(1031001,103101,1,1,1,6.5,2,'今天的运势极差，\n呃，请别在意。\n顺便一提……我的占卜是很准的');
INSERT INTO unit_comments VALUES(1031002,103101,1,2,6,4.5,4,'你身后的人，\n是谁？\n诶？你看不见？');
INSERT INTO unit_comments VALUES(1031003,103101,1,3,1,7.0,2,'塔罗牌、占星、四柱推命……\n占卜的方式很多，\n不过我推荐的是\n用父亲来进行的水晶球占卜');
INSERT INTO unit_comments VALUES(1031004,103101,1,4,4,4.0,5,'父亲好像误解你了\n你明明是个好人……');
INSERT INTO unit_comments VALUES(1031005,103101,1,5,1,5.0,5,'今天你有犯桃花之相\n还是别和除我以外的女性\n说话为好……');
INSERT INTO unit_comments VALUES(1031006,103131,1,1,1,0.0,1,'我的舞很美吗……\n好高兴呀\n不枉我认真修炼了一番');
INSERT INTO unit_comments VALUES(1031007,103131,1,2,1,0.0,1,'这是驱魔之舞\n我现在还在练习\n所以请别看了\n太难为情了');
INSERT INTO unit_comments VALUES(1031008,103131,1,3,1,0.0,1,'父亲每次看这个舞\n都会称赞我说他都快要成佛了\n不过如果父亲真的成佛了\n我会很为难的……');
INSERT INTO unit_comments VALUES(1031009,103131,1,4,1,0.0,1,'得到你的夸奖时\n我就能跳得比平时更好\n积极的状态\n最适合除灵了');
INSERT INTO unit_comments VALUES(1031010,103131,1,5,1,0.0,1,'让活人看我跳舞\n你还是第一个\n虽然有些害羞\n但是我想跳给你看');
INSERT INTO unit_comments VALUES(1031011,103101,2,6,2,10.3,5,'等你上了年纪，\n变成了老爷爷，我也会一起\n变成老奶奶\n当然了，就算变成幽灵……');
INSERT INTO unit_comments VALUES(1031012,103131,2,6,1,0.0,1,'生日快乐\n今天的运势非常好哦\n比如金钱运和健康运……\n还有……恋、恋爱运……');
INSERT INTO unit_comments VALUES(1032001,103201,1,1,2,4.0,1,'要是有困难\n就请尽管说吧！\n我会想办法\n帮你的！');
INSERT INTO unit_comments VALUES(1032002,103201,1,2,2,2.0,1,'贵安\n为了让我成为独当一面的商人，\n烦请多多指导\n和鞭策！');
INSERT INTO unit_comments VALUES(1032003,103201,1,3,4,6.5,5,'我的确是路痴，\n不过还没有那么严重\n话说回来，我家\n在哪边？');
INSERT INTO unit_comments VALUES(1032004,103201,1,4,1,6.0,2,'我会一直等待着\n你成为我的骑士的那一天\n到了那时，我们两人\n就一起四处游玩吧！');
INSERT INTO unit_comments VALUES(1032005,103201,1,5,1,3.5,2,'有的东西是用钱\n也买不到的呢……\n比方说，\n你的心♪');
INSERT INTO unit_comments VALUES(1032006,103231,1,1,1,0.0,1,'“独当一面的商人\n也是独当一面的武者！”\n现在正是守护\n紫藤庐的家训之时！');
INSERT INTO unit_comments VALUES(1032007,103231,1,2,1,0.0,1,'这种程度的敌人，\n根本不是我的对手\n赶快解决，\n去办庆功宴吧！');
INSERT INTO unit_comments VALUES(1032008,103231,1,3,1,0.0,1,'阻碍我做生意的家伙\n无论是谁都不可原谅！\n我要予以惩处！');
INSERT INTO unit_comments VALUES(1032009,103231,1,4,1,0.0,1,'有你在我身边\n能够让我变得\n有多坚强，\n你意识到了吗？');
INSERT INTO unit_comments VALUES(1032010,103231,1,5,1,0.0,1,'和你在一起，\n我就能坚定地\n向着任何地方前进！');
INSERT INTO unit_comments VALUES(1032011,103201,2,6,1,5.2,2,'今天是你的生日？\n这样啊，生日快乐！\n马上举行\n庆祝派对吧！');
INSERT INTO unit_comments VALUES(1032012,103231,2,6,1,0.0,1,'祝你生日快乐\n在你的生日\n面前，不管是什么样的宝石\n都会黯然失色！');
INSERT INTO unit_comments VALUES(1033001,103301,1,1,1,3.0,2,'搞笑之道很深奥的！\n和我一起，\n将搞笑之道贯彻到极致吧！');
INSERT INTO unit_comments VALUES(1033002,103301,1,2,3,4.0,4,'我最怕的，不是输给敌人\n而是包袱不响！');
INSERT INTO unit_comments VALUES(1033003,103301,1,3,2,3.0,1,'小哥，这牛奶好喝“马”？\n但是产奶的不是马，\n而是牛哦！');
INSERT INTO unit_comments VALUES(1033004,103301,1,4,1,5.0,2,'小哥，和我一起，\n朝着搞笑界的顶点进发吧！\n觉醒吧，搞笑冠军！');
INSERT INTO unit_comments VALUES(1033005,103301,1,5,1,4.0,2,'小哥，和我一起想包袱吧！\n在“饱腹”了两小时之后……\n开玩笑的！');
INSERT INTO unit_comments VALUES(1033006,103331,1,1,1,0.0,1,'喂~走一走瞧一瞧！\n伊丽莎白农场的\n牛奶、酸奶、奶酪！\n现场直销咯！');
INSERT INTO unit_comments VALUES(1033007,103331,1,2,1,0.0,1,'父老乡亲们，看一看尝一尝！\n刚出产的，真的很美味哦~\n我虽然爱开玩笑，\n但不会说谎哦！');
INSERT INTO unit_comments VALUES(1033008,103331,1,3,1,0.0,1,'城里的大家都这么\n支持我了！\n我绝对要让大家爆笑！');
INSERT INTO unit_comments VALUES(1033009,103331,1,4,1,0.0,1,'我的段子搞笑吗？\n不管怎么说，小哥都是\n我的第一个粉丝\n我们的脑回路一定是相通的！');
INSERT INTO unit_comments VALUES(1033010,103331,1,5,1,0.0,1,'我、我说……我可以\n叫小哥搭档吗？\n如果不行的话就换成夫婿……\n啊，反了！真羞人！');
INSERT INTO unit_comments VALUES(1033011,103301,2,6,1,4.7,2,'虾仁快乐？\n为什么那么吃惊？\n这是为了今天生日的小哥\n准备的笑话啦！生日快乐♪');
INSERT INTO unit_comments VALUES(1033012,103331,2,6,1,0.0,1,'Happy birthday！\n为了小哥\n今天我要施展浑身解数逗你笑！\n希望你能多多露出笑容♪');
INSERT INTO unit_comments VALUES(1034001,103401,1,1,1,5.5,2,'要是有困难\n就找我商量吧\n无论什么时候，我都会帮忙的\n和姐姐约好了哦？');
INSERT INTO unit_comments VALUES(1034002,103401,1,2,1,6.5,2,'你现在有空吗？\n如果有空，和我一起\n去找个地方喝一杯吧？\n当然，你喝果汁就好');
INSERT INTO unit_comments VALUES(1034003,103401,1,3,6,5.0,5,'我把工作带回家做了\n可是却迟迟做不完！\n呜呜……你能\n稍微帮我一下吗？');
INSERT INTO unit_comments VALUES(1034004,103401,1,4,2,7.5,2,'诶嘿嘿……\n你好温暖~\n正好有点喝多了，\n就用你来取暖吧~');
INSERT INTO unit_comments VALUES(1034005,103401,1,5,1,4.0,2,'你有好好吃饭吗？\n啊，要是不嫌弃的话，姐姐\n给你做吧？');
INSERT INTO unit_comments VALUES(1034006,103431,1,1,1,0.0,1,'哈啊……这浆果好香♪\n做成饮料一定很美味……\n不好不好！\n还在工作呢！');
INSERT INTO unit_comments VALUES(1034007,103431,1,2,1,0.0,1,'不行啊由加莉！\n这样下去就会像往常一样出丑的！');
INSERT INTO unit_comments VALUES(1034008,103431,1,3,1,0.0,1,'总觉得似乎变得\n愉快起来了……\n是错觉……吗？');
INSERT INTO unit_comments VALUES(1034009,103431,1,4,1,0.0,1,'为了你成人的那一刻\n我们酿一瓶酒吧？\n等那天到了，就一起边庆祝边畅饮吧');
INSERT INTO unit_comments VALUES(1034010,103431,1,5,1,0.0,1,'我说……下次一起去约会吧？\n还是说，和我这种人约会，\n你……不愿意？');
INSERT INTO unit_comments VALUES(1034011,103401,2,6,2,2.0,1,'生日快乐！\n今天可要好好\n为你庆祝！');
INSERT INTO unit_comments VALUES(1034012,103431,2,6,1,0.0,1,'今天是你的生日\n所以首先……干杯！\n那个，我今天可以和你\n一直待在一起吗？');
INSERT INTO unit_comments VALUES(1034013,103461,1,1,1,0.0,1,'んふふ～気持ちいい～♪\nほら、キミも飲も飲も！\nもちろんキミはジュースだからね！\nはい、かんぱーいっ！');
INSERT INTO unit_comments VALUES(1034014,103461,1,2,1,0.0,1,'あぁ～染み渡るわぁ～\n仕事の後はこれに限るわよね～！\nそうだキミ、肩揉んでくれない？\nんっ、あ～っ、上手ね、キミ…');
INSERT INTO unit_comments VALUES(1034015,103461,1,3,1,0.0,1,'今日はお姉さんのおごりよぉ\nたくさん食べていいわよぉ～\n本当は手料理とかご馳走して\nあげたいんだけど…今度ね');
INSERT INTO unit_comments VALUES(1034016,103461,1,4,1,0.0,1,'私がこんな風にリラックス\nできるのはキミの前だけよ～\n迷惑かけちゃうかもしれないけど\nキミは許してくれるわよね…♪');
INSERT INTO unit_comments VALUES(1034017,103461,1,5,1,0.0,1,'よぉ～っし言っちゃおうかな～！\n私ねぇ～、キミのことが～…！\nふふ、や～っぱ秘密～！\n…あ、危なかったわ…');
INSERT INTO unit_comments VALUES(1036001,103601,1,1,1,5.9,5,'现在是学习的时间\n请不要和我说话……\n真是的，请不要露出那种表情啦\n我陪你玩就是了！');
INSERT INTO unit_comments VALUES(1036002,103601,1,2,5,4.8,4,'不吃青椒和胡萝卜就\n长不高？\n呜呜……虽然我很想\n长高……');
INSERT INTO unit_comments VALUES(1036003,103601,1,3,2,0.0,2,'请听我说\n我长高了！\n足足长了一毫米！');
INSERT INTO unit_comments VALUES(1036004,103601,1,4,1,5.2,5,'虽然我现在还是孩子，\n不过很快就会长大的\n到了那时……那个……');
INSERT INTO unit_comments VALUES(1036005,103601,1,5,2,0.0,2,'呵呵，你很努力了呢\n今天就表扬表扬你\n摸摸你的头吧');
INSERT INTO unit_comments VALUES(1036006,103631,1,1,1,0.0,1,'你要保护我？\n不需要你操心！\n不、不过……\n谢谢你');
INSERT INTO unit_comments VALUES(1036007,103631,1,2,1,0.0,1,'吓、吓死我了……\n好、好丢人……');
INSERT INTO unit_comments VALUES(1036008,103631,1,3,1,0.0,1,'呜呜……为什么\n每次在他面前的时候\n都会出糗啊……');
INSERT INTO unit_comments VALUES(1036009,103631,1,4,1,0.0,1,'我、我才不怕什么魔物呢！\n但、但是那个……\n幸好有你保护了我……');
INSERT INTO unit_comments VALUES(1036010,103631,1,5,1,0.0,1,'城外有魔物出没，很危险\n所以，我会保护你哦');
INSERT INTO unit_comments VALUES(1036011,103601,2,6,5,3.0,2,'今天是你的生日对吧\n我用攒下的零钱，\n给你买了纸杯蛋糕，\n请享用吧');
INSERT INTO unit_comments VALUES(1036012,103631,2,6,1,0.0,1,'生日快乐\n你在不断长大……\n我也得赶快追上你才行……\n没、没什么！');
INSERT INTO unit_comments VALUES(1037001,103701,1,1,3,8.2,1,'嗯？你在做什么？\n要是你做了可疑的事，\n即使对方是你，我也会逮捕的哦？\n我开玩笑的啦♪');
INSERT INTO unit_comments VALUES(1037002,103701,1,2,1,6.6,2,'想要你陪我练习啊\n没事的没事的！\n我会好好把握力道的！\n呵呵，大概');
INSERT INTO unit_comments VALUES(1037003,103701,1,3,1,5.6,2,'好了，今天也得去\n巡逻了。你也要一起来？\n不管发生什么事，我都会保护你的\n放心吧');
INSERT INTO unit_comments VALUES(1037004,103701,1,4,1,6.4,4,'唔唔，我明明觉得自己还是\n比较能干的……\n但在你面前总是诸事不顺\n……为什么会这样呢');
INSERT INTO unit_comments VALUES(1037005,103701,1,5,4,9.1,5,'明明是想捉弄你，却总是\n反过来被你捉弄\n真是的，被打乱了计划真伤脑筋\n真的……很伤脑筋啊……');
INSERT INTO unit_comments VALUES(1037006,103731,1,1,1,0.0,1,'观察对方的动作，准确地往要害\n刺下必杀的一剑！\n这就是剑术的基础');
INSERT INTO unit_comments VALUES(1037007,103731,1,2,1,0.0,1,'战斗过后就会非常疲累，\n而且饿得慌……你也\n一起用餐吧？');
INSERT INTO unit_comments VALUES(1037008,103731,1,3,1,0.0,1,'只要减少多余的动作，\n就能磨砺剑术\n虽然听起来简单，但其实很有难度呢……');
INSERT INTO unit_comments VALUES(1037009,103731,1,4,1,0.0,1,'为了能够守护你，我要\n变得更强。最近\n我是这么想的\n……真庆幸我学习了剑术');
INSERT INTO unit_comments VALUES(1037010,103731,1,5,1,0.0,1,'和你待在一起，就会不断涌出\n力量。你的存在\n让我变得更加强大');
INSERT INTO unit_comments VALUES(1037011,103701,2,6,1,9.3,2,'唔~今天是你生日啊？\n那么，就让长大成人的你\n请我吃顿饭吧？\n开玩笑的啦！');
INSERT INTO unit_comments VALUES(1037012,103731,2,6,1,0.0,1,'生日快乐！嗯，突然跑来\n是不是给你添麻烦了？我今天\n打算来给你收拾房间\n是不是就像贤妻一样？');
INSERT INTO unit_comments VALUES(1038001,103801,1,1,2,2.5,5,'我有书想推荐给你\n我想，如果能彼此畅谈感想\n应该会很开心……');
INSERT INTO unit_comments VALUES(1038002,103801,1,2,4,2.0,1,'咳咳……\n那个，能帮我\n拍拍背顺顺气吗？');
INSERT INTO unit_comments VALUES(1038003,103801,1,3,2,7.0,5,'等身体再好一点，\n我们就一起去再远点的地方……\n开玩笑的，诶嘿嘿');
INSERT INTO unit_comments VALUES(1038004,103801,1,4,4,4.5,5,'因为我体力很差\n所以很快就会疲劳……\n是不是给你添麻烦了？');
INSERT INTO unit_comments VALUES(1038005,103801,1,5,1,5.5,4,'我不能总是依赖别人\n自己的事情要自己……\n咳咳！\n对不起……请给我水');
INSERT INTO unit_comments VALUES(1038006,103831,1,1,1,0.0,1,'这孩子的名字叫喵特\n是个很听话的孩子哦~');
INSERT INTO unit_comments VALUES(1038007,103831,1,2,1,0.0,1,'虽然体型很大，可喵特很爱撒娇哦\n喵特，真是个好孩子呢~');
INSERT INTO unit_comments VALUES(1038008,103831,1,3,1,0.0,1,'曾经幼小的喵特也长大了呢\n它是我所珍视的重要朋友');
INSERT INTO unit_comments VALUES(1038009,103831,1,4,1,0.0,1,'累了吗？\n如果你不嫌弃……\n要不要跟我一起休息？');
INSERT INTO unit_comments VALUES(1038010,103831,1,5,1,0.0,1,'喵特抱起来毛茸茸的，好舒服\n待在你身边也……很舒服');
INSERT INTO unit_comments VALUES(1038011,103801,2,6,2,8.2,5,'生日快乐\n那个……姐姐她也\n为你庆祝了吗？\n我、我只是有点在意而已……');
INSERT INTO unit_comments VALUES(1038012,103831,2,6,1,0.0,1,'这本书是生日礼物\n是一本恋爱小说，碰巧\n里面女主角的名字叫栞，\n男、男主角的名字是……');
INSERT INTO unit_comments VALUES(1040001,104001,1,1,1,4.5,2,'为了交朋友而成立的队伍，\n那就是BB团！\n诶嘿嘿……今后也请多关照！');
INSERT INTO unit_comments VALUES(1040002,104001,1,2,1,6.5,5,'交友之道\n谁都是从第一步开始踏出的\n我也想……踏出那一步……');
INSERT INTO unit_comments VALUES(1040003,104001,1,3,2,0.0,2,'今天也给花浇了水，\n给动物喂了食♪\n诶嘿嘿，独自一人也能自得其乐');
INSERT INTO unit_comments VALUES(1040004,104001,1,4,5,9.0,7,'我偶尔会想\n只要有你在，\n就算交不到朋友……\n啊、没、没什么……');
INSERT INTO unit_comments VALUES(1040005,104001,1,5,5,3.5,6,'那个人对我……啊、\n你什么时候在那里的！？\n呃，这是鲜花占卜。\n问、问我在占卜什么……保密！');
INSERT INTO unit_comments VALUES(1040006,104031,1,1,1,0.0,1,'虽然我现在的力量很微薄，\n但我一定能帮到大家\n这样的话……有朝一日，我的朋友\n会增加吗？');
INSERT INTO unit_comments VALUES(1040007,104031,1,2,1,0.0,1,'即使独自一人……\n也一定有我\n力所能及的事\n我会拼命努力的！');
INSERT INTO unit_comments VALUES(1040008,104031,1,3,1,0.0,1,'守护想要守护的东西……\n我会为此努力的\nBB团教会了我\n各种各样的道理！');
INSERT INTO unit_comments VALUES(1040009,104031,1,4,1,0.0,1,'很期待每次BB团活动时\n与你相见……\n每天我都焦急地\n等待着第二天的到来！');
INSERT INTO unit_comments VALUES(1040010,104031,1,5,1,0.0,1,'诶嘿嘿……不管我\n今后交了多少朋友，\n你都是排在第一位的……\n请放心吧！');
INSERT INTO unit_comments VALUES(1040011,104001,2,6,5,9.2,2,'生、生日个大笨蛋！\n对不起，我没说过\n开心的祝福语……\n生日快落落略略！');
INSERT INTO unit_comments VALUES(1040012,104031,2,6,1,0.0,1,'生日快乐！\n我和森林里的小鸟们\n组成了合唱团，\n创作了一首生日歌！');
INSERT INTO unit_comments VALUES(1042001,104201,1,1,1,4.5,2,'我的咏唱术，\n是为了保护他人而使用的……\n希望这份力量能发挥更大的作用');
INSERT INTO unit_comments VALUES(1042002,104201,1,2,1,3.5,2,'那个……如果可以，\n能再陪我做偶像的训练吗？');
INSERT INTO unit_comments VALUES(1042003,104201,1,3,7,3.5,6,'♪♪……\n你、你什么时候来的！？\n刚才的，你听到了？');
INSERT INTO unit_comments VALUES(1042004,104201,1,4,1,4.0,5,'在你面前，\n我就能自然放松地唱歌……\n为什么呢？');
INSERT INTO unit_comments VALUES(1042005,104201,1,5,2,3.5,5,'唱歌能引发各种可能性……\n教会我这一点的，\n是同伴……和你');
INSERT INTO unit_comments VALUES(1042006,104231,1,1,1,0.0,1,'希望你……再陪我一会儿……\n仅仅是你在我身边，我的脑海里就会\n不可思议地响起旋律');
INSERT INTO unit_comments VALUES(1042007,104231,1,2,1,0.0,1,'你醒了吗？对面街道的祭典\n开始了哦\n待会儿一起去看看吧？');
INSERT INTO unit_comments VALUES(1042008,104231,1,3,1,0.0,1,'想说的话源源不断地从心底涌出\n多到停不下笔……\n原来我是如此地想要唱歌啊');
INSERT INTO unit_comments VALUES(1042009,104231,1,4,1,0.0,1,'有机会的话……请听听\n我唱给你的歌吧……\n我会大大方方地唱的');
INSERT INTO unit_comments VALUES(1042010,104231,1,5,1,0.0,1,'海浪的声音真舒心呀\n真想和你一直听下去\n…我刚才说了什么吗？');
INSERT INTO unit_comments VALUES(1042011,104201,2,6,1,6.6,5,'生日快乐\n除了唱歌之外，我想不出\n其它更好的礼物了……\n不介意的话，请听吧');
INSERT INTO unit_comments VALUES(1042012,104231,2,6,1,0.0,1,'生日快乐\n我试着写了一首简短的曲子\n为你庆生\n可以在这里唱吗？');
INSERT INTO unit_comments VALUES(1043001,104301,1,1,1,3.3,2,'好了，该去巡视了\n你要是也想去，\n可以跟着一起来');
INSERT INTO unit_comments VALUES(1043002,104301,1,2,1,3.5,2,'喂，你有好好\n吃饭吧？\n要是没吃，\n我来给你做');
INSERT INTO unit_comments VALUES(1043003,104301,1,3,6,6.2,5,'会把我这样的人\n当普通女孩子对待，\n也就只有你了\n我、我还挺高兴的');
INSERT INTO unit_comments VALUES(1043004,104301,1,4,1,4.6,2,'好朋友的恩人，\n就是我的恩人\n让我们和睦相处吧！');
INSERT INTO unit_comments VALUES(1043005,104301,1,5,1,5.1,5,'你挺有男子气概的，\n而且胆量十足……\n和你在一起，我很开心\n你呢？');
INSERT INTO unit_comments VALUES(1043006,104331,1,1,1,0.0,1,'别离开我身边！\n不管来者是何人，\n我都会全部解决！');
INSERT INTO unit_comments VALUES(1043007,104331,1,2,1,0.0,1,'全是一群菜鸟\n都不够我玩的\n就没有强一点的\n家伙吗！？');
INSERT INTO unit_comments VALUES(1043008,104331,1,3,1,0.0,1,'只要和你在一起，\n无论怎样的敌人都是小菜一碟！\n我会保护你的，\n所以我的事也拜托你了♪');
INSERT INTO unit_comments VALUES(1043009,104331,1,4,1,0.0,1,'好！\n把敌人全部收拾掉之后，\n我们就去大吃一顿吧！\n我会做一大堆你喜欢的食物！');
INSERT INTO unit_comments VALUES(1043010,104331,1,5,1,0.0,1,'力量涌出来了！\n别离开我身边！\n无论发生什么\n我都一定会保护你♪');
INSERT INTO unit_comments VALUES(1043011,104301,2,6,1,3.2,2,'嘿，今天是你生日啊\n生日快乐！\n难得遇上今天的日子\n我请你吃东西吧！');
INSERT INTO unit_comments VALUES(1043012,104331,2,6,1,0.0,1,'今天是你的生日对吧？\n我偶、偶然，碰巧\n做了个蛋糕……\n要吃吗？');
INSERT INTO unit_comments VALUES(1044001,104401,1,1,6,5.0,1,'汝……妾身在哪里\n见过汝吗？\n不知为何，看着汝\n就会觉得很怀念……');
INSERT INTO unit_comments VALUES(1044002,104401,1,2,4,6.4,1,'唔……不知为何\n突然很想喝\n番茄汁……\n汝去寻些来吧');
INSERT INTO unit_comments VALUES(1044003,104401,1,3,4,6.9,2,'白天时，身体无法如愿行动……\n在夜晚来临之前，妾身还是安静待着吧\n在那之前，汝来\n陪妾身说话');
INSERT INTO unit_comments VALUES(1044004,104401,1,4,6,4.2,4,'汝还真是一点也\n不惧怕妾身呀？\n什么？可爱？\n区区眷属说些什么呢！');
INSERT INTO unit_comments VALUES(1044005,104401,1,5,2,9.0,5,'汝是特殊的眷属\n从今天开始，汝可以称呼自己为\n“统帅白昼之主”');
INSERT INTO unit_comments VALUES(1044006,104431,1,1,1,0.0,1,'妾身真正的样子很美吧？\n什么？因为不习惯\n所以还是平时的样子更好？\n真让人泄气！');
INSERT INTO unit_comments VALUES(1044007,104431,1,2,1,0.0,1,'汝想知道妾身的野心？\n当然是征服世界了！\n既然生为女性，\n自然是要为此奋斗一回的♪');
INSERT INTO unit_comments VALUES(1044008,104431,1,3,1,0.0,1,'妾身乃传说中的吸血鬼\n要夺走汝的性命\n简直轻而易举……不过，\n汝乃是眷属，还是让汝继续活着吧');
INSERT INTO unit_comments VALUES(1044009,104431,1,4,1,0.0,1,'待妾身征服世界的那一天\n就把世界分汝一半\n汝、汝知道这是什么意思吧？');
INSERT INTO unit_comments VALUES(1044010,104431,1,5,1,0.0,1,'为了能在白天见到汝\n最近昼夜颠倒了……\n熬昼可是美容的\n大敌啊……');
INSERT INTO unit_comments VALUES(1044011,104401,2,6,2,12.9,6,'今天是汝的生日？甚是值得庆贺！\n汝几岁了？\n是吗。妾身在汝这个年纪的时候……\n唔？那时的妾身在做什么来着？');
INSERT INTO unit_comments VALUES(1044012,104431,2,6,1,0.0,1,'作为眷属生日的庆祝，\n赠予汝妾身的膝枕吧\n如何，很舒适吧？\n臂枕或者胸枕也可以的哦？');
INSERT INTO unit_comments VALUES(1045001,104501,1,1,5,4.0,2,'唔呼呼呼……\n诶？没、没什么哦？\n对着你乱想什么的……\n哈哈……');
INSERT INTO unit_comments VALUES(1045002,104501,1,2,5,5.0,6,'“虐待狂先生逼近空花，\n然后强硬地……”\n呵嘿嘿嘿嘿……啊！\n你你你什么时候来的！？');
INSERT INTO unit_comments VALUES(1045003,104501,1,3,2,3.3,6,'前段时间看的书非常棒……诶？\n内容吗？这、这个……\n还是别问为好哦？');
INSERT INTO unit_comments VALUES(1045004,104501,1,4,2,4.0,5,'虐待狂先生竟然出现在梦里了……\n无论睡着还是醒着，空花\n都一直被控制着呢');
INSERT INTO unit_comments VALUES(1045005,104501,1,5,2,3.9,5,'和虐待狂先生独处……\n这、这！？\n空花会受到怎样的对待呢！');
INSERT INTO unit_comments VALUES(1045006,104531,1,1,1,0.0,1,'虐待狂先生看着污秽的空花的\n冷酷视线\n噫、噫噫！\n啊啊，又让人兴奋起来了！');
INSERT INTO unit_comments VALUES(1045007,104531,1,2,1,0.0,1,'空花的愿望就是\n丑态毕露之后\n还被虐待狂先生\n整得狼狈不堪！');
INSERT INTO unit_comments VALUES(1045008,104531,1,3,1,0.0,1,'“被魔物们弄成这样\n你很高兴吧！”\n你是打算这样责问空花吧！？\n不愧是虐待狂先生，真厉害！');
INSERT INTO unit_comments VALUES(1045009,104531,1,4,1,0.0,1,'空花已经嫁不出去了……\n所以，请让我一生都\n追随虐待狂先生吧！\n请尽情地管教我吧！');
INSERT INTO unit_comments VALUES(1045010,104531,1,5,1,0.0,1,'空花已经变成\n没有虐待狂先生就活不下去了……\n这、这份责任\n你会承担的对吧？');
INSERT INTO unit_comments VALUES(1045011,104501,2,6,1,8.5,2,'生日快乐\n深深插在蛋糕上的蜡烛，\n在渐渐地融化\n请“呼”地……把它们吹灭');
INSERT INTO unit_comments VALUES(1045012,104531,2,6,1,0.0,1,'虐待狂先生的生日啊……\n把缠在空花身上的丝带\n粗鲁地扯开，像迫不及待撕开礼物包装纸的\n孩子一样将空花的衣服也……唔呼呼呼');
INSERT INTO unit_comments VALUES(1046001,104601,1,1,1,3.0,2,'我给野猫们买了鲷鱼烧喵！\n它们可高兴了喵♪');
INSERT INTO unit_comments VALUES(1046002,104601,1,2,1,2.3,2,'快点做好准备出门喵！\n“时间就是金钱”喵！');
INSERT INTO unit_comments VALUES(1046003,104601,1,3,1,5.8,2,'这位客人♪\n我这里有一笔秘密的好买卖喵~\n您要不要算上一份喵？');
INSERT INTO unit_comments VALUES(1046004,104601,1,4,5,4.9,2,'我很善变，所以希望你能\n好好抓住我的心喵？\n不然我就会逃走了喵！');
INSERT INTO unit_comments VALUES(1046005,104601,1,5,5,6.0,2,'呵呵呵\n如果你受伤了……\n我也可以帮你……\n舔一舔喵？');
INSERT INTO unit_comments VALUES(1046006,104631,1,1,1,0.0,1,'别看我这样，如果有人有困难\n我是不会坐视不管的喵！\n今晚，我要为了正义，和猫咪\n一起四处奔走喵！');
INSERT INTO unit_comments VALUES(1046007,104631,1,2,1,0.0,1,'下次\n来公会玩吧喵\n喵呵呵，这样人手就增加了……\n没、没什喵！');
INSERT INTO unit_comments VALUES(1046008,104631,1,3,1,0.0,1,'我虽然很随心所欲，\n但也是只该出手时就出手的猫\n今天要出手喵？\n那就……全力以赴喵！');
INSERT INTO unit_comments VALUES(1046009,104631,1,4,1,0.0,1,'诶嘿嘿。能和你一起的话……\n变成家猫\n也无所谓喵~\n我开玩笑的喵？');
INSERT INTO unit_comments VALUES(1046010,104631,1,5,1,0.0,1,'我想待会儿跟你\n一起吃\n所以就把鲷鱼烧留着了喵\n一人一半吃了吧喵~');
INSERT INTO unit_comments VALUES(1046011,104601,2,6,1,4.5,2,'今天好像是你的生日啊喵！\n所以仅限今天\n特别允许你\n吃鲷鱼烧吃个够喵~');
INSERT INTO unit_comments VALUES(1046012,104631,2,6,1,0.0,1,'生日快乐喵！\n即使是任性如我，\n唯独今天……可以成为\n你的专属猫咪哦喵♪');
INSERT INTO unit_comments VALUES(1047001,104701,1,1,1,0.0,1,'虽然我穿成这样，但我也是女性\n因为某些理由不能除去盔甲\n请多关照');
INSERT INTO unit_comments VALUES(1047002,104701,1,2,1,0.0,1,'盔甲晒得太久的话\n就会烫得厉害\n不过，我在里面反而没什么问题');
INSERT INTO unit_comments VALUES(1047003,104701,1,3,1,0.0,1,'我喜欢运动\n不过不擅长游泳\n因为盔甲太重了……');
INSERT INTO unit_comments VALUES(1047004,104701,1,4,1,0.0,1,'我不能露脸\n所以我会更加努力\n去表达自己当前的想法');
INSERT INTO unit_comments VALUES(1047005,104701,1,5,1,0.0,1,'虽然你可能不知道\n我是什么表情，不过你的表情\n我一直都看得很清楚哦');
INSERT INTO unit_comments VALUES(1047006,104731,1,1,1,0.0,1,'为了让这世间\n不再需要挥剑，\n我现在必须挥剑');
INSERT INTO unit_comments VALUES(1047007,104731,1,2,1,0.0,1,'无论对手是怎样的强敌\n为了孩子们的笑容\n我都会无所畏惧地面对');
INSERT INTO unit_comments VALUES(1047008,104731,1,3,1,0.0,1,'即使战场被火焰包围\n只要有求救声\n我就会奔赴那里');
INSERT INTO unit_comments VALUES(1047009,104731,1,4,1,0.0,1,'战斗的时候\n若是能感到身后你的视线\n我会很安心');
INSERT INTO unit_comments VALUES(1047010,104731,1,5,1,0.0,1,'身为团长，我必须平等地守护\n人们的安宁，可只有对你\n我会忍不住将你放在第一位……');
INSERT INTO unit_comments VALUES(1047011,104701,2,6,1,0.0,1,'听说今天是你的生日啊\n既然都长大一岁了，今年\n就不要那么乱来了哦？\n我会担心的');
INSERT INTO unit_comments VALUES(1047012,104731,2,6,1,0.0,1,'生日快乐！你有什么\n愿望吗？那、那\n可不行啊少年！竟然要我脱掉头盔……\n我会害羞到爆炸的！');
INSERT INTO unit_comments VALUES(1048001,104801,1,1,1,3.9,2,'无论是赚素材还是赚金币……\n两方的战略我都制定好了\n有需要就来找我商量吧');
INSERT INTO unit_comments VALUES(1048002,104801,1,2,2,2.9,2,'这次准备的计划也安排妥当了\n如果要去城外\n就快点出发吧');
INSERT INTO unit_comments VALUES(1048003,104801,1,3,2,4.5,1,'高效行动的关键\n在于观察力哦\n你平时有认真锻炼吗？');
INSERT INTO unit_comments VALUES(1048004,104801,1,4,2,4.1,5,'我的兼职太多了\n你能不能来帮忙打工？\n因为你很可靠嘛');
INSERT INTO unit_comments VALUES(1048005,104801,1,5,2,5.8,2,'你的日程，\n要不要交给我来管理？\n我会把24小时都安排好的\n当然了，管理费可是要付的');
INSERT INTO unit_comments VALUES(1048006,104831,1,1,1,0.0,1,'不介意的话，你的钱包\n交给我管理吧？\n诶？我才不会偷钱呢！\n不要误会我！');
INSERT INTO unit_comments VALUES(1048007,104831,1,2,1,0.0,1,'这个城里出现小偷了！？\n那不是对认真工作的人的\n亵渎吗！\n不、不可原谅！');
INSERT INTO unit_comments VALUES(1048008,104831,1,3,1,0.0,1,'今天是特惠促销日！\n当然，你也一起来吧！\n一旦大意可是会送命的！\n那么……突击！');
INSERT INTO unit_comments VALUES(1048009,104831,1,4,1,0.0,1,'那个……\n你有喜欢的类型吗？\n诶？我、我只是有点在意\n而已。就一点点啦！');
INSERT INTO unit_comments VALUES(1048010,104831,1,5,1,0.0,1,'如果有人伤害你……\n我就会像这样把他收拾掉！\n你可是我的宝贝徒弟！');
INSERT INTO unit_comments VALUES(1048011,104801,2,6,2,0.0,2,'蛋糕准备完毕！\n礼物准备完毕！\n好了，高效的\n生日派对开始了哦！');
INSERT INTO unit_comments VALUES(1048012,104831,2,6,1,0.0,1,'生日快乐\n虽然祝福的话语什么的\n也许很没有效率……\n但是对你，我想认真地说出来');
INSERT INTO unit_comments VALUES(1049001,104901,1,1,5,4.8,2,'不管有什么事\n都要跟姐姐说哦！\n多多依靠我，向我撒娇吧~\n不用客气哦☆');
INSERT INTO unit_comments VALUES(1049002,104901,1,2,5,3.2,2,'不论什么时候都可以\n向姐姐撒娇哦☆\n摸头也好，抱抱也好，\n想要多少有多少♪');
INSERT INTO unit_comments VALUES(1049003,104901,1,3,5,4.3,2,'姐姐高兴得\n忍不住要使出手刀了~\n这是姐姐的一番情义，\n好好收下吧！');
INSERT INTO unit_comments VALUES(1049004,104901,1,4,5,2.8,2,'姐姐可以为你做任何事情♪\n毕竟，照顾弟弟\n是姐姐的职责嘛♪');
INSERT INTO unit_comments VALUES(1049005,104901,1,5,6,2.1,2,'怎么了？有事情？\n无论什么时候、无论在哪儿、\n无论有什么事情，\n姐姐都非常欢迎哦！');
INSERT INTO unit_comments VALUES(1049006,104931,1,1,1,0.0,1,'让可爱的弟弟犯愁的人，\n姐姐会将他们全~部惩处的！');
INSERT INTO unit_comments VALUES(1049007,104931,1,2,1,0.0,1,'无论发生什么，\n姐姐都绝对会保护弟弟的！\n我不会让人碰弟弟一根手指的！');
INSERT INTO unit_comments VALUES(1049008,104931,1,3,1,0.0,1,'姐姐是为了保护弟弟才变强的\n姐姐是弟弟的盾，弟弟的剑，\n也是弟弟的盔甲');
INSERT INTO unit_comments VALUES(1049009,104931,1,4,1,0.0,1,'打扰我与弟弟共度时光\n不识趣的家伙，姐姐决不原谅！\n这可不是光吃一记手刀\n就能完事的！');
INSERT INTO unit_comments VALUES(1049010,104931,1,5,1,0.0,1,'竟敢欺负弟弟，\n即使神明允许，\n老天饶恕，\n姐姐也不会原谅！');
INSERT INTO unit_comments VALUES(1049011,104901,2,6,5,7.9,2,'就算你长了一岁，\n姐姐我也会长大，\n我永远都是弟弟的\n姐姐哦♪你放心了吗？');
INSERT INTO unit_comments VALUES(1049012,104931,2,6,1,0.0,1,'生日快乐！\n让姐姐好好瞧瞧，\n弟弟变成熟了多少！\n盯♪');
INSERT INTO unit_comments VALUES(1050001,105001,1,1,1,2.5,2,'你快看看这本杂志！\n右边的衣服和左边的衣服，\n哪一件更适合我？');
INSERT INTO unit_comments VALUES(1050002,105001,1,2,1,6.9,6,'喂，我能问一个问题吗？\n你是不是能计算三位数的乘法？\n莫、莫非，除法也可以！？');
INSERT INTO unit_comments VALUES(1050003,105001,1,3,4,3.5,5,'成年女性的教养也很重要？\n教、教养这东西，\n和成绩没关系吧！？');
INSERT INTO unit_comments VALUES(1050004,105001,1,4,1,5.5,2,'就算和同龄人聊天，\n大家也都很孩子气，太无聊了\n所以我就陪你玩吧\n你可得好好感谢我！');
INSERT INTO unit_comments VALUES(1050005,105001,1,5,2,3.4,5,'先去咖啡店，接下来……\n你看上去对这种事情不太了解\n我得好好引导才行！');
INSERT INTO unit_comments VALUES(1050006,105031,1,1,1,0.0,1,'我可是成年女性，\n驱除魔物轻轻松松啦！\n不过，如果你实在担心，\n也可以跟着我哦？');
INSERT INTO unit_comments VALUES(1050007,105031,1,2,1,0.0,1,'前段时间，我收拾掉魔物\n弄到了好多宝物！\n不过爸妈说“小孩子不需要太多钱”\n于是把我的钱没收了……');
INSERT INTO unit_comments VALUES(1050008,105031,1,3,1,0.0,1,'闪开，魔物们♪\n美咲小姐要从这里经过！\n你要好好\n护送我喔！');
INSERT INTO unit_comments VALUES(1050009,105031,1,4,1,0.0,1,'最近好像经常\n和你一起出门呢\n我觉得很开心哦\n你呢？');
INSERT INTO unit_comments VALUES(1050010,105031,1,5,1,0.0,1,'我们两个一起消灭魔物，\n用那笔钱吃顿豪华大餐吧！\n这么做很有大人的感觉吧♪\n并、并不是约会哦！');
INSERT INTO unit_comments VALUES(1050011,105001,2,6,2,9.8,6,'生日快乐！\n礼物就是……我·本·人·哦♪\n咦？怎么冷场了？\n明明杂志上是这么写的呀？');
INSERT INTO unit_comments VALUES(1050012,105031,2,6,1,0.0,1,'Happy birthday！\n我来帮你筹办一场\n成熟的派对\n敬请期待吧♪');
INSERT INTO unit_comments VALUES(1051001,105101,1,1,1,3.0,2,'我说，你现在有时间吗？\n不用害怕，不会有什么危险的事\n相信我……好吗？');
INSERT INTO unit_comments VALUES(1051002,105101,1,2,1,2.3,5,'可以的话，要和我一起做实验吗？\n有了全新发现时，\n那份兴奋真是无法计量啊♪');
INSERT INTO unit_comments VALUES(1051003,105101,1,3,1,2.5,2,'科学的发展是为了所有人\n别看我这样，我也是立志\n治疗疾病与创伤的哦');
INSERT INTO unit_comments VALUES(1051004,105101,1,4,5,5.4,2,'在调查你的身体的过程中，\n我对你本人也产生了兴趣\n呵呵……');
INSERT INTO unit_comments VALUES(1051005,105101,1,5,2,1.7,5,'谢谢你一直协助我做实验。\n作为答谢，\n下次就送给你珍藏的秘药吧♪');
INSERT INTO unit_comments VALUES(1051006,105131,1,1,1,0.0,1,'哎呀，想要被我惩罚吗？\n是吗……你觉醒了呢\n我会好好疼爱你的♪');
INSERT INTO unit_comments VALUES(1051007,105131,1,2,1,0.0,1,'哎，怎么样？\n看着我，有什么感觉？\n恐惧？真奇怪');
INSERT INTO unit_comments VALUES(1051008,105131,1,3,1,0.0,1,'痛苦转变成快乐的瞬间，\n一定会降临的……\n你也体验看看吧？');
INSERT INTO unit_comments VALUES(1051009,105131,1,4,1,0.0,1,'我明明是为了俘获你\n才喷香水的，\n为什么招来的都是魔物……');
INSERT INTO unit_comments VALUES(1051010,105131,1,5,1,0.0,1,'和你在一起很不可思议……\n心跳会突然加速\n要不要确认一下？');
INSERT INTO unit_comments VALUES(1051011,105101,2,6,5,12.8,2,'今天是你的生日对吧？\n来，这是我送你的礼物\n是我特意调制的药哦。作用？\n你喝了就知道了♪');
INSERT INTO unit_comments VALUES(1051012,105131,2,6,1,0.0,1,'Happy birthday！\n我的礼物是……这个哦\n能够提神的特制饮料♪\n不可以一口气喝完哦？');
INSERT INTO unit_comments VALUES(1052001,105201,1,1,1,5.8,5,'我力气很大，\n所以会利用这一点努力工作♪\n不过，对于女孩子来说\n这样有点奇怪吧？');
INSERT INTO unit_comments VALUES(1052002,105201,1,2,1,7.0,9,'怎样？这铠甲很棒吧？\n设计也很时尚\n还很贴合身体的曲线！\n我很喜欢哦！');
INSERT INTO unit_comments VALUES(1052003,105201,1,3,5,0.0,5,'真是的~你从刚刚开始\n就揉过头了吧？\n难得我才梳理了一番\n整理好了毛发~');
INSERT INTO unit_comments VALUES(1052004,105201,1,4,2,4.8,1,'为了看上去\n能更像女孩子一些，我会努力的！\n希望能像这样\n继续待在你身边……');
INSERT INTO unit_comments VALUES(1052005,105201,1,5,5,0.0,5,'啊~好憧憬啊……\n好想像普通的人类女孩子一样\n精心打扮，边吃边走\n和你约会啊……');
INSERT INTO unit_comments VALUES(1052006,105231,1,1,1,0.0,1,'不能因为长成这样\n就从一开始放弃！\n我不想被你讨厌\n所以会注意着装打扮的');
INSERT INTO unit_comments VALUES(1052007,105231,1,2,1,0.0,1,'怎么了？还要摸啊？\n可以倒是可以啦，既然是你\n我就好好摸摸你的头，夸你乖~乖~吧');
INSERT INTO unit_comments VALUES(1052008,105231,1,3,1,0.0,1,'虽然经常被误认为是魔物\n但我不会输给命运！\n与你相遇后，\n我就更加这样想了');
INSERT INTO unit_comments VALUES(1052009,105231,1,4,1,0.0,1,'即使看到我的这副模样\n你也把我当女孩子对待！\n你会注重人的内在\n我最喜欢这样的你了！');
INSERT INTO unit_comments VALUES(1052010,105231,1,5,1,0.0,1,'在如此美丽的天空之下\n与心仪的人一起漫步\n我好像变成了普通的女孩子\n好开心！');
INSERT INTO unit_comments VALUES(1052011,105201,2,6,1,7.2,9,'生日快乐！\n我为了你想了\n好多庆祝的计划♪\n讨厌，我是不是兴奋过头了？');
INSERT INTO unit_comments VALUES(1052012,105231,2,6,1,0.0,1,'Happy birthday to you♪\n竟然能像这样庆祝\n人类男孩子的生日……\n反倒像是我自己收到了礼物♪');
INSERT INTO unit_comments VALUES(1052013,105261,1,1,1,0.0,1,'いつかこの素敵なドレスを着て\nあなたと踊れたらいいな…\n踊りの練習だけでも\n始めておこうかしら♪');
INSERT INTO unit_comments VALUES(1052014,105261,1,2,1,0.0,1,'舞台の幕が上がったら\n私はお姫様…まさかこんな形で\nお姫様になれるなんて\n思わなかったけど、うれしいわ♪');
INSERT INTO unit_comments VALUES(1052015,105261,1,3,1,0.0,1,'私がお姫様になったら\n何をしようかしら…\n王子様と二人きりの舞踏会とか\nしてみたいわね♪');
INSERT INTO unit_comments VALUES(1052016,105261,1,4,1,0.0,1,'私、あなたと出会ってから毎日が\nどんどん楽しくなっていくの！\n次はいつ会えるんだろうって\n考えてるだけで幸せよ♪');
INSERT INTO unit_comments VALUES(1052017,105261,1,5,1,0.0,1,'あなたが王子様だったら、\n誰のどんなピンチにでも\n駆けつけてくれるんでしょうね！\n優しい王子様に憧れちゃう♪');
INSERT INTO unit_comments VALUES(1053001,105301,1,1,1,4.1,2,'阁下，\n与我一同\n为了纯白之翼的繁荣\n鞠躬尽瘁吧！');
INSERT INTO unit_comments VALUES(1053002,105301,1,2,3,2.7,5,'我、我才不是小孩子！\n虽、虽说个头的确有点矮……\n不过我还在成长期呢，一定是这样！');
INSERT INTO unit_comments VALUES(1053003,105301,1,3,1,4.5,2,'嗯？怎么了？\n想和我一起锻炼吗？\n你的这份意志值得表扬，阁下！');
INSERT INTO unit_comments VALUES(1053004,105301,1,4,1,4.5,2,'军人要知恩图报\n所以……如果有什么\n我能做到的事，就尽管说吧\n放心交给我');
INSERT INTO unit_comments VALUES(1053005,105301,1,5,6,3.0,5,'说、说来……\n下次我想和阁下\n两个人去我的祖国旅行……\n如、如何？');
INSERT INTO unit_comments VALUES(1053006,105331,1,1,1,0.0,1,'要说几次你才懂啊！\n都说了我不是小孩子！\n别、别小看我！\n喂、喂，不许摸我的头！');
INSERT INTO unit_comments VALUES(1053007,105331,1,2,1,0.0,1,'从天而降的\n纯白之翼的剑技！\n就让你见识一下\n我的华丽的身姿吧！');
INSERT INTO unit_comments VALUES(1053008,105331,1,3,1,0.0,1,'阁下！\n你是不是太懒散了？\n马上准备一下，到平原来！\n和我一起来锻炼！');
INSERT INTO unit_comments VALUES(1053009,105331,1,4,1,0.0,1,'我不是小孩子，不过……\n在阁下看来，我果然还是\n像个小孩子吧？\n如果不是就好……');
INSERT INTO unit_comments VALUES(1053010,105331,1,5,1,0.0,1,'我的命就托付给阁下了\n我会鞠躬尽瘁……\n带着军人的骄傲，\n与阁下一起战斗到底！');
INSERT INTO unit_comments VALUES(1053011,105301,2,6,1,8.1,2,'今天似乎是阁下的生日啊\n能与阁下一同庆祝这重要之日，\n我也觉得很高兴！\n让我诚心诚意地为阁下庆祝吧！');
INSERT INTO unit_comments VALUES(1053012,105331,2,6,1,0.0,1,'生日快乐\n作为礼物的代替……\n今天就让我陪你度过吧\n你、你可不要白白浪费时间了哦！');
INSERT INTO unit_comments VALUES(1054001,105401,1,1,1,1.9,5,'听好了，骑士先生\n你要是敢对怜大人做什么奇怪的事\n我是不会轻易放过你的！');
INSERT INTO unit_comments VALUES(1054002,105401,1,2,1,2.8,2,'我会好好盯着骑士先生\n不让你对怜大人\n做奇怪的事情！');
INSERT INTO unit_comments VALUES(1054003,105401,1,3,4,2.0,1,'啊啊，你的衣服又……\n你过来一下\n我马上帮你弄好');
INSERT INTO unit_comments VALUES(1054004,105401,1,4,4,4.8,6,'那个……骑士先生\n果然是对怜大人……\n没、没什么！\n忘了我说的话吧！');
INSERT INTO unit_comments VALUES(1054005,105401,1,5,5,6.5,6,'骑士先生，我做了这么过分的事\n你为什么还要\n对我这么温柔？\n难道说……你还想被我绑起来吗！？');
INSERT INTO unit_comments VALUES(1054006,105431,1,1,1,0.0,1,'来几只都无所谓！\n因为有骑士先生你在我身边！\n这句话可没什么其他的意思哦！？');
INSERT INTO unit_comments VALUES(1054007,105431,1,2,1,0.0,1,'不管怎么说你还是靠得太近了\n请再稍微退后一些\n不然我就要把日常的郁闷\n发泄到你身上了哦？');
INSERT INTO unit_comments VALUES(1054008,105431,1,3,1,0.0,1,'要是材料有剩，我也给\n骑士先生做些什么吧\n我想想……做什么好呢？');
INSERT INTO unit_comments VALUES(1054009,105431,1,4,1,0.0,1,'你最近……和怜大人的关系\n还是一如既往的好吗？\n我、我就是问问！');
INSERT INTO unit_comments VALUES(1054010,105431,1,5,1,0.0,1,'骑士先生\n有什么想要我帮忙的事吗？\n特别给你免费哦');
INSERT INTO unit_comments VALUES(1054011,105401,2,6,1,5.3,5,'你、你生日对吧？\n姑且祝你生日快乐！\n问我从哪里得知的？\n那种事不需要在意！');
INSERT INTO unit_comments VALUES(1054012,105431,2,6,1,0.0,1,'那个，生日快乐……\n我是说骑士先生的生日！\n这是……手帕……诶？\n这是礼物啦！笨蛋！');
INSERT INTO unit_comments VALUES(1055001,105501,1,1,1,2.5,5,'盯——啊！\n前、前辈！？\n呃……那个那个……\n没、没什么……');
INSERT INTO unit_comments VALUES(1055002,105501,1,2,6,4.7,5,'啊哇哇，前呸！？\n今今、今甜也是个好日子，\n咬、咬到舌头了……太丢人了。');
INSERT INTO unit_comments VALUES(1055003,105501,1,3,6,3.6,4,'啊，前、前辈！？\n我、我没有跟着你……\n这、这是……在观察……');
INSERT INTO unit_comments VALUES(1055004,105501,1,4,6,5.7,5,'啊、啊、那个……前前、前辈！\n可以的话下次，一起……\n吃……果、果然还是说不出口！');
INSERT INTO unit_comments VALUES(1055005,105501,1,5,2,5.4,5,'前、前辈……总、总有一天\n我会好好谢谢你的……\n请、请等着吧！');
INSERT INTO unit_comments VALUES(1055006,105531,1,1,1,0.0,1,'谢谢你让我这样的人\n帮忙击退魔物！\n我、我会加油的！');
INSERT INTO unit_comments VALUES(1055007,105531,1,2,1,0.0,1,'虽然我很害怕战斗，\n但是现在有前辈在，\n我觉得非常有底气！');
INSERT INTO unit_comments VALUES(1055008,105531,1,3,1,0.0,1,'前、前辈，请退后！\n我、我来……把魔物……\n呜呜，还是请你帮帮我——');
INSERT INTO unit_comments VALUES(1055009,105531,1,4,1,0.0,1,'我总是得到前辈的帮助，\n所以下次由我来帮前辈！\n我、我想报恩！');
INSERT INTO unit_comments VALUES(1055010,105531,1,5,1,0.0,1,'前、前辈和我的配合\n很、很完美呢！\n诶嘿嘿，齐心协力♪');
INSERT INTO unit_comments VALUES(1055011,105501,2,6,2,8.8,5,'那、那个，前辈！\n生日快乐！\n请让我为你庆猪！\n说、说出来了！');
INSERT INTO unit_comments VALUES(1055012,105531,2,6,1,0.0,1,'生日快乐！\n请、请、请收下礼物！\n我找到一件很适合前辈的衣服，\n还有，前辈喜欢的……');
INSERT INTO unit_comments VALUES(1056001,105601,1,1,1,5.2,2,'不知是不是因为太爱多管闲事，\n我经常被卷入麻烦事\n要是你也有烦恼的事\n无须客气，尽管跟我说');
INSERT INTO unit_comments VALUES(1056002,105601,1,2,2,4.4,1,'工作也告一段落了\n去钓会儿鱼吧……\n你要不要也来呢？');
INSERT INTO unit_comments VALUES(1056003,105601,1,3,2,8.3,5,'你来得正好啊！\n我稍微教训了一下\n作恶的家伙，就收到了谢礼\n来陪我喝一杯吧');
INSERT INTO unit_comments VALUES(1056004,105601,1,4,6,2.7,1,'你说让我别抱住你？\n虽然我也明白\n但谁让你\n是个好男人呢？');
INSERT INTO unit_comments VALUES(1056005,105601,1,5,1,5.0,2,'你看起来一副可爱的样子\n没想到却很有胆量\n下次再碰到麻烦事的时候\n就靠你了哦');
INSERT INTO unit_comments VALUES(1056006,105631,1,1,1,0.0,1,'为了你的话\n拔出这把刀也不是件坏事……\n我会全力以赴！');
INSERT INTO unit_comments VALUES(1056007,105631,1,2,1,0.0,1,'竟敢逼我使出另一把刀\n我会让你发自内心地感到后悔的\n亲身好好体会一下吧！');
INSERT INTO unit_comments VALUES(1056008,105631,1,3,1,0.0,1,'要打退多少敌人才行？\n我还打算赶紧收拾完\n好好休息一下的呢……');
INSERT INTO unit_comments VALUES(1056009,105631,1,4,1,0.0,1,'你要帮我吗？\n有你在我就放心了！\n赶紧解决掉吧！');
INSERT INTO unit_comments VALUES(1056010,105631,1,5,1,0.0,1,'能让我交托后背的人\n也就只有你一个了\n今后也要靠你了');
INSERT INTO unit_comments VALUES(1056011,105601,2,6,1,3.3,2,'今天是你生日吗？\n是吗……在这样的喜庆之日\n可得隆重庆祝一番。既然这样决定了\n那今天的饭我来请！');
INSERT INTO unit_comments VALUES(1056012,105631,2,6,1,0.0,1,'生日快乐！\n我买到一把好钓竿\n送给你吧。下次我们\n一起悠闲地钓鱼吧');
INSERT INTO unit_comments VALUES(1057001,105701,1,1,1,3.2,2,'同为骑空士\n我们一起加油吧！\n要是有什么困难\n就随时找我商量吧！');
INSERT INTO unit_comments VALUES(1057002,105701,1,2,1,3.0,2,'如果你愿意的话，要不要一起修行？\n比起一个人，两个人一起修行\n能变得更强，你不这么认为吗？');
INSERT INTO unit_comments VALUES(1057003,105701,1,3,1,0.0,1,'等我们的骑空艇做好了\n我想先围绕\n阿斯特莱亚大陆飞一圈呢');
INSERT INTO unit_comments VALUES(1057004,105701,1,4,2,3.7,5,'有你和我一起，\n我就什么都不怕！\n我是说真的哦？');
INSERT INTO unit_comments VALUES(1057005,105701,1,5,1,4.0,6,'喂，副团长！喂~\n果然还是叫名字\n比较好吗？');
INSERT INTO unit_comments VALUES(1057006,105731,1,1,1,0.0,1,'给你看看\n我至今为止的修行成果！\n开始喽！');
INSERT INTO unit_comments VALUES(1057007,105731,1,2,1,0.0,1,'为了守护\n重要之物，\n我选择战斗！');
INSERT INTO unit_comments VALUES(1057008,105731,1,3,1,0.0,1,'为了进行冒险，\n我每天都在特训！\n我绝对不会输的！');
INSERT INTO unit_comments VALUES(1057009,105731,1,4,1,0.0,1,'身为团长，我不会让别人\n碰我的同伴一根手指！\n我来应战！');
INSERT INTO unit_comments VALUES(1057010,105731,1,5,1,0.0,1,'和你在一起，\n我就觉得任何困难\n都能克服！');
INSERT INTO unit_comments VALUES(1057011,105701,2,6,2,4.0,1,'今天是你的生日哦，\n生日快乐！\n要尽情庆祝一下才行！');
INSERT INTO unit_comments VALUES(1057012,105731,2,6,1,0.0,1,'生日快乐！\n来开庆祝派对吧！还有，\n如果你有什么想要的礼物\n就跟我说，我会送给你的！');
INSERT INTO unit_comments VALUES(1058001,105801,1,1,1,3.0,2,'诶嘿嘿，佩可莉姆这个绰号，\n我还挺喜欢呢');
INSERT INTO unit_comments VALUES(1058002,105801,1,2,6,3.2,2,'难道说，\n你不喜欢魔物料理？\n我倒是挺喜欢的');
INSERT INTO unit_comments VALUES(1058003,105801,1,3,4,2.5,7,'咕噜噜噜……\n啊，肚子叫了……\n不、不许笑！');
INSERT INTO unit_comments VALUES(1058004,105801,1,4,2,2.1,1,'我们去吃饭吧！\n一起吃饭\n关系会变好哦！');
INSERT INTO unit_comments VALUES(1058005,105801,1,5,1,2.2,5,'要给我食物吗？\n哇啊，我可以爱上你吗？');
INSERT INTO unit_comments VALUES(1058006,105831,1,1,1,0.0,1,'你饿不饿？\n不介意的话，\n我们一起去吃饭吧？');
INSERT INTO unit_comments VALUES(1058007,105831,1,2,1,0.0,1,'要好好珍惜食物\n不过，只要有我在，就不可能会有\n剩饭剩菜……');
INSERT INTO unit_comments VALUES(1058008,105831,1,3,1,0.0,1,'我喜欢的料理吗？\n我想想~\n“自助餐”或\n“无限续杯”之类的吧！');
INSERT INTO unit_comments VALUES(1058009,105831,1,4,1,0.0,1,'和男生单独吃饭\n让人有点心跳加速呢！\n好兴奋啊☆');
INSERT INTO unit_comments VALUES(1058010,105831,1,5,1,0.0,1,'喜欢的男性类型？\n那就是\n“不浪费食物的人”！');
INSERT INTO unit_comments VALUES(1058011,105801,2,6,2,4.5,1,'今天是你的生日呀！\n生日快乐！\n为了庆祝特殊日子而特制的超大蛋糕，\n我们一起吃吧？');
INSERT INTO unit_comments VALUES(1058012,105831,2,6,1,0.0,1,'生日快乐☆\n明年你的生日\n我也一定要帮你庆祝♪\n一定必定以及肯定！');
INSERT INTO unit_comments VALUES(1058013,105861,1,1,1,0.0,1,'あなたが側にいるだけで\n体の奥から\n勇気が溢れちゃいます！\nやばいですね☆');
INSERT INTO unit_comments VALUES(1058014,105861,1,2,1,0.0,1,'ごはんの力！\nそしてあなたとの絆！\n今のわたしは止められませんよ♪');
INSERT INTO unit_comments VALUES(1058015,105861,1,3,1,0.0,1,'あなたのためなら\nわたしはどこまでも\n強くなれます！\n一緒に頑張りましょう☆');
INSERT INTO unit_comments VALUES(1058016,105861,1,4,1,0.0,1,'あなたと食卓を囲むと\nごはんがとっても美味しいんです\nわたしたちって\n相性ばっちりですね！');
INSERT INTO unit_comments VALUES(1058017,105861,1,5,1,0.0,1,'わたし、もっともっと強くなって\nあなたを守り続けてみせます！\nずっとあなたといたいですから♪');
INSERT INTO unit_comments VALUES(1059001,105901,1,1,1,2.0,2,'就让身为引导者的我\n全力支持主人吧');
INSERT INTO unit_comments VALUES(1059002,105901,1,2,1,3.7,2,'主人，肚子饿不饿？\n任何事都要先\n填饱肚子再说');
INSERT INTO unit_comments VALUES(1059003,105901,1,3,6,2.3,2,'看来是累了呢\n管理健康也是我的职责\n要是有什么吩咐\n请尽管说');
INSERT INTO unit_comments VALUES(1059004,105901,1,4,5,0.0,5,'能够服侍主人这样\n温柔的人，\n我真是个幸福的人');
INSERT INTO unit_comments VALUES(1059005,105901,1,5,2,0.0,2,'请让我像主人的夫人一样\n来服侍主人吧♪');
INSERT INTO unit_comments VALUES(1059006,105931,1,1,1,0.0,1,'为了与主人相遇做准备，\n我从小时候起\n就和精灵交谈\n请尽管使唤我吧');
INSERT INTO unit_comments VALUES(1059007,105931,1,2,1,0.0,1,'被主人注视着，\n精灵们好像也很高兴♪');
INSERT INTO unit_comments VALUES(1059008,105931,1,3,1,0.0,1,'我会与大自然一同\n助主人一臂之力');
INSERT INTO unit_comments VALUES(1059009,105931,1,4,1,0.0,1,'我从年幼时起\n就一直期待着\n能够服侍主人');
INSERT INTO unit_comments VALUES(1059010,105931,1,5,1,0.0,1,'有什么事就请呼唤我\n我会全心全意地\n回应主人的要求');
INSERT INTO unit_comments VALUES(1059011,105901,2,6,5,11.3,2,'生日快乐♪\n生日快乐♪\n不管说多少次都觉得不够……\n主人，生日快乐♪');
INSERT INTO unit_comments VALUES(1059012,105931,2,6,1,0.0,1,'主人诞生于世上的这一天，\n对我来说是最为宝贵的一天\n请让我为你献上忠心的祝福\n生日快乐♪');
INSERT INTO unit_comments VALUES(1059013,105961,1,1,1,0.0,1,'主さまとの心のつながりが、\nこの力を生み出したのですね\n主さま…これからもずっと、\nおそばに居させてくださいまし♪');
INSERT INTO unit_comments VALUES(1059014,105961,1,2,1,0.0,1,'わたくしの想いに、\n精霊たちが応えてくれている…\nこの力があれば、\nもっと主さまにご奉仕できます♪');
INSERT INTO unit_comments VALUES(1059015,105961,1,3,1,0.0,1,'なんて優しい風…\nふふっ…まるで精霊たちが、\nわたくしと主さまを\n祝福してくれているようです♪');
INSERT INTO unit_comments VALUES(1059016,105961,1,4,1,0.0,1,'花びらが花嫁のブーケのように…\nわたくしと主さまも…はっ！\nわ、わたくしとしたことが、\nなんて大それたことを…');
INSERT INTO unit_comments VALUES(1059017,105961,1,5,1,0.0,1,'この力があれば、\nもっと主さまのお世話ができます\nおはようからおやすみまで、\n全てわたくしにお任せください');
INSERT INTO unit_comments VALUES(1060001,106001,1,1,1,3.5,2,'你说是你救了我？\n嗯，姑且\n谢谢你啦');
INSERT INTO unit_comments VALUES(1060002,106001,1,2,5,0.0,5,'你别随便\n跟我搭话！\n我们又不是\n朋友！');
INSERT INTO unit_comments VALUES(1060003,106001,1,3,3,6.5,5,'隐藏气息，仔细观察周围，\n小心别被那家伙发现……\n呀啊！为什么我的跟踪\n总是会暴露！？');
INSERT INTO unit_comments VALUES(1060004,106001,1,4,4,6.0,2,'你丧失了记忆\n应该过得很不容易吧，\n所以就由我来照顾你吧\n你可得感谢我♪');
INSERT INTO unit_comments VALUES(1060005,106001,1,5,5,5.0,2,'其实我不能与你\n变得要好的……\n不过还挺开心的，无所谓啦♪');
INSERT INTO unit_comments VALUES(1060006,106031,1,1,1,0.0,1,'你、你看到了对吧，我秘密进行的魔法特训\n既然如此，\n你也要陪我一起训练！');
INSERT INTO unit_comments VALUES(1060007,106031,1,2,1,0.0,1,'我的魔法\n挺厉害的吧？\n哼哼♪\n继续夸不要停♪');
INSERT INTO unit_comments VALUES(1060008,106031,1,3,1,0.0,1,'完美完美！\n我的进步很大嘛♪\n我果然\n只要想做就能做到！');
INSERT INTO unit_comments VALUES(1060009,106031,1,4,1,0.0,1,'哼哼，很华丽吧\n我只告诉你一个\n其实我也还是新手哦\n来，更惊讶点吧！');
INSERT INTO unit_comments VALUES(1060010,106031,1,5,1,0.0,1,'回复魔法我也会用一些哦\n万一你有什么不测，\n我也可以救你\n不过，希望没有万一');
INSERT INTO unit_comments VALUES(1060011,106001,2,6,1,8.9,5,'诶~你过生日啊~\n啊，我碰巧有个蛋糕\n要不要一起吃？\n是碰巧啊！真的啦！');
INSERT INTO unit_comments VALUES(1060012,106031,2,6,1,0.0,1,'拿着，这是生日礼物\n要好好感激我哦！\n因为送你这种东西，\n要是被发现就麻烦了……');
INSERT INTO unit_comments VALUES(1060013,106061,1,1,1,0.0,1,'あたしの魔力とあんたの力が\n一つになっていく…！\nさあ、かかってきなさい！\n今日のあたしは一味違うわよ！');
INSERT INTO unit_comments VALUES(1060014,106061,1,2,1,0.0,1,'これがあんたとあたしの\n真の力なのね…！\nあんたと一緒なら\n負ける気がしないわ！');
INSERT INTO unit_comments VALUES(1060015,106061,1,3,1,0.0,1,'ふっふっふっ…どうよこの力！\nもっと褒めても…うひゃあ！\nきゅ、急に頭なでんな！\nぶ、ぶっ殺すぞ！');
INSERT INTO unit_comments VALUES(1060016,106061,1,4,1,0.0,1,'あいつとの心のつながりが力に…\nこういうのも悪くないわね\nなっ！　あんた聞いてたの！？\nい、今すぐ忘れろばかぁぁ～！');
INSERT INTO unit_comments VALUES(1060017,106061,1,5,1,0.0,1,'この新しい力であんたを\n守ってあげるから…その…\nあ、あたしのそばから\n離れるんじゃないわよ！');
INSERT INTO unit_comments VALUES(1061001,106101,1,1,6,2.2,2,'嗯？啊，是你啊！\n找我有什么事？\n遇上了麻烦的话要跟我说哦？');
INSERT INTO unit_comments VALUES(1061002,106101,1,2,1,5.8,2,'你失忆了啊……\n希望你能早点恢复记忆！\n我去帮你找找有效的药！');
INSERT INTO unit_comments VALUES(1061003,106101,1,3,2,0.0,2,'也把你的公主骑士之力\n用在我身上嘛！\n原先还是你敌人的时候，\n我每次看到都很兴奋！');
INSERT INTO unit_comments VALUES(1061004,106101,1,4,1,2.9,2,'虽然在不久前还为各种事情不安，\n但是现在已经没事了！\n因为能和你还有大家互帮互助呀！');
INSERT INTO unit_comments VALUES(1061005,106101,1,5,1,5.1,3,'没想到曾经互为仇敌的你和我\n现在竟成了同伴……\n所以，尾狗刀也一定……');
INSERT INTO unit_comments VALUES(1061006,106131,1,1,1,0.0,1,'你们全都给我让开！\n敢挡路的家伙，\n我统统斩了！');
INSERT INTO unit_comments VALUES(1061007,106131,1,2,1,0.0,1,'新的天楼霸断剑吗……\n不管是什么样的对手，\n有了它我就是无敌的！上啊！');
INSERT INTO unit_comments VALUES(1061008,106131,1,3,1,0.0,1,'我不会再逃避躲藏了！\n就算遍体鳞伤，\n我都会战斗到最后！我意已决！');
INSERT INTO unit_comments VALUES(1061009,106131,1,4,1,0.0,1,'哈、哈、哈！\n闪开闪开！\n诺维姆大人驾到！');
INSERT INTO unit_comments VALUES(1061010,106131,1,5,1,0.0,1,'终于和你成为同伴了啊。\n真希望能快点让尾狗刀清醒，\n和他一起战斗啊！');
INSERT INTO unit_comments VALUES(1061011,106101,2,6,2,2.1,5,'生日快乐！\n我没怎么给人送过东西……\n你想要什么？');
INSERT INTO unit_comments VALUES(1061012,106131,2,6,1,0.0,1,'Happy birthday！\n好嘞！去买蛋糕吧！\n今天我请客！\n大概明天会下雪哦！');
INSERT INTO unit_comments VALUES(1063001,106301,1,1,6,5.8,1,'兰德索尔好厉害……\n什么都有，面积还很大……\n跟我的故乡完全不一样');
INSERT INTO unit_comments VALUES(1063002,106301,1,2,2,5.3,1,'我的故乡也是个好地方\n虽然什么都没有，\n不过，我觉得你会喜欢的！');
INSERT INTO unit_comments VALUES(1063003,106301,1,3,6,6.1,2,'你和萝沙丽亚\n好像有点像？\n比方说……\n在一起就会很安心');
INSERT INTO unit_comments VALUES(1063004,106301,1,4,5,5.7,2,'和你在一起，\n我有时会心跳加速，有时会心里苦涩……\n可是，总觉得……好开心');
INSERT INTO unit_comments VALUES(1063005,106301,1,5,5,4.1,2,'只要和你在一起……\n我就觉得既安心，又心跳不已');
INSERT INTO unit_comments VALUES(1063006,106331,1,1,1,0.0,1,'总有一天，我会成为森林的守卫\n当然，是和萝沙丽亚一起！');
INSERT INTO unit_comments VALUES(1063007,106331,1,2,1,0.0,1,'兰德索尔很广阔……\n不过，这里的风的气息和故乡的很像');
INSERT INTO unit_comments VALUES(1063008,106331,1,3,1,0.0,1,'在我的故乡\n有很多妖精！\n真想让你也看看呀');
INSERT INTO unit_comments VALUES(1063009,106331,1,4,1,0.0,1,'明明心跳得很快，却觉得很安心……\n明明觉得很安心，偶尔却会觉得苦涩……\n待在你身旁\n心情就会变得很奇妙');
INSERT INTO unit_comments VALUES(1063010,106331,1,5,1,0.0,1,'比起在森林里的时候，\n我觉得现在更开心……\n一定是因为你吧？');
INSERT INTO unit_comments VALUES(1063011,106301,2,6,5,4.9,2,'我做了这个！\n虽然不是很有信心……\n因为今天是特别的日子……\n要不我喂你吃吧？');
INSERT INTO unit_comments VALUES(1063012,106331,2,6,1,0.0,1,'就这样继续成长的话\n你会变成什么样呢？\n总觉得好期待呀\n诶嘿嘿……生日快乐！');
INSERT INTO unit_comments VALUES(1065001,106501,1,1,1,7.7,2,'キミと一緒にいれば\nなんか面白いことが起きるだろ？\nだから期待させてもらってるよ\nさ、どっか遊びにいこうぜ！');
INSERT INTO unit_comments VALUES(1065002,106501,1,2,3,0.0,3,'オレはこの世界に\nなにが起きたのか知りたい…\nそのためにもオレは\nもっと強くなる必要があるんだよ');
INSERT INTO unit_comments VALUES(1065003,106501,1,3,6,8.9,1,'なあ、仕事の依頼はないのか？\nだからケンカだって。ケンカの\n仕事。オレがたぎるようなやつを\n持ってきてくれよな');
INSERT INTO unit_comments VALUES(1065004,106501,1,4,1,7.6,2,'そう心配すんなって\nそこらのヤツにやられるほど\nオレは弱くはないさ\nなんなら、試してみるか？');
INSERT INTO unit_comments VALUES(1065005,106501,1,5,6,5.8,1,'ドラゴン族以外のやつと\nここまで仲良くなるなんて\n思わなかったぜ\nキミ、一体何者なんだ？');
INSERT INTO unit_comments VALUES(1065006,106531,1,1,1,0.0,1,'喧嘩屋の本領、見せてやるよ！\nダチが一緒じゃ負けられねぇ！\n誰だろうとぶっ倒して、オレ様が\n最強だって証明してやるぜ！');
INSERT INTO unit_comments VALUES(1065007,106531,1,2,1,0.0,1,'すげえぜ、この力！\n体の奥から\nどんどん溢れてきやがる！\nオレたちに壊せないものはねえ！');
INSERT INTO unit_comments VALUES(1065008,106531,1,3,1,0.0,1,'これがドラゴン族の力…！\nオレ様が使いこなしてやるよ！\nダチが見てるんだ。情けねえ\nカッコは見せらんねぇよな！');
INSERT INTO unit_comments VALUES(1065009,106531,1,4,1,0.0,1,'ダチと一緒に戦うのが\nこんなに楽しいなんてな…！\n知らないこの世界で知った\n最高にたぎる感情だ！');
INSERT INTO unit_comments VALUES(1065010,106531,1,5,1,0.0,1,'いいぜいいぜ、たぎってきた！\nキミが一緒なら、これっぽっちも\n負ける気がしねえ！\n派手に暴れてやろうぜ！');
INSERT INTO unit_comments VALUES(1065011,106501,2,6,1,4.7,2,'よっ！　おめでとう！\n今日はキミの誕生日だろ？\nパーッとなんか食べに行こうぜ\n今日はオレがおごってやるよ');
INSERT INTO unit_comments VALUES(1065012,106531,2,6,1,0.0,1,'誕生日おめでとう！\nダチにとって良い日ってのは\nこっちまでうれしくなるんだな\nこんな気持ち、初めてだ！');
INSERT INTO unit_comments VALUES(1070001,107001,1,1,1,5.8,2,'敵の敵は味方…\n今回は貴方に協力しましょう\nよろしく頼みます');
INSERT INTO unit_comments VALUES(1070002,107001,1,2,6,5.6,1,'プリンセスナイトとしてではなく\n貴方だけが持つ可能性\nそれに賭けさせてもらいます');
INSERT INTO unit_comments VALUES(1070003,107001,1,3,6,2.0,2,'今の私？　ええ、私本人ですよ\n今の貴方に自分を偽っても\n意味がありませんから');
INSERT INTO unit_comments VALUES(1070004,107001,1,4,1,5.3,6,'貴方といると、犬を\nもう一匹飼い始めた気分です\n飼ったこと？　ありませんよ？');
INSERT INTO unit_comments VALUES(1070005,107001,1,5,1,5.3,2,'貴方の好みのタイプを\n教えなさい。再現してあげます\n妹系？　姉系？　幼馴染？');
INSERT INTO unit_comments VALUES(1070006,107031,1,1,1,0.0,1,'自分の目に映った私が\n本当の私だと思いますか…？\n私は変貌大妃\n千変万化の女ですよ');
INSERT INTO unit_comments VALUES(1070007,107031,1,2,1,0.0,1,'私の『冠』が偽物ではないと\nその身に刻み込んであげます\nもっとも全ての敵は私に模倣され\n消えて無くなりますが');
INSERT INTO unit_comments VALUES(1070008,107031,1,3,1,0.0,1,'鏡よ鏡…世界で最も\n賢く強いのは誰ですか？\n…ええその通り。当然私です');
INSERT INTO unit_comments VALUES(1070009,107031,1,4,1,0.0,1,'私に仕えたくなりましたか？\n私の中にデータさえあれば\n本人は不要なのです\n…彼以外は');
INSERT INTO unit_comments VALUES(1070010,107031,1,5,1,0.0,1,'こそこそ隠れるのは終わりです\nこれから先は打って出ます\n貴方とその仲間たち…\n戦力として当てにしていますよ');
INSERT INTO unit_comments VALUES(1070011,107001,2,6,1,5.8,6,'今日が誕生日ですか\nおめでとうございます\n…これ以上何か必要ですか？');
INSERT INTO unit_comments VALUES(1070012,107031,2,6,1,0.0,1,'誕生日おめでとうございます\n贈り物は何にしましょう…\n計算問題集ではどうですか？');
INSERT INTO unit_comments VALUES(1071001,107101,1,1,1,5.8,2,'谈来谈去的太麻烦了！\n万事从简解决\n没错，用你死我活的\n动真格的决斗来解决☆');
INSERT INTO unit_comments VALUES(1071002,107101,1,2,4,2.5,1,'难道你不觉得\n在这憋屈的世界里\n遵从自己的欲望为所欲为的人，\n才是赢家吗？');
INSERT INTO unit_comments VALUES(1071003,107101,1,3,1,3.1,4,'实力太强也不是件好事\n到处都是没劲的家伙\n碰不到能够赌上\n双方性命的愉快战斗');
INSERT INTO unit_comments VALUES(1071004,107101,1,4,1,8.5,2,'在我面前消失的\n男人们，应该会从心底憎恨\n能触碰到我内心的你吧\n放心，他们全都被埋在地下了☆');
INSERT INTO unit_comments VALUES(1071005,107101,1,5,1,6.8,5,'为了能让你过上幸福的\n婚姻生活，我也在毫不松懈地\n付出努力哦\n我也意外地有可爱的一面，对吧？');
INSERT INTO unit_comments VALUES(1071006,107131,1,1,1,0.0,1,'我没有发狂\n人类的生命虚幻而又脆弱……\n即使只是转瞬的光阴，\n我也要尽情享受☆');
INSERT INTO unit_comments VALUES(1071007,107131,1,2,1,0.0,1,'让我们一起欢乐地\n在遍布战场的\n血泊上踏起舞步吧\n来，把手给我☆');
INSERT INTO unit_comments VALUES(1071008,107131,1,3,1,0.0,1,'我并不是……碍于誓约才这么做的哦？\n至少我是认可的\n你是能触碰到我的男人\n我认同你作为婚姻伴侣☆');
INSERT INTO unit_comments VALUES(1071009,107131,1,4,1,0.0,1,'有没有什么能让我兴致昂扬的\n愉悦之事？\n要是没有的话\n就由我来点燃火种吧☆');
INSERT INTO unit_comments VALUES(1071010,107131,1,5,1,0.0,1,'你说还想多看些\n我幼年时期的照片？难道\n你有只会对青春期\n之前的小孩子才会兴奋的癖好吗？');
INSERT INTO unit_comments VALUES(1071011,107101,2,6,1,9.1,2,'今天好像是你的生日哦？\n来，给长大了一岁的\n可怜小羊羔的礼物\n今年也要逗我开心哦？');
INSERT INTO unit_comments VALUES(1071012,107131,2,6,1,0.0,1,'生日快乐♪\n能够见证你值得纪念的一天\n我很开心哦！\n纪念的誓约……是不是还有点早？');
INSERT INTO unit_comments VALUES(1075001,107501,1,1,1,2.2,2,'我买了泳衣！\n怎么样怎么样？\n适合我吗？');
INSERT INTO unit_comments VALUES(1075002,107501,1,2,1,0.0,1,'这件泳衣还挺方便行动的\n感觉可以直接潜到海里\n捕捉海鲜呢！');
INSERT INTO unit_comments VALUES(1075003,107501,1,3,2,0.0,2,'我们来玩海滩运动吧！\n比如沙滩球啦，\n沙滩抢旗之类的！\n我可不会输的哦~');
INSERT INTO unit_comments VALUES(1075004,107501,1,4,5,4.3,2,'我要穿着泳装\n诱惑你♪\n开玩笑的！');
INSERT INTO unit_comments VALUES(1075005,107501,1,5,1,5.0,2,'难得穿了泳装，\n我们就一起尽情游泳，\n尽情玩闹……\n创造许许多多的回忆吧！');
INSERT INTO unit_comments VALUES(1075006,107531,1,1,1,0.0,1,'哇，你竟然敢这样！\n海水好咸~\n既然这样，我也要动真格了！\n做好觉悟吧！');
INSERT INTO unit_comments VALUES(1075007,107531,1,2,1,0.0,1,'蓝色的大海，白色的沙滩，闪耀的太阳！\n还有好吃的鱼！\n这里真是天国啊！');
INSERT INTO unit_comments VALUES(1075008,107531,1,3,1,0.0,1,'水好舒服啊~\n这么充实的夏天\n我还是第一次体验！');
INSERT INTO unit_comments VALUES(1075009,107531,1,4,1,0.0,1,'诶嘿嘿♪\n能和你在海边玩\n还真是新鲜！\n这就是青春啊♪');
INSERT INTO unit_comments VALUES(1075010,107531,1,5,1,0.0,1,'要是太闹腾，\n被水冲走了泳衣，\n我就抱紧你\n把自己藏起来！');
INSERT INTO unit_comments VALUES(1075011,107501,2,6,2,2.6,5,'生日快乐！\n鱼、贝壳、章鱼、虾、蟹……\n来办个满载海洋美味的\n生日派对吧！');
INSERT INTO unit_comments VALUES(1075012,107531,2,6,1,0.0,1,'穿着泳衣为你庆祝生日！\n感觉好奔放，不由得兴奋起来了！\n来吧，今天让我们俩\n尽情玩闹吧！');
INSERT INTO unit_comments VALUES(1076001,107601,1,1,1,4.8,5,'我让救济院的各位\n帮自己选了泳衣……\n适合吗？');
INSERT INTO unit_comments VALUES(1076002,107601,1,2,1,0.0,1,'主人，可以的话\n一起来\n练习游泳吧');
INSERT INTO unit_comments VALUES(1076003,107601,1,3,2,0.0,2,'主人。我想\n试着堆一个\n沙子城堡，\n主人能来帮忙吗');
INSERT INTO unit_comments VALUES(1076004,107601,1,4,5,0.0,5,'像这样大面积地\n露出肌肤\n果然还是很害羞……\n这只能让主人一个人看');
INSERT INTO unit_comments VALUES(1076005,107601,1,5,1,4.2,2,'能帮我涂一下\n防晒霜吗？\n如果是主人，\n触碰我的肌肤也没问题');
INSERT INTO unit_comments VALUES(1076006,107631,1,1,1,0.0,1,'主人。无论在哪里，\n我都会在主人身边\n请别放开我的手哦');
INSERT INTO unit_comments VALUES(1076007,107631,1,2,1,0.0,1,'主人请看\n鱼儿离我们这么近……\n真神奇啊');
INSERT INTO unit_comments VALUES(1076008,107631,1,3,1,0.0,1,'就这样乘着海浪，\n两个人去往远方\n也不错呢');
INSERT INTO unit_comments VALUES(1076009,107631,1,4,1,0.0,1,'今后我也想和\n主人看同样的风景，\n拥有同样的感动，\n一同并肩前行');
INSERT INTO unit_comments VALUES(1076010,107631,1,5,1,0.0,1,'在这片宽广的大海中，\n和主人单独相处……\n只有这一刻，\n我可以独占主人♪');
INSERT INTO unit_comments VALUES(1076011,107601,2,6,2,0.0,2,'能够在海边庆祝\n主人的生日，我在\n故乡的时候从来没有想过\n好幸福啊');
INSERT INTO unit_comments VALUES(1076012,107631,2,6,1,0.0,1,'天空和海洋都熠熠生辉……\n这世上的一切仿佛都在\n祝福主人的生日\n我也为主人献上衷心的祝贺♪');
INSERT INTO unit_comments VALUES(1077001,107701,1,1,1,2.8,5,'工作可真开心啊！\n和你一起的话，\n就感觉更开心了♪');
INSERT INTO unit_comments VALUES(1077002,107701,1,2,1,2.9,2,'你想在休息时去\n海边？\n当然没问题了~\n一起游泳吧！');
INSERT INTO unit_comments VALUES(1077003,107701,1,3,4,6.2,2,'我笨手笨脚的，\n从来没有建成过沙子城堡~\n你愿意和我一起建吗？\n谢谢你♪');
INSERT INTO unit_comments VALUES(1077004,107701,1,4,1,4.2,2,'虽然被你夸赞穿泳装好看\n我很开心……但、但是\n你一直盯着看的话，我会害羞的~');
INSERT INTO unit_comments VALUES(1077005,107701,1,5,1,4.7,5,'你有没有什么吩咐？\n如果没有的话……\n我、我能待在\n你的身边吗？');
INSERT INTO unit_comments VALUES(1077006,107731,1,1,1,0.0,1,'没想到脚下竟然有烟花！\n小孩子们有危险，\n请离这里远一点！\n呀！好烫！');
INSERT INTO unit_comments VALUES(1077007,107731,1,2,1,0.0,1,'大、大事不妙了！\n现在不是\n说漂亮的时候啦~！');
INSERT INTO unit_comments VALUES(1077008,107731,1,3,1,0.0,1,'大家一起放烟花\n真的好开心啊\n接下来我要\n一手拿一支烟花♪');
INSERT INTO unit_comments VALUES(1077009,107731,1,4,1,0.0,1,'我们一起放烟花\n会不会看起来像情侣呢？\n开玩笑的……诶嘿嘿☆');
INSERT INTO unit_comments VALUES(1077010,107731,1,5,1,0.0,1,'能、能不能让我\n也来服侍你呢？\n算、算了，你还是忘了\n我刚才的话吧！');
INSERT INTO unit_comments VALUES(1077011,107701,2,6,1,8.6,6,'生日快乐♪\n快快尝尝这个放满了奶油\n和大草莓的刨冰蛋糕吧\n啊哇哇，要融化了！');
INSERT INTO unit_comments VALUES(1077012,107731,2,6,1,0.0,1,'生日快乐♪\n今天请让我破例\n只服侍你一个人\n诶嘿嘿，不要告诉大小姐哦');
INSERT INTO unit_comments VALUES(1078001,107801,1,1,1,5.8,5,'你说泳装很适合我？\n被你夸奖也没什么意思~\n不、不过……谢谢了');
INSERT INTO unit_comments VALUES(1078002,107801,1,2,1,2.0,2,'这一天我等了好久！\n我要尽情玩个痛快，\n做好心理准备哦！');
INSERT INTO unit_comments VALUES(1078003,107801,1,3,1,4.3,2,'这个泳圈很可爱吧\n坐着也很舒服\n如果你无论如何都想坐的话，\n我也可以让你坐上来哦？');
INSERT INTO unit_comments VALUES(1078004,107801,1,4,1,5.6,5,'和你在一起就不会觉得无聊\n你问什么意思？\n就是……\n和你在一起很开心啦！');
INSERT INTO unit_comments VALUES(1078005,107801,1,5,1,3.7,2,'偶尔和你\n单独出去也可以啦\n话说在前头，\n你可没有权利拒绝哦♪');
INSERT INTO unit_comments VALUES(1078006,107831,1,1,1,0.0,1,'我、我说，泳圈是不是开了个洞？\n有的吧！？话说\n还出现了魔物对吧！？\n呀啊！别过来！');
INSERT INTO unit_comments VALUES(1078007,107831,1,2,1,0.0,1,'干嘛，一脸渴望的表情\n想喝这果汁吗？\n真拿你没办法，\n只给你喝一口哦？');
INSERT INTO unit_comments VALUES(1078008,107831,1,3,1,0.0,1,'流汗之后喝的饮料\n最棒了\n哈啊~好喝♪');
INSERT INTO unit_comments VALUES(1078009,107831,1,4,1,0.0,1,'你好像累了呢\n一起坐上泳圈，\n休息一下吧？');
INSERT INTO unit_comments VALUES(1078010,107831,1,5,1,0.0,1,'感觉好幸福呀\n这大概……也是因为有你吧？\n那个，谢……\n没、没什么！');
INSERT INTO unit_comments VALUES(1078011,107801,2,6,2,5.9,1,'生日快乐！\n在这种日子里，\n就尽情欢闹一下吧☆\n来~大海，大海！去玩咯！');
INSERT INTO unit_comments VALUES(1078012,107831,2,6,1,0.0,1,'在海上办生日派对\n感觉很时髦对吧？\n来，为你的生日干杯！\nHappy birthday♪');
INSERT INTO unit_comments VALUES(1079001,107901,1,1,6,0.0,1,'怎么了喵？\n看到我穿泳装的样子\n心跳加速了吗喵？');
INSERT INTO unit_comments VALUES(1079002,107901,1,2,1,4.3,2,'你问我在做什么，\n正如你所见，我在为开摊做准备喵！\n我要大赚一笔喵！');
INSERT INTO unit_comments VALUES(1079003,107901,1,3,1,6.2,2,'你会帮我吗喵？\n谢谢喵！\n那我就以身相许……\n开玩笑喵~');
INSERT INTO unit_comments VALUES(1079004,107901,1,4,1,3.4,6,'我讨厌水\n所以就在沙滩上玩吧喵！\n喵！？浪打过来啦喵！');
INSERT INTO unit_comments VALUES(1079005,107901,1,5,1,4.1,4,'海里有好多\n好吃的，真开心喵！\n呜呜……但是，海水好讨厌喵……');
INSERT INTO unit_comments VALUES(1079006,107931,1,1,1,0.0,1,'炒面、刨冰、鲷鱼烧……\n海边的店里有好多\n好吃的东西喵！');
INSERT INTO unit_comments VALUES(1079007,107931,1,2,1,0.0,1,'走过路过\n别错过啊喵！\n招牌是只在本店\n才吃得到的甜点喵！');
INSERT INTO unit_comments VALUES(1079008,107931,1,3,1,0.0,1,'没有在海边吃过这款甜点\n就不能算过夏天喵！\n保证是无人知晓的\n未知美味喵！');
INSERT INTO unit_comments VALUES(1079009,107931,1,4,1,0.0,1,'一口吃得掉吗喵？\n来，嘴巴张开喵！');
INSERT INTO unit_comments VALUES(1079010,107931,1,5,1,0.0,1,'今年的夏天也受你帮助了\n谢谢你喵\n以后也请多多关照喵！');
INSERT INTO unit_comments VALUES(1079011,107901,2,6,2,2.3,1,'今天是你的生日喵！\n要是为我献上猫咪硬币\n约会这种事\n我也不是不能考虑啦喵♪');
INSERT INTO unit_comments VALUES(1079012,107931,2,6,1,0.0,1,'生日快乐！\n今天就破例听一听\n你的愿望吧喵！比如说，\n两个人单独在无人岛上……开玩笑喵♪');
INSERT INTO unit_comments VALUES(1080001,108001,1,1,1,9.2,2,'有能干的徒弟在，真是帮大忙了\n诶？你想要薪水？\n这样啊……啊，我说\n我穿泳装的样子可以当作薪水吗？');
INSERT INTO unit_comments VALUES(1080002,108001,1,2,1,3.2,2,'没忘东西吧？\n都准备好了吗？\n好，出发去冒险咯！');
INSERT INTO unit_comments VALUES(1080003,108001,1,3,6,2.3,2,'你问为什么要穿泳装？\n因为要享受夏日的海滨，\n穿成这样效率才高啊！');
INSERT INTO unit_comments VALUES(1080004,108001,1,4,1,3.2,5,'难得我穿成了这样子……\n不让你见识见识的话\n就太可惜了');
INSERT INTO unit_comments VALUES(1080005,108001,1,5,1,3.6,2,'海边的打工真划算\n太好了！\n再加上能和你一起\n这点也在我心里加分不少♪');
INSERT INTO unit_comments VALUES(1080006,108031,1,1,1,0.0,1,'在你面前\n总感觉好安心啊……\n啊，但是不能一直\n盯着我看哦？我会害羞的');
INSERT INTO unit_comments VALUES(1080007,108031,1,2,1,0.0,1,'真好啊，大海让人好放松\n我都快忘记怎么战斗了……\n呵呵，开玩笑的啦');
INSERT INTO unit_comments VALUES(1080008,108031,1,3,1,0.0,1,'像这样在海边度过\n我还是第一次呢……\n真开心\n是因为和你在一起吗？');
INSERT INTO unit_comments VALUES(1080009,108031,1,4,1,0.0,1,'虽然我整天都想着打工\n但是我\n最近也会想\n珍惜和你在一起的时间哦？');
INSERT INTO unit_comments VALUES(1080010,108031,1,5,1,0.0,1,'明明是师傅与徒弟的关系\n回过神来\n却发现我总是在依赖你……\n呵呵，真难为情啊');
INSERT INTO unit_comments VALUES(1080011,108001,2,6,6,4.6,2,'今天是你的生日啊\n那必须得庆祝了！\n走，我们去海里找些\n派对上吃的食物吧！');
INSERT INTO unit_comments VALUES(1080012,108031,2,6,1,0.0,1,'生日快乐\n今天就两个人一边看海\n一边散步……偶尔也尝试一下\n低效率地度过吧？');
INSERT INTO unit_comments VALUES(1081001,108101,1,1,2,3.9,5,'万、万圣节快乐……\n呜呜，好难为情啊……');
INSERT INTO unit_comments VALUES(1081002,108101,1,2,2,6.7,1,'Trick or treat……\n不给糖果的话就诅咒……\n不、就捉弄你哦');
INSERT INTO unit_comments VALUES(1081003,108101,1,3,1,4.8,4,'到了万圣节\n你的周围也会变得很热闹……\n有没有感觉肩膀沉甸甸的？');
INSERT INTO unit_comments VALUES(1081004,108101,1,4,5,6.3,2,'打扮成这样，看起来就像我很喜欢过万圣节一样，\n令人有些害羞……\n但如果你喜欢的话\n我就很开心了');
INSERT INTO unit_comments VALUES(1081005,108101,1,5,2,6.9,5,'幽灵们好像都很开心……\n毕竟是万圣节嘛\n我、我也……很开心\n因、因为，能和你在一起……');
INSERT INTO unit_comments VALUES(1081006,108131,1,1,1,0.0,1,'万圣节舞会\n开始了哦\n你也一起来……\n美妙的时光在等待着你呢');
INSERT INTO unit_comments VALUES(1081007,108131,1,2,1,0.0,1,'万圣节派对上，\n许多幽灵都来了……\n这个时期灵感会变强，\n所以你应该也能看到？');
INSERT INTO unit_comments VALUES(1081008,108131,1,3,1,0.0,1,'爸爸\n万圣节是一个很棒的节日\n可不要因为骚扰女孩子\n而把它糟蹋了哦？');
INSERT INTO unit_comments VALUES(1081009,108131,1,4,1,0.0,1,'虽然有点难为情……\n可以请你和我们一起跳舞吗？\n我想和幽灵们\n跳到天明');
INSERT INTO unit_comments VALUES(1081010,108131,1,5,1,0.0,1,'我是第一次过\n这么幸福的万圣节……\n所以不小心闹腾起来了\n都是托你的福');
INSERT INTO unit_comments VALUES(1081011,108101,2,6,2,5.3,6,'我爸爸好像也在祝福\n你的生日呢\n啊……爸爸！\n不可以咬这个人！');
INSERT INTO unit_comments VALUES(1081012,108131,2,6,1,0.0,1,'Happy halloween &\nHappy birthday♪\n今天不给糖果，\n给你蛋糕哦');
INSERT INTO unit_comments VALUES(1082001,108201,1,1,1,6.3,1,'现在的宫子，是一只冷血无情\n且狰狞的狼女！\n不想被吃掉的话，\n就把布丁交出来！');
INSERT INTO unit_comments VALUES(1082002,108201,1,2,3,8.4,2,'南瓜布丁？\n那是歪门邪道，绝不认同！\n啊呜……好、好好吃♪\n刚刚的话不算数~');
INSERT INTO unit_comments VALUES(1082003,108201,1,3,6,7.2,6,'有好多幽灵混在\n变装了的孩子们中间\n来要糖果呢~\n快看，在你身后也有！');
INSERT INTO unit_comments VALUES(1082004,108201,1,4,1,2.4,1,'不给布丁就捣蛋~\n没有布丁的话，\n宫子就附身你一辈子~');
INSERT INTO unit_comments VALUES(1082005,108201,1,5,2,5.1,1,'也邀请你来参加\n宫子的万圣节派对吧！\n给10个布丁当参加费\n就成交哦~');
INSERT INTO unit_comments VALUES(1082006,108231,1,1,1,0.0,1,'幽灵大集合~\n大家把万圣节\n气氛炒热起来！\n派对开始了哦~');
INSERT INTO unit_comments VALUES(1082007,108231,1,2,1,0.0,1,'无论活着还是死了，\n万圣节都能让所有人开心\n真是最棒的节日！\n宫子最喜欢万圣节了！');
INSERT INTO unit_comments VALUES(1082008,108231,1,3,1,0.0,1,'不给布丁就捣蛋！\n哪里有布丁\n哪里就有宫子！');
INSERT INTO unit_comments VALUES(1082009,108231,1,4,1,0.0,1,'能和你一起过万圣节\n宫子非常开心！\n能吃到布丁，还有你在，\n宫子的幽灵生真是多姿多彩~');
INSERT INTO unit_comments VALUES(1082010,108231,1,5,1,0.0,1,'今夜的宫子，一定比\n活着的时候还要光芒四射！\n和你一起过的万圣节，\n是宫子重要的回忆~');
INSERT INTO unit_comments VALUES(1082011,108201,2,6,1,5.2,2,'咻~轰隆隆隆~\n生日~\n快乐♪');
INSERT INTO unit_comments VALUES(1082012,108231,2,6,1,0.0,1,'宫子把好多幽灵朋友们\n带到了\n你的生日派对上哦！\n轰隆隆隆~生日快乐♪');
INSERT INTO unit_comments VALUES(1083001,108301,1,1,2,8.5,5,'嗯哼♪这副装扮很棒吧\n简直将大人的性感发挥到了极致！\n我要把你迷得神魂颠倒哦♪');
INSERT INTO unit_comments VALUES(1083002,108301,1,2,4,9.8,2,'哎……真烦恼……\n化装得这么性感的话，\n我会桃花泛滥的……\n我真是个罪恶的女人……');
INSERT INTO unit_comments VALUES(1083003,108301,1,3,2,5.6,3,'诶！给我糖果！？\n太好了太好了！\n呃，我才没有很高兴！\n别把我当小孩子看待！');
INSERT INTO unit_comments VALUES(1083004,108301,1,4,2,6.0,5,'能和我一起过万圣节\n可是很光荣的一件事哦？\n好了，我去要糖果了\n你快引路！');
INSERT INTO unit_comments VALUES(1083005,108301,1,5,2,6.0,5,'我现在的样子\n就像能骑着扫帚在空中飞翔一样♪\n如果真能实现的话\n就让你也坐上来');
INSERT INTO unit_comments VALUES(1083006,108331,1,1,1,0.0,1,'不给糖就捣蛋♪\n今天派对的主角是\n性感魔女小美咲！\n快点，把糖果给我！');
INSERT INTO unit_comments VALUES(1083007,108331,1,2,1,0.0,1,'哦嚯嚯嚯嚯♪怎么样！？\n这华丽优雅的化装！\n简直就是魔性的女人啊~');
INSERT INTO unit_comments VALUES(1083008,108331,1,3,1,0.0,1,'大家都被我散发的成熟魅力\n给迷住了呢~\n真没办法……毕竟\n现在的我，是最闪耀动人的！');
INSERT INTO unit_comments VALUES(1083009,108331,1,4,1,0.0,1,'今天就邀请你参加\n我的派对吧！\n要认真地护送我\n到会场哦♪');
INSERT INTO unit_comments VALUES(1083010,108331,1,5,1,0.0,1,'今年的万圣节太棒了！\n派对、糖果和变装！\n最重要的是……\n有你在！');
INSERT INTO unit_comments VALUES(1083011,108301,2,6,1,11.2,2,'Happy birthday &\nHappy halloween♪\n这么一来的话，送给\n你的礼物就是……恶作剧！');
INSERT INTO unit_comments VALUES(1083012,108331,2,6,1,0.0,1,'生日快乐！\n你想要什么礼物？\n诶？\n你想要大眼睛！？');
INSERT INTO unit_comments VALUES(1084001,108401,1,1,2,1.8,1,'圣诞节快乐！\n愿你一直\n被幸福的歌声包围……');
INSERT INTO unit_comments VALUES(1084002,108401,1,2,1,2.9,2,'和你一起过\n圣诞节……\n我十分期待');
INSERT INTO unit_comments VALUES(1084003,108401,1,3,1,5.3,5,'作为一名唱唤士，我想变得\n更加、更加可靠……\n请你在身旁关注着我');
INSERT INTO unit_comments VALUES(1084004,108401,1,4,5,5.7,2,'这套衣服挺显眼的\n虽然穿着很难为情……\n但被你看着的话\n我好像就能喜欢上它了');
INSERT INTO unit_comments VALUES(1084005,108401,1,5,1,5.7,5,'因为有你在我身边\n我才能专注于唱歌……\n这份心情，总有一天我想\n唱给你听……');
INSERT INTO unit_comments VALUES(1084006,108431,1,1,1,0.0,1,'圣诞节快乐！\n我要投入所有的感情\n演唱歌曲……请听');
INSERT INTO unit_comments VALUES(1084007,108431,1,2,1,0.0,1,'如果能用幸福的歌声\n点缀圣诞节……\n那对唱唤士来说就是至上的幸福了');
INSERT INTO unit_comments VALUES(1084008,108431,1,3,1,0.0,1,'为了守护和平……以及\n为了将小小的幸福\n传递给人们，我会一直歌唱下去');
INSERT INTO unit_comments VALUES(1084009,108431,1,4,1,0.0,1,'我相信……\n蕴藏在我的歌声里的可能性\n因为，\n你一直都信任着我……');
INSERT INTO unit_comments VALUES(1084010,108431,1,5,1,0.0,1,'不管是多么细微的感情\n我都想与你分享\n我好像已经\n无法离开你了……');
INSERT INTO unit_comments VALUES(1084011,108401,2,6,2,4.1,5,'今天是你的生日吧，\n生日快乐\n除了礼物之外，\n请让我为你献上一首歌吧');
INSERT INTO unit_comments VALUES(1084012,108431,2,6,1,0.0,1,'生日快乐\n孩子们和精灵好像\n也要一起为你庆祝呢\n让我们共同度过美好的一天吧');
INSERT INTO unit_comments VALUES(1085001,108501,1,1,6,7.6,2,'不仅有【咲恋救济院】\n的各位在，还可以和哥哥\n一起过圣诞节……\n今年真是最棒的圣诞节♪');
INSERT INTO unit_comments VALUES(1085002,108501,1,2,1,4.8,2,'穿上这套衣服的话，说不定我\n就能扮演圣诞老人了……♪\n诶嘿嘿……我去\n给孩子们送礼物咯♪');
INSERT INTO unit_comments VALUES(1085003,108501,1,3,2,0.0,2,'这是按照圣诞老人的形象\n新做的衣服♪\n我拜托铃莓姐姐\n帮我做的……♪');
INSERT INTO unit_comments VALUES(1085004,108501,1,4,1,4.1,2,'哥哥的手……好暖和……\n哥哥……明年……后年……\n请你也一直陪在我身边吧♪');
INSERT INTO unit_comments VALUES(1085005,108501,1,5,1,6.4,5,'那个……哥哥……\n这套衣服适合……呀啊！\n不、不要突然摸我脑袋呀……');
INSERT INTO unit_comments VALUES(1085006,108531,1,1,1,0.0,1,'呜呜……我够不到\n圣诞树的树顶……\n虽然这么做有点粗野，\n但是踩着凳子……哇呀！？');
INSERT INTO unit_comments VALUES(1085007,108531,1,2,1,0.0,1,'装饰完圣诞树之后，\n还要买东西和准备食物……\n呜呜……小绫音，你不要\n堆雪人了，快来帮忙啦……');
INSERT INTO unit_comments VALUES(1085008,108531,1,3,1,0.0,1,'啊……这个玩偶好可爱♪\n我要不要把制作方法学会，\n试着做哥哥的玩偶呢♪');
INSERT INTO unit_comments VALUES(1085009,108531,1,4,1,0.0,1,'哥、哥哥……\n据说圣诞节是属于\n恋人们的夜晚呢……所、所以那个……\n啊呜呜……没什么……');
INSERT INTO unit_comments VALUES(1085010,108531,1,5,1,0.0,1,'……哥哥你，不会跟我父母\n一样消失不见……哇呀！\n怎、怎么突然抱住我了……\n不过……好温暖呀♪');
INSERT INTO unit_comments VALUES(1085011,108501,2,6,5,7.6,2,'哥哥，生日……\n生日快乐！\n我会用自己的方式拼命为你庆祝\n要、要是你觉得高兴，我就很开心了♪');
INSERT INTO unit_comments VALUES(1085012,108531,2,6,1,0.0,1,'生日快乐\n我给乖乖听话的哥哥\n准、准备了礼物……\n请看看袜子里面♪');
INSERT INTO unit_comments VALUES(1086001,108601,1,1,1,4.0,2,'圣诞节快乐♪\n哥哥，快看快看！\n噗吉也和我一样，\n换上圣诞节的服装了♪');
INSERT INTO unit_comments VALUES(1086002,108601,1,2,2,2.7,5,'好暖和♪\n穿了圣诞节的服装，\n噗吉暖烘烘的♪\n“我的心在熊熊燃烧”');
INSERT INTO unit_comments VALUES(1086003,108601,1,3,1,9.6,5,'来自圣诞老人的礼物……\n巨大的火鸡和香甜的蛋糕……\n果然，\n圣诞节是最棒的♪');
INSERT INTO unit_comments VALUES(1086004,108601,1,4,1,3.1,3,'好期待圣诞节的美食啊♪\n哥哥是不是把我\n当作只会吃的孩子了！\n我也会做饭的啦！');
INSERT INTO unit_comments VALUES(1086005,108601,1,5,1,5.8,5,'今年的圣诞节，\n不仅有噗吉，\n还有哥哥在……诶嘿嘿♪\n感觉好幸福♪');
INSERT INTO unit_comments VALUES(1086006,108631,1,1,1,0.0,1,'穿了圣诞节的服装，\n噗吉也变得\n更加可爱了对吧♪\n“我很可爱吧？”');
INSERT INTO unit_comments VALUES(1086007,108631,1,2,1,0.0,1,'哼哼哼……快看这个王冠！\n圣诞节的我就是国王♪\n所以哥哥，\n你要让我尽情撒娇才行♪');
INSERT INTO unit_comments VALUES(1086008,108631,1,3,1,0.0,1,'噗吉大家庭完成~♪\n这边是噗爸和噗吉……\n那边是噗妈和噗哥……\n不对，是哥哥♪');
INSERT INTO unit_comments VALUES(1086009,108631,1,4,1,0.0,1,'堆了雪人手好冷啊……\n摸摸哥哥取暖♪\n诶嘿嘿……果然\n哥哥好暖和啊♪');
INSERT INTO unit_comments VALUES(1086010,108631,1,5,1,0.0,1,'虽然天气变暖后，\n雪人就会融化了……\n但是我们会永远在一起对吧\n哥哥♪');
INSERT INTO unit_comments VALUES(1086011,108601,2,6,2,5.8,6,'生日快乐！\n生日蛋糕，\n我做成了噗吉的形状哦！\n不行、那个是真的噗吉啦！');
INSERT INTO unit_comments VALUES(1086012,108631,2,6,1,0.0,1,'哥哥，生日快乐！\n能像家人一样和你共度\n这个重要的日子，真开心啊！\n噗吉也很高兴呢！');
INSERT INTO unit_comments VALUES(1087001,108701,1,1,6,3.6,1,'年末年初大家好像都很忙！\n骑士先生，如果有什么需要帮忙的，\n尽管跟我说哦！');
INSERT INTO unit_comments VALUES(1087002,108701,1,2,1,4.5,2,'这衣服穿着活动方便，\n干力气活也完全没问题！\n我说啊，我该做什么呢？');
INSERT INTO unit_comments VALUES(1087003,108701,1,3,1,6.1,2,'我好喜欢新年的氛围呀～\n迎来新的一年\n就会觉得心情焕然一新。');
INSERT INTO unit_comments VALUES(1087004,108701,1,4,1,5.4,5,'是骑士先生！\n喂～我在这里！\n骑士先生不管在哪，\n我都能马上找到你哦！');
INSERT INTO unit_comments VALUES(1087005,108701,1,5,1,7.8,5,'初次参拜的抽签？我抽到了大吉！\n写的都是好事～\n恋爱运！？不告诉你！');
INSERT INTO unit_comments VALUES(1087006,108731,1,1,1,0.0,1,'接下来去那边的小摊吧！\n拿不了的就赶快吃掉吧！\n骑士先生也是，快吃快吃！');
INSERT INTO unit_comments VALUES(1087007,108731,1,2,1,0.0,1,'小优衣做的年夜菜\n加上小摊的小吃！\n这个新年太棒了！');
INSERT INTO unit_comments VALUES(1087008,108731,1,3,1,0.0,1,'除夕那天我那么努力，\n今天多吃点\n应该没关系吧？');
INSERT INTO unit_comments VALUES(1087009,108731,1,4,1,0.0,1,'啊……那个点心，\n我刚才咬过了……\n你不在意？是、是吗！\n那就没问题！嗯！');
INSERT INTO unit_comments VALUES(1087010,108731,1,5,1,0.0,1,'骑士先生……我可以牵着你的手吗？\n啊，必须先把这个吃掉才行呢！\n来，骑士先生！张嘴！');
INSERT INTO unit_comments VALUES(1087011,108701,2,6,2,4.4,1,'骑士先生，生日快乐！\n这对骑士先生来说，\n是新的一年的开始！\n我们一起庆祝吧！');
INSERT INTO unit_comments VALUES(1087012,108731,2,6,1,0.0,1,'Happy birthday！\n祝愿骑士先生在接下来的一年里，\n都能够过得幸福！');
INSERT INTO unit_comments VALUES(1088001,108801,1,1,2,4.7,1,'新年快乐！\n骑士君，今年也请多指教\n你抽签了吗？');
INSERT INTO unit_comments VALUES(1088002,108801,1,2,3,5.1,1,'即使是新年也不能放松\n必须进行魔法的修行……\n希望今年能够有更大的进步');
INSERT INTO unit_comments VALUES(1088003,108801,1,3,1,3.1,2,'骑士君，你冷吗？\n要是需要防寒魔法\n可以随时跟我说哦');
INSERT INTO unit_comments VALUES(1088004,108801,1,4,5,7.4,8,'真好啊……恋人结伴来参拜\n我也想以后……\n没、没什么！');
INSERT INTO unit_comments VALUES(1088005,108801,1,5,1,7.8,5,'今年的恋爱运是……\n“抓住对方的胃”……\n骑士君，你饿不饿？');
INSERT INTO unit_comments VALUES(1088006,108831,1,1,1,0.0,1,'骑士君，去年谢谢你了！\n今年也请多指教！\n明年……还有将来也是！');
INSERT INTO unit_comments VALUES(1088007,108831,1,2,1,0.0,1,'希望你过来这边\n因为风太大了，好冷！\n你能待在我身边吗？');
INSERT INTO unit_comments VALUES(1088008,108831,1,3,1,0.0,1,'新年第一天的日出，好美啊……\n骑士君？怎么了？\n太阳是不是有点耀眼？');
INSERT INTO unit_comments VALUES(1088009,108831,1,4,1,0.0,1,'我说啊，我想像今天这样\n每年都和骑士君……\n一起看日出！\n我在说些什么呀！');
INSERT INTO unit_comments VALUES(1088010,108831,1,5,1,0.0,1,'骑士君。我啊……\n我觉得和骑士君在一起的\n所有时间都好幸福！\n我刚才说出来了！？');
INSERT INTO unit_comments VALUES(1088011,108801,2,6,5,11.3,2,'新、新年快乐！\n啊啊，搞错了……\n应该是……呃……\n生日快乐，骑士君！');
INSERT INTO unit_comments VALUES(1088012,108831,2,6,1,0.0,1,'骑士君，生日快乐！\n祝愿骑士君今后\n也能够过得幸福……');
INSERT INTO unit_comments VALUES(1089001,108901,1,1,1,0.0,1,'一穿上和服，就会比平时\n更注意走路的方式\n和行为举止了呢');
INSERT INTO unit_comments VALUES(1089002,108901,1,2,3,7.1,2,'就算正在过新年，要是有闹腾\n过头的家伙，我也要用这把刀惩处！\n开玩笑的啦。你当真了？');
INSERT INTO unit_comments VALUES(1089003,108901,1,3,1,2.9,2,'你是怎么过新年的？\n我想参考一下，可以的话\n能告诉我吗？');
INSERT INTO unit_comments VALUES(1089004,108901,1,4,6,6.8,5,'我和平时的感觉不太一样？\n嗯，可能是因为穿着和服，\n还有你在身边……吧');
INSERT INTO unit_comments VALUES(1089005,108901,1,5,1,5.2,2,'我抽了签\n结果是恋爱运很好……\n看来马上就时来运转了啊');
INSERT INTO unit_comments VALUES(1089006,108931,1,1,1,0.0,1,'新年伊始就有碍事的家伙出现！\n阻碍人们获得幸福的家伙\n我会全力斩杀！要上了哦！');
INSERT INTO unit_comments VALUES(1089007,108931,1,2,1,0.0,1,'我今年也果然还是\n需要你啊……拜托了。希望你今后也能\n助我一臂之力');
INSERT INTO unit_comments VALUES(1089008,108931,1,3,1,0.0,1,'这套剑技夹带着寒气……\n托你的福，我得到了进一步的\n磨砺。谢谢你');
INSERT INTO unit_comments VALUES(1089009,108931,1,4,1,0.0,1,'我想要亲手开拓\n自己前进的道路\n然后，想与重要的同伴\n还有你一起并肩前行');
INSERT INTO unit_comments VALUES(1089010,108931,1,5,1,0.0,1,'烦恼应该都丢在去年了才对\n不要介意！没关系！\n应该是没关系的……');
INSERT INTO unit_comments VALUES(1089011,108901,2,6,1,2.1,2,'生日快乐！\n今后与你共度的时光\n对我来说\n也会是重要的宝物哦');
INSERT INTO unit_comments VALUES(1089012,108931,2,6,1,0.0,1,'今天是你诞生在这个世上的\n特别的日子。让我为你献上\n特别的祝福吧。不管什么都行\n……除了假装亲吻……');
INSERT INTO unit_comments VALUES(1090001,109001,1,1,1,12.7,2,'亲爱的。这是我亲手做的巧克力，\n您能收下吗？\n里面包含了我满满的爱意。\n来，请张开嘴……');
INSERT INTO unit_comments VALUES(1090002,109001,1,2,1,10.8,2,'很香对吧？\n我在里面加了香草。\n您问有什么功能？\n嘻嘻……您猜呢？');
INSERT INTO unit_comments VALUES(1090003,109001,1,3,6,3.7,2,'您在介意周围的小瓶子吗？\n请放心，那些都是调味料。\n烹饪的时候想要用最顺手的器具，\n这不过是少女心而已。');
INSERT INTO unit_comments VALUES(1090004,109001,1,4,1,3.2,5,'真不可思议，\n做巧克力的时候，\n心想着要看到您高兴的表情，\n就变得拼命起来了。');
INSERT INTO unit_comments VALUES(1090005,109001,1,5,5,11.2,2,'像这样为您做料理，\n仿佛我就是您真正的伴侣一样呢。\n当然了，总有一天会成真的……嘻嘻。');
INSERT INTO unit_comments VALUES(1090006,109031,1,1,1,0.0,1,'所谓情人节，\n是把巧克力送给心爱之人的日子……\n让我用甜蜜的爱\n填满您的肚子吧。');
INSERT INTO unit_comments VALUES(1090007,109031,1,2,1,0.0,1,'仅凭巧克力无法完全表达\n我对您这位真命天子的爱意。\n我要附上一封\n花了三天三夜、饱含爱意的信……');
INSERT INTO unit_comments VALUES(1090008,109031,1,3,1,0.0,1,'我穿围裙好看吗？\n嘻嘻……我好开心。\n这样的话，我每天都穿给您看。\n不论时间，不论地点……');
INSERT INTO unit_comments VALUES(1090009,109031,1,4,1,0.0,1,'情人节这天，\n听说也有人会在送巧克力时告白。\n但是……就算不是特别的日子，\n我也会每天向您示爱的哦。');
INSERT INTO unit_comments VALUES(1090010,109031,1,5,1,0.0,1,'虽然加入我调制的药也是个好办法，\n但是，最能让巧克力变得甘甜的\n果然还是其中蕴藏的爱意。\n我爱您……亲爱的。');
INSERT INTO unit_comments VALUES(1090011,109001,2,6,5,11.2,2,'您诞生的日子……\n也就是我们的命运开始的那一天。\n让我们举杯庆祝吧。\n来，请饮下这杯……');
INSERT INTO unit_comments VALUES(1090012,109031,2,6,1,0.0,1,'祝您生日快乐。\n我已经准备好了\n只有两个人的庆祝会。\n在那之后……嘻嘻。');
INSERT INTO unit_comments VALUES(1091001,109101,1,1,1,6.7,2,'弟弟，你知道情人节吗？\n那是给最喜欢的人\n送巧克力的日子。\n姐姐的爱，你要收下哦？');
INSERT INTO unit_comments VALUES(1091002,109101,1,2,1,7.5,5,'和姐姐的宠溺一样甜蜜的巧克力，\n或者是和姐姐的摸摸一样，\n只有大人才能品尝的巧克力，\n弟弟你喜欢哪一种呢？');
INSERT INTO unit_comments VALUES(1091003,109101,1,3,6,2.0,1,'感觉要蛀牙了？\n没关系，姐姐我\n会帮弟弟把牙齿刷干净的。\n张开嘴♪');
INSERT INTO unit_comments VALUES(1091004,109101,1,4,5,5.5,2,'要我摸摸你的头吗？\n还是要揉肩？枕大腿？\n巧克力附赠\n姐姐宠溺的照料怎么样？');
INSERT INTO unit_comments VALUES(1091005,109101,1,5,5,7.1,2,'哎呀，你的嘴边粘到巧克力了哦。\n姐姐帮你拿掉……啊唔。\n嗯，很甜♪');
INSERT INTO unit_comments VALUES(1091006,109131,1,1,1,0.0,1,'情人节是把香甜巧克力\n与爱意一起送出的日子♪\n姐姐的爱可比巧克力甜多了，\n你可要做好准备哦♪');
INSERT INTO unit_comments VALUES(1091007,109131,1,2,1,0.0,1,'你收到了多少巧克力？\n说给姐姐听听吧？\n我想要知道弟弟的全部。\n这是爱弟心切啊♪');
INSERT INTO unit_comments VALUES(1091008,109131,1,3,1,0.0,1,'你怎么一副心神不定的样子？\n弟弟你在等什么呢？\n呵呵，急不可耐的弟弟\n也好可爱啊♪');
INSERT INTO unit_comments VALUES(1091009,109131,1,4,1,0.0,1,'诶？这身衣服很适合我？\n弟弟真是的，\n夸女孩子的技巧提高了啊♪\n姐姐都心动了呢。');
INSERT INTO unit_comments VALUES(1091010,109131,1,5,1,0.0,1,'我最喜欢弟弟了♪\n还有好多巧克力，\n每送一份，我就会说一次哦♪');
INSERT INTO unit_comments VALUES(1091011,109101,2,6,1,2.8,2,'今天是弟弟的生日！\n我做了个好大的巧克力蛋糕，\n一起吃吧♪');
INSERT INTO unit_comments VALUES(1091012,109131,2,6,1,0.0,1,'你过生日啦！\n姐姐我为了今天这个纪念日，\n考虑了好久该送什么礼物！\n来，把姐姐送给你哦☆');
INSERT INTO unit_comments VALUES(1092001,109201,1,1,1,5.0,2,'难得来到了异世界，\n我也想和你友好相处！\n请多指教');
INSERT INTO unit_comments VALUES(1092002,109201,1,2,6,4.0,1,'我确实是玛娜莉亚的第一公主\n……不过请别太在意，\n要是你能平常地和我说话，我会很开心的！');
INSERT INTO unit_comments VALUES(1092003,109201,1,3,1,3.7,2,'平常我大多都和古蕾雅两人\n待在一起！待在古蕾雅身边\n就会觉得特别平静，很安心');
INSERT INTO unit_comments VALUES(1092004,109201,1,4,2,3.0,1,'听你讲话非常有意思！\n我还想再听多一些');
INSERT INTO unit_comments VALUES(1092005,109201,1,5,5,3.2,2,'可以的话，待会一起\n喝茶吧？其实我还\n挺擅长泡红茶的');
INSERT INTO unit_comments VALUES(1092006,109231,1,1,1,0.0,1,'英灵啊，回应我的呼唤吧！\n为了保护大家\n请赐予我力量！');
INSERT INTO unit_comments VALUES(1092007,109231,1,2,1,0.0,1,'我决不会\n让古蕾雅露出悲伤的表情！\n我会加油的！');
INSERT INTO unit_comments VALUES(1092008,109231,1,3,1,0.0,1,'与重要的人们之间的情谊，\n将赐予我\n无限的力量！');
INSERT INTO unit_comments VALUES(1092009,109231,1,4,1,0.0,1,'我的魔法是为了保护大家……\n保护你而存在的！');
INSERT INTO unit_comments VALUES(1092010,109231,1,5,1,0.0,1,'我绝对不会\n让你受伤的！\n这里就交给我吧！');
INSERT INTO unit_comments VALUES(1092011,109201,2,6,1,1.8,2,'生日快乐！\n希望你今天能尽情地\n享受幸福！');
INSERT INTO unit_comments VALUES(1092012,109231,2,6,1,0.0,1,'Happy birthday！\n一起度过快乐的一天吧！\n先做什么好呢！？');
INSERT INTO unit_comments VALUES(1093001,109301,1,1,1,3.1,2,'交到更多朋友了，\n露好开心啊！\n露想和朋友们一起享受青春');
INSERT INTO unit_comments VALUES(1093002,109301,1,2,4,3.0,1,'虽然每天都是补考和不及格地狱，\n但露还是很喜欢\n玛娜莉亚魔法学院♪');
INSERT INTO unit_comments VALUES(1093003,109301,1,3,1,2.7,2,'你在享受青春吗？\n露每天都在享受哦！');
INSERT INTO unit_comments VALUES(1093004,109301,1,4,1,1.2,2,'希望你多多陪\n露一起玩耍♪');
INSERT INTO unit_comments VALUES(1093005,109301,1,5,6,3.4,1,'你很在意\n小眼睛吗？\n要不要戳戳看？');
INSERT INTO unit_comments VALUES(1093006,109331,1,1,1,0.0,1,'让你见识见识\n露操控小眼睛的华丽技术！\n看招看招！');
INSERT INTO unit_comments VALUES(1093007,109331,1,2,1,0.0,1,'露和小眼睛\n一起合作\n干掉敌人啦！');
INSERT INTO unit_comments VALUES(1093008,109331,1,3,1,0.0,1,'在异世界的回忆，\n也是青春的一页！\n露，十分开心！');
INSERT INTO unit_comments VALUES(1093009,109331,1,4,1,0.0,1,'诶，你问小眼睛的真实身份？\n露也不知道……但是，\n它和露一直在一起，我们是好朋友哦！');
INSERT INTO unit_comments VALUES(1093010,109331,1,5,1,0.0,1,'补考的女帝，不及格女王……\n让你见识一下\n拥有众多外号的露的实力！');
INSERT INTO unit_comments VALUES(1093011,109301,2,6,6,2.8,2,'今天是你的生日？\n那么，露也来庆祝！\n生日快乐！');
INSERT INTO unit_comments VALUES(1093012,109331,2,6,1,0.0,1,'露不擅长记东西……\n但是朋友的生日\n是一定会记住的！\n生日快乐！');
INSERT INTO unit_comments VALUES(1094001,109401,1,1,1,4.4,4,'对于我的模样，以及龙的力量……\n能请你\n不要感到害怕吗？');
INSERT INTO unit_comments VALUES(1094002,109401,1,2,5,3.6,2,'虽然我不像其他人那样\n经常欢笑或嬉闹……\n但其实也觉得很开心。');
INSERT INTO unit_comments VALUES(1094003,109401,1,3,1,5.3,2,'安总是陪伴在\n独自一人的我身边……\n是她照亮了我的世界。');
INSERT INTO unit_comments VALUES(1094004,109401,1,4,6,3.7,2,'没想到会来到一个陌生的世界……\n但是，能与像你们这样\n温柔的人邂逅，真是太好了。');
INSERT INTO unit_comments VALUES(1094005,109401,1,5,2,0.0,2,'我真的非常感谢你……\n要是有什么我能帮忙的地方\n请尽管说，我会尽力的。');
INSERT INTO unit_comments VALUES(1094006,109431,1,1,1,0.0,1,'如果是为了保护大家，\n我就能使出无穷的力量！\n见识下龙的火焰吧！');
INSERT INTO unit_comments VALUES(1094007,109431,1,2,1,0.0,1,'我绝对……\n不会让大家受伤的\n我的火焰，就是为此而燃烧的！');
INSERT INTO unit_comments VALUES(1094008,109431,1,3,1,0.0,1,'我所欠缺的，\n是不会输给自己的强大……\n而现在，已经没问题了！');
INSERT INTO unit_comments VALUES(1094009,109431,1,4,1,0.0,1,'虽然我曾认为\n自己不需要这种力量……\n但现在，我有些为它感到骄傲了。');
INSERT INTO unit_comments VALUES(1094010,109431,1,5,1,0.0,1,'我还是挺结实的\n所以，就交给我吧！');
INSERT INTO unit_comments VALUES(1094011,109401,2,6,1,4.3,2,'今天是你的生日吧\n那个……生日快乐\n你有什么想要的礼物吗？');
INSERT INTO unit_comments VALUES(1094012,109431,2,6,1,0.0,1,'生日快乐\n这明明是为你庆生的日子，\n我却也开心起来了，真是奇妙。');
INSERT INTO unit_comments VALUES(1095001,109501,1,1,1,2.6,2,'这就是传说中的和服吗……\n是既能看，\n又能脱，\n还可以让人旋转起来的魔性衣服！');
INSERT INTO unit_comments VALUES(1095002,109501,1,2,5,2.9,6,'哎～呀～～～\n虐待狂先生，你都看到了吗！？\n看着空花旋转的样子\n笑得那么开心……果然是有人格缺陷！');
INSERT INTO unit_comments VALUES(1095003,109501,1,3,1,4.8,5,'据说穿和服时\n下面是不穿的……\n虐、虐待狂先生，你可不能说\n要亲眼确认一下哦……唔呼呼。');
INSERT INTO unit_comments VALUES(1095004,109501,1,4,6,7.4,5,'啊，腰带松了……\n虐待狂先生，能帮忙\n重新系紧它吗？　吸溜。好……\n好棒！　再紧一点！');
INSERT INTO unit_comments VALUES(1095005,109501,1,5,2,5.6,5,'虐待狂先生……不，现在应该\n叫作虐待狂大人吧……\n空花是服侍虐待狂大人的\n顺从的……吸溜。');
INSERT INTO unit_comments VALUES(1095006,109531,1,1,1,0.0,1,'这件和服露得有些多\n感觉有些难为情……不过，\n一想到虐待狂先生正看着自己\n好像就感觉……吸溜。');
INSERT INTO unit_comments VALUES(1095007,109531,1,2,1,0.0,1,'哎～呀～～～\n好像要对这种旋转上瘾了\n还想、还想继续转～\n唔呼、唔呼呼！');
INSERT INTO unit_comments VALUES(1095008,109531,1,3,1,0.0,1,'穿着这件衣服跳舞，\n感觉能跳得比平时更美……\n而像蝴蝶般起舞的空花\n就会被像蜘蛛一样的虐待狂先生……唔呼呼。');
INSERT INTO unit_comments VALUES(1095009,109531,1,4,1,0.0,1,'「扒下空花身上的和服后，\n虐待狂先生就像野兽般……」\n唔呼呼……哈！　忽然有种\n正在被虐待狂先生注视的感觉！');
INSERT INTO unit_comments VALUES(1095010,109531,1,5,1,0.0,1,'很在意这条腰带吗？\n这、这就说明你想拉开它，让空花旋转……\n然后把脱去了和服的空花\n就那样美味地……唔呼呼。');
INSERT INTO unit_comments VALUES(1095011,109501,2,6,1,5.7,5,'今天是个很重要的日子……\n是虐待狂先生的生日……吸溜\n换句话说，就是要用空花的身体\n来庆祝了吧！？');
INSERT INTO unit_comments VALUES(1095012,109531,2,6,1,0.0,1,'生日快乐\n话说回来，虐待狂先生，和服的腰带\n也很像包装礼物的缎带吧\n我就是礼物，开玩笑的……唔呼呼。');
INSERT INTO unit_comments VALUES(1096001,109601,1,1,1,7.9,2,'如果想偷偷溜出去的话\n我知道一个好地方\n下次让我来带路吧！\n一定会很有趣的！');
INSERT INTO unit_comments VALUES(1096002,109601,1,2,1,4.9,6,'我喜欢红叶\n但也喜欢红叶馒头哦\n一想起来就感觉饿了……\n正可谓“舍华求实”！');
INSERT INTO unit_comments VALUES(1096003,109601,1,3,3,8.6,2,'扔一次是为了一统天下……\n扔两次也是为了一统天下……\n将军也要来进行\n扔手里剑的特训吗？　可以扔个痛快！');
INSERT INTO unit_comments VALUES(1096004,109601,1,4,1,11.0,2,'这副狐狸面具是在\n大江户镇上办庙会时买的。走起来\n会发出响声，非常可爱\n嗷嗷是～也！');
INSERT INTO unit_comments VALUES(1096005,109601,1,5,6,4.0,1,'我想到了，忍术·冻结地狱\n强迫敌人吃下\n冰凉的刨冰\n是能让对方脑袋发痛的招数！');
INSERT INTO unit_comments VALUES(1096006,109631,1,1,1,0.0,1,'向与将军为敌之人\n降下天诛！　在一统天下之前\n我可不会停下！\n以我的必杀忍术，定能百战百胜！');
INSERT INTO unit_comments VALUES(1096007,109631,1,2,1,0.0,1,'哼哼哼……就为将军展露下\n我秘藏的新忍术吧！\n唔，还不到使用它的时候吗？\n将军你真是坏心眼～');
INSERT INTO unit_comments VALUES(1096008,109631,1,3,1,0.0,1,'将军定会建立一个\n天下太平的盛世吧！\n而我会一直为将军\n守护那片河山的！');
INSERT INTO unit_comments VALUES(1096009,109631,1,4,1,0.0,1,'请看清楚\n我活跃的身姿吧！　我会时刻不离将军身边，\n总有一天，\n我一定会作为将军的忠臣，被人们称颂。');
INSERT INTO unit_comments VALUES(1096010,109631,1,5,1,0.0,1,'只要是为了将军，无论面对\n怎样的敌人，我都会去战斗！\n因为我相信着，忠诚心\n才是一统天下的王牌！');
INSERT INTO unit_comments VALUES(1096011,109601,2,6,2,7.4,1,'生日快乐！\n我准备的礼物是\n将军专用的手里剑是～也！\n请尽情地扔吧！');
INSERT INTO unit_comments VALUES(1096012,109631,2,6,1,0.0,1,'今天真是恭喜了！\n就让我来领头干杯吧！\n把双手举起来！\n要欢闹到早晨哦！');
INSERT INTO unit_comments VALUES(1097001,109701,1,1,1,4.9,2,'雷姆擅长做所有的家务，\n要是有什么\n雷姆能帮上忙的事，\n请尽管吩咐。');
INSERT INTO unit_comments VALUES(1097002,109701,1,2,6,5.8,2,'姐姐抛下女仆的工作，\n正在休息？\n不愧是姐姐！\n休息的姿态也很优雅！');
INSERT INTO unit_comments VALUES(1097003,109701,1,3,1,4.0,2,'虽说是鬼族，\n其实也与人类没什么不同哦。\n只是能横扫魔兽、挥动铁球罢了。');
INSERT INTO unit_comments VALUES(1097004,109701,1,4,1,0.0,1,'请把照顾起居的工作\n交给雷姆吧。\n雷姆一定会\n全心全意地服侍您。');
INSERT INTO unit_comments VALUES(1097005,109701,1,5,6,5.1,2,'看来在这边的世界，\n哪怕头上长角\n也不值得奇怪呢。\n总觉得有些开心。');
INSERT INTO unit_comments VALUES(1097006,109731,1,1,1,0.0,1,'这个铁球？\n它是便携式的，\n因为缩短过长度，\n所以比普通的铁球更轻一些哦。');
INSERT INTO unit_comments VALUES(1097007,109731,1,2,1,0.0,1,'即便是巨木，\n也无法阻挡\n身为鬼族的雷姆。\n就让雷姆来将其全部粉碎。');
INSERT INTO unit_comments VALUES(1097008,109731,1,3,1,0.0,1,'这种巨木，\n靠雷姆的力量也能移开。\n那、那个，如果成功了，\n也可以夸奖一下雷姆哦？');
INSERT INTO unit_comments VALUES(1097009,109731,1,4,1,0.0,1,'女孩子无法\n破坏这棵倒下的树木？\n不必多言，\n请站在那边看清楚了。');
INSERT INTO unit_comments VALUES(1097010,109731,1,5,1,0.0,1,'雷姆很强……\n这样称赞女孩子\n可能不太合适吧？');
INSERT INTO unit_comments VALUES(1097011,109701,2,6,2,5.4,1,'祝您生日快乐。\n雷姆来做一个蛋糕庆祝吧。\n这次要做得豪华点，\n首先得找来飞龙的蛋才行。');
INSERT INTO unit_comments VALUES(1097012,109731,2,6,1,0.0,1,'祝您生日快乐！\n为了今天，\n雷姆做了许多准备，\n让我们来盛大地庆祝一番吧。');
INSERT INTO unit_comments VALUES(1098001,109801,1,1,6,5.0,3,'这里是异世界。说实话，\n尽管确实很让人难以置信……\n但是总比巴鲁斯的油嘴滑舌\n多了那么几分可信度吧。');
INSERT INTO unit_comments VALUES(1098002,109801,1,2,4,2.4,3,'真是陷入麻烦的局面了呢。\n算了，似乎也没有危险人物，\n既然能轻松点，那也没什么问题。');
INSERT INTO unit_comments VALUES(1098003,109801,1,3,3,1.9,2,'拉姆可是有在工作的哦。\n拉姆在用自己品尝红茶的优雅模样，\n使周围的人感到赏心悦目啊。');
INSERT INTO unit_comments VALUES(1098004,109801,1,4,3,5.4,4,'这是风系的魔法。\n只要拉姆愿意，\n可以把任何东西都切成碎片。\n不过那很累人，所以拉姆不会那么做。');
INSERT INTO unit_comments VALUES(1098005,109801,1,5,3,5.9,2,'吃下这个蒸地瓜吧。\n很好吃吧。\n是刚出炉……不，\n刚蒸好的哦。');
INSERT INTO unit_comments VALUES(1098006,109831,1,1,1,0.0,1,'在拥有千里眼的拉姆看来，\n你是绝对逃不掉的。\n你要是还惜命，\n就尽管发挥自己的智慧与勇气吧。');
INSERT INTO unit_comments VALUES(1098007,109831,1,2,1,0.0,1,'给我记住了。\n哪怕失去了角，\n本天才拉姆\n也是常人遥不可及的存在。');
INSERT INTO unit_comments VALUES(1098008,109831,1,3,1,0.0,1,'你在战斗中真是派不上用场呢。\n拉姆还得费一番功夫掩藏失望。\n你可不要……\n从拉姆身边离开哦。');
INSERT INTO unit_comments VALUES(1098009,109831,1,4,1,0.0,1,'就让我一口气解决掉吧。\n你问为什么？\n因为拉姆……没什么，\n只是想尽快回去罢了。');
INSERT INTO unit_comments VALUES(1098010,109831,1,5,1,0.0,1,'风魔法很方便哦。不仅能切碎敌人，\n还能把麻烦的男人\n彻底拧断。\n你问拧哪里？任你想象。');
INSERT INTO unit_comments VALUES(1098011,109801,2,6,3,6.5,2,'能让拉姆给你庆祝生日，\n可要感到光荣哦。\n你问礼物是什么？那当然\n是刚蒸好的地瓜咯！');
INSERT INTO unit_comments VALUES(1098012,109831,2,6,1,0.0,1,'你想要什么？\n尽管说吧。\n毕竟，今天可是你的生日哦？\n奖励还是得给你的。');
INSERT INTO unit_comments VALUES(1099001,109901,1,1,6,7.9,1,'真是奇妙啊\n尽管都有玛那和精灵\n却有这么多没见过的事物\n这里就是异世界吗……');
INSERT INTO unit_comments VALUES(1099002,109901,1,2,1,6.3,2,'要去散散步吗？\n我要去和精灵们说说话\n那是和这些孩子们\n契约中的条件之一。');
INSERT INTO unit_comments VALUES(1099003,109901,1,3,1,5.0,6,'我刚才去城镇里\n好像看到了像是精灵的人\n难道这个世界里\n也有半精灵吗？');
INSERT INTO unit_comments VALUES(1099004,109901,1,4,5,5.2,6,'小可可萝很聪明\n而且非常可爱呢\n如果她真是我的妹妹\n该有多好呢。');
INSERT INTO unit_comments VALUES(1099005,109901,1,5,1,5.4,6,'自从来到这个世界后\n帕克就不怎么出来了\n莫非是担心用掉太多玛那\n所以才很少出现吗？');
INSERT INTO unit_comments VALUES(1099006,109931,1,1,1,0.0,1,'你要不要也来坐坐？\n兰德索尔的花也非常漂亮\n微精灵们也很开心。');
INSERT INTO unit_comments VALUES(1099007,109931,1,2,1,0.0,1,'来聊聊天吧？\n关于这个世界\n以及我的世界……\n我也有好多话想说。');
INSERT INTO unit_comments VALUES(1099008,109931,1,3,1,0.0,1,'对了，小可可萝她\n也能和精灵们对话吧\n呵呵，我果然没法\n把她当成是外人呢。');
INSERT INTO unit_comments VALUES(1099009,109931,1,4,1,0.0,1,'哎呀，花瓣落在头发上了\n我帮你拿下来吧\n头靠过来一点。\n就是这样……好，已经拿下来了♪');
INSERT INTO unit_comments VALUES(1099010,109931,1,5,1,0.0,1,'我说……下次还能\n一起来这个花田看看吗？\n对精灵术士而言\n承诺是最为重要的事物。');
INSERT INTO unit_comments VALUES(1099011,109901,2,6,1,6.0,2,'在这边的世界\n要说“happy birthday”\n对吧？\n生日快乐');
INSERT INTO unit_comments VALUES(1099012,109931,2,6,1,0.0,1,'今天是你的生日呢，生日快乐\n能像这样为你庆生\n真的让我非常高兴。');
INSERT INTO unit_comments VALUES(1100001,110001,1,1,1,4.9,2,'ちょす～♪\n夏の海ってキラキラしてて\nマジヤバいよね！\nめいっぱい楽しんじゃお～！');
INSERT INTO unit_comments VALUES(1100002,110001,1,2,1,6.9,6,'ヒデサイ、知ってた？\n海の水ってしょっぱいんだよ！\nうち物知りっしょ～♪\nでぃすいずあんだすたんど？');
INSERT INTO unit_comments VALUES(1100003,110001,1,3,2,3.4,1,'撮影が終わったら\n一緒に海で遊ぼうよ！\nすぐ終わるから\nちょいまっててね♪');
INSERT INTO unit_comments VALUES(1100004,110001,1,4,2,2.5,5,'この水着、イケてるっしょ！\nファンのみんなに\n喜んでもらえるといいな～♪');
INSERT INTO unit_comments VALUES(1100005,110001,1,5,2,7.7,1,'んー！　海って最高ー！\nヒデサイといっぱい遊べるし\nマジで来てよかったよー♪\n今は夏の宿題の話はナシでよろ～');
INSERT INTO unit_comments VALUES(1100006,110031,1,1,1,0.0,1,'見て見て、うちのモデル姿！\nどう？　いい感じっしょ♪\nこんなに近くで見ていいのは\nヒデサイだけだかんね？');
INSERT INTO unit_comments VALUES(1100007,110031,1,2,1,0.0,1,'夏の海って楽しいことが\nマジでいっぱいあるけど…\nヒデサイといるだけで、めっちゃ\nバイブス上がるんだよね～');
INSERT INTO unit_comments VALUES(1100008,110031,1,3,1,0.0,1,'セクシーってよく分かんないけど\nいまのうち、セクシーかな？\nヒデサイがドキドキ\nしてたら、うれしいな♪');
INSERT INTO unit_comments VALUES(1100009,110031,1,4,1,0.0,1,'夏って気分アガるよね♪\nヒデサイも応援してくれるし、\nもう怖いもんなしって感じだよ☆');
INSERT INTO unit_comments VALUES(1100010,110031,1,5,1,0.0,1,'日ざしも砂浜もあっついねー！\n今日はめっちゃ\nフィーリングがいいんだ～♪\n超かわいい写真が撮れそう！');
INSERT INTO unit_comments VALUES(1100011,110001,2,6,1,3.3,2,'ちょすー！\nヒデサイ、誕生日なんだよね？\nおめでとー！\n今日はパーティーだね！');
INSERT INTO unit_comments VALUES(1100012,110031,2,6,1,0.0,1,'ちょす！　ヒデサイ、ハピバー☆\nお祝いしなきゃだよね！\nうちにしてほしいことがあったら\nなんでも言っていいよ！');
INSERT INTO unit_comments VALUES(1101001,110101,1,1,1,7.4,2,'海に入る前には\n準備運動をしなくちゃ！\n手伝ってあげるから力を抜いて？\nそう、とっても上手よ～♪');
INSERT INTO unit_comments VALUES(1101002,110101,1,2,6,7.1,1,'海って楽しいところだけど\n危ないこともあるんだから\n気を付けて遊びましょうね？\n先生がついていてあげる');
INSERT INTO unit_comments VALUES(1101003,110101,1,3,6,4.8,4,'なんだかやたら男の人が\n話しかけてくるの。もしかして\n水着がずれちゃってるのかな\n君、ちょっと見てくれる？');
INSERT INTO unit_comments VALUES(1101004,110101,1,4,5,7.0,4,'ああ～ん、\n砂が水着の中に入ってきちゃって\nくすぐったいわ～\nお願い、なんとかして～');
INSERT INTO unit_comments VALUES(1101005,110101,1,5,5,8.0,6,'久しぶりに水着を着るから\n今日のために新調したのよ\n似合ってるかな？　ってどうして\n顔をそむけちゃうのよ～');
INSERT INTO unit_comments VALUES(1101006,110131,1,1,1,0.0,1,'くしゅんっ…\nちょっと冷えてきちゃった\n君にくっついて\n暖まってもいいかな？');
INSERT INTO unit_comments VALUES(1101007,110131,1,2,1,0.0,1,'君があまりにも頼もしいから\n私たちが先生と生徒って事を\n忘れちゃいそうなの…\nそれでも…ううん、なんでもない');
INSERT INTO unit_comments VALUES(1101008,110131,1,3,1,0.0,1,'こうして男の子と海にくるなんて\n初めてのことだから\nなんだか照れちゃうわ\nねえ、君はいまどんな気持ち？');
INSERT INTO unit_comments VALUES(1101009,110131,1,4,1,0.0,1,'夏休みの一番の思い出は\nこのきれいな夜の海かな\n君と一緒に見たんだから\n一生忘れないわ、きっと…');
INSERT INTO unit_comments VALUES(1101010,110131,1,5,1,0.0,1,'君、もしかして少し背が伸びた？\n気のせいじゃないと思うよ\n君の成長を感じると、なんだか\nうれしくて胸がドキドキするから');
INSERT INTO unit_comments VALUES(1101011,110101,2,6,1,9.2,2,'今日は君のお誕生日ね\nふふ、もちろん忘れてないわ\n一つ大人になったお祝いに\n海の家でパーティーしましょ！');
INSERT INTO unit_comments VALUES(1101012,110131,2,6,1,0.0,1,'お誕生日おめでとう～\n浜辺で見る君の顔が\nすっかり大人にみえるわ\nふふ、夏のせいかしらね');
INSERT INTO unit_comments VALUES(1103001,110301,1,1,2,4.9,1,'あははっ\n日が暮れるまで浜辺でゆっくり…\nこんな日があってもいいわよね');
INSERT INTO unit_comments VALUES(1103002,110301,1,2,1,6.0,6,'貝殻を集めてアクセサリー作り\nなんてのも悪くないわね\n思い出作りよ？　売ろうなんて\n考えてないわよ？　たぶんね');
INSERT INTO unit_comments VALUES(1103003,110301,1,3,1,4.1,2,'海の砂ってサラサラしてて気持ち\nいいわよね。持ち帰って救護院に\n砂場を作ったりできないかしら？');
INSERT INTO unit_comments VALUES(1103004,110301,1,4,1,4.9,5,'子どもたちと賑やかに\n過ごすのはもちろん楽しいけど\nあんたと二人きりで過ごすのも\nなかなかいいわね');
INSERT INTO unit_comments VALUES(1103005,110301,1,5,1,3.1,2,'あたしの水着をこんな間近で\n見られるなんて、あんた本当に\n得してるわよ♪');
INSERT INTO unit_comments VALUES(1103006,110331,1,1,1,0.0,1,'エレガントじゃないかもだけど\n今だけは許してね\nあたし今、とびっきり\n舞い上がっちゃってる！');
INSERT INTO unit_comments VALUES(1103007,110331,1,2,1,0.0,1,'夏の日差しって、開放的な\n気分にさせるわよね！\nあたしももう少し\n冒険してみようかしら♪');
INSERT INTO unit_comments VALUES(1103008,110331,1,3,1,0.0,1,'海に来るのなんて久しぶりね～\nせっかくのバカンスなんだから\n楽しまなきゃ損よ！');
INSERT INTO unit_comments VALUES(1103009,110331,1,4,1,0.0,1,'本当にきれいな海よね\nもし誰もいない浜辺であんたと\n二人っきりだったら…って！\nなんであたしそんなことを！');
INSERT INTO unit_comments VALUES(1103010,110331,1,5,1,0.0,1,'捕まえてみなさ～い♪\nな、なんてことあたしが\nできるわけないじゃない！！');
INSERT INTO unit_comments VALUES(1103011,110301,2,6,2,1.9,1,'誕生日おめでとう♪\n救護院のみんなもあんたの\n誕生日を祝いたがってるから\n楽しみにしてなさいね！');
INSERT INTO unit_comments VALUES(1103012,110331,2,6,1,0.0,1,'誕生日おめでとう！\n二人きりでバースデーパーティー\nしましょうか♪　なんてね');
INSERT INTO unit_comments VALUES(1104001,110401,1,1,2,2.9,6,'へへっ、海だー！\nな、なんだよ、あたしが\nはしゃいじゃおかしいかよ？');
INSERT INTO unit_comments VALUES(1104002,110401,1,2,1,2.6,2,'ビーチで焼きそばもいいよな\nよし！　材料持っていって\n昼飯は作ってやるよ');
INSERT INTO unit_comments VALUES(1104003,110401,1,3,2,3.2,1,'暑い日に泳ぐのって最高だよな！\nそうだ、浮き輪引っ張って\n泳いでやろうか？');
INSERT INTO unit_comments VALUES(1104004,110401,1,4,1,3.3,5,'おまえと一緒だと、なんか楽…\nなっ、なんでもねぇ！\n…海だからって、開放的に\nなりすぎるのもよくないな、うん');
INSERT INTO unit_comments VALUES(1104005,110401,1,5,1,8.0,6,'ちょっと休憩しようぜ\nお、なんかおまえの手、\nひんやりして冷たいな！\nって…わりぃ、触りすぎたな');
INSERT INTO unit_comments VALUES(1104006,110431,1,1,1,0.0,1,'うぉわっ！？　み、水着がっ！？\nだ、誰も見てねぇ、よな…って\nなんでそこにいやがんだよ！？\n見てんじゃねぇ！　…ったく');
INSERT INTO unit_comments VALUES(1104007,110431,1,2,1,0.0,1,'ん～、風が気持ちいいな！\nへへっ、おまえと二人で海なんて、\nくすぐったい感じだぜ\nさぁて、まずは何をする？');
INSERT INTO unit_comments VALUES(1104008,110431,1,3,1,0.0,1,'なんか、おまえには恥ずかしいとこ\nばかり見られてる気がするな…\nイヤってわけじゃねぇけどよ\n…って何言ってんだあたしは！？');
INSERT INTO unit_comments VALUES(1104009,110431,1,4,1,0.0,1,'不思議なんだけどさ\nおまえといると、時間が\nあっという間に過ぎちまう…\nおまえもか？　…そ、そっか！');
INSERT INTO unit_comments VALUES(1104010,110431,1,5,1,0.0,1,'か、かわいい？\nあたしの水着姿が？\nう、うれしくねぇっての！\n尻尾？　振ってねぇ！　見んな！');
INSERT INTO unit_comments VALUES(1104011,110401,2,6,1,8.7,2,'誕生日なんだってな。そうだな…\n砂浜特訓メニューのプレゼント、\nってのは冗談で、\nあとでカキ氷でもおごってやるよ');
INSERT INTO unit_comments VALUES(1104012,110431,2,6,1,0.0,1,'今日、誕生日なんだろ？\n食材の買い出し、付き合えよ\nあとでなんか作ってやるからさ\nおまえの好きなもの…教えろよな');
INSERT INTO unit_comments VALUES(1105001,110501,1,1,6,1.9,1,'元気してるさー？\n私はいつでも元気だよー！\nさーさ、一緒に何するさー？');
INSERT INTO unit_comments VALUES(1105002,110501,1,2,2,3.2,1,'なんくるないさー！\n海を見ると叫びたくならないー？\nほらキミも、なんくるないさー！');
INSERT INTO unit_comments VALUES(1105003,110501,1,3,1,5.7,2,'海は楽しいことでいっぱいよー！\n走って、泳いで、歌って、踊って\n思う存分楽しむさー！');
INSERT INTO unit_comments VALUES(1105004,110501,1,4,6,8.2,1,'ん～？　どこを見てるのー？\nこの水着が気になるさー？\nじゃあ触ってみるー？　\nほらほら、遠慮はいらないさ～！');
INSERT INTO unit_comments VALUES(1105005,110501,1,5,2,3.1,5,'波の音は心地いいさ～\nキミと一緒に聞いてるとなんだか\n胸がドキドキするね～\nキミも？　聞いてみていい～？');
INSERT INTO unit_comments VALUES(1105006,110531,1,1,1,0.0,1,'夏の楽しみ方は色々あるけど～\nこうして演奏するのは格別さ～\n聞こえる波のリズムに乗って\n心のままに音楽を楽しむさー！');
INSERT INTO unit_comments VALUES(1105007,110531,1,2,1,0.0,1,'あは～♪　楽しいねぇ～！\n夏は体がウズウズするさ～！\nほら、キミも一緒に歌うさ～\n恥ずかしがってちゃダメだよ～♪');
INSERT INTO unit_comments VALUES(1105008,110531,1,3,1,0.0,1,'思う存分歌ったあとは\n浜辺を走って、海で泳ぐよー！\n夏は待っててくれないからね～\n今日は私と限界を超えるさ～！');
INSERT INTO unit_comments VALUES(1105009,110531,1,4,1,0.0,1,'今度、キミをテーマに\n一曲作ってみたいさ～\nきっと楽しい曲になるさ～！');
INSERT INTO unit_comments VALUES(1105010,110531,1,5,1,0.0,1,'キミのことを考えて演奏すると\n音がいつもと違う感じになるさー\nなんて言えばいいのかな～…\nとにかく聴いてほしいさー！');
INSERT INTO unit_comments VALUES(1105011,110501,2,6,2,2.8,1,'ばっちり準備万端さー！\n何って、キミの誕生日だよー！\nさぁ手と手を繋いで、\nレッツダンスさー！');
INSERT INTO unit_comments VALUES(1105012,110531,2,6,1,0.0,1,'誕生日、めでたいさ～！\n体が勝手に踊りだすさ～、あっ！\nぶつかってごめんねぇ、でも\nやわいとこだから平気だよねー？');
INSERT INTO unit_comments VALUES(1106001,110601,1,1,1,4.2,2,'夏の海は不思議の国どすえ\nうちと一緒に\n楽しく過ごしまひょ♪');
INSERT INTO unit_comments VALUES(1106002,110601,1,2,6,5.5,1,'ふう、日差しが暑くて\nかなわんわぁ～\n王子はん、あっちの日陰で\n涼みまへんか♪');
INSERT INTO unit_comments VALUES(1106003,110601,1,3,1,4.1,2,'冷たいスイカ、切ってきましたぇ\nぬるならへんうちに一緒に\n食べまひょ～');
INSERT INTO unit_comments VALUES(1106004,110601,1,4,1,7.9,5,'夏と言えば、花火どす\n夜空に咲く大輪の花…\nはぁ、王子はんと二人で\n見に行きたいわぁ');
INSERT INTO unit_comments VALUES(1106005,110601,1,5,6,5.0,2,'この水着、\nうちに似合うてますやろか？\n王子はんに見せたくて\n一生懸命選んだんどす♪');
INSERT INTO unit_comments VALUES(1106006,110631,1,1,1,0.0,1,'いやぁ、気持ちええわぁ～♪\nこうして海辺で寝転がってると\n夏を全身で感じられそうやわぁ\n王子はんも一緒に、どうやろか？');
INSERT INTO unit_comments VALUES(1106007,110631,1,2,1,0.0,1,'ぬいぐるみのみんなも\nお日さま浴びて気持ちよさそうや\n王子はんもこっち来て触ります？\nふかふか、もふもふですよ…♪');
INSERT INTO unit_comments VALUES(1106008,110631,1,3,1,0.0,1,'うちの水着、\nヒラヒラしててかいらしやろ？\nじっくり見てもええんよ？\nもっと近くで、ほら、な♪');
INSERT INTO unit_comments VALUES(1106009,110631,1,4,1,0.0,1,'夏の海辺はロマンチックやねぇ\nこんな場所に王子はんと二人きり\nゆったり暮らしたいどすなぁ\n…お城はどこに建てまひょか♪');
INSERT INTO unit_comments VALUES(1106010,110631,1,5,1,0.0,1,'王子はん、一緒に踊りまへんか？\nマホ姫と王子はん、二人だけの\n海辺の舞踏会や♪\nさぁ、一緒に…くるりんぱ～♪');
INSERT INTO unit_comments VALUES(1106011,110601,2,6,1,6.2,2,'お誕生日おめでとうなぁ～\nまだまだ暑いさかい\n今日は、和菓子と冷やし抹茶で\nお祝いしまひょ♪');
INSERT INTO unit_comments VALUES(1106012,110631,2,6,1,0.0,1,'今日は王子はんの誕生日やねぇ\nほんま、めでたい日やわぁ\n魔法でお祝いの花火\nあげるからしっかり見といてな');
INSERT INTO unit_comments VALUES(1107001,110701,1,1,6,3.0,4,'制服って、スカートなんですね…\nうう…足元がすーすーして\n落ち着かないです…');
INSERT INTO unit_comments VALUES(1107002,110701,1,2,6,3.7,2,'私が学院に行くのが心配ですか？\nご安心ください！\nいざとなったら、\n保健室登校しますから！');
INSERT INTO unit_comments VALUES(1107003,110701,1,3,6,2.7,2,'学院にも植物が…！\nぼっちのままでも、\nお花さんたちに\n話しかければ寂しくないです！');
INSERT INTO unit_comments VALUES(1107004,110701,1,4,1,6.3,2,'ＢＢ団の特訓が無かったら、\n学院に近づくだけで\n失神していたかも…えへへ♪\nあなたには、とっても感謝です♪');
INSERT INTO unit_comments VALUES(1107005,110701,1,5,4,7.2,5,'あ、あの…\n私の制服姿はどうでしょうか？\nえ…か、かわいいって…\nあわ、あわわわわ…！');
INSERT INTO unit_comments VALUES(1107006,110731,1,1,1,0.0,1,'とにかく目立たない！\nそして、常に隅っこに！\nこうすれば、ぼっちの私でも\n平和な学院生活が…ひぃ！！');
INSERT INTO unit_comments VALUES(1107007,110731,1,2,1,0.0,1,'あ、あの人と一緒の学院…\nなんだかドキドキして、\n授業に集中できな…はうぅ！？\nちゃんと授業も頑張ります～！');
INSERT INTO unit_comments VALUES(1107008,110731,1,3,1,0.0,1,'つ、次の授業、団体行動がある…\n保健室に逃げ込むしか…ひぃぃ！\nう、嘘です嘘です！\nそんなこと考えてませんから～！');
INSERT INTO unit_comments VALUES(1107009,110731,1,4,1,0.0,1,'きょ、今日は友達運が最高！？\n誰かに話しかけられるかもって…\n早速話しかけられる練習を\n始めないと…！');
INSERT INTO unit_comments VALUES(1107010,110731,1,5,1,0.0,1,'きょ、今日こそ、\nあの人と下校を…はわぁ！\nご、ごめんなさい！\n私には大それた願いでした！');
INSERT INTO unit_comments VALUES(1107011,110701,2,6,5,6.8,2,'お、おお、お誕生日\nおめでとうございます！\nああ…他の誰かの誕生日を\n祝えるだけでも幸せです…');
INSERT INTO unit_comments VALUES(1107012,110731,2,6,1,0.0,1,'お誕生日おめでとうございます！\nこんな日に\nあなたと一緒に居られるなんて…\n友達ができるよりうれしいです♪');
INSERT INTO unit_comments VALUES(1108001,110801,1,1,1,6.1,2,'いいけど、\nなんかガッコーとかだと\nうち謎に恐がられてンだよね\nあんたは違うみたいだけどさ');
INSERT INTO unit_comments VALUES(1108002,110801,1,2,6,5.1,1,'ちょっと小耳に挟んだんだけど、\nあんたお金食べようとしたこと\nあんだって？　ウケる\nアレあんま美味しくないよね');
INSERT INTO unit_comments VALUES(1108003,110801,1,3,2,3.0,4,'いらっしゃいませぇ～！\n…や、なんかバイト先で愛想よく\nしろとか言われて。はーマジだる');
INSERT INTO unit_comments VALUES(1108004,110801,1,4,6,3.4,2,'なんてーか、あんたのバイブス\nわりと悪くないんじゃない\n知んないけど');
INSERT INTO unit_comments VALUES(1108005,110801,1,5,6,3.1,5,'うわ、きも…\n何カッコつけてんの\n不覚にも一瞬キュンって\n来ちゃったじゃん…うち、きもっ');
INSERT INTO unit_comments VALUES(1108006,110831,1,1,1,0.0,1,'夜っていいよね。なんてゆか…\n色々見えすぎないカンジとか\n言えないこと言えちゃうノリとか\nあと…なんかカッコいいじゃん');
INSERT INTO unit_comments VALUES(1108007,110831,1,2,1,0.0,1,'何。あんたまた道に迷ってんの\nしゃーないなー、送ってくよ\nいや、礼とかいいって、べつに\nなんかそんな気分なんだよね');
INSERT INTO unit_comments VALUES(1108008,110831,1,3,1,0.0,1,'夜に佇むミステリアスな少女…\nどーよコレ。カンジ出てない？\nただの夜勤休憩中なんだけどさ\nいいの、こういうのは気分だから');
INSERT INTO unit_comments VALUES(1108009,110831,1,4,1,0.0,1,'あのさ。あんたのことだから\nべつに意味とか無いンだろうけど\n無言で人のカオ見んのやめて\nなんか謎にモジついて来るから');
INSERT INTO unit_comments VALUES(1108010,110831,1,5,1,0.0,1,'あんたが？　送ってくれんの？\n何ソレ、誰が誰に言ってんのよ\n夜道は危ないって？　ウケる\nちょっとカッコいいじゃん');
INSERT INTO unit_comments VALUES(1108011,110801,2,6,1,9.3,5,'あのさ。弟たちに作ったケーキ、\n余ってるから食べてくんない\nべつに無理にとは言わないけど\nあー…あと、おたおめ');
INSERT INTO unit_comments VALUES(1108012,110831,2,6,1,0.0,1,'いいけど、あんた、\n今日が誕生日なんだって？\nおめでと。よかったね\nキラーペンシルあげるよ');
INSERT INTO unit_comments VALUES(1111001,111101,1,1,2,4.9,6,'ハロウィンですね…\nみんな仮装して、楽しそうです\n…怖いわけじゃありませんよ？');
INSERT INTO unit_comments VALUES(1111002,111101,1,2,4,6.0,6,'カボチャがいっぱいです…\nみんな顔があって、うぅ…\nな、なんで被ってるんですか！\nビックリさせないでください！');
INSERT INTO unit_comments VALUES(1111003,111101,1,3,1,8.7,5,'ど、どうでしょうか、この衣装…\n私は子供っぽいと思うんですけど\n…似合ってる？　そ、そうですか\nだったら…よかったです');
INSERT INTO unit_comments VALUES(1111004,111101,1,4,1,11.5,6,'あなたは仮装しないんですか？\nどんな衣装が似合うか、ですか？\nえっと…う～ん…吸血鬼さん？\nあ、血は吸わないでくださいね！');
INSERT INTO unit_comments VALUES(1111005,111101,1,5,6,6.0,5,'さっきからどうして\n頭を撫でてるんですか！\nかわいいから、って…\nもう！　もう～～～っ！');
INSERT INTO unit_comments VALUES(1111006,111131,1,1,1,0.0,1,'お菓子、ありますか？\nない？　だったらイタズラですね\nか、噛みついちゃいますから！\nえ…えーいっ！　かぷっ！');
INSERT INTO unit_comments VALUES(1111007,111131,1,2,1,0.0,1,'私は子供じゃないので言いません\n絶対に言いませんから！　え？\nお菓子、おいしいんですか？\n…トリック・オア・トリート…');
INSERT INTO unit_comments VALUES(1111008,111131,1,3,1,0.0,1,'私、カップケーキ作ったんです\n食べますか？　え？\nイタズラの方がいい？\nへんた～い！　もうあげません！');
INSERT INTO unit_comments VALUES(1111009,111131,1,4,1,0.0,1,'ハロウィン、\n一緒に…回りませんか？\n怖いんじゃありません！　もう、\nあなたはしょうがない人ですね！');
INSERT INTO unit_comments VALUES(1111010,111131,1,5,1,0.0,1,'え？　お菓子、くれるんですか？\n……甘くて、おいしいです\nあ、手についちゃった…ぺろっ\nなっ、なに見てるんですかぁっ！');
INSERT INTO unit_comments VALUES(1111011,111101,2,6,2,7.1,6,'あなたのお誕生日…これ、どうぞ\n私が作ったカップケーキです\nあっ、そんなに急いで食べたら\nポロポロこぼしちゃいますよ！');
INSERT INTO unit_comments VALUES(1111012,111131,2,6,1,0.0,1,'お誕生日おめでとうございます\nこれ、パンプキンケーキです\nカボチャは少し苦手ですけど、\n頑張って味見もしたので…！');
INSERT INTO unit_comments VALUES(1112001,111201,1,1,2,5.5,6,'ハロウィンって\nいくらいたずらしても\n怒られないから、最高だよね！\nえ？　違うの？');
INSERT INTO unit_comments VALUES(1112002,111201,1,2,2,1.6,1,'ハッピーハロウィン！\nにいちゃんにいたずらできて\nお菓子ももらえるなんて、ミソギ\n本当にハッピーだよ！');
INSERT INTO unit_comments VALUES(1112003,111201,1,3,1,4.8,2,'トリックオアトリート！\nお菓子をくれなきゃいたずら\nするぞ！　ミソギはお菓子\nもらってもするけどね！');
INSERT INTO unit_comments VALUES(1112004,111201,1,4,6,3.1,1,'にいちゃんはハロウィンに\nいたずらしないの？　しないなら\nミソギがにいちゃんのかわりに\nいたずらしておくね！');
INSERT INTO unit_comments VALUES(1112005,111201,1,5,2,5.1,1,'にいちゃん見て見て！\nミソギ、こんなにたくさん\nお菓子もらっちゃった！\n一緒に食べよ！');
INSERT INTO unit_comments VALUES(1112006,111231,1,1,1,0.0,1,'ハッピーハロウィン！\nお菓子のお礼にとびっきりの\nいたずらをプレゼントだよ！\nちゃんと受け取ってね！');
INSERT INTO unit_comments VALUES(1112007,111231,1,2,1,0.0,1,'あはははは！\nみんな驚いてる驚いてる！\nいたずらって本当に楽しいね！\nえ？　少し手加減しなきゃだめ？');
INSERT INTO unit_comments VALUES(1112008,111231,1,3,1,0.0,1,'走り回ってたら\nお腹空いてきちゃった…\nもらったお菓子、少しだけ\nつまみ食いしちゃおうかな！');
INSERT INTO unit_comments VALUES(1112009,111231,1,4,1,0.0,1,'にいちゃんはいっつも\nミソギと一緒にいてくれるよね！\nにいちゃんがいるからミソギは\n安心していたずらできるんだ！');
INSERT INTO unit_comments VALUES(1112010,111231,1,5,1,0.0,1,'いろんな人にいたずらするのも\n楽しいけど\nにいちゃんにいたずらするのが\n一番楽しいなー！');
INSERT INTO unit_comments VALUES(1112011,111201,2,6,2,2.7,1,'誕生日おめでとう、にいちゃん！\nミソギからのお祝いはー、んー\nやっぱりいたずらかな！');
INSERT INTO unit_comments VALUES(1112012,111231,2,6,1,0.0,1,'ハッピーバースデーにいちゃん！\nいっつもミソギと遊んでくれて\nありがとう！\nこれからも一緒に遊ぼうね！');
INSERT INTO unit_comments VALUES(1113001,111301,1,1,3,4.2,1,'お菓子をくれなきゃ\nいたずらしちゃうよ～！\nえっと…どんないたずらをすれば\nいいのかな～？');
INSERT INTO unit_comments VALUES(1113002,111301,1,2,2,1.4,1,'おにいちゃん♪\nハロウィンのパレードで\nぴょんぴょんってしよ～！');
INSERT INTO unit_comments VALUES(1113003,111301,1,3,1,5.2,2,'ハロウィンって楽しいね～\nミミも、みんなが楽しそうだと\nもっとも～っと\n楽しくなってきちゃうんだ～！');
INSERT INTO unit_comments VALUES(1113004,111301,1,4,2,3.1,5,'トリックオアトリート～！\nえへへ、おにいちゃん\nお菓子ちょうだい♪');
INSERT INTO unit_comments VALUES(1113005,111301,1,5,1,6.0,2,'こちょこちょこちょ…えへへ～\nハロウィンのいたずらだよ～♪\nトリックオアトリート！');
INSERT INTO unit_comments VALUES(1113006,111331,1,1,1,0.0,1,'にこにこ笑顔でおさんぽすれば\nハロウィンの魔法がうまれるの～\nえへへ、ハッピーハロウィン♪');
INSERT INTO unit_comments VALUES(1113007,111331,1,2,1,0.0,1,'ふぁぁ…ハロウィンって\nとっても楽しいね～！\nねえねえ、おにいちゃんも\nいっしょにパレードしよ～♪');
INSERT INTO unit_comments VALUES(1113008,111331,1,3,1,0.0,1,'ハッピーハロウィン♪\nおにいちゃん\nミミのハロウィンのおようふく\nにあってるかな～？');
INSERT INTO unit_comments VALUES(1113009,111331,1,4,1,0.0,1,'おにいちゃんと\nいっしょのハロウィン\nミミず～っとおぼえてるからね♪');
INSERT INTO unit_comments VALUES(1113010,111331,1,5,1,0.0,1,'毎日がハロウィンならいいのに…\nだってだって\nそしたらミミ、おにいちゃんと\nず～っといっしょに遊べるもん！');
INSERT INTO unit_comments VALUES(1113011,111301,2,6,6,4.1,2,'ハッピーハロ…じゃなくて\nハッピーバースデイ！\nおにいちゃん！');
INSERT INTO unit_comments VALUES(1113012,111331,2,6,1,0.0,1,'おにいちゃん\nお誕生日おめでとう～！\nミミね、おにいちゃんのこと\nだーいすきだよ～♪');
INSERT INTO unit_comments VALUES(1114001,111401,1,1,1,2.7,4,'ルナ、おともだちが欲しいんだ\nさみしいのは嫌いだもん');
INSERT INTO unit_comments VALUES(1114002,111401,1,2,1,2.9,2,'ルナ、優しい人好きだなー\nおにいさんは優しいね');
INSERT INTO unit_comments VALUES(1114003,111401,1,3,1,2.9,4,'パパとママはここにいるよ\nでも、喋ってくれないんだ\nさみしいなぁ…');
INSERT INTO unit_comments VALUES(1114004,111401,1,4,2,3.0,6,'おにいさんの手ってあったかい\nルナの手、冷たい？\nもっとぎゅってしたら、\nあったかくなるかな？');
INSERT INTO unit_comments VALUES(1114005,111401,1,5,2,6.7,5,'ルナ、もっとおっきくなるよ\nお料理もできるようになるよ\nえへへ…ルナ、いいこ？');
INSERT INTO unit_comments VALUES(1114006,111431,1,1,1,0.0,1,'ルナね、サンドイッチ好きなんだ\nおにいさんは好き？');
INSERT INTO unit_comments VALUES(1114007,111431,1,2,1,0.0,1,'ルナ、お外は好きじゃないんだ\nみんな、一人じゃなくて…\nルナだけ一人だから');
INSERT INTO unit_comments VALUES(1114008,111431,1,3,1,0.0,1,'おにいさんは何が好き？\nルナはお人形さんと、\nサンドイッチと…\nあと、おにいさん！');
INSERT INTO unit_comments VALUES(1114009,111431,1,4,1,0.0,1,'パパとママみたいになれたら、\nきっと楽しいと思うんだ\nだから待っててね、おにいさん');
INSERT INTO unit_comments VALUES(1114010,111431,1,5,1,0.0,1,'おにいさんはルナのこと好き？\nルナはおにいさんのこと好き！\nだから嫌いにならないでね');
INSERT INTO unit_comments VALUES(1114011,111401,2,6,6,1.7,2,'お誕生日だね！\nおにいさんが一番！\nふふっ、うれしいね！');
INSERT INTO unit_comments VALUES(1114012,111431,2,6,1,0.0,1,'お誕生日なの？　すごいすごい！\nルナ、お誕生日大好き！\nだからいっぱいお祝いしようね！');
INSERT INTO unit_comments VALUES(1115001,111501,1,1,2,6.1,1,'メリークリスマス☆\nクリスティーナさんと一緒に\n愉しい聖夜を過ごそうじゃないか\n問答無用だ、こっちへ来い♪');
INSERT INTO unit_comments VALUES(1115002,111501,1,2,6,6.4,4,'この飲み物が気になるのか？\n飲ませてやってもいいが、\n後悔はするなよ？\nなんだ、やめるのか…残念だ☆');
INSERT INTO unit_comments VALUES(1115003,111501,1,3,3,8.4,1,'さぁ坊や、ワタシを愉しませろ！\n特別な日だ、くだらんことを\nしたら命はないと思え！\nまぁ、優しめに採点してやるさ♪');
INSERT INTO unit_comments VALUES(1115004,111501,1,4,6,6.1,2,'おや、震えているじゃないか\n寒いだと？　この軟弱者め\nだったらこのコートに入るか？\nふっ、遠慮しなくていいんだぞ♪');
INSERT INTO unit_comments VALUES(1115005,111501,1,5,1,3.1,2,'クリスマスをオマエと一緒に\n過ごすとはな…\nまぁ悪い気分じゃないさ\n坊やは見ていて愉しいからな');
INSERT INTO unit_comments VALUES(1115006,111531,1,1,1,0.0,1,'どうだい、坊や\n聖夜に相応しい絶景だろう？\n感謝するがいいよ。ここに連れて\n来たのはオマエが初めてだ♪');
INSERT INTO unit_comments VALUES(1115007,111531,1,2,1,0.0,1,'こちらを見つめてどうした？\nいいや言わんでも分かるさ\n景色よりワタシの方が綺麗だと\n言いたいんだろう？　あっはは☆');
INSERT INTO unit_comments VALUES(1115008,111531,1,3,1,0.0,1,'体が冷えてきたな…\nよし坊や、剣を抜け！\nこういう時は、剣と剣をぶつけて\n温まろうじゃないか！');
INSERT INTO unit_comments VALUES(1115009,111531,1,4,1,0.0,1,'クリスマスを誰かと過ごすのも\n悪くはないな…\nそれがワタシを愉しませてくれる\n男なら、なおさらだ♪');
INSERT INTO unit_comments VALUES(1115010,111531,1,5,1,0.0,1,'愉快なクリスマスだったな\nまた次も、よろしく頼むぞ？\nふふっ、忘れるなよ？\nワタシと交わす誓いは絶対だ♪');
INSERT INTO unit_comments VALUES(1115011,111501,2,6,6,6.5,1,'ふぅん。今日が誕生日なのか\nなんだそのもの欲しそうな顔は？\nあはは、プレゼントが欲しいなら\n贈るに相応しいか示して見せろ！');
INSERT INTO unit_comments VALUES(1115012,111531,2,6,1,0.0,1,'オマエが生まれた日を祝し\nクリスティーナさんがプレゼント\nを用意してあげたよ♪\nい～い反応を期待しているぞ☆');
INSERT INTO unit_comments VALUES(1116001,111601,1,1,2,1.7,1,'メリークリスマス！\n私のライブがキミにとって\n最高のプレゼントに\nなったらうれしいな');
INSERT INTO unit_comments VALUES(1116002,111601,1,2,2,4.6,6,'チキンにケーキ…クリスマスの\nごちそうって大好き！\n今日だけは\n食べ過ぎちゃってもいいよね？');
INSERT INTO unit_comments VALUES(1116003,111601,1,3,1,3.0,2,'いつものクリスマスより\n楽しく感じるのは\nキミと一緒だからかな？');
INSERT INTO unit_comments VALUES(1116004,111601,1,4,6,4.1,5,'はぁ～\n今日はすごく寒いね\n…ねえ、もう少しだけ\nそっちに行ってもいいかな？');
INSERT INTO unit_comments VALUES(1116005,111601,1,5,1,3.7,2,'どうかな？\nこの服いい感じでしょ♪\nキミには一番かわいい私を\n見てほしいんだ！');
INSERT INTO unit_comments VALUES(1116006,111631,1,1,1,0.0,1,'みんなが笑顔になるクリスマスが\n大好きだよ！\nこの思いは最高の\nパフォーマンスでお返しするね！');
INSERT INTO unit_comments VALUES(1116007,111631,1,2,1,0.0,1,'私のライブでクリスマスを\n盛り上げちゃうよー♪\nサンタもアイドルも夢を届ける\nお仕事だもんね！');
INSERT INTO unit_comments VALUES(1116008,111631,1,3,1,0.0,1,'メリークリスマス！\nイルミネーションよりも\n輝いてみせるよ！');
INSERT INTO unit_comments VALUES(1116009,111631,1,4,1,0.0,1,'クリスマスソングの別バージョン\nキミをイメージして作ったんだ\nちょっと恥ずかしいけど\n聴いてほしいな！');
INSERT INTO unit_comments VALUES(1116010,111631,1,5,1,0.0,1,'これまでの人生で一番の\nクリスマスだよ！\nキミと過ごした最高の一日を\n絶対に忘れないからね！');
INSERT INTO unit_comments VALUES(1116011,111601,2,6,2,1.6,1,'ハッピーバースデー！\nはい、プレゼント\n私のサイン入りだよ\n大事にしてね！');
INSERT INTO unit_comments VALUES(1116012,111631,2,6,1,0.0,1,'誕生日おめでとう！\nバースデーソングでお祝いするよ！\nキミだけのためのライブ、\n楽しんでってね！');
INSERT INTO unit_comments VALUES(1117001,111701,1,1,4,3.1,1,'『聖なる夜』というのは\n気に入らんが、\nせっかくの夜の宴じゃ\n楽しませてもらおうかのう♪');
INSERT INTO unit_comments VALUES(1117002,111701,1,2,2,4.1,5,'わらわの美貌を\n引き立てる良い衣装じゃな\n…これで、あやつを\n誘惑するのもよいかもしれぬ♪');
INSERT INTO unit_comments VALUES(1117003,111701,1,3,1,3.7,2,'今のわらわは\nサンタクロースじゃからな\n聖夜を盛り上げるために、\n協力してやろうではないか');
INSERT INTO unit_comments VALUES(1117004,111701,1,4,2,5.2,4,'聖夜の宴ならば、\nチキンとケーキを食べねばな♪\n…な、なんじゃその目は？\nべ、別に浮かれてなどおらぬわ！');
INSERT INTO unit_comments VALUES(1117005,111701,1,5,1,5.0,5,'この格好でプレゼントを\n用意せぬのは無粋というもの…\nし、仕方ないから、あやつにも\nプレゼントを用意してやるか！');
INSERT INTO unit_comments VALUES(1117006,111731,1,1,1,0.0,1,'ク、クリスマスはサンタが\nプレゼントを贈る日で、\n今のわらわはサンタだから…\nと、とにかく受け取れ馬鹿者～！');
INSERT INTO unit_comments VALUES(1117007,111731,1,2,1,0.0,1,'アカリに借りた本によると…\nイリヤサンタが、キミに\nプレゼントをあげちゃうぞ～☆\n…できるかこんなの～～！');
INSERT INTO unit_comments VALUES(1117008,111731,1,3,1,0.0,1,'夜を統べる者たる、このわらわが\nプレゼントをくれてやろう！\nありがたく受け取るがよい！\nうむ、これなら完璧じゃ！');
INSERT INTO unit_comments VALUES(1117009,111731,1,4,1,0.0,1,'このプレゼント、あやつは\n喜んでくれるじゃろうか…\nうう…なんだか\nドキドキしてきたのじゃ…');
INSERT INTO unit_comments VALUES(1117010,111731,1,5,1,0.0,1,'…じ、時間じゃな！\nプレゼントの準備…よし！\nこ、ここ、心の準備…よ、よし！\nあとはあやつに渡すだけじゃ！');
INSERT INTO unit_comments VALUES(1117011,111701,2,6,6,4.8,2,'たしか、今日はお主の\n誕生日であったな\nならばこのわらわが、\n祝ってやろうではないか');
INSERT INTO unit_comments VALUES(1117012,111731,2,6,1,0.0,1,'今日はあやつの誕生日…！\nプレゼントを渡しつつお礼を…\nえ…お主、いつからそこに…\nい、今すぐ忘れろ馬鹿者ぉ～！！');
INSERT INTO unit_comments VALUES(1119001,111901,1,1,1,5.2,2,'主さま、新年あけまして\nおめでとうございます\n今年も主さまと一緒に\n歩めることを幸せに思います');
INSERT INTO unit_comments VALUES(1119002,111901,1,2,4,3.9,1,'まだまだ寒い日が続きますね\n主さま、お手手を繋ぎ\n暖めあって歩くといたしましょう\nぎゅっと握ってくださいまし');
INSERT INTO unit_comments VALUES(1119003,111901,1,3,4,8.0,5,'たまの里帰りもいいものですが\n主さまと離れるのは心細いです\n次の里帰りは一緒に、というのは\n欲張りでしょうか…？');
INSERT INTO unit_comments VALUES(1119004,111901,1,4,6,4.9,1,'甘酒には疲労回復の効果が\nあるとのことです。お疲れの際は\nわたくしがいつでもフーフーして\n飲ませて差し上げます');
INSERT INTO unit_comments VALUES(1119005,111901,1,5,1,6.7,5,'主さまにお仕えするという至高の\n初夢を見ることができました\n主さまの初夢にも、わたくしが\n出ていたならいいのですが…');
INSERT INTO unit_comments VALUES(1119006,111931,1,1,1,0.0,1,'一つ一つの動きに、主さまへの\n想いを込めております\n拙い舞ではございますが\n見守っていてくださいまし');
INSERT INTO unit_comments VALUES(1119007,111931,1,2,1,0.0,1,'わたくしの想いに反応して\n精霊たちも楽しそうに\n舞っておりますね。主さまも\nお楽しみいただけておりますか？');
INSERT INTO unit_comments VALUES(1119008,111931,1,3,1,0.0,1,'風情のある舞台で主さまと\n二人きりで過ごせる…\nわたくしにとって最高の\nお年玉でございます');
INSERT INTO unit_comments VALUES(1119009,111931,1,4,1,0.0,1,'今年の抱負でございますか？\nそれはもちろんこれまで通り…\nいえ、これまで以上に主さまを\nお導きすることでございます');
INSERT INTO unit_comments VALUES(1119010,111931,1,5,1,0.0,1,'主さまを想って舞っていると\n心も体も暖かくなります\nこの熱を主さまと分け合うことが\nできればいいのに…');
INSERT INTO unit_comments VALUES(1119011,111901,2,6,2,5.9,5,'お誕生日おめでとうございます\nもし…もし主さまがよろしければ\n二人きりで過ごすことを\nお許しいただきたく…');
INSERT INTO unit_comments VALUES(1119012,111931,2,6,1,0.0,1,'今日は主さまにとって\nおめでたい一日。祝いの舞を\n奉納させてくださいまし\n心からの祝福をお贈りいたします');
INSERT INTO unit_comments VALUES(1120001,112001,1,1,1,4.0,2,'なに？　あたしと羽根つきで\n勝負したいの？\n手加減しないからね！\n墨まみれにしてあげるわ！');
INSERT INTO unit_comments VALUES(1120002,112001,1,2,5,4.6,1,'あけましておめでとう\nまぁ今年もよろしくね\nまた騒がしい一年に\nなりそうだけど…');
INSERT INTO unit_comments VALUES(1120003,112001,1,3,4,7.8,6,'みんな年の始めから元気ね\nあたしは家で静かに過ごしたい…\nい、いや、別にあんたと居るのが\n嫌ってワケじゃないから！');
INSERT INTO unit_comments VALUES(1120004,112001,1,4,3,5.5,2,'着物が似合ってる？\nいちいち言わなくていいわよ！\nけど、見る目あるじゃない\nふふん。いい気分だわ♪');
INSERT INTO unit_comments VALUES(1120005,112001,1,5,1,5.9,5,'おみくじ引いたんだけど、\n待ち人はすぐそばに、だって\nまさかあんたのこと…？\nあ、あっちいきなさい！');
INSERT INTO unit_comments VALUES(1120006,112031,1,1,1,0.0,1,'せっかくだからいろいろ願い事\nしたいわよね。あんたは何を\n願うの？　あたしのこと？\nふ、ふーん。そ、そう…');
INSERT INTO unit_comments VALUES(1120007,112031,1,2,1,0.0,1,'夜の参道ってのも悪くないわね\n一緒なら怖くないし…って！\n暗いの平気だから！\n勘違いしないでよね！');
INSERT INTO unit_comments VALUES(1120008,112031,1,3,1,0.0,1,'ほら、行くわよ\n疲れたっていうなら\n手繋いでやっても…て、本当に\n繋ぐ気？　べ、別にいいけど…');
INSERT INTO unit_comments VALUES(1120009,112031,1,4,1,0.0,1,'神殿にお参りするだけなのに\n特別な気がするのは\n着物のおかげ？　それとも…\nあんたと一緒だから？');
INSERT INTO unit_comments VALUES(1120010,112031,1,5,1,0.0,1,'すごく静かね…まさか道間違えて\nないわよね？　まぁ、のんびり\n行くのも悪くないかもね。二人で\nゆっくり歩きましょ');
INSERT INTO unit_comments VALUES(1120011,112001,2,6,1,5.7,4,'ハッピーバースデー\nケーキ買ってきたから\n一緒に食べましょう\n変なモノは入ってないわよ？');
INSERT INTO unit_comments VALUES(1120012,112031,2,6,1,0.0,1,'お誕生日おめでとう\nい、一応プレゼントも用意して\nあるのよ？　\nありがたく受け取りなさい！');
INSERT INTO unit_comments VALUES(1121001,112101,1,1,2,5.9,1,'あなたもお参りに来たんですね\n私は見ての通り、巫女さんです！\n年末から新年にかけて、神殿の\nお仕事をお手伝いしてるんです');
INSERT INTO unit_comments VALUES(1121002,112101,1,2,2,2.7,1,'この服、かわいいですよね♪\n巫女さんの服を着る機会なんて\n普段はありませんから\n新鮮で楽しいんです');
INSERT INTO unit_comments VALUES(1121003,112101,1,3,1,5.2,2,'神殿には色んなお守りがあるので\nぜひ見ていってくださいね\n私は家内安全のお守りを\n買っておきました～');
INSERT INTO unit_comments VALUES(1121004,112101,1,4,4,4.5,5,'はぁ～…寒くて手が冷えますね\n…はわっ！？　あ、あのぉ…\nい、いきなり手をギュって\n握られると、恥ずかしいです…');
INSERT INTO unit_comments VALUES(1121005,112101,1,5,1,3.4,6,'もしよければ、甘酒を飲みます？\n…え、私と分け合いっこ！？\nあ、ああ、あの、仕事中なので！\nわわ、私は、だ、大丈夫れしゅ！！');
INSERT INTO unit_comments VALUES(1121006,112131,1,1,1,0.0,1,'おはようございますっ！\n今日は参拝に来たんですか？\n私は神殿のお掃除中です\nほら、きれいになりましたよ～♪');
INSERT INTO unit_comments VALUES(1121007,112131,1,2,1,0.0,1,'もうすぐ休憩時間なので\n私もご一緒していいですか？\nこの神殿の見どころを\n色々教えて頂いたんです！');
INSERT INTO unit_comments VALUES(1121008,112131,1,3,1,0.0,1,'それじゃあ、張り切って続きを…\nえ、危ない？　何がですか？\n…はわっ！　崩れちゃう！？\nわ、ちょ、まっ、きゃー！！');
INSERT INTO unit_comments VALUES(1121009,112131,1,4,1,0.0,1,'恋愛運に書かれていたんですけど\n今年は「身近なところに縁あり」\nらしくて…あっ！？　い、いえ！\nべ、別に深い意味はないですよ！');
INSERT INTO unit_comments VALUES(1121010,112131,1,5,1,0.0,1,'あなたの恋愛運はどうでした？\n…え？　失敗から始まる縁？\nそれってもしかして、私と…\nはっ！　な、なんでもないです！');
INSERT INTO unit_comments VALUES(1121011,112101,2,6,2,5.1,6,'お誕生日おめでとうございます！\n私もあなたにプレゼントを…\nって、あれ…？　ない！？\nわ、忘れちゃいましたぁ…！');
INSERT INTO unit_comments VALUES(1121012,112131,2,6,1,0.0,1,'今日はあなたのお誕生日ですよね\nおめでとうございます！\nこれから良い一年を過ごせる\nおまじないをかけちゃいますね♪');
INSERT INTO unit_comments VALUES(1122001,112201,1,1,6,6.4,4,'へ、変身したら、\n髪型まで変わってる！？\n一体どういう理論で…はあ\nもう、考えないようにしよう…');
INSERT INTO unit_comments VALUES(1122002,112201,1,2,1,5.9,5,'魔法探偵ミスティ★カスミか…\nちょっとだけ楽しいかも♪\n…って、助手くん！？　\nい、今のは忘れてくれたまえ！');
INSERT INTO unit_comments VALUES(1122003,112201,1,3,4,5.1,1,'うぅ、周りの視線が気になる…\nん…助手くん、前に立ってくれる\nのかい？　ありがたいよ\nふふ。お姫様気分だね');
INSERT INTO unit_comments VALUES(1122004,112201,1,4,1,3.0,4,'ミスティ、セオリー、\nトゥルーリィ！\n…はぁ。だんだんと\n慣れてきているのが複雑だね…');
INSERT INTO unit_comments VALUES(1122005,112201,1,5,5,6.3,1,'歩いているだけで子供に囲まれる\nなんて…魔法少女って人気なんだ\n助手くん、君も魔法少女のこと\n好きだったりするのかな？');
INSERT INTO unit_comments VALUES(1122006,112231,1,1,1,0.0,1,'ミスティセオリートゥルーリィ！\nマジカルリボン、スパイラル！\n魔法探偵ミスティ★カスミ\n魔法の力で捜査開始！');
INSERT INTO unit_comments VALUES(1122007,112231,1,2,1,0.0,1,'…や、やっぱり恥ずかしいね\nあまりジロジロ見てはダメだよ？\nこ、こら！　近い、近いったら！\nもう、これは現行犯で確保だね！');
INSERT INTO unit_comments VALUES(1122008,112231,1,3,1,0.0,1,'体の奥からすごい力を感じるよ\nけれど、この格好で動き回ると\nその、スカートとかヒラヒラして\n落ち着かないんだよね…');
INSERT INTO unit_comments VALUES(1122009,112231,1,4,1,0.0,1,'助手くん、どうかな？\n魔法少女らしく見えるかね？\nこんなはじけた姿、君の前でしか\n見せられないよ。まったく…');
INSERT INTO unit_comments VALUES(1122010,112231,1,5,1,0.0,1,'魔法少女と、パートナー\n探偵と、助手\n私たちはどうやら、離れられない\n運命のようだね。…ふふっ♪');
INSERT INTO unit_comments VALUES(1122011,112201,2,6,1,5.9,2,'今日は助手くんの誕生日だよね\n魔法少女の役目を\n早く終わらせてくるから、二人で\n一緒にお祝いしようじゃないか♪');
INSERT INTO unit_comments VALUES(1122012,112231,2,6,1,0.0,1,'…魔法の力で捜査開始！\nた、誕生日のお祝いとして、\n君のためだけに変身して\nみたんだけど…ど、どうかな？');
INSERT INTO unit_comments VALUES(1123001,112301,1,1,2,0.0,2,'私、魔法少女になったんです\n私もお姉ちゃんみたいに\nたくさん人助けできたらいいな\nなんて…えへへ');
INSERT INTO unit_comments VALUES(1123002,112301,1,2,1,5.4,2,'魔法少女になってから\nあまり体力のことで\n悩まなくなったんです\nなんだか出来すぎた物語みたい…');
INSERT INTO unit_comments VALUES(1123003,112301,1,3,2,2.8,5,'魔法少女って憧れだったんです\n見た目は、その…\nちょっと恥ずかしいですけど');
INSERT INTO unit_comments VALUES(1123004,112301,1,4,1,5.0,4,'早く一人前の魔法少女に\nならなきゃって思うんですけど…\n読書の時間がなくなっちゃう…！');
INSERT INTO unit_comments VALUES(1123005,112301,1,5,6,5.0,2,'ランドソルの街を歩いてたら\n子供たちに囲まれちゃいました\nあの子たちを守るためにも\nもっともっと頑張らなくちゃ…');
INSERT INTO unit_comments VALUES(1123006,112331,1,1,1,0.0,1,'ピュアなハートにロックオン\nマジカルアロー、クリティカル！\n魔法狩人ピュアリー★シオリ\nあなたの心に愛の矢を！');
INSERT INTO unit_comments VALUES(1123007,112331,1,2,1,0.0,1,'ランドソルの平和は私が守ります\n体からあふれる愛と希望の力…\nこの一射に愛をこめて！');
INSERT INTO unit_comments VALUES(1123008,112331,1,3,1,0.0,1,'ここは私に任せてください！\nアンチビーストを倒して\nみなさんに笑顔を取り戻します！');
INSERT INTO unit_comments VALUES(1123009,112331,1,4,1,0.0,1,'魔法少女になったおかげで、少し\n自信がついたかもしれません\nこの街を守る覚悟ができました\nあなたのことも…');
INSERT INTO unit_comments VALUES(1123010,112331,1,5,1,0.0,1,'アンチビーストが街を襲うかぎり\n魔法少女は戦い続けます\nそばにいてくださいね…あなたの\n存在が、私の力になりますから！');
INSERT INTO unit_comments VALUES(1123011,112301,2,6,2,0.0,2,'今日、お誕生日ですよね\nふふ、ちゃんと覚えてますよ\n魔法で盛大にお祝いしなきゃ');
INSERT INTO unit_comments VALUES(1123012,112331,2,6,1,0.0,1,'お誕生日おめでとうございます\n今後もお祝いさせてくださいね\nこれからも、ずっと…');
INSERT INTO unit_comments VALUES(1701001,170101,1,1,1,1.1,2,'和别人比较\n也没有意义嘛！\n我得打磨自己的武器！');
INSERT INTO unit_comments VALUES(1701002,170101,1,2,2,0.0,2,'这个肉味道真是超级美味！');
INSERT INTO unit_comments VALUES(1701003,170101,1,3,2,1.1,1,'耶！你看你看，\n我一个人就把魔物打倒了！\n谢谢你陪我特训呀！');
INSERT INTO unit_comments VALUES(1701004,170101,1,4,1,0.0,1,'我自己都觉得自己很要强。\n越是困难的挑战，\n就越让我觉得兴奋呢。');
INSERT INTO unit_comments VALUES(1701005,170101,1,5,1,0.0,1,'碰壁的时候，\n就要向前向上向后，\n总之四处找出路！');
INSERT INTO unit_comments VALUES(1701006,170131,1,1,10,1.3,1,'我也好想像小佩可莉姆一样\n帅气地战斗啊！\n又帅又可爱，岂不是无敌了？');
INSERT INTO unit_comments VALUES(1701007,170131,1,2,6,1.5,2,'魔物竟然这么好吃，吓我一跳！\n特别是眼珠！\n');
INSERT INTO unit_comments VALUES(1701008,170131,1,3,1,0.0,1,'我的爱好是说话！\n而且经常说话之前不过脑子！\n有时候自己也会吓一跳！');
INSERT INTO unit_comments VALUES(1701009,170131,1,4,1,2.0,2,'烤肉有烤肉的好，\n鲷鱼烧有鲷鱼烧的好，\n各有千秋！\n换句话说，我都想吃！');
INSERT INTO unit_comments VALUES(1701010,170131,1,5,6,2.1,1,'骑士不露饿相……\n这怎么可能啊！？\n人是铁饭是钢啊！？');
INSERT INTO unit_comments VALUES(1701011,170101,2,6,2,2.3,6,'生日一定要吃顿大餐！\n你想去哪儿吃？\n诶，想吃我做的菜？真的？');
INSERT INTO unit_comments VALUES(1701012,170131,2,6,1,2.2,2,'生日快乐！\n没想到吧？没想到吧？\n诶嘿嘿，我知道今天是你生日哦♪给，送你的礼物！');
INSERT INTO unit_comments VALUES(1702001,170201,1,1,3,2.0,1,'好冷！这种天气\n就让人想吃火锅呀！\n比如寿喜烧！');
INSERT INTO unit_comments VALUES(1702002,170201,1,2,1,7.0,2,'这件振袖和服不仅超可爱，\n防御力还很高，\n穿上之后怎么运动都不会松散！是不是很厉害！？');
INSERT INTO unit_comments VALUES(1702003,170201,1,3,2,2.5,1,'今年能演什么样的角色呢？\n要是能尝试\n一大堆新的挑战就好啦♪');
INSERT INTO unit_comments VALUES(1702004,170201,1,4,1,2.0,2,'我要参加羽毛毽比赛哦！\n好期待啊～');
INSERT INTO unit_comments VALUES(1702005,170201,1,5,1,0.0,1,'今年也请多多关照。\n好！写好新年贺卡了！\n今年写了一大堆♪');
INSERT INTO unit_comments VALUES(1702006,170231,1,1,1,0.0,1,'抽签或者占卜\n出了不好的结果反而让人斗志昂扬！\n我会想，\n就由我来改变这未来！');
INSERT INTO unit_comments VALUES(1702007,170231,1,2,2,4.0,1,'能和美食殿堂的成员们结交，\n我超级开心！\n希望今后我们的感情会更好♪');
INSERT INTO unit_comments VALUES(1702008,170231,1,3,2,3.5,1,'来这里能见到许多人，\n我好开心！美食殿堂\n今年也很受欢迎呢！');
INSERT INTO unit_comments VALUES(1702009,170231,1,4,1,0.0,1,'木屐真好啊♪\n走起路来咔啦咔啦的，\n声音好可爱！');
INSERT INTO unit_comments VALUES(1702010,170231,1,5,1,0.0,1,'穿上振袖和服的小环奈今年也要全力冲刺！');
INSERT INTO unit_comments VALUES(1702011,170231,2,6,2,1.5,1,'生日快乐！\n希望接下来的一年，\n会成为你最美好的一年！');
INSERT INTO unit_comments VALUES(1702012,170201,2,6,2,3.5,1,'今天是你的生日，\n所以我们开了烤肉派对！\n我会烤很多的，你也要吃很多哦！');
INSERT INTO unit_comments VALUES(1804001,180401,1,1,2,4.5,1,'オイッスー☆\n一緒にお散歩、行きませんか？\n王女様なんて呼んじゃだめですよ\n今のわたしはペコリーヌです☆');
INSERT INTO unit_comments VALUES(1804002,180401,1,2,6,10.9,2,'いつもと違うこの姿、\nあなたはどう思いますか？\n強そう？　う～ん、そっちですか\nかわいい？　えっへへ、それです');
INSERT INTO unit_comments VALUES(1804003,180401,1,3,1,7.1,2,'王宮から抜けだして会っていると\nなんだかドキドキしますね♪\nお付きの護衛もいませんから\nあなたが守ってくださいね☆');
INSERT INTO unit_comments VALUES(1804004,180401,1,4,4,4.2,1,'わたしがごはんのことばかり\n考えてると思っていませんか？\n半分は正解ですけど、もう半分は\n…あなたのことですよ☆');
INSERT INTO unit_comments VALUES(1804005,180401,1,5,2,7.7,6,'やっぱりあなたと囲む食卓が\n一番ですね☆　いっそのこと\n王宮で一緒に住みませんか？\nやっぱり、まずいですかね～？');
INSERT INTO unit_comments VALUES(1804006,180431,1,1,1,0.0,1,'超全力全開☆\n私は守られるだけの王女様じゃ\nありませんよ？　この国と…\nあなたのために、戦います！');
INSERT INTO unit_comments VALUES(1804007,180431,1,2,1,0.0,1,'この姿、とっても強いんですけど\nその分お腹が減るんですよ～…\nでも、それってつまり…\nもっとごはんが食べられますね☆');
INSERT INTO unit_comments VALUES(1804008,180431,1,3,1,0.0,1,'力が溢れ出て、止まりません！\nこの力で、わたしの大切なものを\nぜ～んぶ守ってみせますね\n今度は何もなくしたりしません！');
INSERT INTO unit_comments VALUES(1804009,180431,1,4,1,0.0,1,'あなたの力をこれまでより\n強く感じます…温かくて、\n胸がキュンキュンしちゃいます…\nえへへ、やばいですね☆');
INSERT INTO unit_comments VALUES(1804010,180431,1,5,1,0.0,1,'お腹が空きましたね！\nさぁ、わたしと一緒にごはんを\n食べに行きましょう♪\n今日も明日も仲良くごはんです☆');
INSERT INTO unit_comments VALUES(1804011,180401,2,6,1,8.1,5,'あなたの誕生日となれば、\n国を挙げてお祝いしなきゃですね\nそれとも二人きりがいいですか？\n…えへへ、照れちゃいますね☆');
INSERT INTO unit_comments VALUES(1804012,180431,2,6,1,0.0,1,'今日はあなたの誕生日ですね\nおめでとうございます！　\nそれと…ありがとうございます\nこうしてわたしと出会ってくれて');
