INSERT INTO unit_enemy_data VALUES(206900,'嗜睡猫头鹰',206900,0,0,270,645,0,3.5,0,0,'【魔法】中距离攻击\n因睡眠不足而有些倦怠的猫头鹰魔物。\n瞪着敌人，对其施加法术，令对方动弹不得。');
INSERT INTO unit_enemy_data VALUES(207000,'淘气豆狸',207000,0,0,100,120,2,0.5,0,0,'【魔法】一定时间内无敌·中距离攻击\n虽然年幼，但会利用灵巧的变身术\n拼命兼顾攻守的豆狸魔物。');
