INSERT INTO unit_enemy_data VALUES(300100,'弥诺陶洛斯',300100,0,3,270,1000,1,1.9,0,0,'【物理】盘踞在迷宫里的金牛座魔星兽。\n挥舞巨大的战斧，夺去冒险者的性命。\n\n·对敌方全体造成高等伤害，使其变为\n 灼伤状态。\n·给1名敌人施加高等伤害，在击退\n敌人的同时将其眩晕。\n·给前方范围内的敌人施加中等魔法伤害，\n使其麻痹。');
INSERT INTO unit_enemy_data VALUES(300200,'利蝎巴鲁托',300200,0,6,270,1000,1,1.5,0,0,'使用猛毒的尾棘散播灾厄的天蝎座魔星兽。\n守护沙漠的神虫失去信仰后，变成了邪神。\n\n·对敌方全体造成大量物理伤害。\n　精英怪物每次使用必杀技能，\n　都会提升对敌人造成的伤害。\n　这个效果最多可以累积三次。\n·对前方范围内的敌人造成中等物理伤害，\n　并赋予中毒状态。\n·解除自身的属性值降低效果，\n　并提升自己的物理防御力。');
INSERT INTO unit_enemy_data VALUES(300300,'梦魇牡羊',300300,0,7,270,1000,2,2.5,0,0,'【魔法】获得强大力量的白羊座魔星兽。\n在阴暗的沉睡监牢中将冒险者的灵魂吞噬殆尽。\n\n·对敌方全体造成魔法伤害（大），对束缚状态的\n　敌人额外造成魔法伤害（小）。\n·对前方范围内的敌人造成魔法伤害（中）。\n·对一名敌人施加束缚状态。');
INSERT INTO unit_enemy_data VALUES(300301,'梦魇牡羊',300301,0,7,270,1000,2,2.5,0,0,'【魔法】获得强大力量的白羊座魔星兽。\n在阴暗的沉睡监牢中将冒险者的灵魂吞噬殆尽。\n\n·对敌方全体造成魔法伤害（大），对束缚状态的\n　敌人额外造成魔法伤害（小）。\n·对前方范围内的敌人造成魔法伤害（中）。\n·对一名敌人施加束缚状态。\n·对剩余生命值最低的一名敌人赋予\n  嘲讽状态。\n  这个技能的异常状态在伤害免疫的情况下\n  也会成功赋予。');
INSERT INTO unit_enemy_data VALUES(300400,'魔羯教主',300400,0,7,270,1000,2,1.8,0,0,'企图利用可疑邪教支配世界的摩羯座魔星兽。\n会引诱沙漠里的迷途之人，用恶魔之仪式将其变为俘虏。\n\n·必杀技能对敌方全体给予高等魔法伤害\n　另外降低物理攻击角色的物理攻击力\n　降低魔法攻击角色的魔法防御力。\n·对物理攻击力最高的一名角色给予特高等魔法伤害，\n　并降低其技能值。\n·对前方两名敌人给予低等魔法伤害，并\n　赋予沉默状态。\n　并对敌方全体给予中等魔法伤害。');
INSERT INTO unit_enemy_data VALUES(300500,'蜥蜴怪人',300500,0,4,270,1000,1,1.8,0,0,'【物理】自由操纵双刀，\n用烟雾弹遮蔽\n对方双眼的\n巨蜥战士。\n\n·随机对一名角色给予\n　中等物理伤害。\n\n·以随机的敌人为中心，给予小范围\n　低等物理伤害，\n　并赋予黑暗状态。');
INSERT INTO unit_enemy_data VALUES(300600,'攀缘花',300600,0,2,270,1000,1,1.8,0,0,'【物理】从头上释放剧毒攻击\n并用爬山虎进行范围物理攻击\n一口气夺去靠近的冒险者体力的\n恶魔之花。\n\n·对前方两名角色给予\n　中等物理伤害。\n\n·降低前方三名角色的物理防御力，\n　并赋予中毒状态。');
INSERT INTO unit_enemy_data VALUES(300601,'针刺攀缘花',300601,0,2,270,1000,1,1.8,0,0,'【物理】受到异常力量影响而变异的攀缘花。\n使用带有无数尖刺的藤蔓缠绕敌人，令其动弹不得。\n\n·对前方范围内的敌人造成物理伤害（中），\n　并施加束缚状态。\n·降低前方范围内的敌人的物理防御力，并赋予\n　中毒状态。');
INSERT INTO unit_enemy_data VALUES(300602,'针刺攀缘花',300602,0,2,270,1000,1,1.8,0,0,'【物理】受到异常力量影响而变异的攀缘花。\n使用带有无数尖刺的藤蔓缠绕敌人，令其动弹不得。\n\n·对前方范围内的敌人造成物理伤害（中），\n　并施加束缚状态。\n·降低前方范围内的敌人的物理防御力，\n　并施加中毒状态。');
INSERT INTO unit_enemy_data VALUES(300700,'兽人首领',300700,0,13,270,1000,1,1.5,0,0,'【物理】身为族长鼓舞群落，\n用足以粉碎大地的连击\n给敌人施加高等伤害的\n顶级兽人。\n\n·以随机敌人为中心，\n　对三名角色给予中等物理伤害，\n　并提升物理防御力。\n\n·提升己方全体的物理攻击力。');
INSERT INTO unit_enemy_data VALUES(300701,'兽人头目',300701,0,13,270,1000,1,1.5,0,0,'由于特异力量而变异的兽人首领。\n用巨大的盾牌发出攻防一体的一击。\n\n·对前方范围内的敌人造成大量物理伤害，\n　并赋予眩晕状态。\n·大幅度降低前方范围内敌人的防御力。');
INSERT INTO unit_enemy_data VALUES(300800,'长老角鹿',300800,0,1,270,1000,1,1.8,0,0,'【物理】一旦爆发便\n无法对付，其嘶吼声\n据说能传到天际，\n传说中的狂野角鹿。\n\n·对敌方全体给予中等物理伤害，\n　并小幅度击飞。\n\n·对前方三名角色给予中等物理伤害，\n　并降低敌人的物理攻击力和\n　物理防御力。');
INSERT INTO unit_enemy_data VALUES(300801,'灵魂角鹿',300801,0,1,270,1000,1,1.8,0,0,'【物理】由于特异力量而变异的狂野角鹿。\n将犄角中注入魔力，释放比打击更强力的一击。\n\n·给前方范围内的敌人施加高等魔法伤害，\n 将其击退的同时使其眩晕。\n·给前方范围的敌人施加低等魔法伤害，\n 同时降低其魔法防御与攻击速度。');
INSERT INTO unit_enemy_data VALUES(300900,'暴君寿龟',300900,0,13,270,1000,1,1.8,0,0,'【物理】用强力的头槌砸碎地面\n给全体施加高等伤害。\n身体和甲壳进化得十分巨大\n像石山一样的海龟暴君。');
INSERT INTO unit_enemy_data VALUES(301000,'海龙',301000,0,14,270,1000,1,1.8,0,0,'【物理】用强力的冰冻之气\n瞬间将前卫冰冻，\n咬碎其身体。\n凶猛的深海龙。\n\n·对前方三名角色给予\n　中等物理伤害。\n\n·对前方两名角色给予中等物理伤害，\n　并赋予冻结状态。');
INSERT INTO unit_enemy_data VALUES(301100,'野蛮魔熊',301100,0,13,270,1000,1,1.7,0,0,'【物理】用不符体格的敏捷与\n咬噬攻击夺走对方的体力，\n用巨大的利爪撕裂敌人。\n残忍的森之统治者。\n\n·对前方四名角色给予\n　高等物理伤害。\n\n·对前方一名角色给予低等物理伤害，\n　并降低其物理攻击力。\n　同时提升自身的\n　物理攻击力。');
INSERT INTO unit_enemy_data VALUES(301101,'狂乱魔熊',301101,0,13,270,1000,1,2.0,0,0,'由于特异力量而变异的野蛮魔熊。\n注入怒气的强壮手臂将敌人连同地面一齐撕裂。\n\n·对前方范围内的敌人造成物理伤害（大），\n　并施加眩晕状态。\n·对前方的一名敌方角色造成物理伤害（小），\n　并中幅降低目标的物理攻击力。');
INSERT INTO unit_enemy_data VALUES(301200,'巨型魔像',301200,0,13,270,1000,1,1.6,0,0,'【物理】无限制召唤魔像\n用石针削减体力与精神力。\n用魔力将无数魔像的残骸\n重新构筑起来的巨大魔像。\n\n·对前方4名角色给予中等物理伤害，\n　并降低其技能值。\n\n·召唤魔像。');
INSERT INTO unit_enemy_data VALUES(301300,'暴食魔兽',301300,0,4,270,1000,1,3.0,0,0,'恶名昭著的暴食猛兽，天秤座的魔星兽。\n分辨自己夺取的灵魂的善恶，享受滋味的差异。\n\n·对来自敌人的攻击所造成的麻痹、眩晕、束缚、\n　行动速度低下效果不具有抗性。\n·对敌方全体造成大量魔法伤害，同时\n　特大幅度提升自身的物理攻击力和魔法攻击力。\n·对后方2名角色造成中等魔法伤害。\n·对前方3名角色造成中等魔法伤害，\n　并小幅度削减其技能值。同时中等幅度提升自身的\n　技能值。');
INSERT INTO unit_enemy_data VALUES(301400,'美杜莎',301400,0,3,270,1000,1,2.5,0,0,'头发由无数毒蛇组成的处女座魔星兽。\n悲恋的痛苦使它抗拒一切，把一切都变成石头。\n\n·对敌方全体造成大量魔法伤害，并对前方范围内\n　的敌人赋予石化状态。\n·对前方范围内的敌人给造成物理伤害，同时大幅度削减陷入石化\n　状态的敌人的技能值。\n·对物理攻击力最高的一名角色造成大量\n　魔法伤害，并对其赋予中毒状态。');
INSERT INTO unit_enemy_data VALUES(301500,'魔界人狼',301500,0,12,270,1000,1,1.8,0,0,'【物理】用长啸麻痹猎物\n在其怯懦时用爪子将其撕裂。\n阻拦了无数冒险者，\n残暴至极的魔界之狼。\n\n·对随机的两名角色给予\n　高等物理伤害。\n\n·对前方三名角色给予低等魔法伤害，\n　并赋予麻痹状态。');
INSERT INTO unit_enemy_data VALUES(301600,'基兹',301600,0,3,270,1000,1,1.8,0,0,'【物理】卷起暴风的大怪鸟。\n从魔物图鉴中逃出，大肆进行破坏。\n\n·对敌方全体施加与角色最大生命值对应的\n　大幅伤害。\n·\n　对前方范围内的敌人施加中幅物理伤害，\n　并令其眩晕。');
INSERT INTO unit_enemy_data VALUES(301601,'基兹',301600,0,3,270,1000,1,1.8,0,0,'【物理】卷起暴风的大怪鸟。\n从魔物图鉴中逃出，大肆进行破坏。\n\n·给敌方全体施加与角色最大生命值对应的\n　大幅伤害。\n·\n　给前方范围内的敌人施加中等物理伤害，\n　并令其眩晕。\n·给前方范围内的敌人施加与角色最大生命值对应的\n　中幅伤害，降低其物理防御力。\n·基兹剩余生命值低于50%时\n　攻击会更加凶猛。');
INSERT INTO unit_enemy_data VALUES(301700,'炸脖龙',301700,0,3,270,1000,1,2.6,0,0,'');
INSERT INTO unit_enemy_data VALUES(301800,'人马射手',301800,0,3,270,1000,1,2.0,0,0,'射手座的魔星兽，射杀众多翱翔于天空中的目标。\n身怀绝技，缠绕着雷光的箭可在刹那间穿透一切。\n\n·必杀技能是根据敌方全体的最大生命值，\n　给予魔法伤害。\n　对处于黑暗状态的敌人给予3倍伤害。\n·对敌方全体给予中等魔法伤害，同时，\n　对剩余生命值比例最少的一名敌方角色赋予强力的\n　黑暗状态。\n·对前方的三名敌方角色给予物理伤害。');
INSERT INTO unit_enemy_data VALUES(301900,'虎鲸猎手',301900,0,11,270,1000,1,1.7,0,0,'【物理】用敏锐的眼神搜寻\n猎物与海浪的海之猎手。\n用魔力产生无限的潮水\n削弱敌人的武器与气力。 \n\n·对前方四名角色造成少量物理伤害\n　并降低物理攻击力。\n\n·对前方两名角色造成少量魔法伤害\n　并削减其技能值。');
INSERT INTO unit_enemy_data VALUES(302000,'野性狮鹫',302000,0,11,270,1000,1,1.6,0,0,'在残酷环境中成长进化的狮鹫。\n使用龙卷风将猎物卷起到空中进行狩猎。\n\n・对前方范围内的敌人造成中等魔法伤害。');
INSERT INTO unit_enemy_data VALUES(302001,'野性狮鹫',302001,0,11,270,1000,1,1.6,0,0,'在残酷环境中成长进化的狮鹫。\n使用龙卷风将猎物卷起到空中进行狩猎。\n\n・对前方范围内的敌人造成中等魔法伤害。');
INSERT INTO unit_enemy_data VALUES(302100,'双足飞龙',302100,0,11,270,1000,1,1.6,0,0,'张开巨大的飞翼翱翔天空的翼龙。\n能够喷射巨大的火球，阻挡侵入者。\n\n·对前方范围内的敌人造成中等物理伤害。');
INSERT INTO unit_enemy_data VALUES(302101,'双足飞龙',302101,0,11,270,1000,1,1.6,0,0,'张开巨大的飞翼翱翔天空的翼龙。\n能够喷射巨大的火球，阻挡侵入者。\n\n·对前方范围内的敌人造成中等物理伤害。');
INSERT INTO unit_enemy_data VALUES(302200,'依科托·格拉迪恩',302200,0,16,270,1000,2,1.6,0,0,'【魔法】借由露娜魔力而觉醒的古代机械兵器。\n毫不留情地释放出魔力来阻止入侵者。\n\n·对敌方全体造成特大魔法伤害\n　并大幅度降低敌人的魔法防御力。\n·对前方范围内的敌人造成大量物理伤害\n·对前方范围内的敌人造成大量魔法伤害\n　并赋予麻痹状态。');
INSERT INTO unit_enemy_data VALUES(302300,'聪慧猫头鹰',302300,0,16,270,1000,2,1.8,0,0,'');
INSERT INTO unit_enemy_data VALUES(302400,'方舟守护者',302400,0,16,270,1000,1,1.6,0,0,'');
INSERT INTO unit_enemy_data VALUES(302500,'奇美拉',302500,0,16,270,1000,1,1.4,0,0,'');
INSERT INTO unit_enemy_data VALUES(302600,'双子魔猪',302600,0,16,270,1000,1,1.6,0,0,'【物理】盗行不断的双子座魔星兽。\n通过超凡的配合攻击领地内的入侵者。\n\n·根据角色最大生命值，对敌方全体造成\n　大量物理伤害。\n·对后卫的敌人造成中等物理伤害，\n　小幅度降低攻击速度。\n·对敌方全体造成低等物理伤害。');
INSERT INTO unit_enemy_data VALUES(302601,'双子魔猪',302601,0,16,270,1000,1,1.9,0,0,'【物理】盗行不断的双子座魔星兽。\n通过超凡的配合攻击领地内的入侵者。\n\n·根据角色最大生命值，对敌方全体造成\n　大量物理伤害。\n·对后卫的敌人造成中等物理伤害，\n　小幅度降低攻击速度。\n·对敌方全体造成低等物理伤害。');
INSERT INTO unit_enemy_data VALUES(302700,'巨钳魔蟹',302700,0,16,270,1000,1,1.8,0,0,'【物理】在船墓的入海口筑巢的魔星兽。\n巨大的钳子只要一击就能让无数艘船只沉没。\n\n・对全体敌人造成大量魔法伤害，\n　降低物理防御力。\n・对前方范围内的全体敌人造成中等物理伤害，\n　另外让敌人陷入眩晕状态。\n・对全体敌人造成中等物理伤害。');
INSERT INTO unit_enemy_data VALUES(302800,'炎吼狮王',302800,0,16,270,1000,1,1.8,0,0,'享有热带草原霸主之名的狮子座魔星兽。\n用灼热的咆哮烧尽所有与它为敌的对象。\n\n·根据最大生命值，对敌方全体造成无视防御力的\n　大量物理伤害，再对残余生命值较少的\n　敌人赋予烧伤状态。\n·对一名敌方角色造成大量物理伤害，在对方处于\n　烧伤状态时再给予追加伤害。\n·对前方范围内的敌人造成中等物理伤害，\n　在对方处于烧伤状态时再造成追加伤害。');
INSERT INTO unit_enemy_data VALUES(302900,'阿克艾利欧斯',302900,0,16,270,1000,1,1.8,0,0,'被寄宿在神瓶中的魔力所吸收的水瓶座魔星兽。\n沉溺于强大的力量，为了支配大海而肆虐。\n\n·必杀技能对敌方全体给予共计6次的\n　低等魔法伤害。\n　当角色的生命值低于80%时造成击退效果，\n　当低于40%时进一步击退并\n　赋予眩晕状态。\n·对眼前的三名敌方角色给予中等物理伤害。\n·解除自身的属性值降低效果和敌方全体的\n　属性值提升效果，并提升自身的\n　技能值。');
INSERT INTO unit_enemy_data VALUES(303000,'托尔佩顿',303000,0,16,270,1000,1,1.8,0,0,'在海上大闹、呼风唤雨的双鱼座魔星兽。\n它的跟班们红色的鳍是它作为优秀首领的证明。\n\n·很容易受到物理攻击力提升或下降效果\n　的影响。\n·必杀技能对敌方全体给予高等物理伤害，\n　同时提升自身的行动速度和物理\n　攻击力。\n·对敌方全体给予低等物理伤害。\n·对前方两名角色给予中等物理伤害，小幅度降低\n　物理防御力。');
INSERT INTO unit_enemy_data VALUES(303100,'冬眠魔熊',303100,0,13,270,1000,1,3.5,0,0,'【物理】终生藏身在冰穴最深处\n冬眠的巨熊。\n在睡梦中度过了十分漫长的岁月，\n却会在狩猎猎物的瞬间醒来。\n\n·对敌方全体造成大量魔法伤害，\n　并降低物理攻击力。\n\n·对前方三名角色造成少量物理伤\n　害。同时，大幅度击飞前方一名\n　角色，并赋予冻结状态，使其移\n　动速度下降。');
INSERT INTO unit_enemy_data VALUES(303200,'冰雪夫人',303200,0,6,270,1000,1,1.6,0,0,'【物理】与暴风雪一同出现，\n召唤大军袭击扰乱地盘的人。\n雪国第一华美夫人。\n\n·对前方四名角色造成中等魔法伤害\n　并降低其物理攻击力。\n\n·对随机两名角色造成少量魔法伤害\n　并赋予冻结状态。');
INSERT INTO unit_enemy_data VALUES(303300,'滴水嘴兽',303300,0,13,270,1000,1,2.1,0,0,'【物理】留在腐朽的遗迹\n保护无主之地的神秘鸟型石像。\n如今街道和命令都消失成沙，\n不管是谁它都会喷出火焰袭击对方。\n\n·对前方四名角色造成中等魔法伤害\n　并赋予烧伤状态。\n　同时降低敌人的魔法防御力。\n\n·对前方三名角色造成少量魔法伤害\n　并赋予黑暗状态。');
INSERT INTO unit_enemy_data VALUES(303400,'沙之槌',303400,0,13,270,1000,1,1.9,0,0,'【物理】君临生存竞争激烈的沙漠\n顶端的巨大蜥蜴。\n利用毒、活用沙子的战法是在\n残酷的环境中习得的生存术。\n\n·对敌方全体造成中等物理\n　伤害，并降低其行动速度。\n　同时提升己方全体的物理\n　攻击力。\n\n·以前方一名角色为中心，对小范围\n　内的敌人造成少量物理伤害，\n　并赋予中毒状态。\n　同时，降低敌人的物理防御力。');
INSERT INTO unit_enemy_data VALUES(303401,'巨型蜥蜴',303401,0,13,270,1000,1,1.9,0,0,'【物理】带着露娜魔力而变异的巨蜥。\n硬刺状的鳞片可以削弱敌人的体力和防御。\n\n·对敌方全体造成大量物理伤害\n　同时削减技能值。\n·对前方两名角色造成中等物理伤害\n　并赋予中毒状态。\n·对前方三名角色造成中等物理伤害。\n　同时对前方一名角色造成大量物理伤害，\n　并中等幅度地降低其物理防御力和魔法防御力。');
INSERT INTO unit_enemy_data VALUES(303402,'巨型蜥蜴',303402,0,13,270,1000,1,1.9,0,0,'【物理】带着露娜魔力而变异的巨蜥。\n硬刺状的鳞片可以削弱敌人的体力和防御。\n\n·对敌方全体造成大量物理伤害\n　同时削减技能值。\n·对前方两名角色给予中等物理伤害\n　并赋予中毒状态。\n·对前方三名角色造成中等物理伤害。\n　同时对前方一名角色造成\n　大量物理伤害，并中等幅度地降低其物理防御力和魔法防御力。');
INSERT INTO unit_enemy_data VALUES(303500,'怒蛇',303500,0,13,270,1000,1,1.8,0,0,'【物理】凭借持续吸收其他生物\n的魔力来掌握妖术的魔物。\n从敌人身上的伤口处夺取魔力，\n进而获得更多的力量。\n\n·对敌方全体造成中等魔法伤害\n　削减所有使用魔法攻击的\n　敌人的技能值。\n\n·以前方一名角色为中心，\n　对小范围敌人造成少量魔法伤害，\n　并降低魔法防御力。');
INSERT INTO unit_enemy_data VALUES(303501,'怒蛇',303501,0,13,270,1000,1,1.8,0,0,'【物理】凭借持续吸收其他生物\n的魔力来掌握妖术的魔物。\n从敌人身上的伤口处夺取魔力，\n进而获得更多的力量。\n\n·对敌方全体造成中等魔法伤害\n　削减所有使用魔法攻击的\n　敌人的技能值。\n\n·对前方两名角色造成少量魔法伤害\n　并降低其魔法防御力。');
INSERT INTO unit_enemy_data VALUES(303600,'熔岩龙',303600,0,13,270,1000,1,1.7,0,0,'【物理】凶猛的恐龙，使用强韧的脚\n和锐利的爪子来瞬间捕获猎物。\n在毁灭的大地上以岩浆为食，\n孤高地生存着。\n\n·对敌方全体造成中等物理伤害\n　提升自身的行动速度。\n\n·对前方三名角色造成中等\n　物理伤害，并使其行动速度下降。');
INSERT INTO unit_enemy_data VALUES(303700,'地狱犬',303700,0,14,270,1000,1,0.7,0,0,'【物理】坠入火山后，全身缠绕着\n地狱的火焰而复苏的双头魔犬。\n与爱犬波尼一同在领地驰骋，\n用灼热的火焰来招待入侵者。\n\n·对敌方全体给予中等物理伤害，\n　并赋予烧伤状态。同时回复\n　召唤的怪物的生命值。\n\n·召唤波尼。');
INSERT INTO unit_enemy_data VALUES(303800,'阿尔玛',303800,0,16,270,1000,1,2.0,0,0,'【物理】从远古开始守护迷宫的防卫兵器。静静等待跨越所有陷阱，侵入最深处的侵入者。\n·根据角色的最大生命值，对敌方全体给予\n　中等魔法伤害。\n·对前方范围内的敌人给予中等物理伤害。');
INSERT INTO unit_enemy_data VALUES(303801,'阿尔玛',303800,0,16,270,1000,1,2.0,0,0,'【物理】从远古开始守护迷宫的防卫兵器。静静等待跨越所有陷阱，侵入最深处的侵入者。\n·根据角色的最大生命值，对敌方全体给予\n　特大魔法伤害。\n·对前方范围内的敌人给予中等物理伤害。\n·对残余生命值最高的敌人，根据其最大生命值，\n　给予中等魔法伤害并使其眩晕。\n·当阿尔玛自身的生命值低于50％时，\n　它的攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(303900,'独眼巨人',303900,0,13,270,1000,1,2.0,0,0,'浑身怪力的凶残独眼巨人。\n眼中积蓄着魔力，放出强大的奇异光线。\n\n·对全体敌人造成大量魔法伤害。\n　对生命值为100%的角色造成的伤害\n　会有所增加，同时降低该角色的行动速度。\n·对物理攻击力最高的一名角色，根据其最大生命值\n　造成中等物理伤害，并降低其技能值。\n');
INSERT INTO unit_enemy_data VALUES(303901,'独眼巨人',303901,0,13,270,1000,1,2.0,0,0,'浑身怪力的凶残独眼巨人。\n眼中积蓄着魔力，放出强大的奇怪光线。\n\n·对全体敌人造成大量魔法伤害。\n　对生命值为100%的角色造成的伤害\n　会有所增加，同时降低该角色的行动速度。\n·对物理攻击力最高的一名角色，根据其最大生命值\n　造成中等物理伤害，并降低其技能值。\n');
INSERT INTO unit_enemy_data VALUES(304000,'暗黑独角兽',304000,0,13,270,1000,1,2.0,0,0,'生于梦幻、彷徨于黑暗的魔兽。\n在噩梦之牢中束缚住猎物，美味地吞吃其生机。\n\n·对物理攻击最高的1个敌人造成特大魔法伤害、\n　以及技能值特大幅度下降。\n·对以物理攻击力最高的1个敌人为中心的\n　范围内敌人造成中等魔法伤害\n　另外令其陷入诅咒状态。');
INSERT INTO unit_enemy_data VALUES(304100,'泰坦海龟',304100,0,13,270,1000,1,2.5,0,0,'又老又大的海龟，龟壳上甚至有大树扎根。\n持续忍耐敌人的攻击，施加愤怒的反击。\n\n·对全体敌人造成大量物理伤害。\n·根据自己缩在龟壳期间受到的伤害量\n　来给予敌人相应的物理伤害。');
INSERT INTO unit_enemy_data VALUES(304200,'卡尔古',304200,0,16,270,1000,1,1.8,0,0,'【物理】吞吃尸体的凶暴大型魔兽。\n全身缠绕着强韧的尖刺，执着的捕获猎物。\n·对全体敌人造成与其最大生命值相适应的\n中等物理伤害。\n·对后方范围造成中等物理伤害\n　并令其眩晕。');
INSERT INTO unit_enemy_data VALUES(304201,'卡尔古',304200,0,16,270,1000,1,1.8,0,0,'【物理】吞吃尸体的凶暴大型魔兽。\n全身缠绕着强韧的尖刺，执着的捕获猎物。\n·对全体敌人造成与其最大生命值相适应的\n中等物理伤害。\n·对后方范围造成中等物理伤害\n　并令其眩晕。\n·对前方第2个敌人造成中等物理伤害。\n·卡尔古在生命值低于50%后会展开\n　更为激烈凶暴的攻击。');
INSERT INTO unit_enemy_data VALUES(304300,'腾特帕斯',304300,0,16,270,1000,1,1.8,0,0,'【物理】君临海边的巨大软体魔兽。\n强韧的触手和钢铁的大鱼叉可以彻底破坏一切。\n·根据角色最大生命值，对全体敌人造成相应威力\n　的大量物理伤害。\n·对后方范围造成中等魔法伤害，\n　令其陷入麻痹状态，另外特大幅降低物理攻击力、\n　魔法攻击力。\n·经过一定时间后对单个敌人造成\n　超大量物理伤害。\n·腾特帕斯自己的生命值低于40％后，\n　会展开更为激烈凶暴的攻击。');
INSERT INTO unit_enemy_data VALUES(304301,'腾特帕斯',304300,0,16,270,1000,1,1.8,0,0,'【物理】君临海边的巨大软体魔兽。\n强韧的触手和钢铁的大鱼叉可以彻底破坏一切。\n·根据角色最大生命值，对全体敌人造成相应威力\n　的大量物理伤害。\n·对后方范围造成中等魔法伤害，\n　令其陷入麻痹状态，另外特大幅降低物理攻击力、\n　魔法攻击力。');
