INSERT INTO unit_enemy_data VALUES(304704,'崩坏的黑甲骑士',304704,0,16,270,1000,1,1.8,0,0,'【物理】吞噬露娜之塔的魔力，不断增殖融合的\n神秘魔物。无差别地碾压一切。\n\n·给予全体敌人高等物理伤害，\n　另外中等幅度降低物理防御力。\n·对前方范围内的敌人造成物理伤害，\n　并使其中物理攻击力最高的两名敌人的\n　物理攻击力特大幅度下降，同时\n　技能值特大幅度下降。\n　自身的物理防御力大幅度下降。\n·对前方三名敌人造成中等物理伤害，\n　中等提高物理攻击力。');
INSERT INTO unit_enemy_data VALUES(304800,'幽灵领主',304800,0,16,270,1000,1,1.8,0,0,'冥府的高官，率领作为亡灵的强者之魂。\n将死者的执念附于刀刃之上，使对手的动作变得迟缓。\n\n·对敌方全体造成大量魔法伤害，\n　并降低物理攻击力。\n·对敌方全体造成大量魔法伤害。\n　对进行物理攻击的敌人造成额外伤害，\n　并降低其行动速度。');
INSERT INTO unit_enemy_data VALUES(304900,'电子吉加斯',304900,0,16,270,1000,1,1.8,0,0,'【物理】通过露娜之魔力醒来的巨腕改造人。\n小型反应堆的最大输出连自身都能感电。\n\n·对前方范围内的敌人造成大量物理伤害＆麻痹。\n　解除自身状态提升效果，\n　中等幅度降低物理防御力和魔法防御力。\n·对敌方全体造成大量魔法伤害，\n　大幅度长时间提高自身的物理攻击力。\n·大幅度提高己方全体的行动速度。');
INSERT INTO unit_enemy_data VALUES(305000,'布丁南瓜灯',305000,0,16,270,1000,1,0.8,0,0,'【物理】企图向人类复仇的灵界居民。\n「我是南瓜灯！布丁南瓜灯！\n　不陪我玩就捣乱哟~」\n\n·对全体敌人造成大量物理伤害，\n　角色的生命值低于40%后，\n　变为诱惑状态。\n·对全体敌人造成少量魔法伤害，\n　解除敌人状态提升效果。\n·对最近的敌人造成中等物理伤害，\n　另外，对前方三名敌人造成中等物理\n　伤害＆赋予眩晕。\n·布丁南瓜灯自身的生命值\n　低于40%后，会展开更为激烈\n　凶暴的攻击。\n');
