INSERT INTO unit_enemy_data VALUES(305300,'高等食尸兽',305300,0,16,270,1000,1,1.8,0,0,'【物理】沐浴在露娜魔力之下而变异的狰狞魔兽。\n强韧的爪牙能轻而易举地将历练的冒险者撕裂。\n\n·必杀技能对敌方全体造成大量物理伤害，\n　并会回复自己的生命值。\n·对后方范围内的敌人造成物理伤害，\n　使其眩晕。\n　并使敌方全体的物理防御力中幅度下降。\n·对前方范围内的敌人造成物理伤害\n　使其陷入中毒状态。');
INSERT INTO unit_enemy_data VALUES(305400,'冰角龙',305400,0,16,270,1000,1,1.8,0,0,'【物理】强而有力的带冰头部能\n贯穿坚韧护盾的独角之兽。\n在布满冰丘的北方尽头，\n将冒险者推落到冰之地狱。\n\n·对敌方全体造成大量魔法伤害，\n　降低物理防御力。\n　并使己方全体的技能值\n　上升。\n\n·对前方小范围造成少量魔法伤害，\n　使其陷入结冰状态。并且\n　对全体造成少量魔法伤害，\n　将前方三名角色小幅度\n　击飞。');
INSERT INTO unit_enemy_data VALUES(305500,'雾精',305500,0,16,270,2160,2,3.0,0,0,'【魔法】在无数传说中都有所记载的记忆之大精灵。\n没有实体，如雾般缥缈隐藏全身。\n\n·必杀技能给予敌方全体\n　高等魔法伤害，\n　并且在敌人处于诅咒状态时\n　提升伤害。\n·给予敌方全体中等魔法伤害。\n　并使其陷入诅咒状态。\n·随机召唤\n　分身（紫）（红）（蓝）之中的其中一个。\n　召唤时给予敌人全体反击。\n·【物理】分身（紫）会为自身展开吸收\n　物理/魔法的屏障。\n·【物理】分身（红）会给予敌方全体\n　中等魔法伤害。\n·【物理】分身（蓝）给予敌方全体\n　中等魔法伤害。\n　并且使其陷入诅咒状态。\n　分身（蓝）的生命值降到30%以下时，\n　回复雾精的300技能值。\n·雾精自身的生命值\n　降到50%时，\n　会变得更为凶暴，攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(305501,'雾精',305500,0,16,270,2160,2,3.0,0,0,'【魔法】身负无数传承之名，记忆的大精灵。\n没有实体，如雾般缥缈隐藏全身。\n\n·必杀技能给予敌方全体\n　高等魔法伤害，\n　并且在敌人处于诅咒状态时\n　提升伤害。\n·敵给予敌方全体中等魔法伤害。\n　并使其陷入诅咒状态。\n·随机召唤\n　分身（紫）（红）之中的其中一个。\n　召唤时给予敌人全体反击。\n·【物理】分身（紫）会为自身展开吸收\n　物理/魔法的屏障。\n·【物理】分身（红）会给予敌方全体\n　中等魔法伤害。');
INSERT INTO unit_enemy_data VALUES(305502,'雾精',305500,0,16,270,2160,2,3.0,0,0,'【魔法】身负无数传承之名，记忆的大精灵。\n没有实体，如雾般缥缈隐藏全身。\n\n·必杀技能给予敌方全体\n　高等魔法伤害，\n　并且在敌人处于诅咒状态时\n　提升伤害。\n·敵给予敌方全体中等魔法伤害。\n　并使其陷入诅咒状态。\n·随机召唤\n　分身（紫）（红）之中的其中一个。\n　　召唤时给予敌人全体反击。\n·【物理】分身（紫）会为自身展开吸收\n　物理/魔法的屏障。\n·【物理】分身（红）会给予敌方全体\n　中等魔法伤害。\n·雾精自身的生命值\n　降到50%时，\n　会变得更为凶暴，攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(305503,'雾精',305500,0,16,270,2160,2,3.0,0,0,'【魔法】身负无数传承之名，记忆的大精灵。\n没有实体，如雾般缥缈隐藏全身。\n\n·必杀技能给予敌方全体\n　高等魔法伤害，\n　并且在敌人处于诅咒状态时\n　提升伤害。\n·敵给予敌方全体中等魔法伤害。\n　并使其陷入诅咒状态。\n·随机召唤\n　分身（紫）（红）（蓝）之中的其中一个。\n　召喚する際は敵全体を反击させる。\n·【物理】分身（紫）会为自身展开吸收\n　物理/魔法的屏障。\n·【物理】分身（红）会给予敌方全体\n　中等魔法伤害。\n·【物理】分身（蓝）给予敌方全体\n　中等魔法伤害。\n　并且使其陷入诅咒状态。\n　分身（蓝）的生命值降到30%以下时，\n　回复雾精的300技能值。\n·雾精自身的生命值\n　降到50%时，\n　会变得更为凶暴，攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(305600,'异界魔主',305600,0,16,270,1000,1,1.8,0,0,'【魔法】渴望获得露娜魔力而出现的异界魔物。\n企图凭魔眼和自己率领的士兵征服露娜之塔。\n\n·对敌方全体造成大量魔法伤害\n　并且中幅降低敌方全体的魔法防御力。\n·随机六次对一名角色造成少量\n　魔法伤害。\n·对倒数第三名角色造成中等魔法伤害\n　以及长时间的拘束。\n　并对全体敌人造成少量魔法伤害。');
INSERT INTO unit_enemy_data VALUES(305700,'巨型哥布林',305700,0,16,270,1000,1,1.8,0,0,'豪爽地挥舞着战斧，熟练的哥布林战士。\n将年幼的战士夹在腋下，向其展示魂之技能。\n\n·必杀技能中幅提升自身的\n　物理攻击力，小幅降低物理防御力，并且\n　给予面前的一名角色特高等物理伤害。');
INSERT INTO unit_enemy_data VALUES(305800,'海蛞蝓',305800,0,16,270,1000,2,1.8,0,0,'传说中的海神使者，神奇的软体魔物。\n全身基本由蕴含魔力的海水组成。\n\n·必杀技能给予敌方全体\n　高等魔法伤害。\n·给予第二名和第三名角色\n　中等魔法伤害。\n·在一定范围内，降低物理攻击力和\n　魔法防御力，展开持续给予魔法伤害的\n　领域。');
INSERT INTO unit_enemy_data VALUES(305900,'极彩鸟',305900,0,16,270,1000,1,2.2,0,0,'释放压倒性的魅力，外表华美的极彩鸟。\n若被其的美貌诱惑，就会其攻击同伴的仆人。\n\n·必杀技能是给予处于诱惑状态的角色\n　之外的敌方全体高等物理伤害。\n·给予第二名角色低等魔法伤害\n　并赋予诱惑状态，并且其后大幅提升\n　物理攻击力和魔法攻击力。');
INSERT INTO unit_enemy_data VALUES(306000,'邪饼神咖米',306000,0,16,270,2160,1,1.6,0,0,'【物理】汇聚了邪念残渣化身而成的邪神。\n将显现出的全部烦恼都进行净化，迎接新年吧！\n\n·必杀技能给予敌方全体\n　高等物理伤害。\n·给予前方三名角色中等魔法伤害，\n　降低物理攻击力。\n·在前方中距离召唤倒计时式炸弹年糕。\n·炸弹年糕会在召唤后\n　8秒产生大爆炸，给予范围内的敌方全体\n　中等物理伤害。\n　但是，在爆炸前将炸弹年糕的生命值\n　降到50%，\n　则小幅回复敌方全体生命值，并消失。\n·邪饼神咖米自身的生命值\n　降到50%时，会变得更加凶暴，\n　攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(306001,'邪饼神咖米',306000,0,16,270,2160,1,1.6,0,0,'【物理】汇聚了邪念残渣化身而成的邪神。\n净化显现的全部烦恼，迎接新年吧！\n\n·必杀技能は敵全体に\n　必杀技能给予敌方全体\n　高等物理伤害。\n·给予前方三名角色中等魔法伤害，\n　降低物理攻击力。');
INSERT INTO unit_enemy_data VALUES(306002,'邪饼神咖米',306000,0,16,270,2160,1,1.6,0,0,'【物理】汇聚了邪念残渣化身而成的邪神。\n净化显现的全部烦恼，迎接新年吧！\n\n·必杀技能给予敌方全体\n　高等物理伤害。\n·给予前方三名角色中等魔法伤害，\n　降低物理攻击力。\n·在前方中距离召唤倒计时式炸弹年糕。\n·炸弹年糕会在召唤后\n　8秒产生大爆炸，给予范围内的敌方全体\n　中等物理伤害。\n　但是，在爆炸前将炸弹年糕的生命值\n　降到50%，\n　则小幅回复敌方全体生命值，并消失。\n·邪饼神咖米自身的生命值\n　降到50%时，会变得更加凶暴，\n　攻击更为猛烈。');
