INSERT INTO unit_enemy_data VALUES(306004,'邪饼神咖米',306004,0,16,270,3000,1,0.0,0,0,'【MODE1】汇聚了邪念残渣化身而成的邪神。\n净化显现的全部烦恼，迎接新年吧！\n\n·邪饼神咖米自身收到攻击时，\n 小幅回复己方全体的生命值，\n　回复邪饼神咖米自身的全部生命值。');
INSERT INTO unit_enemy_data VALUES(306005,'邪饼神咖米',306005,0,16,270,3000,1,1.6,0,0,'【MODE2】汇聚了邪念残渣化身而成的邪神。\n净化显现的全部烦恼，迎接新年吧！\n\n·给予前方一名角色物理伤害。\n·给予前方范围内中等魔法伤害，\n　并大幅度击飞。\n·召唤6个蕴含邪念的烦恼（红）。\n·召唤三名蕴含邪念的烦恼（紫）。\n·召唤一名蕴含邪念的烦恼（蓝）。');
INSERT INTO unit_enemy_data VALUES(306006,'邪饼神咖米',306006,0,16,270,2160,1,1.6,0,0,'【MODE3】汇聚了邪念残渣化身而成的邪神。\n净化显现的全部烦恼，迎接新年吧！\n\n·给予前方一名角色物理伤害。\n·必杀技能给予敌方全体\n　高等物理伤害。\n·给予前方三名角色中等魔法伤害，\n　降低物理攻击力。\n·在前方中距离召唤倒计时式炸弹年糕。\n·炸弹年糕会在召唤后\n　8秒产生大爆炸，给予范围内的敌方全体\n　中等物理伤害。\n　但是，在爆炸前将炸弹年糕的生命值\n　降到50%，\n　则小幅回复敌方全体生命值，并消失。');
INSERT INTO unit_enemy_data VALUES(306100,'偷盗豺狼',306100,0,16,270,1000,1,1.8,0,0,'【物理】潜藏在黑暗之中，在村落中偷盗的\n魔物头领。\n毫不留情舍弃蒙骗回来的猎物，\n妨碍开拓的流氓。\n\n·给予前方三名角色中等\n　物理伤害，使其麻痹。\n\n·给予敌方全体中等物理伤害，\n　降低物理防御力和行动速度。\n　并且使前方小范围陷入\n　中毒状态。');
INSERT INTO unit_enemy_data VALUES(306200,'狸猫首领',306200,0,16,270,1000,1,1.8,0,0,'【物理】占领森林深处，和手下一同支配周边一带，\n老奸巨猾的狸猫。\n展现神秘的幻影，巧妙地操纵着侵蚀精神的\n幻影攻击。\n\n·给予敌方全体中等魔法伤害，\n　召唤两只淘气豆狸。\n\n·给予敌方全体低等魔法伤害，\n　让非无敌状态的召唤兽\n　消失。\n　并且当敌人的生命值处于40%以下时，\n　使其陷入麻痹状态。');
INSERT INTO unit_enemy_data VALUES(306300,'长耳猴首领',306300,0,16,270,1000,1,1.8,0,0,'【物理】使用露娜魔力，统领长耳猴的仙猿。\n自在操纵宝武具之棍，击退所有敌人。\n\n·对敌方全体造成大量物理伤害\n　该攻击对处于烧伤状态的敌人，\n　伤害会变为1.8倍。\n·对前方中范围的敌人造成中等物理伤害\n·对前方小范围的敌方造成中等魔法伤害，\n　使其变成烧伤状态。');
INSERT INTO unit_enemy_data VALUES(306301,'长耳猴首领',306301,0,16,270,1000,1,1.8,0,0,'【物理】使用露娜魔力，统领长耳猴的仙猿。\n自在操纵宝武具之棍，击退所有敌人。\n\n·对敌方全体造成大量物理伤害，\n　并使物理攻击角色的物理攻击力下降。\n　该攻击对处于烧伤状态的敌人，\n　伤害会变为1.8倍。\n·对前方中范围的敌人造成中等物理伤害。\n·对前方小范围的敌人造成中等魔法伤害，\n　使其变成烧伤状态。');
INSERT INTO unit_enemy_data VALUES(306700,'丑萌巧克力兽',306700,0,16,270,2160,1,1.8,0,0,'【物理】痛骂后暴走的巨大巧克力魔兽。\n被误解的呢喃爱意，也会化作伤害他人的刀刃。\n\n·必杀技能会根据敌方全体角色的\n　最大生命值给予高等物理伤害，\n　处于烧伤状态时，会追加伤害。\n·给予前方三名角色中等物理伤害，\n　使其眩晕。\n　并且角色的生命值50%以下时，\n　会使其变成烧伤状态。\n·丑萌巧克力兽自身的生命值\n　50%时，会变得更加凶暴，\n　攻击更为猛烈。');
