INSERT INTO unit_enemy_data VALUES(306704,'丑萌巧克力兽',306700,0,16,270,3000,1,1.8,0,0,'【MODE1】强烈喷射巧克力，将自身覆盖隐藏。\n不断受到敌人的攻击，爆发愤怒的一击。\n\n·给予敌方全体低等物理伤害，使其战斗期间\n　变为烧伤状态。\n·藏在巧克力巨蛋之中时，\n　丑萌巧克力兽自身的物理防御力中幅提升。\n　解除巧克力巨蛋状态后，会根据在此\n　期间所受到的伤害量反击\n　给予物理伤害。\n　并且丑萌巧克力兽自身的物理防御力\n　大幅下降。');
INSERT INTO unit_enemy_data VALUES(306705,'丑萌巧克力兽',306700,0,16,270,3000,1,1.8,0,0,'【MODE2】可可兽振奋起来，\n为了从激化的魔物攻击下，\n 保护重要的人们\n 投身死地。\n\n·必杀技能根据敌方全体角色的\n　最大生命值给予高等物理伤害，\n·可可兽会作为角色的同伴出现。\n　可可兽出现期间，角色全体的物理\n　攻击力和行动速度中幅提升。\n·给予敌方全体低等魔法伤害，\n　大幅降低行动速度\n');
INSERT INTO unit_enemy_data VALUES(306706,'丑萌巧克力兽',306700,0,16,270,2160,1,1.8,0,0,'【MODE3】轰鸣的咆哮声回响在会场之中。\n爱恨情仇的情人节事变终于迎来终局。\n\n·必杀技能根据敌方全体角色的\n　最大生命值给予高等物理伤害，\n　烧伤状态下，追加伤害。\n·给予前方三名角色中等物理伤害，\n　使其眩晕。\n　角色的生命值50%以下时，\n　使其陷入烧伤状态。');
INSERT INTO unit_enemy_data VALUES(306800,'哀叹女神',306800,0,16,270,3000,2,1.6,0,0,'【魔法】因失意和绝望，而堕落成魔的悲哀女王。\n痛哭着用恐怖和诅咒填满一切。\n\n·必杀技能根据敌方全体角色的\n　最大生命值给予高等魔法伤害，\n　降低魔法防御力。\n·给予前方第二名、第三名角色\n　中等魔法伤害，使其眩晕。\n·给予前方两名角色低等魔法伤害，\n　降低物理攻击力，减少技能值。\n　并且使其陷入诅咒状态。\n·哀叹女神自身的生命值\n　降到40％时，会变得更加凶暴，\n　攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(306801,'哀叹女神',306800,0,16,270,3000,2,1.6,0,0,'【魔法】因失意和绝望，而堕落成魔的悲哀女王。\n痛哭着用恐怖和诅咒填满一切。\n\n·必杀技能根据敌方全体角色的\n　最大生命值给予高等魔法伤害，\n　降低魔法防御力。\n·给予前方第二名、第三名角色\n　中等魔法伤害，使其眩晕。');
INSERT INTO unit_enemy_data VALUES(306802,'哀叹女神',306800,0,16,270,3000,2,1.6,0,0,'【魔法】因失意和绝望，而堕落成魔的悲哀女王。\n痛哭着用恐怖和诅咒填满一切。\n\n·必杀技能根据敌方全体角色的\n　最大生命值给予高等魔法伤害，\n　降低魔法防御力。\n·给予前方第二名、第三名角色\n　中等魔法伤害，使其眩晕。\n·给予前方两名角色低等魔法伤害，\n　降低物理攻击力，减少技能值。\n　并且使其陷入诅咒状态。\n　即使被赋予了异常状态免疫效果，\n　该技能也会生效。\n·哀叹女神自身的生命值\n　降到40％时，会变得更加凶暴，\n　攻击更为猛烈。');
