INSERT INTO unit_enemy_data VALUES(300100,'弥诺陶洛斯',300100,0,3,270,1000,1,1.9,0,0,0,'【物理】盘踞在迷宫里的金牛座魔星兽。\n挥舞巨大的战斧，夺去冒险者的性命。\n\n·对敌方全体造成物理伤害（大），使其变为\n 灼伤状态。\n·对1名敌人造成物理伤害（大），在击退\n敌人的同时将其眩晕。\n·对前方范围内的敌人造成魔法伤害（中），\n使其麻痹。');
INSERT INTO unit_enemy_data VALUES(300101,'弥诺陶洛斯',300101,0,3,270,1000,1,1.9,0,0,0,'【物理】获得强大力量的金牛座魔星兽。\n其战斧的攻击超越了力量的极限，能将一切粉碎。\n\n·对敌方全体造成物理伤害（大），并赋予\n　烧伤状态。\n·对一名敌人造成物理伤害（大），\n　在击退敌人的同时使其眩晕。\n·对前方范围内的敌人造成魔法伤害（中），\n　使其麻痹。\n·大幅提升自身的物理攻击力和魔法攻击力，\n　对前方第一名、第二名敌人\n　造成物理伤害（大）。\n　发动技能后，大幅降低自身的\n　物理防御力与魔法防御力。');
INSERT INTO unit_enemy_data VALUES(300200,'利蝎巴鲁托',300200,0,6,270,1000,1,1.5,0,0,0,'使用猛毒的尾棘散播灾厄的天蝎座魔星兽。\n守护沙漠的神虫失去信仰后，变成了邪神。\n\n·对敌方全体造成大量物理伤害。\n　BOSS每次使用必杀技能，\n　都会提升对敌人造成的伤害。\n　这个效果最多可以累积三次。\n·对前方范围内的敌人造成中等物理伤害，\n　并赋予中毒状态。\n·解除自身的属性值降低效果，\n　并提升自己的物理防御力。');
INSERT INTO unit_enemy_data VALUES(300201,'利蝎巴鲁托',300201,0,6,270,1000,1,1.5,0,0,0,'【物理】被遗留在失去信仰了的神殿里的天蝎座魔星兽。\n忘却了曾经获得过的虔诚祈祷，如今将带来毁灭。\n\n·对敌方全体造成物理伤害（大）。\n　使用此技能后，\n　每次攻击敌人的时候，利蝎巴鲁托\n　对那名敌人造成的伤害\n　都会提升。\n　这个效果最多可以累积三次。\n·以前方一名敌人为中心，造成小范围\n　物理伤害（中），并赋予中毒状态。\n·解除自身的属性值降低效果，\n　并提升自身的物理防御力。');
INSERT INTO unit_enemy_data VALUES(300202,'利蝎巴鲁托',300202,0,6,270,1000,1,1.5,0,0,0,'【物理】被遗留在失去信仰了的神殿里的天蝎座魔星兽。\n忘却了曾经获得过的虔诚祈祷，如今将带来毁灭。\n\n·对敌方全体造成物理伤害（大）。\n　使用此技能后，\n　每次攻击敌人的时候，利蝎巴鲁托\n　对那名敌人造成的伤害\n　都会提升。\n　这个效果最多可以累积三次。\n·以前方一名敌人为中心，造成小范围\n　物理伤害（中），并赋予中毒状态。\n·解除自身的属性值降低效果，\n　并提升自身的物理防御力。\n·召唤1只利蝎守护者。\n　在召唤时，所有已召唤的利蝎守护者\n　都将被赋予1秒的无敌状态。\n　召唤第3只利蝎守护者时，\n　对敌方全体造成与角色的最大生命值\n　相应的比例物理伤害。\n　当利蝎守护者的生命值降至0时，\n　对利蝎巴鲁托造成固定的物理伤害，\n　并中幅降低其物理攻击力。');
INSERT INTO unit_enemy_data VALUES(300203,'利蝎巴鲁托',300203,0,6,270,1000,1,1.5,0,0,0,'【物理】被遗留在失去信仰了的神殿里的天蝎座魔星兽。\n忘却了曾经获得过的虔诚祈祷，如今将带来毁灭。\n\n·对敌方全体造成物理伤害（大）。\n　使用此技能后，\n　每次攻击敌人的时候，利蝎巴鲁托\n　对那名敌人造成的伤害\n　都会提升。\n　这个效果最多可以累积三次。\n·以前方一名敌人为中心，造成小范围\n　物理伤害（中），并赋予中毒状态。\n·解除自身的属性值降低效果，\n　并提升自身的物理防御力。');
INSERT INTO unit_enemy_data VALUES(300204,'利蝎巴鲁托',300204,0,6,270,1000,1,1.5,0,0,0,'【物理】被遗留在失去信仰了的神殿里的天蝎座魔星兽。\n忘却了曾经获得过的虔诚祈祷，如今将带来毁灭。\n\n·对敌方全体造成物理伤害（大）。\n　使用此技能后，\n　每次攻击敌人的时候，利蝎巴鲁托\n　对那名敌人造成的伤害\n　都会提升。\n　这个效果最多可以累积三次。\n·以前方一名敌人为中心，造成小范围\n　物理伤害（中），并赋予中毒状态。\n·解除自身的属性值降低效果，\n　并提升自身的物理防御力。\n·召唤1只利蝎守护者。\n　在召唤时，所有已召唤的利蝎守护者\n　都将被赋予1秒的无敌状态。\n　召唤第3只利蝎守护者时，\n　对敌方全体造成与角色的最大生命值\n　相应的比例物理伤害。\n　当利蝎守护者的生命值降至0时，\n　对利蝎巴鲁托造成固定的物理伤害，\n　并中幅降低其物理攻击力。');
INSERT INTO unit_enemy_data VALUES(300300,'梦魇牡羊',300300,0,7,270,1000,2,2.5,0,0,0,'【魔法】获得强大力量的白羊座魔星兽。\n在阴暗的沉睡监牢中将冒险者的灵魂吞噬殆尽。\n\n·对敌方全体造成魔法伤害（大），对束缚状态的\n　敌人额外造成魔法伤害（小）。\n·对前方范围内的敌人造成魔法伤害（中）。\n·对一名敌人施加束缚状态。');
INSERT INTO unit_enemy_data VALUES(300301,'梦魇牡羊',300301,0,7,270,1000,2,2.5,0,0,0,'【魔法】获得强大力量的白羊座魔星兽。\n在阴暗的沉睡监牢中将冒险者的灵魂吞噬殆尽。\n\n·对敌方全体造成魔法伤害（大），对束缚状态的\n　敌人额外造成魔法伤害（小）。\n·对前方范围内的敌人造成魔法伤害（中）。\n·对一名敌人施加束缚状态。\n·对剩余生命值最低的一名敌人赋予\n  嘲讽状态，并造成魔法伤害（小）。\n  这个技能的异常状态在伤害免疫的情况下\n  也会成功赋予。');
INSERT INTO unit_enemy_data VALUES(300400,'摩羯教主',300400,0,7,270,1000,2,1.8,0,0,0,'企图利用可疑邪教支配世界的摩羯座魔星兽。\n会引诱沙漠里的迷途之人，用恶魔之仪式将其变为俘虏。\n\n·必杀技能对敌方全体造成魔法伤害（大）\n　另外降低物理攻击角色的物理攻击力\n　降低魔法攻击角色的魔法防御力。\n·对物理攻击力最高的一名角色造成魔法伤害（特大），\n　并降低其技能值。\n·对前方两名敌人造成魔法伤害（小），并\n　赋予沉默状态。\n　并对敌方全体造成魔法伤害（中）。');
INSERT INTO unit_enemy_data VALUES(300401,'摩羯教主',300401,0,7,270,1000,2,1.8,0,0,0,'【魔法】在邪教秘术的领域登峰造极的摩羯座魔星兽。\n能随心所欲地操纵其庞大的魔力，剥夺敌人的自由。\n\n·必杀技能对敌方全体造成魔法伤害（大）\n　并降低使用物理攻击角色的物理攻击力\n　降低使用魔法攻击角色的魔法攻击力。\n·对前方一名敌人造成魔法伤害（特大）\n　并赋予沉默状态。\n　同时对敌方全体造成魔法伤害（中）。\n·对物理攻击力最高的一名敌人\n　造成魔法伤害（大），并降低其技能值。');
INSERT INTO unit_enemy_data VALUES(300402,'摩羯教主',300402,0,7,270,1000,2,1.8,0,0,0,'【魔法】在邪教秘术的领域登峰造极的摩羯座魔星兽。\n能随心所欲地操纵其庞大的魔力，剥夺敌人的自由。\n\n·必杀技能对敌方全体造成魔法伤害（大）\n　并降低使用物理攻击角色的物理攻击力\n　降低使用魔法攻击角色的魔法攻击力。\n·对前方一名敌人造成魔法伤害（特大）\n　并赋予沉默状态。\n　同时对敌方全体造成魔法伤害（中）。\n·对物理攻击力最高的一名敌人\n　造成魔法伤害（大），并降低其技能值。\n·当摩羯教主获得毒、猛毒、诅咒、烧伤\n　效果时，赋予自身陷入沉默状态，\n　并受到魔法伤害（大）。\n　没有获得上述效果时，则对敌方全体\n　赋予沉默状态，\n　并造成魔法伤害（大）。');
INSERT INTO unit_enemy_data VALUES(300403,'摩羯教主',300403,0,7,270,1000,2,1.8,0,0,0,'【魔法】在邪教秘术的领域登峰造极的摩羯座魔星兽。\n能随心所欲地操纵其庞大的魔力，剥夺敌人的自由。\n\n·必杀技能对敌方全体造成魔法伤害（大）\n　并降低使用物理攻击角色的物理攻击力\n　降低使用魔法攻击角色的魔法攻击力。\n·对前方一名敌人造成魔法伤害（特大）\n　并赋予沉默状态。\n　同时对敌方全体造成魔法伤害（中）。\n·对物理攻击力最高的一名敌人\n　造成魔法伤害（大），并降低其技能值。\n·当摩羯教主获得毒、猛毒、诅咒、烧伤\n　效果时，赋予自身陷入沉默状态，\n　并受到魔法伤害（大）。\n　没有获得上述效果时，则对敌方全体\n　赋予沉默状态，\n　并造成魔法伤害（大）。');
INSERT INTO unit_enemy_data VALUES(300500,'蜥蜴怪人',300500,0,4,270,1000,1,1.8,0,0,0,'【物理】自由操纵双刀，\n用烟雾弹遮蔽\n对方双眼的\n巨蜥战士。\n\n·随机对一名角色给予\n　中等物理伤害。\n\n·以随机的敌人为中心，给予小范围\n　低等物理伤害，\n　并赋予黑暗状态。');
INSERT INTO unit_enemy_data VALUES(300600,'攀缘花',300600,0,2,270,1000,1,1.8,0,0,0,'【物理】从头上释放剧毒攻击\n并用爬山虎进行范围物理攻击\n一口气夺去靠近的冒险者体力的\n恶魔之花。\n\n·对前方两名角色给予\n　中等物理伤害。\n\n·降低前方三名角色的物理防御力，\n　并赋予中毒状态。');
INSERT INTO unit_enemy_data VALUES(300601,'针刺攀缘花',300601,0,2,270,1000,1,1.8,0,0,0,'【物理】受到异常力量影响而变异的攀缘花。\n使用带有无数尖刺的藤蔓缠绕敌人，令其动弹不得。\n\n·对前方范围内的敌人造成中等物理伤害，\n　并施加束缚状态。\n·降低前方范围内的敌人的物理防御力，\n　并施加中毒状态。');
INSERT INTO unit_enemy_data VALUES(300602,'针刺攀缘花',300602,0,2,270,1000,1,1.8,0,0,0,'【物理】受到异常力量影响而变异的攀缘花。\n使用带有无数尖刺的藤蔓缠绕敌人，令其动弹不得。\n\n·对前方范围内的敌人造成中等物理伤害，\n　并施加束缚状态。\n·降低前方范围内的敌人的物理防御力，\n　并施加中毒状态。');
INSERT INTO unit_enemy_data VALUES(300603,'针刺攀缘花',300603,0,2,270,1000,1,1.8,0,0,0,'【物理】受到异常力量影响而变异的攀缘花。\n\n使用带有无数尖刺的藤蔓缠绕敌人，令其动弹不得。\n\n·对前方两名敌人造成物理伤害（中），\n　并赋予束缚状态。\n·降低前方三名角色的物理防御力，\n　并赋予中毒状态。');
INSERT INTO unit_enemy_data VALUES(300700,'兽人首领',300700,0,13,270,1000,1,1.5,0,0,0,'【物理】身为族长鼓舞群落，\n用足以粉碎大地的连击\n给敌人施加大量伤害的\n顶级兽人。\n\n·以随机敌人为中心，\n　对三名角色造成物理伤害（中），\n　并提升物理防御力。\n\n·提升己方全体的物理攻击力。');
INSERT INTO unit_enemy_data VALUES(300701,'兽人头目',300701,0,13,270,1000,1,1.5,0,0,0,'由于特异力量而变异的兽人首领。\n用巨大的盾牌发出攻防一体的一击。\n\n·对前方范围内的敌人造成大量物理伤害，\n　并赋予眩晕状态。\n·大幅度降低前方范围内敌人的防御力。');
INSERT INTO unit_enemy_data VALUES(300800,'长老角鹿',300800,0,1,270,1000,1,1.8,0,0,0,'【物理】一旦爆发便无法对付，其嘶吼声据说能传到天际，传说中的狂野角鹿。\n\n·对敌方全体造成物理伤害（中），并小幅度击飞。\n\n·对前方三名角色造成物理伤害（中），并降低敌人的物理攻击力和物理防御力。');
INSERT INTO unit_enemy_data VALUES(300801,'灵魂角鹿',300801,0,1,270,3000,1,1.8,0,0,0,'【物理】由于特异力量而变异的狂野角鹿。\n将犄角中注入魔力，释放比打击更强力的一击。\n\n·对前方范围内的敌人造成魔法伤害（大），\n 将其击退的同时使其眩晕。\n·对前方范围的敌人造成魔法伤害（小），\n 同时降低其魔法防御与攻击速度。');
INSERT INTO unit_enemy_data VALUES(300900,'暴君寿龟',300900,0,13,270,1000,1,1.8,0,0,0,'【物理】如岩山般进化的巨大寿龟。\n强力无比的头槌可以轻易地击碎大地。\n\n·必杀技能对前方范围内造成物理伤害，并使其麻痹。\n·降低前方3名角色的物理防御力。');
INSERT INTO unit_enemy_data VALUES(301000,'海龙',301000,0,14,270,1000,1,1.8,0,0,0,'【物理】用强大的冰之吐息瞬间将前卫冰封并咬碎其身体的凶猛深海龙。\n\n·对前三名敌人造成物理伤害（中）。\n\n·对前两名敌人造成物理伤害（中），并赋予冻结状态。');
INSERT INTO unit_enemy_data VALUES(301001,'海龙',301001,0,14,270,1000,1,1.8,0,0,0,'【物理】用强大的冰之吐息瞬间将前卫冰封并咬碎其身体的凶猛深海龙。\n\n·对前方三名敌人造成物理伤害（中）。\n·对前方一名角色造成物理伤害（中），并赋予冻结状态。');
INSERT INTO unit_enemy_data VALUES(301002,'海龙',301002,0,14,270,1000,1,1.8,0,0,0,'【物理】用强大的冰之吐息瞬间将前卫冰封并咬碎其身体的凶猛深海龙。\n\n·对前方三名敌人造成物理伤害（中）。\n·对前方两名角色造成物理伤害（中），并赋予冻结状态。');
INSERT INTO unit_enemy_data VALUES(301100,'野蛮魔熊',301100,0,13,270,1000,1,1.7,0,0,0,'【物理】用不符体格的敏捷与咬噬攻击夺走对方的体力，用巨大的利爪撕裂敌人。残忍的森之统治者。\n\n·对前方四名角色造成物理伤害（大）。\n\n·对前方一名角色造成物理伤害（小），并降低其物理攻击力。');
INSERT INTO unit_enemy_data VALUES(301101,'狂乱魔熊',301101,0,13,270,1000,1,2.0,0,0,0,'由于特异力量而变异的野蛮魔熊。\n注入怒气的强壮手臂将敌人连同地面一齐撕裂。\n\n·对前方范围内的敌人造成物理伤害（大），\n　并施加眩晕状态。\n·对前方的一名敌方角色造成物理伤害（小），\n　并中幅降低目标的物理攻击力。');
INSERT INTO unit_enemy_data VALUES(301200,'巨型魔像',301200,0,13,270,1000,1,1.6,0,0,0,'【物理】能召唤出无限的魔像，以岩针同时削减敌人的体力与精神。并能以魔力将无数的魔像残骸重构起来的巨大魔像。\n\n·对前方四名敌人造成物理伤害（中），并降低其技能值。\n\n·召唤魔像。');
INSERT INTO unit_enemy_data VALUES(301201,'水晶魔像',301201,0,13,270,1000,1,1.3,0,0,0,'【物理】以月神魔力的结晶包裹全身的巨大魔像。\n附着于全身的结晶既如坚实的盾牌，又如可穿刺一切的锐矛。\n·当水晶魔像获得毒、猛毒、诅咒、烧伤状态时，\n　行动速度将大幅降低。\n·解除自身所有的减益效果，\n　并大幅提升物理防御力与魔法防御力。\n　此外，中幅提升我方除自身以外同伴的\n　物理防御力与魔法防御力。\n·对敌方全体造成物理伤害（中），使其眩晕。\n·对前方范围内的敌人造成物理伤害（大），\n　并小幅提升我方全体的\n　物理攻击力与魔法攻击力。\n　同时中幅回复自身技能值。');
INSERT INTO unit_enemy_data VALUES(301300,'暴食魔兽',301300,0,4,270,1000,1,3.0,0,0,0,'天秤座魔星兽是恶名昭彰的暴食凶兽。\n衡量夺走的灵魂的善恶，喜欢享受其味道的差异。\n\n·对来自敌人的攻击而造成的麻痹、眩晕、束缚、\n　降低行动速度等效果没有抗性。\n·对敌方全体造成魔法伤害（大），并\n　特大幅度提升自身的物理攻击力与魔法攻击力。\n·对后方范围内的敌人造成魔法伤害（中）。\n·对前方范围内的敌人造成魔法伤害（中），\n　并小幅降低其技能值。　同时中幅回复\n　自身技能值。');
INSERT INTO unit_enemy_data VALUES(301301,'暴食魔兽',301301,0,4,270,1000,2,3.0,0,0,0,'【魔法】吞噬一切灵魂的天秤座魔星兽。\n其天秤与严正公平相距甚远，更加倾向于暴食兽的食欲。\n\n·对敌方全体造成魔法伤害（大），并\n　特大幅度提升自身的物理攻击力与魔法攻击力。\n·对后方两名角色造成魔法伤害（中）。\n·对前方三名角色造成魔法伤害（中），\n　并小幅降低其技能值。同时中幅提升自身技能值。');
INSERT INTO unit_enemy_data VALUES(301302,'暴食魔兽',301302,0,4,270,1000,2,3.0,0,0,0,'【魔法】吞噬一切灵魂的天秤座魔星兽。\n其天秤与严正公平相距甚远，更加倾向于暴食兽的食欲。\n\n·对敌方全体造成魔法伤害（大），并\n　特大幅度提升自身的物理攻击力与魔法攻击力。\n·对前方三名角色造成魔法伤害（中），\n　并小幅降低其技能值。　同时中幅提升自身技能值。\n·对后方两名角色造成魔法伤害（中）。\n·对前方三名敌人赋予束缚状态，\n　并造成魔法伤害（大）。\n　当自身的目标被击破时，\n　特大幅度提升自身的魔法攻击力。');
INSERT INTO unit_enemy_data VALUES(301303,'暴食魔兽',301303,0,4,270,1000,2,3.0,0,0,0,'【魔法】吞噬一切灵魂的天秤座魔星兽。\n其天秤与严正公平相距甚远，更加倾向于暴食兽的食欲。\n\n·对敌方全体造成魔法伤害（大），并\n　特大幅度提升自身的物理攻击力与魔法攻击力。\n·对后方两名角色造成魔法伤害（中）。\n·对前方三名角色造成魔法伤害（中），\n　并小幅降低其技能值。同时中幅提升自身技能值。');
INSERT INTO unit_enemy_data VALUES(301304,'暴食魔兽',301304,0,4,270,1000,2,3.0,0,0,0,'【魔法】吞噬一切灵魂的天秤座魔星兽。\n其天秤与严正公平相距甚远，更加倾向于暴食兽的食欲。\n\n·对敌方全体造成魔法伤害（大），并\n　特大幅度提升自身的物理攻击力与魔法攻击力。\n·对前方三名角色造成魔法伤害（中），\n　并小幅降低其技能值。　同时中幅提升自身技能值。\n·对前方三名敌人赋予束缚状态，\n　并造成魔法伤害（大）。\n　当自身的目标被击破时，\n　特大幅度提升自身的魔法攻击力。');
INSERT INTO unit_enemy_data VALUES(301400,'美杜莎',301400,0,3,270,1000,1,2.5,0,0,0,'头发由无数毒蛇构成的处女座魔星兽。\n悲恋的痛苦使其拒绝一切，并将眼前的敌人都变成石头。\n\n·对敌方全体造成魔法伤害（大），并对\n　前方范围内的敌人赋予石化状态。\n·对前方范围内的敌人造成物理伤害，\n　大幅降低石化状态敌人的技能值。\n·对物理攻击力最高的一名敌人造成魔法伤害（大），\n　并赋予中毒状态。');
INSERT INTO unit_enemy_data VALUES(301401,'美杜莎',301401,0,3,270,1000,1,2.5,0,0,0,'【魔法】因无止境的爱憎而疯狂的处女座魔星兽。\n以惊人的嫉妒与抗拒心，让所见的一切全部石化。\n\n·对敌方全体造成魔法伤害（大），并对\n　前方范围内的敌人赋予石化状态。\n　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n·对前方范围内的敌人造成物理伤害（大），\n　大幅降低石化状态敌人的技能值。\n·以后方第二名敌人为中心，造成小范围\n　魔法伤害（小），并赋予毒状态。');
INSERT INTO unit_enemy_data VALUES(301402,'美杜莎',301402,0,3,270,1000,1,2.5,0,0,0,'【魔法】因无止境的爱憎而疯狂的处女座魔星兽。\n以惊人的嫉妒与抗拒心，让所见的一切全部石化。\n\n·对敌方全体造成魔法伤害（大），并对\n　前方范围内的敌人赋予石化状态。\n　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n·对前方范围内的敌人造成物理伤害（大），\n　大幅降低石化状态敌人的技能值。\n·以后方第二名敌人为中心，造成小范围\n　魔法伤害（小），并赋予毒状态。\n·以后方第二名敌人为中心，造成小范围\n　魔法伤害（小），并对剩余生命值较低的\n　敌人赋予石化状态。');
INSERT INTO unit_enemy_data VALUES(301500,'魔界人狼',301500,0,12,270,1000,1,1.8,0,0,0,'【物理】以远吠麻痹猎物，趁其畏惧时以爪子撕裂对方。阻断了不计其数的冒险者的去路，是穷凶极恶的魔界之狼。\n\n·随机对两名敌人造成物理伤害（大）。\n\n·对前方三名敌人造成魔法伤害（小），并赋予麻痹状态。');
INSERT INTO unit_enemy_data VALUES(301501,'魔界人狼',301501,0,12,270,1000,1,1.8,0,0,0,'【物理】以远吠麻痹猎物，趁其畏惧时以爪子撕裂。顺从自身的欲望，极尽残暴之事的魔狼。\n\n·对前方两名角色造成物理伤害（大）。\n·对敌方全体造成魔法伤害（中），并对前方一名敌人赋予麻痹状态。这个技能的异常状态在伤害免疫的情况下也会成功赋予。');
INSERT INTO unit_enemy_data VALUES(301600,'基兹',301600,0,3,270,1000,1,1.8,0,0,0,'【物理】卷起暴风的大怪鸟。\n从魔物图鉴中逃出，大肆进行破坏。\n\n·对敌方全体施加与角色最大生命值对应的\n　大幅伤害。\n·\n　对前方范围内的敌人施加中幅物理伤害，\n　并令其眩晕。');
INSERT INTO unit_enemy_data VALUES(301601,'基兹',301600,0,3,270,1000,1,1.8,0,0,0,'【物理】卷起暴风的大怪鸟。\n从魔物图鉴中逃出，大肆进行破坏。\n\n·给敌方全体施加与角色最大生命值对应的\n　大幅伤害。\n·\n　给前方范围内的敌人施加中等物理伤害，\n　并令其眩晕。\n·给前方范围内的敌人施加与角色最大生命值对应的\n　中幅伤害，降低其物理防御力。\n·基兹剩余生命值低于50%时\n　攻击会更加凶猛。');
INSERT INTO unit_enemy_data VALUES(301700,'炸脖龙',301700,0,3,270,1000,1,2.6,0,0,0,'【物理】栖息于毒瘴的暗棱顶部的丑恶狂兽。\n通过剧毒的瘴气夺取生命，啃食尸体作为食粮。\n\n·必杀技能对敌方全体造成魔法伤害（大），\n赋予中毒状态并击飞。\n·对前方两名角色造成物理伤害，\n并降低其物理攻击力和技能值。\n·炸脖龙自身剩余生命值低于40%时，\n攻击会更加猛烈，同时还会召集3只怪兽。\n·当剩余时间小于20秒时，赋予敌方全体强力中毒状态，特大幅降低自身技能值。');
INSERT INTO unit_enemy_data VALUES(301800,'人马射手',301800,0,3,270,1000,1,2.0,0,0,0,'射手座的魔星兽，射杀众多翱翔于天空中的目标。\n身怀绝技，缠绕着雷光的箭可在刹那间穿透一切。\n\n·必杀技能根据敌方全体的最大生命值，\n　造成魔法伤害。\n　对处于黑暗状态的敌人造成3倍伤害。\n·对敌方全体造成魔法伤害（中），同时，\n　对剩余生命值比例最少的一名敌方角色赋予强力的黑暗状态。\n·对前方的三名敌方角色造成物理伤害。');
INSERT INTO unit_enemy_data VALUES(301801,'人马射手',301801,0,3,270,1000,1,2.0,0,0,0,'【物理】守护星穹神殿的射手座魔星兽。\n其能够射穿万物的一闪，是紫电闪烁的神域绝技。\n\n·必杀技能会根据敌方全体的最大生命值\n　造成魔法伤害，\n　并特大幅度降低其魔法攻击力。\n　此外，提升对处于黑暗状态的敌人造成的伤害。\n·对敌方全体造成魔法伤害（中），\n并对剩余生命值最少的一名敌人\n　赋予黑暗状态。\n·对前方三名敌人造成物理伤害。');
INSERT INTO unit_enemy_data VALUES(301802,'人马射手',301802,0,3,270,1000,1,2.0,0,0,0,'【物理】守护星穹神殿的射手座魔星兽。\n其能够射穿万物的一闪，是紫电闪烁的神域绝技。\n\n·必杀技能会根据敌方全体的最大生命值\n　造成魔法伤害，\n　并特大幅度降低其魔法攻击力。\n　此外，提升对处于黑暗状态的敌人造成的伤害。\n·对敌方全体造成魔法伤害（中），\n并对剩余生命值最少的一名敌人赋予黑暗状态。\n·对前方三名敌人造成物理伤害。\n·在自己的箭矢中增加1个“黑星”，\n·对魔法攻击力最高的一名敌人造成\n　物理伤害（特大），并根据“黑星”的数量\n　赋予追加效果。\n　当“黑星”的数量为2时\n　大幅降低目标物理防御力。\n　当“黑星”的数量为3时\n　提升该技能的威力。');
INSERT INTO unit_enemy_data VALUES(301900,'虎鲸猎手',301900,0,11,270,1000,1,1.7,0,0,0,'【物理】用敏锐的眼神搜寻\n猎物与海浪的海之猎手。\n用魔力产生无限的潮水\n削弱敌人的武器与气力。 \n\n·对前方四名角色造成少量物理伤害\n　并降低物理攻击力。\n\n·对前方两名角色造成少量魔法伤害\n　并削减其技能值。');
INSERT INTO unit_enemy_data VALUES(301901,'鲸王猎手',301901,0,11,270,1000,1,1.5,0,0,0,'【物理】因月神魔力而变得凶恶的大海猎手。\n其千锤百炼的身体能将水流化为刀刃进行攻击。\n\n·对前方四名敌人造成物理伤害（大）\n　并中幅降低其物理攻击力。同时，\n　小幅提升我方全体的技能值。\n·对前方小范围的敌方造成魔法伤害（中），\n　并大幅降低其技能值。\n·以后方第二名敌人为中心，造成\n　小范围的物理伤害（中），\n　并大幅降低其魔法攻击力。');
INSERT INTO unit_enemy_data VALUES(302000,'野性狮鹫',302000,0,11,270,1000,1,1.6,0,0,0,'在残酷环境中成长进化的狮鹫。\n使用龙卷风将猎物卷起到空中进行狩猎。\n\n・对前方范围内的敌人造成魔法伤害（中）。');
INSERT INTO unit_enemy_data VALUES(302001,'野性狮鹫',302001,0,11,270,1000,1,1.6,0,0,0,'在残酷环境中成长进化的狮鹫。\n使用龙卷风将猎物卷起到空中进行狩猎。\n\n・对前方范围内的敌人造成魔法伤害（中）。');
INSERT INTO unit_enemy_data VALUES(302100,'双足飞龙',302100,0,11,270,1000,1,1.6,0,0,0,'张开巨大的飞翼翱翔天空的翼龙。\n能够喷射巨大的火球，阻挡侵入者。\n\n·对前方范围内的敌人造成中等物理伤害。');
INSERT INTO unit_enemy_data VALUES(302101,'双足飞龙',302101,0,11,270,1000,1,1.6,0,0,0,'张开巨大的飞翼翱翔天空的翼龙。\n能够喷射巨大的火球，阻挡侵入者。\n\n·对前方范围内的敌人造成中等物理伤害。');
INSERT INTO unit_enemy_data VALUES(302200,'依科托·格拉迪恩',302200,0,16,270,1000,2,1.6,0,0,0,'【魔法】借由露娜魔力而觉醒的古代机械兵器。\n毫不留情地释放出魔力来阻止入侵者。\n\n·对敌方全体造成特大魔法伤害\n　并大幅度降低敌人的魔法防御力。\n·对前方范围内的敌人造成大量物理伤害\n·对前方范围内的敌人造成大量魔法伤害\n　并赋予麻痹状态。');
INSERT INTO unit_enemy_data VALUES(302300,'聪慧猫头鹰',302300,0,16,270,1000,2,1.8,0,0,0,'【魔法】栖息于大树顶端，绝顶聪明的大枭。\n融会森林生者的才智，能巧妙地施展魔术。\n\n·必杀技能对敌方全体造成魔法伤害。\n·随机对1名角色造成物理伤害，\n并使其麻痹。');
INSERT INTO unit_enemy_data VALUES(302400,'方舟守护者',302400,0,16,270,1000,1,1.6,0,0,0,'【物理】自远古便守护此遗迹至今的人工生命体。\n借由制造者的秘术，获得了能永久行动的无限动力。\n\n·必杀技能对敌方全体造成魔法伤害（大）。\n·对前方范围造成物理伤害。\n·对敌方全体造成魔法伤害，同时赋予\n沉默状态。');
INSERT INTO unit_enemy_data VALUES(302500,'奇美拉',302500,0,16,270,1000,1,1.4,0,0,0,'【物理】镇守于孤塔顶部的暴虐混成魔兽。\n其千变万化的猛烈攻击已屠虐了众多挑战者。\n\n·必杀技能会对前方范围内造成\n物理伤害（大），并将其击退。\n·对前方三名角色造成物理伤害，并赋予烧伤状态。\n·对敌方全体造成魔法伤害，并降低其\n行动速度、物理防御力、魔法防御力。\n·当剩余时间小于10秒时，对敌方全体造成\n魔法伤害（特大），特大幅降低自身技能值。');
INSERT INTO unit_enemy_data VALUES(302600,'双子魔猪',302600,0,16,270,1000,1,1.6,0,0,0,'【物理】获得强大力量的双子座魔星兽。\n一边在前方防御，一边以登峰造极的招数进行攻击。\n\n·对敌方全体造成与其最大生命值\n　相应的大量伤害。\n·对敌人的后卫造成物理伤害（中），\n　并小幅降低其攻击速度。\n·对敌方全体造成物理伤害（小）。');
INSERT INTO unit_enemy_data VALUES(302601,'双子魔猪',302601,0,16,270,1000,1,1.6,0,0,0,'【物理】获得强大力量的双子座魔星兽。\n一边在前方防御，一边以登峰造极的招数进行攻击。\n\n·对敌方全体造成与其最大生命值\n　相应的大量伤害。\n·对敌人的后卫造成物理伤害（中），\n　并小幅降低其攻击速度。\n·对敌方全体造成物理伤害（小），\n·暴食中，两只都极大幅度降低自身的\n　物理防御力与魔法防御力。\n　之后极大幅度提升自身的技能值上升量与暴击率\n·并对敌方全体造成\n　物理伤害（大）。');
INSERT INTO unit_enemy_data VALUES(302700,'巨钳魔蟹',302700,0,16,270,1000,1,1.8,0,0,0,'【物理】在船墓的入海口筑巢的巨蟹座魔星兽。\n其巨大钳子造成的一击，击沉过无数船只。\n\n·对敌方全体造成魔法伤害（大），\n　并降低其物理防御力。\n·对前方范围内的所有敌人造成物理伤害（中）\n　并赋予眩晕状态。\n·对敌方全体造成物理伤害（中）。');
INSERT INTO unit_enemy_data VALUES(302701,'巨钳魔蟹',302701,0,16,270,1000,1,1.8,0,0,0,'【物理】击沉过无数船只的巨蟹座魔星兽。\n大海的霸者可斩断万物、压垮万物、歼灭万物。\n\n·对敌方全体造成魔法伤害（大），\n　并降低其物理防御力。\n·对前方范围内的所有敌人造成物理伤害（中）\n　并赋予眩晕状态。\n·对敌方全体造成物理伤害（中）。');
INSERT INTO unit_enemy_data VALUES(302702,'巨钳魔蟹',302702,0,16,270,1000,1,1.8,0,0,0,'【物理】击沉过无数船只的巨蟹座魔星兽。\n大海的霸者可斩断万物、压垮万物、歼灭万物。\n\n·对敌方全体造成魔法伤害（大），\n　并降低其物理防御力。\n·对前方范围内的所有敌人造成物理伤害（中）\n　并赋予眩晕状态。\n·对敌方全体造成物理伤害（中）。\n·在一段时间内改变自身的形态，特大幅度\n　提升自身的魔法攻击力，将所有的技能\n　都转变为魔法攻击。\n　在此期间，特大幅度降低自身的\n　物理防御力与魔法防御力。');
INSERT INTO unit_enemy_data VALUES(302800,'炎吼狮王',302800,0,16,270,1000,1,1.8,0,0,0,'以草原霸主闻名四方的狮子座魔星兽。\n灼热的咆哮能够燃尽一切向其举起刀刃的敌人。\n\n·无视防御力，对敌方全体造成与其最大生命值相应的\n　物理伤害（大），并对剩余生命值较低的敌人\n　赋予烧伤状态。\n·对一名敌人造成物理伤害（大），\n　当目标处于烧伤状态时，追加伤害。\n·对前方范围内的敌人造成物理伤害（中），　当目标处于烧伤状态时，追加伤害。');
INSERT INTO unit_enemy_data VALUES(302801,'炎吼狮王',302801,0,16,270,1000,1,3.0,0,0,0,'【物理】君临于荒凉原野上的狮子座魔星兽。\n其利爪可切裂空气，能喷出业火的咆哮可烧灼大地。\n\n·无视防御力，对敌方全体造成与其最大生命值相应的\n　物理伤害（大），并对剩余生命值较低的敌人\n　赋予烧伤状态。\n·对一名敌人造成物理伤害（大），\n　当目标处于烧伤状态时，追加伤害。\n·对前方范围内的敌人造成物理伤害（中），　当目标处于烧伤状态时，追加伤害。');
INSERT INTO unit_enemy_data VALUES(302802,'炎吼狮王',302802,0,16,270,1000,1,3.0,0,0,0,'【物理】君临于荒凉原野上的狮子座魔星兽。\n其利爪可切裂空气，能喷出业火的咆哮可烧灼大地。\n\n·无视防御力，对敌方全体造成与其最大生命值相应的\n　物理伤害（大），并对剩余生命值较低的敌人\n　赋予烧伤状态。\n·对一名敌人造成物理伤害（大），\n　当目标处于烧伤状态时，追加伤害。\n·对前方范围内的敌人造成物理伤害（中），　当目标处于烧伤状态时，追加伤害。\n·无视防御力，对敌方全体造成与其最大生命值相应的\n　魔法伤害（中），并使其眩晕。\n　剩余生命值较少的敌人有更高概率被眩晕。');
INSERT INTO unit_enemy_data VALUES(302900,'阿克艾利欧斯',302900,0,16,270,1000,1,1.8,0,0,0,'被寄宿在神瓶中的魔力所吸收的水瓶座魔星兽。\n沉溺于强大的力量，为了支配大海而肆虐。\n\n·必杀技能对敌方全体造成共计6次的\n　魔法伤害（小）。\n　当角色的生命值低于80%时造成击退效果，\n　当低于40%时进一步击退并\n　赋予眩晕状态。\n·对眼前的三名敌方角色造成物理伤害（中）。\n·解除自身的属性值降低效果和敌方全体的\n　属性值提升效果，并提升自身的\n　技能值。');
INSERT INTO unit_enemy_data VALUES(302901,'阿克艾利欧斯',302901,0,16,270,1000,1,1.8,0,0,0,'【物理】操纵着寄宿在神瓶中魔力的水瓶座魔星兽。\n作为能吞噬一切激流的沧海的统治者君临此地。\n\n·必杀技能对敌方全体造成共计6次的\n　魔法伤害（小）。\n　当角色的生命值低于80%时造成击退效果，\n　当低于40%时进一步击退并\n　赋予眩晕状态。\n·对眼前一名敌人造成物理伤害（特大）\n·对前方第二名、第三名敌人造成物理伤害（大）。\n　解除敌方全体的属性提升效果，\n　小幅提升自身的技能值、物理防御力和魔法防御力。');
INSERT INTO unit_enemy_data VALUES(302902,'阿克艾利欧斯',302902,0,16,270,1000,1,1.8,0,0,0,'【物理】操纵着寄宿在神瓶中魔力的水瓶座魔星兽。\n作为能吞噬一切激流的沧海的统治者君临此地。\n\n·必杀技能对敌方全体造成共计6次的\n　魔法伤害（小）。\n　当角色的生命值低于80%时造成击退效果，\n　当低于40%时进一步击退并\n　赋予眩晕状态。\n·对眼前一名敌人造成物理伤害（特大）\n·对前方第二名、第三名敌人造成物理伤害（大）。\n　解除敌方全体的属性提升效果，\n　小幅提升自身的技能值、物理防御力和魔法防御力。\n\n【战斗领域效果】\n经过一段时间后，产生激流。\n·对队伍全体连续造成魔法伤害（小）。\n　当角色的生命值低于40%时，\n　造成击退效果。\n　当阿克艾利欧斯的目标被击破时，\n　对阿克艾利欧斯造成固定的魔法伤害，\n　并小幅降低其技能值。\n　当阿克艾利欧斯的目标没有被击破时，\n　特大幅度提升阿克艾利欧斯的\n　魔法攻击力。');
INSERT INTO unit_enemy_data VALUES(302903,'阿克艾利欧斯',302903,0,16,270,1000,1,1.8,0,0,0,'【物理】操纵着寄宿在神瓶中魔力的水瓶座魔星兽。\n作为能吞噬一切激流的沧海的统治者君临此地。\n\n·必杀技能对敌方全体造成共计6次的\n　魔法伤害（小）。\n　当角色的生命值低于80%时造成击退效果，\n　当低于40%时进一步击退并\n　赋予眩晕状态。\n·对眼前一名敌人造成物理伤害（特大）\n·对前方第二名、第三名敌人造成物理伤害（大）。\n　解除敌方全体的属性提升效果，\n　小幅提升自身的技能值、物理防御力和魔法防御力。\n\n【战斗领域效果】\n经过一段时间后，产生激流。\n·对队伍全体连续造成魔法伤害（小）。\n　当角色的生命值低于40%时，\n　造成击退效果。\n　当阿克艾利欧斯的目标被击破时，\n　对阿克艾利欧斯造成固定的魔法伤害，\n　并小幅降低其技能值。\n　当阿克艾利欧斯的目标没有被击破时，\n　特大幅度提升阿克艾利欧斯的\n　魔法攻击力。');
INSERT INTO unit_enemy_data VALUES(303000,'托尔佩顿',303000,0,16,270,1000,1,1.8,0,0,0,'在沧海间遨游，唤来暴风雨的双鱼座魔星兽。\n围在身边的手下们那红色鱼鳍正是不服来战的高傲证明。\n\n·容易受到物理攻击力提升·下降\n　的效果。\n·必杀技能对敌方全体造成物理伤害（大）\n　并提升自身的行动速度\n　与物理攻击力。\n·对敌方全体造成物理伤害（小）。\n·对前方两名敌人造成物理伤害（中）\n　并小幅降低其物理防御力。');
INSERT INTO unit_enemy_data VALUES(303001,'托尔佩顿',303001,0,16,270,1000,1,1.8,0,0,0,'【物理】背负大海而屹立的暴走双鱼座魔星兽。\n被那背影所打动的小弟们，都自愿与其共同追求霸道。\n\n·容易受到物理攻击力提升·下降的\n　效果影响。\n·必杀技能对敌方全体造成物理伤害（大）\n　并提升自身的行动速度与物理攻击力。\n\n·对敌方全体造成物理伤害（小）。\n·对前方两名敌人造成物理伤害（中）\n　并小幅降低其物理防御力。');
INSERT INTO unit_enemy_data VALUES(303002,'托尔佩顿',303002,0,16,270,1000,1,1.8,0,0,0,'【物理】背负大海而屹立的暴走双鱼座魔星兽。\n被那背影所打动的小弟们，都自愿与其共同追求霸道。\n\n·容易受到物理攻击力提升·下降的\n　效果影响。\n·必杀技能对敌方全体造成物理伤害（大）\n　并提升自身的行动速度与物理攻击力。\n\n·对敌方全体造成物理伤害（小）。\n·对前方两名敌人造成物理伤害（中）\n　并小幅降低其物理防御力。\n·召唤1只旗手小弟。\n　旗手小弟可以回复托尔佩顿的技能值。\n　当旗手小弟的生命值为0时，\n　大幅提升我方全体的物理攻击力\n　与魔法攻击力');
INSERT INTO unit_enemy_data VALUES(303003,'托尔佩顿',303003,0,16,270,1000,1,1.8,0,0,0,'【物理】背负大海而屹立的暴走双鱼座魔星兽。\n被那背影所打动的小弟们，都自愿与其共同追求霸道。\n\n·容易受到物理攻击力提升·下降的\n　效果影响。\n·必杀技能对敌方全体造成物理伤害（大）\n　并提升自身的行动速度与物理攻击力。\n\n·对敌方全体造成物理伤害（小）。\n·对前方两名敌人造成物理伤害（中）\n　并小幅降低其物理防御力。\n·召唤1只旗手小弟。\n　旗手小弟可以回复托尔佩顿的技能值。\n　当旗手小弟的生命值为0时，\n　大幅提升我方全体的物理攻击力\n　与魔法攻击力');
INSERT INTO unit_enemy_data VALUES(303004,'托尔佩顿',303002,0,16,270,1000,1,1.8,0,0,0,'【物理】背负大海而屹立的暴走双鱼座魔星兽。\n被那背影所打动的小弟们，都自愿与其共同追求霸道。\n\n·容易受到物理攻击力提升·下降的\n　效果影响。\n·必杀技能对敌方全体造成物理伤害（大）\n　并提升自身的行动速度与物理攻击力。\n\n·对敌方全体造成物理伤害（小）。\n·对前方两名敌人造成物理伤害（中）\n　并小幅降低其物理防御力。\n·召唤1只旗手小弟。\n　旗手小弟可以回复托尔佩顿的技能值。\n　当旗手小弟的生命值为0时，\n　大幅提升我方全体的物理攻击力\n　与魔法攻击力');
INSERT INTO unit_enemy_data VALUES(303100,'冬眠魔熊',303100,0,13,270,1000,1,3.5,0,0,0,'【物理】终生藏身在冰穴最深处冬眠的巨熊。在睡梦中度过了十分漫长的岁月，却会在狩猎猎物的瞬间醒来。\n\n·对敌方全体造成魔法伤害（大），并降低物理攻击力。\n\n·对前方三名角色造成物理伤害（小）。同时，大幅度击飞前方一名角色，并赋予冻结状态，使其移动速度下降。');
INSERT INTO unit_enemy_data VALUES(303200,'冰雪夫人',303200,0,6,270,1000,1,1.6,0,0,0,'【物理】与暴风雪一同出现，\n召唤大军袭击扰乱地盘的人。\n雪国第一华美夫人。\n\n·对前方四名角色造成魔法伤害（中）。\n\n·对随机两名角色造成魔法伤害（小）\n　并赋予冻结状态。');
INSERT INTO unit_enemy_data VALUES(303300,'滴水嘴兽',303300,0,13,270,1000,1,2.1,0,0,0,'【物理】留在腐朽的遗迹保护无主之地的神秘鸟型石像。如今街道和命令都消失成沙，不管是谁它都会喷出火焰袭击对方。\n\n·对前方5名角色造成魔法伤害（中），并赋予烧伤状态。同时降低敌人的魔法防御力。\n\n·对随机三名角色造成魔法伤害（小），并赋予黑暗状态。');
INSERT INTO unit_enemy_data VALUES(303301,'暗黑滴水嘴兽',303301,0,13,270,1000,1,2.1,0,0,0,'【物理】侍奉着暴食君主的近卫黑铁像。\n徘徊于冥府，巧妙地操纵灵魂，为主人奉献。\n\n·对敌方全体造成魔法伤害（中），赋予烧伤状态\n　并特大幅度降低其魔法防御力。\n·对敌方全体造成魔法伤害（小），\n　并大幅降低其物理攻击力，特大幅度降低其\n　魔法攻击力。');
INSERT INTO unit_enemy_data VALUES(303302,'暗黑滴水嘴兽',303302,0,13,270,1000,1,2.1,0,0,0,'【物理】侍奉着暴食君主的近卫黑铁像。\n徘徊于冥府，巧妙地操纵灵魂，为主人奉献。\n\n·对敌方全体造成魔法伤害（中），赋予烧伤状态\n　并特大幅度降低其魔法防御力。\n·对敌方全体造成魔法伤害（小），\n　并大幅降低其物理攻击力，特大幅度降低其\n　魔法攻击力。');
INSERT INTO unit_enemy_data VALUES(303400,'沙之槌',303400,0,13,270,1000,1,1.9,0,0,0,'【物理】君临生存竞争激烈的沙漠\n顶端的巨大蜥蜴。\n利用毒、活用沙子的战法是在\n残酷的环境中习得的生存术。\n\n·对敌方全体造成中等物理\n　伤害，并降低其行动速度。\n　同时提升己方全体的物理\n　攻击力。\n\n·以前方一名角色为中心，对小范围\n　内的敌人造成少量物理伤害，\n　并赋予中毒状态。\n　同时，降低敌人的物理防御力。');
INSERT INTO unit_enemy_data VALUES(303401,'巨型蜥蜴',303401,0,13,270,1000,1,1.9,0,0,0,'【物理】带着露娜魔力而变异的巨蜥。\n硬刺状的鳞片可以削弱敌人的体力和防御。\n\n·对敌方全体造成物理伤害（大），\n　同时削减技能值。\n·对前方两名角色造成物理伤害（中），\n　并赋予中毒状态。\n·对前方三名角色造成物理伤害（中）。\n　同时对前方一名角色造成物理伤害（大），\n　并中幅降低其物理防御力\n　和魔法防御力。');
INSERT INTO unit_enemy_data VALUES(303402,'巨型蜥蜴',303402,0,13,270,1000,1,1.9,0,0,0,'【物理】带着露娜魔力而变异的巨蜥。\n硬刺状的鳞片可以削弱敌人的体力和防御。\n\n·对敌方全体造成物理伤害（大），\n　同时削减技能值。\n·对前方两名角色造成物理伤害（中），\n　并赋予中毒状态。\n·对前方三名角色造成物理伤害（中）。\n　同时对前方一名角色造成物理伤害（大），\n　并中幅降低其物理防御力\n　和魔法防御力。');
INSERT INTO unit_enemy_data VALUES(303500,'怒蛇',303500,0,13,270,1000,1,1.8,0,0,0,'【物理】凭借持续吸收其他生物\n的魔力来掌握妖术的魔物。\n从敌人身上的伤口处夺取魔力，\n进而获得更多的力量。\n\n·对敌方全体造成魔法伤害（中）\n　削减所有使用魔法攻击的\n　敌人的技能值。\n\n·以前方一名角色为中心，\n　对小范围敌人造成魔法伤害（小），\n　并降低魔法防御力。');
INSERT INTO unit_enemy_data VALUES(303501,'怒蛇',303501,0,13,270,1000,1,1.8,0,0,0,'【物理】凭借持续吸收其他生物\n的魔力来掌握妖术的魔物。\n从敌人身上的伤口处夺取魔力，\n进而获得更多的力量。\n\n·对敌方全体造成魔法伤害（中）\n　削减所有使用魔法攻击的\n　敌人的技能值。\n\n·对前方两名角色造成魔法伤害（小），\n　并降低其魔法防御力。');
INSERT INTO unit_enemy_data VALUES(303600,'熔岩龙',303600,0,13,270,1000,1,1.7,0,0,0,'【物理】凶猛的恐龙，使用强韧的脚\n和锐利的爪子来瞬间捕获猎物。\n在毁灭的大地上以岩浆为食，\n孤高地生存着。\n\n·对敌方全体造成中等物理伤害\n　提升自身的行动速度。\n\n·对前方三名角色造成中等\n　物理伤害，并使其行动速度下降。');
INSERT INTO unit_enemy_data VALUES(303700,'地狱犬',303700,0,14,270,1000,1,0.7,0,0,0,'【物理】坠入火山后，全身缠绕着\n地狱的火焰而复苏的双头魔犬。\n与爱犬波尼一同在领地驰骋，\n用灼热的火焰来招待入侵者。\n\n·对敌方全体给予中等物理伤害，\n　并赋予烧伤状态。同时回复\n　召唤的怪物的生命值。\n\n·召唤波尼。');
INSERT INTO unit_enemy_data VALUES(303800,'阿尔玛',303800,0,16,270,1000,1,2.0,0,0,0,'【物理】从远古开始守护迷宫的防卫兵器。静静等待跨越所有陷阱，侵入最深处的侵入者。\n·根据角色的最大生命值，对敌方全体给予\n　中等魔法伤害。\n·对前方范围内的敌人给予中等物理伤害。');
INSERT INTO unit_enemy_data VALUES(303801,'阿尔玛',303800,0,16,270,1000,1,2.0,0,0,0,'【物理】从远古开始守护迷宫的防卫兵器。静静等待跨越所有陷阱，侵入最深处的侵入者。\n·根据角色的最大生命值，对敌方全体给予\n　特大魔法伤害。\n·对前方范围内的敌人给予中等物理伤害。\n·对残余生命值最高的敌人，根据其最大生命值，\n　给予中等魔法伤害并使其眩晕。\n·当阿尔玛自身的生命值低于50％时，\n　它的攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(303900,'独眼巨人',303900,0,13,270,1000,1,2.0,0,0,0,'【物理】以自己的怪力为傲的独眼巨人。\n充满魔力的眼瞳能释放出强大的奇怪光线。\n\n·对敌方全体造成魔法伤害（大）。\n　提升对生命值为100%的敌人造成的伤害\n　并降低其行动速度。\n·对物理攻击力最高的一名敌人，根据其最大\n　生命值，造成物理伤害（中），并降低其技能值。');
INSERT INTO unit_enemy_data VALUES(303901,'独眼巨人',303901,0,13,270,1000,1,2.0,0,0,0,'【物理】以自己的怪力为傲的独眼巨人。\n充满魔力的眼瞳能释放出强大的奇怪光线。\n\n·对敌方全体造成魔法伤害（大）。\n　提升对生命值为100%的敌人造成的伤害\n　并降低其行动速度。\n·对物理攻击力最高的一名敌人，根据其最大\n　生命值，造成物理伤害（中），并降低其技能值。');
INSERT INTO unit_enemy_data VALUES(303902,'独眼巨人',303902,0,13,270,1000,1,2.0,0,0,0,'【物理】以自己的怪力为傲的独眼巨人。\n充满魔力的眼瞳能释放出强大的奇怪光线。\n\n·对敌方全体造成魔法伤害（大）。\n　提升对生命值为100%的敌人造成的伤害\n　并降低其行动速度。\n·对物理攻击力最高的一名敌人，根据其最大\n　生命值，造成物理伤害（中），并降低其技能值。');
INSERT INTO unit_enemy_data VALUES(304000,'暗黑独角兽',304000,0,13,270,1000,1,2.0,0,0,0,'【物理】诞生于梦幻，徘徊于黑暗中的魔兽。\n以恶梦的牢笼束缚猎物，津津有味地享用其活力。\n\n·对物理攻击力最高的一名敌人造成魔法伤害（特大），\n　并特大幅度降低其技能值。\n·以物理攻击力最高的一名敌人为中心，\n　对范围内的所有敌人造成魔法伤害（中），\n　并赋予诅咒状态。');
INSERT INTO unit_enemy_data VALUES(304001,'暗黑独角兽',304001,0,13,270,1000,1,2.0,0,0,0,'【物理】诞生于梦幻，徘徊于黑暗中的魔兽。\n以恶梦的牢笼束缚猎物，津津有味地享用其活力。\n\n·对物理攻击力最高的一名敌人造成魔法伤害（特大），\n　并特大幅度降低其技能值。\n·以物理攻击力最高的一名敌人为中心，\n　对范围内的所有敌人造成魔法伤害（中），\n　并中幅降低其技能值，同时赋予诅咒状态。');
INSERT INTO unit_enemy_data VALUES(304100,'泰坦陆龟',304100,0,13,270,1000,1,2.5,0,0,0,'又老又大的陆龟，龟壳上甚至有大树扎根。\n持续忍耐敌人的攻击，施加愤怒的反击。\n\n·对全体敌人造成大量物理伤害。\n·根据自己缩在龟壳期间受到的伤害量\n　来给予敌人相应的物理伤害。');
INSERT INTO unit_enemy_data VALUES(304200,'卡尔古',304200,0,16,270,1000,1,1.8,0,0,0,'【物理】吞吃尸体的凶暴大型魔兽。\n全身缠绕着强韧的尖刺，执着的捕获猎物。\n·对全体敌人造成与其最大生命值相适应的\n中等物理伤害。\n·对后方范围造成中等物理伤害\n　并令其眩晕。');
INSERT INTO unit_enemy_data VALUES(304201,'卡尔古',304200,0,16,270,1000,1,1.8,0,0,0,'【物理】吞吃尸体的凶暴大型魔兽。\n全身缠绕着强韧的尖刺，执着的捕获猎物。\n·对全体敌人造成与其最大生命值相适应的\n中等物理伤害。\n·对后方范围造成中等物理伤害\n　并令其眩晕。\n·对前方第2个敌人造成中等物理伤害。\n·卡尔古在生命值低于50%后会展开\n　更为激烈凶暴的攻击。');
INSERT INTO unit_enemy_data VALUES(304300,'腾特帕斯',304300,0,16,270,1000,1,1.8,0,0,0,'【物理】君临海边的巨大软体魔兽。\n强韧的触手和钢铁的大鱼叉可以彻底破坏一切。\n·根据角色最大生命值，对全体敌人造成相应威力\n　的大量物理伤害。\n·对后方范围造成中等魔法伤害，\n　令其陷入麻痹状态，另外特大幅降低物理攻击力、\n　魔法攻击力。\n·经过一定时间后对单个敌人造成\n　超大量物理伤害。\n·腾特帕斯自己的生命值低于40％后，\n　会展开更为激烈凶暴的攻击。');
