INSERT INTO unit_enemy_data VALUES(304800,'幽灵领主',304800,0,16,270,1000,1,1.8,0,0,0,'冥府的高官，率领作为亡灵的强者之魂。\n将死者的执念附于刀刃之上，使对手的动作变得迟缓。\n\n·对敌方全体造成魔法伤害（大），\n　并降低其物理攻击力。\n·对敌方全体造成魔法伤害（大）。\n　对进行物理攻击的敌人造成额外伤害，\n　并降低其行动速度。');
