INSERT INTO unit_enemy_data VALUES(306709,'丑萌巧克力兽',306700,0,16,270,2160,1,1.8,0,0,0,'【物理】因恶毒的言语而暴走的巨大巧克力魔物。\n被误解的呢喃爱意，也会化作伤害他人的刀刃。\n\n·必杀技能时，对敌方全体造成与角色\n　最大生命值相应的物理伤害（大），\n　当目标处于烧伤状态时，追加伤害。\n·对前方三名敌人造成与角色最大生命值\n　相应的物理伤害（中），并使其眩晕。\n　当角色的生命值低于40%时，\n　赋予烧伤状态。\n·当丑萌巧克力兽自身的生命值\n　低于45％时，会变得更加凶暴，\n　攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(306710,'丑萌巧克力兽',306700,0,16,270,2160,1,1.8,0,0,0,'【物理】因恶毒的言语而暴走的巨大巧克力魔物。\n被误解的呢喃爱意，也会化作伤害他人的刀刃。\n\n·必杀技能时，对敌方全体造成与角色\n　最大生命值相应的物理伤害（大），\n　当目标处于烧伤状态时，追加伤害。\n·对前方三名敌人造成与角色最大生命值\n　相应的物理伤害（中），并使其眩晕。\n　当角色的生命值低于40%时，\n　赋予烧伤状态。\n·当丑萌巧克力兽自身的生命值\n　低于45％时，会变得更加凶暴，\n　攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(306711,'丑萌巧克力兽',306700,0,16,270,3000,1,1.8,0,0,0,'【模式1】强烈喷射巧克力，覆盖自身，隐藏起来。\n不断受到敌人的攻击，爆发愤怒的一击。\n\n·对敌方全体造成物理伤害（小），使其在战斗期间\n　一直处于烧伤状态。\n·藏在巧克力巨蛋之中时，\n　中幅提升丑萌巧克力兽自身的物理防御力。\n　解除巧克力巨蛋状态后，会根据在此\n　期间所受到的伤害量进行反击，\n·造成物理伤害。\n　并且大幅降低丑萌巧克力兽自身的\n　物理防御力。');
INSERT INTO unit_enemy_data VALUES(306712,'丑萌巧克力兽',306700,0,16,270,3000,1,1.8,0,0,0,'【模式2】可可兽振奋起来，\n为了在狂暴化的魔物攻击下，\n保护重要的人们\n而自我牺牲。\n\n·必杀技能时，对敌方全体造成与角色\n 最大生命值相应的物理伤害（大）。\n·可可兽会作为角色的同伴出现。\n 可可兽出现期间，中幅提升全体角色的\n 物理攻击力、魔法攻击力和行动速度。\n·对敌方全体造成魔法伤害（小），\n 并大幅降低其行动速度。\n');
INSERT INTO unit_enemy_data VALUES(306713,'丑萌巧克力兽',306700,0,16,270,2160,1,1.8,0,0,0,'【模式3】轰鸣的咆哮声回响在会场之中。\n爱恨交织的情人节事变也终于迎来了尾声。\n\n·必杀技能时，对敌方全体造成与角色\n　最大生命值相应的物理伤害（大），\n　当目标处于烧伤状态时，追加伤害。\n·对前方三名敌人造成物理伤害（中），　并使其眩晕。\n　当角色的生命值降至50％以下时，\n　赋予烧伤状态。\n·在战斗开始后，立刻提升自身的\n　物理攻击力与行动速度。');
INSERT INTO unit_enemy_data VALUES(306800,'哀叹女神',306800,0,16,270,3000,2,1.6,0,0,0,'【魔法】因失意和绝望，而堕落成魔的悲哀女王。\n痛哭着用恐怖和诅咒填满一切。\n\n·必杀技能根据敌方全体角色的\n　最大生命值造成魔法伤害（大），\n　降低魔法防御力。\n·对前方第二名、第三名角色\n　造成魔法伤害（中），使其眩晕。\n·对前方两名角色造成魔法伤害（小），\n　降低物理攻击力，减少技能值。\n　并且使其陷入诅咒状态。\n·哀叹女神自身的生命值\n　降到40％时，会变得更加凶暴，\n　攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(306801,'哀叹女神',306800,0,16,270,3000,2,1.6,0,0,0,'【魔法】因失意和绝望，而堕落成魔的悲哀女王。\n痛哭着用恐怖和诅咒填满一切。\n\n·必杀技能根据敌方全体角色的\n　最大生命值造成魔法伤害（大），\n　降低魔法防御力。\n·对前方第二名、第三名角色\n　造成魔法伤害（中），使其眩晕。');
INSERT INTO unit_enemy_data VALUES(306802,'哀叹女神',306800,0,16,270,3000,2,1.6,0,0,0,'【魔法】因失意和绝望，而堕落成魔的悲哀女王。\n痛哭着用恐怖和诅咒填满一切。\n\n·必杀技能根据敌方全体角色的\n　最大生命值造成魔法伤害（大），\n　降低魔法防御力。\n·对前方第二名、第三名角色\n　造成魔法伤害（中），使其眩晕。\n·对前方两名角色造成魔法伤害（小），\n　降低物理攻击力，减少技能值。\n　并且使其陷入诅咒状态。\n　即使被赋予了异常状态免疫效果，\n　该技能也会生效。\n·哀叹女神自身的生命值\n　降到40％时，会变得更加凶暴，\n　攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(306803,'哀叹女神',306800,0,16,270,3000,2,1.6,0,0,0,'【魔法】因失意和绝望，而堕落成魔的悲哀女王。\n痛哭着用恐怖和诅咒填满一切。\n\n·必杀技能根据敌方全体角色的\n　最大生命值造成魔法伤害（大），\n　降低魔法防御力。\n·对前方第二名、第三名角色\n　造成魔法伤害（中），使其眩晕。\n·对前方两名角色造成魔法伤害（小），\n　降低物理攻击力，减少技能值。\n　并且使其陷入诅咒状态。\n　即使被赋予了异常状态免疫效果，\n　该技能也会生效。\n·哀叹女神自身的生命值\n　降到40％时，会变得更加凶暴，\n　攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(306804,'哀叹女神',306800,0,16,270,3000,2,1.6,0,0,0,'【MODE1】将【自卫团】成员作为暗影召唤。\n企图斩断羁绊，攻击挑战者内心软弱之处。\n\n·让暗影出现的\n·哀叹女神自身不会做出攻击行动。');
INSERT INTO unit_enemy_data VALUES(306805,'哀叹女神',306800,0,16,270,3000,2,1.6,0,0,0,'【MODE2】以永久之黑暗充斥着周围，\n　可使用诅咒咒语\n　不断阻碍着冒险者的一切。\n\n·必杀技能根据敌方全体角色的\n　最大生命值造成魔法伤害（大），\n　降低魔法防御力。\n·对前方三名角色造成魔法伤害（小），\n　降低物理防御力，减少技能值，\n　降低获取率。\n　并且使其变成诅咒、混乱状态，降低行动速度。\n　即使被赋予了异常状态免疫效果，\n　该技能也会生效。\n·哀叹女神自身的物理防御力下降。\n　并且变成诅咒状态，减少技能值。');
INSERT INTO unit_enemy_data VALUES(306806,'哀叹女神',306800,0,16,270,3000,2,1.6,0,0,0,'【MODE3】充满狂气的悲哀，让女王解放了真正的身姿。被诅咒的愿望容不下一丝的希望。\n\n·必杀技能根据敌方全体角色的\n　最大生命值造成魔法伤害（大），\n　降低魔法防御力。\n·对前方第二名、第三名角色\n　造成魔法伤害（中），使其眩晕。\n·对前方两名角色造成魔法伤害（小），\n　降低物理攻击力，减少技能值。\n　并且使其陷入诅咒状态。\n　即使被赋予了异常状态免疫效果，\n　该技能也会生效。');
INSERT INTO unit_enemy_data VALUES(306900,'三战士图腾',306900,0,16,270,1000,2,1.8,0,0,0,'【魔法】过去曾被人供奉的三名战士的石像。\n会提升各自的能力，释放出必杀的一击。\n\n·更容易受到降低物理攻击力与\n魔法攻击力的效果影响。\n·极大幅度提升自身的物理攻击力与\n　魔法攻击力，对敌方全体造成物理伤害（中）\n　与魔法伤害（中）。\n·对前方范围内的敌人造成魔法伤害（小）。\n·对敌方全体造成物理伤害（中）。');
