INSERT INTO unit_enemy_data VALUES(307001,'魔石巨人',307000,0,16,270,3000,1,1.8,0,0,0,'【物理】从异界的魔石中诞生的无畏巨人。\n以强韧的肉体作为武器，不断释放猛烈的攻击。\n\n·容易受物理攻击力提升·下降的效果\n　影响。\n·必杀技能给予敌方全体物理伤害（大）。\n·给予前方三名角色物理伤害（中）。\n·给予敌方全体物理伤害，造成\n　大力的击退效果。');
INSERT INTO unit_enemy_data VALUES(307002,'魔石巨人',307002,0,16,270,3000,1,1.8,0,0,0,'');
INSERT INTO unit_enemy_data VALUES(307003,'魔石巨人',307002,0,16,270,3000,1,1.8,0,0,0,'【物理】从异界的魔石中诞生的无畏巨人。\n以强韧的肉体作为武器，不断释放猛烈的攻击。\n\n·容易受物理攻击力提升·下降的效果\n　影响。\n·必杀技能给予敌方全体物理伤害（大）。\n·给予前方三名角色物理伤害（中）。\n·一定时间内做出防御后，根据期间所受到的\n　伤害量给予敌方全体物理伤害，\n　使前方两名角色麻痹。\n　并且提升自身的物理攻击力。\n·给予敌方全体物理伤害，造成\n　大力的击退效果。\n·魔石巨人自身的生命值降到40%时，\n　攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(307004,'魔石巨人',307002,0,16,270,3000,1,1.8,0,0,0,'【物理】从异界的魔石中诞生的无畏巨人。\n以强韧的肉体作为武器，不断释放猛烈的攻击。\n\n·容易受物理攻击力提升·下降的效果\n　影响。\n·必杀技能给予敌方全体物理伤害（大）。\n·给予前方三名角色物理伤害（中）。\n·一定时间内做出防御后，根据期间所受到的\n　伤害量给予敌方全体物理伤害，\n　使前方两名角色麻痹。\n　并且提升自身的物理攻击力。\n·给予敌方全体物理伤害，造成\n　大力的击退效果。\n·魔石巨人自身的生命值降到40%时，\n　攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(307005,'魔石巨人',307002,0,16,270,3000,1,5.0,0,0,0,'【MODE1】将【美食殿堂】成员作为暗影\n召唤。以旅途的回忆为盾，试探三人\n的弱点。\n\n·让暗影出现，\n·给予前方三名角色物理伤害（中）。\n·给予敌方全体物理伤害，造成\n　大力的击退效果。');
INSERT INTO unit_enemy_data VALUES(307006,'魔石巨人',307002,0,16,270,3000,1,5.0,0,0,0,'【MODE2】强韧的肉体能抵挡\n一切攻击。以愤怒为食粮，巨人会做出\n怒涛般的强力反击。\n\n·容易受物理攻击力提升·下降的效果\n　影响。\n·必杀技能给予敌方全体物理伤害（大）。\n·给予前方三名角色物理伤害（中）。\n·一定时间内做出防御后，根据期间所受到的\n　伤害量给予敌方全体物理伤害，\n　使前方两名角色麻痹。\n　并且提升自身的物理攻击力。');
INSERT INTO unit_enemy_data VALUES(307007,'魔石巨人',307002,0,16,270,3000,1,3.0,0,0,0,'【MODE3】无畏的巨人，解放了\n全身的力量。在强大无比的破坏力前\n耍小聪明的攻击无法奏效。\n\n·容易受物理攻击力提升·下降的效果\n　影响。\n·必杀技能给予敌方全体物理伤害（大）。\n·给予前方三名角色物理伤害（中）。\n·一定时间内做出防御后，根据期间所受到的\n　伤害量给予敌方全体物理伤害，\n　使前方两名角色麻痹。\n　并且提升自身的物理攻击力。\n·给予敌方全体物理伤害，造成\n　大力的击退效果。');
INSERT INTO unit_enemy_data VALUES(307100,'机械莉玛（MODE1）',307100,0,16,270,3000,1,2.8,0,0,0,'拥有坚固多彩的技能，谜之巨大兵器的第1形态。\n使用苹果型的补给机器充填未知的能量，\n注意其使宽广范围焦土化的连续攻击！\n\n·必杀技能对敌方全体造成物理伤害（大）。\n·对前方一名角色造成物理伤害（小）。\n·降低一定范围内的物理攻击力和回避，\n　展开物理伤害的领域。\n·对前方范围内造成物理伤害（中），\n　提升自身的技能值。');
INSERT INTO unit_enemy_data VALUES(307101,'机械莉玛（MODE2）',307101,0,16,270,3000,1,0.1,0,0,0,'切换到攻击一切模式的谜之巨大兵器的第2形态。\n核动力炉释放的炽热光线，将大地燃烧殆尽。\n对毫无防备的巨大兵器造成会心一击！\n\n·必杀技能对前方小范围\n　造成物理伤害（特大），中范围造成物理伤害（大），\n　对宽广范围内造成物理伤害（中），\n　并将其大幅击飞。\n·对前方一名角色造成物理伤害（小）。\n·随机对一名角色造成物理伤害（小）。\n·在一定范围内，降低技能值的上升和回避，\n　展开魔法伤害的领域。');
INSERT INTO unit_enemy_data VALUES(307102,'机械莉玛（MODE3）',307102,0,16,270,3000,1,5.5,0,0,0,'自我修复损耗的谜之巨大兵器的第3形态。\n防卫无人机会阻止对充满破绽的本体的攻击。\n使用范围攻击将无人机击退，捉住好机会！\n\n·将敌方全体大幅击飞，\n　召唤3台迷你机械莉玛。\n·迷你机械莉玛会对前方一名角色\n　造成物理伤害（小）。\n·迷你机械莉玛随机对一名角色\n　造成魔法伤害（中）。');
INSERT INTO unit_enemy_data VALUES(307200,'巴吉里斯克',307200,0,16,270,3000,2,1.8,0,0,0,'【魔法】追求露娜魔力飞来的大怪鸟。\n率领雏鸟，挥下石化的咒剑。\n\n·对非石化状态的角色全体造成魔法伤害（大）\n　使其短时间石化。\n　对石化状态的角色全员造成最大生命值\n　80%的伤害。\n·对前方三名角色造成魔法伤害（小）。\n　并且因该技能伤害\n　陷入中毒状态的角色会长时间石化。\n·对前方范围内的角色造成魔法伤害（中）\n　并且使其变为中毒状态。\n　自身的生命值降到50％以下时，会对敌方全体\n　做出反击，召唤出可可。\n·可可会对前方范围内物理攻击力\n　最高的一名角色造成物理伤害（小），\n　并且使其陷入中毒状态。\n　当自身的生命值处于最大生命的35%以下时，\n　中幅提升物理攻击力最高的己方同伴的技能值，\n　自身变为无法战斗状态。');
INSERT INTO unit_enemy_data VALUES(307300,'恶霸地方官',307300,0,16,270,3000,1,1.6,0,0,0,'【物理】充斥着贪婪欲念的机关人偶。\n作为大江户镇的暴政者君临此地，\n极尽蛮横之事。\n\n·对敌方全体造成与角色的最大生命值\n　相应的物理伤害（大），\n　并中幅提升自身的物理攻击力。');
INSERT INTO unit_enemy_data VALUES(307301,'恶霸地方官',307300,0,16,270,3000,1,1.6,0,0,0,'【物理】充斥着贪婪欲念的机关人偶。\n作为大江户镇的暴政者君临此地，\n极尽蛮横之事。\n\n·对敌方全体造成与角色的最大生命值\n　相应的物理伤害（大），\n　并中幅提升自身的物理攻击力。');
INSERT INTO unit_enemy_data VALUES(307302,'恶霸地方官',307300,0,16,270,3000,1,1.6,0,0,0,'【物理】充斥着贪婪欲念的机关人偶。\n作为大江户镇的暴政者君临此地，\n极尽蛮横之事。\n\n·对敌方全体造成与角色的最大生命值\n　相应的物理伤害（大），\n　并中幅提升自身的物理攻击力。');
INSERT INTO unit_enemy_data VALUES(307303,'恶霸地方官',307300,0,16,270,3000,1,1.6,0,0,0,'【物理】充斥着贪婪欲念的机关人偶。\n作为大江户镇的暴政者君临此地，\n极尽蛮横之事。\n\n·对敌方全体造成与角色的最大生命值\n　相应的物理伤害（大），\n　并中幅提升自身的物理攻击力。同时，\n　令所有已被召唤的家臣的生命值\n　变为0。\n·召唤一名会对眼前一名敌人\n　进行物理攻击的家臣。\n　当家臣自己的生命值为0时，\n　会发生大爆炸，对范围内的所有敌人造成\n　物理伤害（中）。\n·当恶霸地方官自身的生命值\n　低于40%时，会变得更加凶暴，\n　攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(307304,'恶霸地方官',307300,0,16,270,3000,1,1.6,0,0,0,'【模式1】将【纯白之翼\n兰德索尔分部】的成员作为暗影\n召唤出来。嘲笑三人坚固情谊的\n欲望之影将发动强袭。\n\n·召唤暗影\n·对眼前一名敌人进行物理攻击\n');
INSERT INTO unit_enemy_data VALUES(307305,'恶霸地方官',307300,0,16,270,3000,1,1.6,0,0,0,'【模式2】以物欲使周围的生物服从自己，\n其制造出的假象能将任何敌人拖入欲望的旋涡。\n\n·对眼前一名敌人进行物理攻击\n·对敌方全体造成物理伤害（小），\n　并赋予魅惑状态。\n');
INSERT INTO unit_enemy_data VALUES(307306,'恶霸地方官',307300,0,16,270,3000,1,1.6,0,0,0,'【模式3】当内心充满虚荣之时，\n就会释放出真正的力量，想以此来支配一切。\n\n·对敌方全体造成与角色的最大生命值\n　相应的物理伤害（大），\n　并中幅提升自身的物理攻击力。同时，\n　令所有已被召唤的家臣的生命值\n　变为0。\n·召唤一名会对眼前一名敌人\n　进行物理攻击的家臣。\n　当家臣自己的生命值为0时，\n　会发生大爆炸，对范围内的所有敌人造成\n　物理伤害（中）。\n');
