INSERT INTO unit_enemy_data VALUES(307401,'培提尔其乌斯',307400,0,16,270,3000,1,1.6,0,0,0,'【魔法】在露格尼卡恶名昭彰的魔女教干部。\n哪怕到了异世界，也仍然遵从着自己的信念，\n凭借强大的权能“不可见之手”在暗中活跃。\n\n·必杀技能时，对敌方全体造成魔法伤害（大）。\n·对前方范围内的所有敌人造成魔法伤害（小），\n　并降低其技能值。\n　同时对敌方全体造成魔法伤害（小）。\n·对后方两名敌人造成物理伤害（大）。');
INSERT INTO unit_enemy_data VALUES(307402,'培提尔其乌斯',307400,0,16,270,3000,1,1.6,0,0,0,'【魔法】在露格尼卡恶名昭彰的魔女教干部。\n哪怕到了异世界，也仍然遵从着自己的信念，\n凭借强大的权能“不可见之手”在暗中活跃。\n\n·必杀技能时，对敌方全体造成魔法伤害（大）。\n·对前方范围内的所有敌人造成魔法伤害（小），\n　并降低其技能值。\n　同时对敌方全体造成魔法伤害（小）。\n·对后方两名敌人造成物理伤害（大）。\n·战斗开始50秒后，极大幅度提升自身的\n　魔法攻击力。');
INSERT INTO unit_enemy_data VALUES(307403,'培提尔其乌斯',307400,0,16,270,3000,1,1.6,0,0,0,'【魔法】在露格尼卡恶名昭彰的魔女教干部。\n哪怕到了异世界，也仍然遵从着自己的信念，\n凭借强大的权能“不可见之手”在暗中活跃。\n\n·必杀技能时，对敌方全体造成魔法伤害（大）。\n·对前方范围内的所有敌人造成魔法伤害（小），\n　并降低其技能值。\n　同时对敌方全体造成魔法伤害（小）。\n·对后方两名敌人造成物理伤害（大）。\n·战斗开始50秒后，极大幅度提升自身的\n　魔法攻击力。');
INSERT INTO unit_enemy_data VALUES(307404,'培提尔其乌斯',307400,0,16,270,3000,1,1.6,0,0,0,'【模式1】召唤雷姆和拉姆的暗影。\n被大罪司教的疯狂所吞噬的双胞胎暗影，\n会化为魔女教的爪牙，阻挡在伙伴的面前。\n\n·对眼前一名敌人进行物理攻击。');
INSERT INTO unit_enemy_data VALUES(307405,'培提尔其乌斯',307400,0,16,270,3000,1,1.6,0,0,0,'【模式2】威力增强后的权能变得强大无比，\n将会毫不留情地消灭怠惰的仇敌。\n\n·必杀技能时，对敌方全体造成魔法伤害（大）。\n·对后方两名敌人造成物理伤害（大）。\n·对剩余生命值百分比最少的一名敌人造成\n　魔法伤害（大），并赋予倒计时。\n　当倒计时为0时，给目标造成最大生命值95%的\n　百分比伤害。');
INSERT INTO unit_enemy_data VALUES(307406,'培提尔其乌斯',307400,0,16,270,3000,1,1.6,0,0,0,'【模式3】失去理性和人类姿态的狂热信徒，\n只为执念所行动，为忠爱竭尽全力。\n\n·必杀技能时，对敌方全体造成魔法伤害（大）。\n·对前方范围内的所有敌人造成魔法伤害（小），\n　并降低其技能值。\n　同时对敌方全体造成魔法伤害（小）。\n·对后方两名敌人造成物理伤害（大）。\n·对剩余生命值百分比最少的一名敌人造成\n　魔法伤害（大），并赋予倒计时。\n　当倒计时为0时，给目标造成最大生命值95%的\n　百分比伤害。\n·战斗开始50秒后，极大幅度提升自身的\n　魔法攻击力。');
INSERT INTO unit_enemy_data VALUES(307500,'基兹',307500,0,3,270,1000,1,1.8,0,0,0,'【物理】卷起暴风的大怪鸟。\n从魔物图鉴中逃出，大肆进行破坏。\n\n·根据敌方全体角色的最大生命值\n　造成大量物理伤害。\n·对前方范围内的敌人造成中等物理伤害，\n　并使其眩晕。');
INSERT INTO unit_enemy_data VALUES(307501,'基兹',307500,0,3,270,1000,1,1.8,0,0,0,'【物理】卷起暴风的大怪鸟。\n从魔物图鉴中逃出，大肆进行破坏。\n\n·根据敌方全体角色的最大生命值\n　造成大量物理伤害。\n·对前方范围内的敌人造成中等物理伤害，\n　并使其眩晕。');
INSERT INTO unit_enemy_data VALUES(307502,'基兹',307500,0,3,270,1000,1,1.8,0,0,0,'【物理】卷起暴风的大怪鸟。\n从魔物图鉴中逃出，大肆进行破坏。\n\n·根据敌方全体角色的最大生命值\n　造成大量物理伤害。\n·对前方范围内的敌人造成中等物理伤害，\n　并使其眩晕。\n·根据前方范围内的敌方角色的最大生命值\n　造成中等物理伤害。\n·基兹自身的生命值降到50%时，\n　攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(307503,'基兹',307500,0,3,270,1000,1,1.8,0,0,0,'【物理】卷起暴风的大怪鸟。\n从魔物图鉴中逃出，大肆进行破坏。\n\n·根据敌方全体角色的最大生命值\n　造成大量物理伤害。\n·对前方范围内的敌人造成中等物理伤害，\n　并使其眩晕。\n·根据前方范围内的敌方角色的最大生命值\n　造成中等物理伤害。\n·基兹自身的生命值降到50%时，\n　攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(307504,'红极鸟',307504,0,3,270,1000,1,1.8,0,0,0,'【物理】吞噬塔中的魔力，四处肆虐的巨大魔鸟。\n要是掉以轻心地接近它，便会被暴风与狂雷撕裂。\n\n·对敌方全体造成物理伤害（大），并对所有\n　使用魔法攻击的敌人追加物理伤害（中）。\n·以前方第二名敌人为中心，造成小范围\n　物理伤害（小），并使其眩晕。\n·对前方范围内的敌人造成物理伤害（中），　并小幅度击飞敌人。');
INSERT INTO unit_enemy_data VALUES(307600,'疯狂小丑',307600,0,16,270,1000,1,1.8,0,0,0,'【物理】意图将露娜之塔作为自己舞台的\n魔界小丑。面带开朗而疯狂的笑容，玩弄着观众的生命。\n\n·对敌方全体造成魔法伤害（大），\n　并赋予黑暗状态。\n·随机对一名敌人造成六次物理伤害（小），\n　并对敌方全体造成物理伤害（大）。\n　越是位于前列的角色，\n　越容易受到该攻击。\n·在前方中距离召唤倒计时式炸弹。\n　炸弹会在召唤后8秒发生大爆炸，\n　对范围内的所有敌人造成物理伤害（中）。\n　但是，若炸弹的生命值在爆炸前\n　低于1%，则会对疯狂小丑造成\n　最大生命值15％的物理伤害。');
INSERT INTO unit_enemy_data VALUES(307700,'阿尔玛',307700,0,16,270,1000,1,1.8,0,0,0,'【物理】从远古开始守护迷宫的防卫兵器。在最深处静静等待着跨越所有陷阱而来的入侵者。\n\n·对敌方全体造成与角色的最大生命值\n　相应的魔法伤害（特大）。\n·对前方范围内的敌人造成物理伤害（中）。');
INSERT INTO unit_enemy_data VALUES(307701,'阿尔玛',307700,0,16,270,1000,1,1.8,0,0,0,'【物理】从远古开始守护迷宫的防卫兵器。在最深处静静等待着跨越所有陷阱而来的入侵者。\n\n·对敌方全体造成与角色的最大生命值\n　相应的魔法伤害（特大）。\n·对前方范围内的敌人造成物理伤害（中）。');
INSERT INTO unit_enemy_data VALUES(307702,'阿尔玛',307700,0,16,270,1000,1,1.8,0,0,0,'【物理】从远古开始守护迷宫的防卫兵器。在最深处静静等待着跨越所有陷阱而来的入侵者。\n\n·对敌方全体造成与角色的最大生命值\n　相应的魔法伤害（特大）。\n·对前方范围内的敌人造成物理伤害（中）。\n·对剩余生命值最高的敌人造成与其最大生命值\n　相应的魔法伤害（中），并赋予麻痹状态。\n·当阿尔玛自身的生命值低于50％时，\n　它的攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(307703,'阿尔玛',307700,0,16,270,1000,1,1.8,0,0,0,'【物理】从远古开始守护迷宫的防卫兵器。在最深处静静等待着跨越所有陷阱而来的入侵者。\n\n·对敌方全体造成与角色的最大生命值\n　相应的魔法伤害（特大）。\n·对前方范围内的敌人造成物理伤害（中）。\n·对剩余生命值最高的敌人造成与其最大生命值\n　相应的魔法伤害（中），并赋予麻痹状态。\n·当阿尔玛自身的生命值低于50％时，\n　它的攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(307800,'雨神大人',307800,0,16,270,3000,2,1.6,0,0,0,'【魔法】自古以来便守护着岛上气候的守护神。\n对友人的思念令其忧心忡忡，最终变成了令人害怕的模样。\n为无垠的大地降下哀泣的雨。\n\n·必杀技能对敌方全体造成\n　魔法伤害（大），同时击飞敌人。\n　并对前方范围内的敌人造成\n　追加的魔法伤害（中）。\n·对最远的一名敌人造成魔法伤害（中），\n　并小幅度拉近目标。');
INSERT INTO unit_enemy_data VALUES(307801,'雨神大人',307800,0,16,270,3000,2,1.6,0,0,0,'【魔法】自古以来便守护着岛上气候的守护神。\n对友人的思念令其忧心忡忡，最终变成了令人害怕的模样。\n为无垠的大地降下哀泣的雨。\n\n·必杀技能对敌方全体造成\n　魔法伤害（大），同时击飞敌人。\n　并对前方范围内的敌人造成\n　追加的魔法伤害（中）。\n·对最远的一名敌人造成魔法伤害（中），\n　并小幅度拉近目标。');
INSERT INTO unit_enemy_data VALUES(307802,'雨神大人',307800,0,16,270,3000,2,1.6,0,0,0,'【魔法】自古以来便守护着岛上气候的守护神。\n对友人的思念令其忧心忡忡，最终变成了令人害怕的模样。\n为无垠的大地降下哀泣的雨。\n\n·必杀技能对敌方全体造成\n　魔法伤害（大），同时击飞敌人。\n　并对前方范围内的敌人造成\n　魔法伤害（中）。\n·对前方范围内的所有敌人造成魔法伤害（中），\n　并小幅度击飞敌人。再对离自己最近的一名敌人\n　造成追加的魔法伤害（中）。\n·对最远的一名敌人造成魔法伤害（中），\n　并小幅度拉近目标。\n·当雨神大人自身的生命值低于40%时，\n　攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(307803,'雨神大人',307800,0,16,270,3000,2,1.6,0,0,0,'【魔法】自古以来便守护着岛上气候的守护神。\n对友人的思念令其忧心忡忡，最终变成了令人害怕的模样。\n为无垠的大地降下哀泣的雨。\n\n·必杀技能对敌方全体造成\n　魔法伤害（大），同时击飞敌人。\n　并对前方范围内的敌人造成\n　追加的魔法伤害（中）。\n·对前方范围内的所有敌人造成魔法伤害（中），\n　并小幅度击飞敌人。再对离自己最近的一名敌人\n　造成追加的魔法伤害（中）。\n·对最远的一名敌人造成魔法伤害（中），\n　并小幅度拉近目标。\n·当雨神大人自身的生命值低于40%时，\n　攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(307804,'雨神大人',307801,0,16,270,3000,2,1.6,0,0,0,'【模式1】沉浸在悲叹中的雨吉\n抗拒着众人的想法，又因其过于焦急\n甚至连自己心念已久的晴吉的声音\n都听不到了。\n\n·展开屏障，完全防御敌人的攻击。\n　屏障的耐久与伤害量无关，\n　只要被攻击命中就会减少耐久度，\n　受到200次攻击后，屏障就会被\n　破坏。\n·必杀技能对敌方全体造成魔法伤害（大），\n　并击飞敌人。同时，对前方范围内的所有敌人\n　造成追加的魔法伤害（中）。\n·对前方范围内的所有敌人造成魔法伤害（中），\n　并小幅度击飞敌人。再对离自己最近的一名敌人\n　造成追加的魔法伤害（中）。');
INSERT INTO unit_enemy_data VALUES(307805,'雨神大人',307800,0,16,270,3000,2,1.6,0,0,0,'【模式2】哀泣的雨伴随着悲伤\n不断降下。那漆黑的雨滴如悲哀的渴求\n侵蚀着人们的身心。\n\n·在战斗开始时召唤诅咒水晶。\n　场上存在诅咒水晶期间，\n　会对敌方全体造成持续伤害。同时，\n　因某种效果而回复生命值时，\n　回复效果将会无效，并受到原本回复量10%的\n　伤害。\n·必杀技能对敌方全体造成魔法伤害（大），\n　并击飞敌人。同时，对前方范围内的所有敌人\n　造成追加的魔法伤害（中）。\n·对前方范围内的所有敌人造成魔法伤害（中），\n　并小幅度击飞敌人。再对离自己最近的一名敌人\n　造成追加的魔法伤害（中）。\n·对最远的一名敌人造成魔法伤害（中），\n　并小幅度拉近目标。');
INSERT INTO unit_enemy_data VALUES(307806,'雨神大人',307800,0,16,270,3000,2,1.6,0,0,0,'【模式3】乞求着想要向晴吉赎罪的心\n化为恸哭的奔流，撕裂着对峙者的内心。\n爱、友情、所有的感情都将化为尘土。\n\n·必杀技能对敌方全体造成魔法伤害（大），\n　并击飞敌人。同时，对前方范围内的所有敌人\n　造成追加的魔法伤害（中）。\n·对前方范围内的所有敌人造成魔法伤害（中），\n　并小幅度击飞敌人。再对离自己最近的一名敌人\n　造成追加的魔法伤害（中）。\n·对最远的一名敌人造成魔法伤害（中），\n　并小幅度拉近目标。\n·当雨神大人自身的生命值低于40%时，\n　攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(307900,'卡尔古',307900,0,16,270,1000,1,1.6,0,0,0,'【物理】吞食尸体的凶暴大型魔兽。\n全身缠绕着强韧的尖刺，会执着地追捕猎物。\n\n·对敌方全体造成与角色的最大生命值\n　相应的物理伤害（中）。\n·对后方范围内的敌人造成物理伤害（中）\n　并使其眩晕。');
INSERT INTO unit_enemy_data VALUES(307901,'卡尔古',307900,0,16,270,1000,1,1.9,0,0,0,'【物理】吞食尸体的凶暴大型魔兽。\n全身缠绕着强韧的尖刺，会执着地追捕猎物。\n\n·对敌方全体造成与角色的最大生命值\n　相应的物理伤害（中）。\n·对后方范围内的敌人造成物理伤害（中）\n　并使其眩晕。');
INSERT INTO unit_enemy_data VALUES(307902,'卡尔古',307900,0,16,270,1000,1,1.6,0,0,0,'【物理】吞食尸体的凶暴大型魔兽。\n全身缠绕着强韧的尖刺，会执着地追捕猎物。\n\n·对敌方全体造成与角色的最大生命值\n　相应的物理伤害（中）。\n·对后方范围内的敌人造成物理伤害（中）\n　并使其眩晕。\n·对前方两名敌人造成物理伤害（中）。\n·当卡尔古自身的生命值低于50%时，　它的攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(307903,'卡尔古',307900,0,16,270,1000,1,1.6,0,0,0,'【物理】吞食尸体的凶暴大型魔兽。\n全身缠绕着强韧的尖刺，会执着地追捕猎物。\n\n·对敌方全体造成与角色的最大生命值\n　相应的物理伤害（中）。\n·对后方范围内的敌人造成物理伤害（中）\n　并使其眩晕。\n·对前方两名敌人造成物理伤害（中）。\n·当卡尔古自身的生命值低于50%时，　它的攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(308000,'奄美魔物',308000,0,16,270,3000,1,1.6,0,0,0,'【物理】以善良的灵魂为食的凶狠恶灵。\n为了寻求更好的祭品，会分出更多邪恶的身躯。\n\n·奄美魔物会根据保有的“吸魂”属性值的\n　层数，强化自身的普通攻击与\n　各种技能的效果。\n·每发动一次普通攻击就能获得6层“吸魂”。\n·必杀技能对敌方全体造成\n　魔法伤害（特大）。并获得5层“吸魂”。\n·对敌方全体造成魔法伤害（小），并降低其技能值，\n　再展开一个能降低技能值上升量的\n　领域。技能值的减少量与领域的效果值，\n　会随奄美魔物保持的“吸魂”层数\n　而强化。\n·每次进行攻击，奄美魔物便获得1层“吸魂”\n　并召唤3只分身，再小幅度击飞\n　所有敌人。\n·奄美魔物每受到一次暴击伤害\n　便减少奄美魔物2层“吸魂”。\n·每当分身受到暴击伤害时，\n　便减少奄美魔物1层“吸魂”。\n·当奄美魔物自身的生命值低于40％时，\n　它的攻击会变得更为凶暴。\n　并获得20层“吸魂”。');
