INSERT INTO unit_enemy_data VALUES(308201,'剧毒曼德拉草',308200,0,16,270,3000,1,1.6,0,0,0,'【物理】生长异常的曼德拉草。\n会用强韧的根茎与坚硬的种子子弹压制眼前的敌人。\n\n·对前方两名敌人造成物理伤害。\n·必杀技能对敌方全体造成物理伤害（大），\n　并根据造成的累积伤害\n　回复自身的生命值。\n·对敌方全体造成连续的物理伤害（小），\n　并降低3次其物理防御力。\n·提升自身的物理攻击力，同时\n　提升自身的技能值。');
