INSERT INTO unit_enemy_data VALUES(309301,'复仇驯鹿',309300,0,16,270,3000,1,1.8,0,0,0,'【物理】下定决心背叛主人的精英驯鹿。\n对贪得无厌的圣诞老人们降下随从真心实意的制裁。\n\n·必杀技能会在召唤角色消灭后，\n　对敌方全体造成物理百分比伤害。\n　受到该伤害后生命值低于50％的角色\n　将会在一段时间内变身为驯鹿，\n　同时被降低物理暴击率与\n　魔法暴击率。变成驯鹿后的角色，\n　将只能使用普通攻击。\n　一段时间内，大幅降低自身的物理防御力。\n·以最前面的角色为中心，\n　在一定范围内造成物理伤害。并提升自身的\n　物理攻击力。\n·对最前方的一名敌人造成物理伤害，\n　在击退的同时，使其眩晕。');
INSERT INTO unit_enemy_data VALUES(309302,'复仇驯鹿',309300,0,16,270,3000,1,1.8,0,0,0,'【物理】下定决心背叛主人的精英驯鹿。\n对贪得无厌的圣诞老人们降下随从真心实意的制裁。\n\n·必杀技能会在召唤角色消灭后，\n　对敌方全体造成物理百分比伤害。\n　受到该伤害后生命值低于50％的角色\n　将会在一段时间内变身为驯鹿，\n　同时被降低物理暴击率与\n　魔法暴击率。变成驯鹿后的角色，\n　将只能使用普通攻击。\n　一段时间内，大幅降低自身的物理防御力。\n·以最前面的角色为中心，\n　在一定范围内造成物理伤害。并提升自身的\n　物理攻击力。\n·对最前方的一名敌人造成物理伤害，\n　在击退的同时，使其眩晕。');
INSERT INTO unit_enemy_data VALUES(309303,'复仇驯鹿',309300,0,16,270,3000,1,1.8,0,0,0,'【物理】下定决心背叛主人的精英驯鹿。\n对贪得无厌的圣诞老人们降下随从真心实意的制裁。\n\n·必杀技能会在召唤角色消灭后，\n　对敌方全体造成物理百分比伤害。\n　受到该伤害后生命值低于50％的角色\n　将会在一段时间内变身为驯鹿，\n　同时被降低物理暴击率与\n　魔法暴击率。变成驯鹿后的角色，\n　将只能使用普通攻击。\n　一段时间内，大幅降低自身的物理防御力。\n·以最前面的角色为中心，\n　在一定范围内造成物理伤害。并提升自身的\n　物理攻击力。\n·对最前方的一名敌人造成物理伤害，\n　在击退的同时，使其眩晕。\n·当复仇驯鹿的生命值低于50％时，\n　它的攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(309304,'复仇驯鹿',309300,0,16,270,3000,1,4.2,0,0,0,'【模式1】召唤克莉丝提娜、望、\n伊莉亚的暗影。三人扭曲的欲望与多管闲事的心，\n将化为实体，令圣夜陷入混沌。\n\n·对最前方的一名敌人造成物理伤害。');
INSERT INTO unit_enemy_data VALUES(309305,'复仇驯鹿',309300,0,16,270,3000,1,1.8,0,0,0,'【模式2】绝望如雪花般洒落在圣夜的街道上。积蓄已久的憎恶将溢出充满欲望的\n礼物袋。\n\n·必杀技能会在召唤角色消灭后，\n　对敌方全体造成物理百分比伤害。\n　受到该伤害后生命值低于50％的角色\n　将会在一段时间内变身为驯鹿，\n　同时被降低物理暴击率与\n　魔法暴击率。变成驯鹿后的角色，\n　将只能使用普通攻击。\n　一段时间内，大幅降低自身的物理防御力。\n·以最前方的一名敌人为中心，\n·在一定范围内造成物理伤害。同时提升自身的\n　物理攻击力。\n·在前方展开一个持续一段时间的领域。\n　进入该领域的角色，\n　物理攻击力、物理暴击率以及技能值上升量\n　都将被大幅降低。\n·对最前方的一名敌人造成物理伤害，\n　在击退的同时，使其眩晕。');
INSERT INTO unit_enemy_data VALUES(309306,'复仇驯鹿',309300,0,16,270,3000,1,1.8,0,0,0,'【模式3】负伤的巨兽点燃了反击的狼烟。完全陷于憎恶与愤怒中，将变得\n残忍无比。\n\n·必杀技能会在召唤角色消灭后，\n　对敌方全体造成物理百分比伤害。\n　受到该伤害后生命值低于50％的角色\n　将会在一段时间内变身为驯鹿，\n　同时被降低物理暴击率与\n　魔法暴击率。变成驯鹿后的角色，\n　将只能使用普通攻击。\n　一段时间内，大幅降低自身的物理防御力。\n·以最前面的角色为中心，\n　在一定范围内造成物理伤害。并提升自身的\n　物理攻击力。\n·对最前方的一名敌人造成物理伤害，\n　在击退的同时，使其眩晕。\n·当复仇驯鹿的生命值低于50％时，\n　它的攻击会变得更为凶暴。');
INSERT INTO unit_enemy_data VALUES(309400,'侍从·刀刃（物理）',309400,0,16,270,1000,1,1.8,0,0,0,'【物理】霸瞳皇帝创造的移动力量储藏库。\n掌管力量的权能，其巨大的剑戟可斩破一切。\n\n·眩晕与击飞有效。\n\n·必杀技能会对敌方全体造成与角色\n　最大生命值相应的物理百分比伤害。\n\n·以眼前一名敌人为中心，对范围内的所有敌人\n　造成与角色的最大生命值相应的\n　物理百分比伤害。\n\n·当侍从·刀刃（物理）的生命值\n　低于40%时，会变得更加凶暴，\n　攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(309500,'侍从·魔能（魔法）',309500,0,16,270,1000,2,1.8,0,0,0,'【魔法】霸瞳皇帝创造的移动力量储藏库。\n掌管魔法的权能，其无穷的魔力可毁灭一切。\n\n·必杀技能会对敌方全体造成与角色\n　最大生命值相应的魔法百分比伤害。\n　同时，小幅降低其物理攻击力与魔法攻击力。\n\n·对随机一名敌人造成魔法伤害，\n　并赋予诅咒、麻痹状态，大幅降低其技能值。\n\n·当侍从·魔能（魔法）的生命值\n　低于40%时，会变得更加凶暴，\n　攻击更为猛烈。');
INSERT INTO unit_enemy_data VALUES(309600,'侍从·洛克斯（多部位）',309600,0,16,270,1000,1,1.8,0,0,0,'【物理】霸瞳皇帝创造的移动力量储藏库。\n掌管责任与义务的权能，可屠戮广阔领域中的一切。\n\n·必杀技能会以眼前一名敌人为中心，\n　对范围内的所有敌人造成与角色最大生命值\n　相应的物理百分比伤害，\n　并对眼前一名敌人造成物理伤害（小）。\n\n·以眼前一名敌人为中心，造成与\n　角色最大生命值相应的\n物理百分比伤害。\n\n·当侍从·洛克斯（多部位）的生命值\n　低于40%时，会变得更加顽强，\n　进一步提升自身的防御力。');
INSERT INTO unit_enemy_data VALUES(309700,'侍从·城墙（防卫）',309700,0,16,270,1000,2,1.8,0,0,0,'【魔法】霸瞳皇帝创造的移动力量储藏库。\n掌管守护的权能，其坚固的铁壁可防御一切。\n\n·必杀技能会大幅提升自身的\n　物理防御力与魔法防御力。\n\n·以眼前一名敌人为中心，对范围内的所有敌人\n　造成魔法伤害。同时，小幅提升自身的物理防御力\n　和魔法防御力。\n\n·当侍从·城墙（防卫）的生命值\n　低于40%时，会变得更加顽强，\n　进一步提升自身的防御力。');
INSERT INTO unit_enemy_data VALUES(309800,'霸瞳皇帝',309800,0,16,270,1000,1,3.6,309800,309800,0,'王都最终决战_基座单元');
INSERT INTO unit_enemy_data VALUES(309801,'霸瞳皇帝',309801,0,16,270,1000,1,2.4,309800,309800,0,'【模式1】抗拒着一切的悲伤公主。\n以神自居，沉浸于不详的力量之中。\n并向整个阿斯特朗举起了狂乱的刀刃。\n就用无数的剑刃，来穿破守护着自欺欺人的公主的铁壁吧。\n\n·展开屏障，完全防御敌人的攻击。\n　屏障的耐久与伤害量无关，\n　只要被攻击命中就会减少耐久度，\n　受到280次攻击后，屏障就会被\n　破坏。\n\n·必杀技能会根据敌方全体的剩余生命值\n　造成特大百分比伤害。\n\n·对敌方全体造成物理伤害（小）与\n　魔法伤害（小）。');
INSERT INTO unit_enemy_data VALUES(309802,'霸瞳皇帝',309800,0,16,270,1000,1,1.8,309800,309800,0,'【模式2】对渺小之人的反抗表现出些许的急躁。\n将以自己的大义去制裁那些心怀希望、阻挡自己的人。\n举起那柄悲愤之剑，将其灵魂化为尘土。\n以正义的魔法，去对抗伪神所挥出的\n漆黑之刃吧。\n\n·必杀技能会根据敌方全体的剩余生命值\n　造成特大百分比伤害。\n\n·对眼前一名敌人造成物理伤害（特大），\n　并对其周围的所有敌人造成魔法伤害（中），\n　使其昏迷。昏迷的角色\n　在受到下次攻击前，都无法行动。\n\n·对敌方全体造成物理伤害（小）与\n　魔法伤害（小）。');
INSERT INTO unit_enemy_data VALUES(309803,'霸瞳皇帝',309800,0,16,270,1000,1,1.6,309800,309800,0,'【模式3】创世神发出了临终的呼声。\n那因怨恨而吐露的拒绝之语将化为诅咒，\n折磨众人的身体、精神、灵魂，让一切回归黑暗。\n此刻即为决战。各位，举起剑来，用自己的双手\n开拓明天吧。\n\n·必杀技能会根据敌方全体的剩余生命值\n　造成特大百分比伤害。\n\n·对眼前一名敌人造成物理伤害（特大），\n　并对其周围的所有敌人造成魔法伤害（中），\n　使其昏迷。昏迷的角色\n　在受到下次攻击前，都无法行动。\n\n·对敌方全体造成物理伤害（小）与\n　魔法伤害（小）。\n\n·以最后一名敌人为中心，展开中幅降低\n　物理防御力与与魔法防御力的领域，\n　并造成魔法伤害（小），\n　同时赋予诅咒状态。诅咒所造成的持续伤害\n　会逐秒增加。\n\n·在战斗开始后，大幅提升自身的\n　行动速度，并小幅提升自身的\n　魔法防御力。');
INSERT INTO unit_enemy_data VALUES(309804,'霸瞳皇帝',309804,0,16,270,1000,1,1.8,309800,309800,0,'显示地图用');
INSERT INTO unit_enemy_data VALUES(309900,'专家哥布林',309900,0,17,270,1100,2,1.8,0,0,0,'【魔法】企图将塔的魔力占为己有的矮小学者。\n用禁术操纵泥塑人偶，强行掠夺敌人的自由。\n\n·对敌方全体造成魔法伤害（大），\n　让所有召唤体、分身即死。\n·对前方大范围造成与生命值相应的\n　百分比物理伤害（中），并大幅击飞敌人。\n　同时，召唤1只污泥人偶。\n　污泥人偶会对前方第一名或第二名敌人\n　造成物理伤害，并降低其行动速度。\n·以前方第二名敌人为中心，造成小范围的\n　物理伤害（中），并赋予猛毒、眩晕状态。');
INSERT INTO unit_enemy_data VALUES(310000,'山神大人',310000,0,16,270,3000,1,1.6,0,0,0,'【物理】传说拥有迅捷速度的山中土地神。\n自太古时代便为了寻求劲敌，而每日奔波着。\n\n·必杀技能会对敌方全体造成物理伤害（中）\n　同时击退敌人。\n　对前排的敌人造成追加的物理伤害（特大），\n　同时大幅度击飞敌人，使其眩晕。\n　山神大人每使用一次必杀技能，\n　都会降低自身的物理防御力。\n　这个效果可以累积。\n·对魔法攻击力最高的一名敌人造成\n　魔法伤害（大），并大幅降低其技能值。\n·提升自身的技能值与行动速度。');
INSERT INTO unit_enemy_data VALUES(310001,'山神大人',310000,0,16,270,3000,1,1.6,0,0,0,'【物理】传说拥有迅捷速度的山中土地神。\n自太古时代便为了寻求劲敌，而每日奔波着。\n\n·必杀技能会对敌方全体造成物理伤害（中）\n　同时击退敌人。\n　对前排的敌人造成追加的物理伤害（特大），\n　同时大幅度击飞敌人，使其眩晕。\n　山神大人每使用一次必杀技能，\n　都会降低自身的物理防御力。\n　这个效果可以累积。\n·对魔法攻击力最高的一名敌人造成\n　魔法伤害（大），并大幅降低其技能值。\n·提升自身的技能值与行动速度。');
INSERT INTO unit_enemy_data VALUES(310002,'山神大人',310000,0,16,270,3000,1,1.6,0,0,0,'【物理】传说拥有迅捷速度的山中土地神。\n自太古时代便为了寻求劲敌，而每日奔波着。\n\n·必杀技能会对敌方全体造成物理伤害（中）\n　并击退敌人。\n　对前排的敌人造成追加的物理伤害（特大），\n　同时大幅度击飞敌人，使其眩晕。\n　山神大人每使用一次必杀技能，\n　都会降低自身的物理防御力。\n　这个效果可以累积。\n·对魔法攻击力最高的一名敌人造成\n　魔法伤害（大），并大幅降低其技能值。\n·提升自身的技能值与行动速度。\n·当山神大人自身的生命值低于55％时，\n　攻击会变得更为凶暴，同时\n　降低自身的物理防御力。');
INSERT INTO unit_enemy_data VALUES(310003,'山神大人',310000,0,16,270,3000,1,1.6,0,0,0,'【物理】传说拥有迅捷速度的山中土地神。\n自太古时代便为了寻求劲敌，而每日奔波着。\n\n·必杀技能会对敌方全体造成物理伤害（中）\n　并击退敌人。\n　对前排的敌人造成追加的物理伤害（特大），\n　同时大幅度击飞敌人，使其眩晕。\n　山神大人每使用一次必杀技能，\n　都会降低自身的物理防御力。\n　这个效果可以累积。\n·对魔法攻击力最高的一名敌人造成\n　魔法伤害（大），并大幅降低其技能值。\n·提升自身的技能值与行动速度。\n·当山神大人自身的生命值低于55％时，\n　攻击会变得更为凶暴，同时\n　降低自身的物理防御力。');
INSERT INTO unit_enemy_data VALUES(310004,'山神大人',310000,0,16,270,3000,1,1.6,0,0,0,'【模式1】试探冒险者的实力，召唤内在的\n影子。寒风凛冽，天下险峻，自身的脆弱\n将受到考验。\n\n·对最前方的一名敌人造成物理伤害。');
INSERT INTO unit_enemy_data VALUES(310005,'山神大人',310000,0,16,270,3000,1,1.6,0,0,0,'【模式2】男子汉的疾驰可震撼大地。\n仿佛是为了庆祝这幅盛景，目出鲷也飞舞于高山之上。\n\n·必杀技能会对敌方全体造成物理伤害（中）\n　同时击退敌人。\n　对前排的敌人造成追加的\n　物理伤害（特大），并大幅度击飞敌人，\n　并使其眩晕。\n　山神大人每使用一次必杀技能，\n　都会降低自身的物理防御力。\n　这个效果可以累积。\n·提升自身的技能值与行动速度。\n·在后方展开一个降低物理防御力的领域，\n　并对敌方全体造成中等伤害。\n　当目出鲷离开后背期间，特大幅度降低\n　山神大人的魔法防御力。');
INSERT INTO unit_enemy_data VALUES(310006,'山神大人',310000,0,16,270,3000,1,1.6,0,0,0,'【模式3】给予向神发起竞争的强者最后的试炼。\n拼上彼此的一切，奔向思念之人。\n\n·必杀技能会对敌方全体造成物理伤害（中）\n　同时击退敌人。\n　对前排的敌人造成追加的\n　物理伤害（特大），并大幅度击飞敌人，\n　并使其眩晕。\n　山神大人每使用一次必杀技能，\n　都会降低自身的物理防御力。\n　这个效果可以累积。\n·对魔法攻击力最高的两名敌人造成\n　魔法伤害（大），并大幅降低其技能值。\n·提升自身的技能值与行动速度。\n·在战斗开始后，提升自身的物理攻击力，\n　同时降低自身的物理防御力。');
INSERT INTO unit_enemy_data VALUES(310200,'硬头鲨',310200,0,16,270,1000,1,1.6,0,0,0,'【物理】作为海蚀洞的支配者\n君临于此的鲨鱼魔物。将凶恶的\n欲望倾注到捕食中的海中大恶徒。\n\n·对敌方全体造成物理伤害（特大）\n　并降低其物理防御力与\n　魔法攻击力。\n\n·对前方范围内的所有敌人\n　造成魔法伤害（小），并降低其技能值与\n　技能值上升量。');
